[11586] = {"La veillée de Hurlenfer", {"Parlez à Garrosh Hurlenfer au Bastion Chanteguerre."}},
[11587] = {"La grande évasion", {"Le Bibliothécaire Donathan de l'Escarpement d'Ambre vous demande de récupérer la Clé de la prison de Béryl sur un Chasseur de mages de Béryl, à la Halte de Béryl, et de vous en servir pour libérer un mage retenu dans une Prison arcanique."}},
[11590] = {"Enlèvement", {"Donathan veut que vous utilisiez le Lieur d’arcanes sur un Ensorceleur de Béryl blessé, à la Halte de Béryl, et que vous le rameniez à l’Escarpement d’Ambre."}},
[11591] = {"Se présenter à la caravane de Mâchoire-d'acier", {"Parlez au Grunt Ragepoing à la Caravane de Mâchoire-d'acier."}},
[11592] = {"Frapper", {"Aidez le Long-coureur Sabot-fier et ses écumeurs chanteguerre à survivre à l'assaut contre la Caravane de Mâchoire-d'acier et à en finir avec l'esprit du commandant de corps Mâchoire-d'acier.","Si vous réussissez, allez faire votre rapport au Suzerain Bor'gorok à l'Avant-poste de Bor'gorok."}},
[11593] = {"Les morts honorés", {"Le Grunt Ragepoing, à la Caravane de Mâchoire-d'acier, vous demande d'utiliser sa torche pour brûler un total de 10 Gardes ou Ouvriers morts de la caravane."}},
[11594] = {"Donnez-leur le repos", {"Détruisez 20 esprits taunkas, qui peuvent être des Sages fantomatiques ou des Long-coureurs ressuscités, puis retournez voir le Grunt Ragepoing à la Caravane de Mâchoire-d'acier."}},
[11595] = {"La défense du bastion Chanteguerre", {"Présentez-vous au suzerain Razgor, à la hutte du côté sud du Bastion Chanteguerre."}},
[11596] = {"La défense du bastion Chanteguerre", {"Présentez-vous au suzerain Razgor, à la hutte du côté sud du Bastion Chanteguerre."}},
[11597] = {"La défense du bastion Chanteguerre", {"Présentez-vous au suzerain Razgor, à la hutte du côté sud du Bastion Chanteguerre."}},
[11598] = {"Reprendre la carrière de pierres de pouvoir", {"Le suzerain Razgor du Bastion Chanteguerre vous demande de tuer 15 Nérubiens nérub'ar."}},
[11599] = {"Thassarian, mon frère", {"Partez à la recherche de William Allerton à l'intérieur de la Mine de Comté-lointaine."}},
[11600] = {"Feu William Allerton", {"Apportez la Carte d'enrôlement à Leryssa au Donjon de la Bravoure, dans la Toundra Boréenne."}},
[11601] = {"Objets trouvés", {"Parlez à James Lediacre à l’auberge du Donjon de la Bravoure."}},
[11602] = {"Assécher la source", {"Le suzerain Razgor du Bastion Chanteguerre vous demande de détruire 10 Sacs d'œufs nérub'ar."}},
[11603] = {"In vino veritas", {"Repêchez une bouteille de Vin de Kul Tiras dans l’épave, au nord-est du Donjon de la Bravoure, et apportez-la au Vieux Colburn à l'auberge du Donjon de la Bravoure."}},
[11604] = {"Un déserteur", {"Trouvez la prison dans le Bastion intérieur du Donjon de la Bravoure et parlez au déserteur."}},
[11605] = {"Les ancêtres honorés", {"L'Ancien Atkanok, à l'extérieur de la Carrière de Rochefroide, vous demande d'identifier les statues de l'Ancien Kesuk, l'Ancien Sagani et l'Ancien Takret."}},
[11606] = {"La patience, une vertu dont nous nous passerons bien", {"L'intendant Holgoth, au Bastion Chanteguerre, vous demande de récupérer 15 caisses de Munitions chanteguerres."}},
[11607] = {"Les esprits égarés", {"L'Ancien Atkanok, à la lisière de la Carrière de Rochefroide, vous demande de libérer les esprits de 3 Artisans de Kaskala et 3 Chamans de Kaskala."}},
[11608] = {"Enterrez-moi ces cafards !", {"L'intendant Holgoth, au Bastion chanteguerre, vous demande d'utiliser le Paquet de charges de profondeur à l'hydroglycérine pour détruire les puisards nérub'ar au sud, à l'est, à l'ouest et au nord du Bastion Chanteguerre."}},
[11609] = {"Ramasser les morceaux", {"Apportez 6 Objets rituels roharts à l'Ancien Atkanok, qui se trouve à l'extérieur de la Carrière de Rochefroide."}},
[11610] = {"Conduire les ancêtres chez eux", {"L'Ancien Atkanok vous demande de guider les esprits des Anciens Kesuk, Sagani et Takret jusqu'à leurs monuments de pierre respectifs."}},
[11611] = {"Pris par le Fléau", {"Le Contremaître Mortuus au Bastion Chanteguerre veut que vous libériez 5 Péons chanteguerre."}},
[11612] = {"Reprendre la carrière", {"Etaruk, qui campe près de l'entrée de la Carrière de Rochefroide, vous demande de tuer 12 Chasseurs de trésor de Béryl."}},
[11613] = {"Le serment de Karuk", {"Karuk vous demande de tuer 6 Ecumeurs skadir et 5 Skadirs d'équipage."}},
[11614] = {"Vérités tues", {"Trouvez le Traqueur des ombres Luther dans la Carrière de pierres de pouvoir."}},
[11615] = {"Les secrets nérub'ar", {"Apportez le Journal de Luther au Traqueur des ombres Barthus au Bastion Chanteguerre."}},
[11616] = {"Un message pour Hurlenfer", {"Apportez la Note de Barthus à Garrosh Hurlenfer au Bastion Chanteguerre."}},
[11617] = {"Gênez leur départ", {"Etaruk, à l'extérieur de la Carrière de Rochefroide, vous demande de détruire la Plate-forme nord, la Plate-forme est et la Plate-forme ouest."}},
[11618] = {"Et voilà les renforts...", {"Allez trouver le Traqueur des ombres Ickoris à l'Avant-poste des fermes chanteguerres, à l'ouest du Bastion Chanteguerre."}},
[11619] = {"Gamel le Cruel", {"Karuk vous demande de triompher de Gamel le Cruel sur la Grève des Courcinglants."}},
[11620] = {"Les paroles d'un père", {"Partez à la recherche de Veehja dans le Sanctuaire des Écailles de la Grève des Courcinglants."}},
[11621] = {"La tablette de Leviroth", {"Retournez voir Karuk avec les informations que vous avez trouvées sur la tablette."}},
[11622] = {"Les secrets des Courcinglants", {"Trouvez Veehja dans les Ruines des Courcinglants de la toundra Boréenne."}},
[11623] = {"En visite chez le conservateur", {"Etaruk, à la lisière de la Carrière de Rochefroide, vous demande de tuer le Conservateur Insivius."}},
[11624] = {"Le ciel saura", {"Allez voir Impéréen dans les Ruines d'Eldra'nath."}},
[11625] = {"Le trident de Naz'jan", {"Veehja, aux Courcinglants, vous demande d'aller à l'extrémité nord-est des ruines des Courcinglants. Une fois là-bas, tuez Ragnar Drakkarlund et prenez-lui le Trident de Naz'jan."}},
[11626] = {"L'émissaire", {"Trouvez Leviroth sous l'iceberg flottant, le long de l'extrémité nord des Ruines des Courcinglants. Utilisez le Trident de Naz'jan pour le tuer, puis retournez voir Karuk au nord de la Grève des Courcinglants."}},
[11627] = {"Le point d'ébullition", {"Impéréen vous demande de faire entendre raison à Frémiss et Bouillonn. Retournez la voir aux Ruines d'Eldra'nath quand ils auront plié."}},
[11628] = {"Le voile du Fléau", {"Le Chef Trombe-de-l'hiver à Taunka'le veut que vous lui apportiez 5 Fourrures de mammouths flaellés."}},
[11629] = {"Retourner voir la spirite", {"Apportez la Prime-essence d'Impéréen à la Spirite Grognecroc, à l'Avant-poste de Bor'gorok."}},
[11630] = {"La mauvaise terre", {"Rapportez 8 mottes de Terre flaellée au Chef Trombe-de-l'hiver à Taunka'le."}},
[11631] = {"Vision de l'Air", {"Utilisez la Prime-essence d'Impéréen pour accomplir un rituel devant le Totem de Grognecroc, à l'Avant-poste de Bor'gorok, afin de découvrir des informations sur le Long-voyant Marche-sinistre.","Ensuite, allez parler à la Spirite Grognecroc."}},
[11632] = {"Ce qu'apporte le vent froid…", {"Apportez la Carapace durcie d'Ith'rix au Haut seigneur Saurcroc au Bastion Chanteguerre."}},
[11633] = {"Se fondre dans le paysage", {"Le Chef Trombe-de-l'hiver à Taunka'le veut que vous portiez le Voile du Fléau imprégné dans la Ville-temple d’En’kilah, et que vous alliez explorer la Flèche du Sang, la Flèche de la Douleur et la Flèche de la Décomposition."}},
[11634] = {"Le maître du vent To'bor", {"Présentez-vous au Maître du vent To'bor au Bastion Chanteguerre."}},
[11635] = {"L'esprit du long-voyant Marche-sinistre", {"Parlez à l’Esprit du long-voyant Marche-sinistre, à l’intérieur de Magmoth."}},
[11636] = {"Un tour en tapis volant", {"Parlez à Yanni au Bastion Chanteguerre pour organiser votre voyage jusqu’à la côte. Une fois au Point d’accostage de Garrosh, trouvez l’annihilateur Grek’lor."}},
[11637] = {"Kaganishu", {"Retrouvez le Fétiche de Kaganishu et utilisez-le près des Restes du long-voyant Marche-sinistre.","Vous devrez donner le fétiche à l’Esprit du long-voyant Marche-sinistre dans Magmoth."}},
[11638] = {"Rapatrier mes restes", {"Remettez les Restes du long-voyant Marche-sinistre, qui reposent en dessous de son esprit, à la Spirite Grognecroc, à l’Avant-poste de Bor’gorok."}},
[11639] = {"La vengeance s'abat sur Magmoth", {"Tuez 10 Chamans de Magmoth, 5 Fourrageurs de Magmoth, 3 Écraseurs de Magmoth et 3 Compagnes de Magmothregar. Lorsque ce sera fait, retournez voir Ortrosh à l’Avant-poste de Bor’gorok."}},
[11640] = {"Les mots de puissance", {"Apportez le Parchemin du grand prêtre Naferset, le Parchemin du grand prêtre Talet-Kha et le Parchemin du grand prêtre Andorath au Chef Trombe-de-l'hiver à Taunka'le."}},
[11641] = {"Une frappe courageuse", {"Tuez 15 Goules d’En’kilah et 5 Nécromanciens d’En’kilah, puis allez faire votre rapport à Durm Cuir-de-glace à Taunka’le."}},
[11642] = {"Un char, ça s'répare pas tout seul", {"Parlez à Mobu au Point d'accostage de Garrosh."}},
[11643] = {"Le transbidulotron pneumatique pour char de Mobu", {"Mobu, sur la Côte des Échos dans la Toundra Boréenne veut que vous récupériez son Transbidulotron pneumatique pour char."}},
[11644] = {"Des plaques de métal ultra-résistantes !", {"Mobu, sur la Côte des Échos dans la Toundra Boréenne veut que vous récupériez 10 Plaques de métal ultra-résistantes."}},
[11645] = {"Stupides frigbolds joufflus !", {"Récupérez 10 boîtes de Matériel de Fufute et rapportez-les à Fufute Tremblerouage sur la Piste d'atterrissage de Spumelevier."}},
[11646] = {"L'inquisition boréenne", {"Donathan vous a ordonné de proposer votre aide au Bibliothécaire Normantis dans la tour des mages de l'Escarpement d'Ambre."}},
[11647] = {"Neutraliser les chaudrons", {"La Sage Aeire de Taunka'le vous demande d'utiliser son totem pour neutraliser le Chaudron de l'est, le Chaudron central et le Chaudron de l'ouest dans la Ville-temple d'En'kilah."}},
[11648] = {"L'art de la persuasion", {"Le Bibliothécaire Normantis de l'Escarpement d'Ambre vous demande d'utiliser l'Aiguillonneur neural sur l'Ensorceleur de Béryl emprisonné jusqu'à ce qu'il révèle où se trouve dame Evanor."}},
[11649] = {"Granules des enragés", {"Rassemblez 5 Granules de tempête et rapportez-les à Impéréen aux Ruines d'Eldra'nath."}},
[11650] = {"Encore quelques bricoles…", {"Récupérez les Outils de Fufute, un Condensateur surchargé et une Poignée de sable de Rocnar. Apportez-les à Fufute Tremblerouage à la Piste d'atterrissage de Spumelevier."}},
[11651] = {"Le char l'attend", {"Parlez à l’Annihilateur Grek’lor sur la Côte des Échos."}},
[11652] = {"Les plaines de Nasam", {"Utilisez le Char de siège de la Horde pour détruire 100 Soldats du Fléau et secourir 3 Soldats chanteguerre blessés, sur les Plaines de Nasam. Vous devez aussi découvrir l’identité du chef de l’attaque du Fléau.","Allez ensuite faire votre rapport à Garrosh Hurlenfer au Bastion Chanteguerre si vous réussissez."}},
[11653] = {"Ha ! Z'êtes plus si grands que ça maintenant !", {"Testez la Pétoire Raccourcissante Proto-Typique Ultra-Avancée de Fufute sur 5 Écraseurs de Magmoth ou Compagnes de Magmothregar. Quand ce sera fait, retournez voir cette dernière à la Piste d'atterrissage de Spumelevier."}},
[11654] = {"La flèche du Sang", {"Fouillez la Flèche du Sang à la recherche d'informations sur la Fiole de sang frais."}},
[11655] = {"Dans la brume", {"Waltor, de Pal'ea sur la Côte des Échos dans la Toundra Boréenne, vous demande de tuer des kvaldirs pour récupérer 8 Reliques roharts."}},
[11656] = {"Brûler en effigie", {"Waltor de Pal’ea, sur la Côte des Échos de la Toundra Boréenne, veut que vous utilisiez la Torche rohart pour brûler les vaisseaux kvaldirs à Pal’ea et au Point d'accostage de Garrosh. Vous devez détruire les vaisseaux suivants : La Gueule du serpent, Le Drakkar de Kur, Le Marteau de Bor et L’Enclume de Bor."}},
[11657] = {"Attraper des torches", {"Attrapez 4 torches de suite, puis parlez au Maître mangeur de feu."}},
[11658] = {"Plan B", {"Rapportez 10 Tenues chanteguerres et une Bannière chanteguerre à Fufute Tremblerouage à la Piste d'atterrissage de Spumelevier."}},
[11659] = {"Brisez les orbes !", {"Détruisez 5 Globes de sang et allez avertir le Pisteur des neiges Junek à Taunka'le."}},
[11660] = {"Le cor du vieux marinier", {"Waltor, de Pal'ea sur la Côte des Échos dans la Toundra Boréenne, vous demande de lui apporter le Cor du vieux marinier."}},
[11661] = {"Orabus le Timonier", {"Waltor de Pal’ea, sur la Côte des échos, dans la Toundra Boréenne, veut que vous utilisiez le Cor du vieux marinier, pour appeler Orabus le Timonier. Quand vous l’aurez appelé, tuez-le."}},
[11662] = {"Allez trouver Karuk !", {"Allez trouver Karuk sur la Grève des Courcinglants."}},
[11663] = {"Rapporter les informations", {"Normantis vous a demandé de vous présenter au Bibliothécaire Donathan à l'Escarpement d'Ambre."}},
[11664] = {"S'échapper de la brume", {"Suivez Meuhtou la Jeune hors de la brume. Parlez à l'Ancien Meuhtou si vous parvenez à sauver Meuhtou la Jeune."}},
[11665] = {"Les crocilisques sont lâchés", {"Apportez un Bébé crocilisque au vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath, au bord du lac Silmyr."}},
[11666] = {"Succomber à vos appâts", {"Apportez un Dard aileron-noir au vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath, au bord du lac Silmyr."}},
[11667] = {"Celui qui s'échappa", {"Attrapez le Plus grand éperlan du monde et apportez-le au Vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath au bord du lac Silmyr."}},
[11668] = {"Les crevettes, c'est pas pour les mauviettes", {"Le Vieux Barlo veut 10 Crevettes géantes d'eau douce. Vous pouvez le trouver au nord-est de Shattrath, près du lac Silmyr."}},
[11669] = {"Filet de gangresang", {"Apportez un Lutjan gangresang monstrueux au Vieux Barlo. Vous le trouverez en train de pêcher au nord-est de Shattrath, au bord du lac Silmyr."}},
[11670] = {"C'était les orcs, j'vous jure !", {"Plantez la Bannière chanteguerre dans le cadavre de Magmothregar en portant le Déguisement d'orc chanteguerre. Puis rapportez le déguisement à Fufute Tremblerouage à la Piste d'atterrissage de Spumelevier."}},
[11671] = {"Une course contre la montre", {"Donathan de l'Escarpement d'Ambre vous a chargé d'utiliser le Détonateur contre le bouclier de Béryl afin d'abattre le bouclier à la Halte de Béryl. Vous devez ensuite vaincre Salrand et prendre la clé qu'elle détient."}},
[11672] = {"Le jour de l'incorporation", {"Rencontrez le Général Arlos à l'intérieur de la caserne du donjon de la Bravoure."}},
[11673] = {"Sortez-moi de là !", {"Escortez Bonker Virevolt jusqu'en lieu sûr puis présentez-vous à Spumelevier Pleingaz à la Piste d'atterrissage de Spumelevier."}},
[11674] = {"La sage Haute-mesa a disparu", {"Trouver la Sage Haute-mesa près des Tanières du Trépas."}},
[11675] = {"Une mort décente", {"Détruisez 10 Magnataures pestiférés puis retournez voir la Sage Haute-mesa au sud des Tanières du Trépas."}},
[11676] = {"Le cadeau de la liberté", {"Le traqueur des ombres Canarius, à l'Avant-poste des fermes chanteguerres, vous demande de libérer 5 Prisonniers du Fléau."}},
[11677] = {"Mettre fin à la peste", {"La Sage Haute-mesa, qui se trouve juste au sud des tanières du Trépas, vous a demandé d'utiliser les Graines purificatrices de Haute-mesa à un Chaudron de peste des tanières du Trépas, afin de le neutraliser."}},
[11678] = {"Trouvez Corne-dressée", {"Trouvez le Long-coureur Corne-dressée à Talramas."}},
[11679] = {"Reforger la clé", {"Donathan veut que vous parliez à Surristrasz sur l'Escarpement d'Ambre."}},
[11680] = {"L'envol", {"Lorsque vous serez prêt à libérer Evanor, donnez la Clé de Salrand au Mage de guerre Anzim."}},
[11681] = {"Au secours d'Evanor", {"Anzim, de l'Escarpement d'Ambre, vous demande de vous envoler avec ses mages de guerre pour libérer dame Evanor à la Halte de Béryl. Vous devrez ensuite lui faire votre rapport dans la tour des mages de l'Escarpement d'Ambre."}},
[11682] = {"Jargon & dragons", {"Dame Evanor vous a envoyé parler à Surristrasz sur l'Escarpement d'Ambre."}},
[11683] = {"La nécropole écrasée", {"Détruisez 20 créatures du Fléau de n’importe quel type à Talramas et dans les Bassins Purulents. Lorsque ce sera fait, retournez voir la Sage Haute-mesa, juste au sud des Tanières du Trépas."}},
[11684] = {"Reconnaissance des puisards", {"Grandpère Mahan, à Taunka'le, vous demande d'utiliser la Carte des champs de Geysers afin d'y écrire l'emplacement du Puisard sud, du Puisard nord-ouest et du Puisard nord-est."}},
[11685] = {"Le cœur des éléments", {"Apportez 5 Cœurs élémentaires à la Dompteuse des vents Barah à Taunka'le."}},
[11686] = {"Les fermes chanteguerre", {"Le traqueur des ombres Ickoris, à l'Avant-poste des fermes chanteguerres, vous demande d'explorer le Grenier chanteguerre, la Ferme de Torp et l'Abattoir chanteguerre."}},
[11687] = {"Le docteur et le seigneur-liche", {"Détruisez le Docteur Trancherictus et le Seigneur-liche Frisshiver. Puis retournez voir la Sage Haute-mesa juste au sud des Tanières du Trépas."}},
[11688] = {"Saleté de cochonnerie de pourceaux", {"Le fermier Torp, à l’Avant-poste des fermes chanteguerres, veut que vous tuiez 10 Pourceaux non-vivants."}},
[11689] = {"Transmettre les mauvaises nouvelles", {"Parlez à la Grande-mère Taiga à Taunka'le."}},
[11690] = {"Ram'nez-les vivants", {"Le fermier Torp, à l’Avant-poste des fermes chanteguerres, veut que vous sauviez 8 Kodos infectés."}},
[11691] = {"L'invocation d'Ahune", {"Apportez le Totem de magma du Cercle terrestre à la Pierre de glace."}},
[11692] = {"Allez voir Bixie", {"Mordle Tournepignon vous a demandé de trouver Bixie Tournechancre au sud des Tanières du Trépas."}},
[11693] = {"Super… des magnataures pestiférés !", {"Tuez 10 Magnataures pestiférés puis retournez voir Bixie Tournechancre au sud des Tanières du Trépas."}},
[11694] = {"Il se passe quelque chose dans ces grottes", {"Bixie Tournechancre, qui se planque au sud des Tanières du Trépas, veut que vous utilisiez la Poudre inhibitrice de Bixie sur un Chaudron de peste des Tanières du Trépas, afin de le neutraliser."}},
[11695] = {"Le cor de fureur élémentaire", {"Reprenez la Moitié inférieure du cor et la Moitié supérieure du cor au Chef Brouillegargouille et au Chef Grouillebarbouille et apportez-les à la Dompteuse des vents Barah à Taunka'le."}},
[11696] = {"Ahune est là !", {"Trouvez Luma Mère-des-cieux dans les Enclos aux esclaves."}},
[11697] = {"Zut, Bricole est entrée dans la nécropole !", {"Trouvez Bricole Sifflemèche à Talramas."}},
[11698] = {"On pourrait aussi bien éradiquer le Fléau", {"Détruisez 20 créatures du Fléau de n'importe quel type à Talramas et aux Bassins Purulents. Puis retournez voir Bixie Tournechancre juste au sud des Tanières du Trépas."}},
[11699] = {"Je suis coincée dans cette maudite cage… mais pas pour longtemps !", {"Récupérez un Cristal du bloc-moteur, un Gyroscope magique et une Pierre du Fléau de pilotage, et remettez-les à Bricole Sifflemèche, à Talramas."}},
[11700] = {"Prévenez Bixie", {"Parlez à Bixie Tournechancre juste au sud des Tanières du Trépas."}},
[11701] = {"Retour à la piste d'atterrissage", {"Parlez à Spumelevier Pleingaz sur la Piste d'atterrissage de Spumelevier, dans la Toundra Boréenne."}},
[11702] = {"Le roi Mrgl-Mrgl", {"Allez voir le Roi Mrgl-Mrgl à la Retraite des Ailerons-d'hiver."}},
[11703] = {"Rallier Getry", {"Allez voir le Traqueur des ombres Getry aux Fermes chanteguerre."}},
[11704] = {"Le roi Mrgl-Mrgl", {"Parlez au Roi Mrgl-Mrgl à la Retraite des Ailerons-d'hiver."}},
[11705] = {"Opération absurde", {"Aidez Getry à vaincre Varidus le Dépiauteur aux Fermes chanteguerre.","Faites votre rapport à Garrosh Hurlenfer si vous deviez réussir."}},
[11706] = {"L'effondrement", {"La Dompteuse des vents Barah, de Taunka'le, vous demande de faire s'effondrer les puisards nérubiens à l'aide du Cor de fureur élémentaire et de vaincre le seigneur Kryxix."}},
[11707] = {"Appel de détresse", {"Allez voir Spumelevier Pleingaz à la Piste d'atterrissage de Spumelevier."}},
[11708] = {"Les mécagnomes", {"Écoutez l'histoire de Spumelevier Pleingaz puis allez voir Voltij Lazimuté à la Piste d'atterrissage de Spumelevier."}},
[11709] = {"La charge de Nork Frénésie-sanglante", {"Parlez au Gardien Nork Frénésie-sanglante au Bastion Chanteguerre, dans la Toundra Boréenne."}},
[11710] = {"Transmatière à rire", {"Ramassez 15 Pièces détachées de Spumelevier et rapportez-les à Mordle Tournepignon à la Piste d'atterrissage de Spumelevier."}},
[11711] = {"Livraison de lâche… En moins de 30 minutes, ou c'est gratuit", {"Emmenez le Déserteur de l'Alliance à la croisée des chemins à l'est du Bastion Chanteguerre puis utilisez le Lance-fusée chanteguerre pour envoyer un signal à l'Alliance.","Allez rendre compte à l'Éclaireur Tungok une fois que le Déserteur de l'Alliance sera bien arrivé."}},
[11712] = {"Le re-maudisseur", {"Voltij Lazimuté, à la Piste d'atterrissage de Spumelevier, vous a demandé d'utiliser l'Injecteur transmatière re-maudisseur sur 6 Mécagnomes de Spumelevier."}},
[11713] = {"Reconnaissance des puisards", {"Abner Patranivelle, à la Piste d’atterrissage de Spumelevier, vous demande d'utiliser la Carte des champs de Geysers afin d'y marquer l'emplacement du Puisard sud, du Puisard nord-ouest et du Puisard nord-est."}},
[11714] = {"Extermination de la vermine", {"L'éclaireur Tungok, aux Plaines de Spore-sang dans la Toundra Boréenne, vous a demandé de tuer 8 Moissonneurs spore-sang, 5 Boute-flammes spore-sang et 2 Rôtisseurs spore-sang."}},
[11715] = {"Faire carburer le projet", {"Abner Patranivelle, à la Piste d’atterrissage de Spumelevier, vous demande de récupérer 8 tonneaux de Naphte à l'aide du Collecteur de naphte portable."}},
[11716] = {"Spore-sang merveilleuse !", {"La Mage de sang Laurith, des Plaines de Spore-sang dans la Toundra Boréenne, vous a demandé de récupérer 10 Carpelles spore-sang."}},
[11717] = {"Le pollen à sa source", {"La Mage de sang Laurith, des Plaines de Spore-sang dans la Toundra Boréenne, vous a demandé de récupérer 5 échantillons de Pollen de phalène spore-sang."}},
[11718] = {"L'habit fera le mammouth", {"Abner Patranivelle, à la Piste d’atterrissage de Spumelevier, veut que vous lui rapportiez 6 Peaux de mammouth épaisses."}},
[11719] = {"Un cobaye adéquat", {"La Mage de sang Laurith, des Plaines de Spore-sang dans la Toundra Boréenne, vous a demandé d’utiliser la Fleur de spore-sang pollinisée et de la laisser examiner le résultat."}},
[11720] = {"L'invasion de Gammoth", {"Parlez à l'Aîné Corne-de-pouvoir dans les Plaines de Spore-sang, dans la Toundra Boréenne"}},
[11721] = {"Gammothra le Tourmenteur", {"Tuez Gammothra et rapportez la Tête de Gammothra à l'Aîné Corne-de-pouvoir dans les Plaines de Spore-sang, dans la Toundra Boréenne."}},
[11722] = {"Les trophés de Gammoth", {"Apportez la Tête de Gammothra à Garrosh Hurlenfer au Bastion Chanteguerre."}},
[11723] = {"Déployez le Sismo-trembleur !", {"Abner Patranivelle, à la Piste d’atterrissage de Spumelevier, vous demande d'utiliser le Sismo-trembleur 5000 pour faire s'effondrer les tunnels nérubiens et de vaincre le Seigneur Kryxix."}},
[11724] = {"Une gigantesque omelette de phalènes ?", {"Apportez l'Oeuf massif luminescent à la Mage de sang Laurith dans les Plaines de Spore-sang, dans la Toundra Boréenne."}},
[11725] = {"Trouver le pilote la Vrille", {"Trouvez Iggy \"la Vrille\" Virouage dans les champs de Geysers."}},
[11726] = {"Un peu de piquant", {"Iggy \"la Vrille\" Virouage, dans les Bassins Brûlants, vous demande de ramasser 4 Bourses d'épices de Gorloc."}},
[11727] = {"L'époque est aux héros", {"Présentez-vous au Sergent Martèlemont au Donjon de la Bravoure."}},
[11728] = {"Lupus Pupus", {"Iggy \"la Vrille\" Virouage, dans les Bassins Brûlants, vous demande de lui apporter 8 morceaux de Microfilm."}},
[11729] = {"Le Tournevis Ultrasonique", {"Apportez le Tournevis Ultrasonique à Fufute Tremblerouage à la Piste d'atterrissage de Spumelevier."}},
[11730] = {"Maître et serviteur", {"Utilisez le Tournevis ultrasonique pour reprogrammer 15 robots, puis retournez voir Fufute Tremblerouage à la Piste d'atterrissage de Spumelevier.","N'oubliez pas de prendre votre butin sur le robot avant de le reprogrammer."}},
[11731] = {"Lancer des torches", {"Gagnez le jeu de lancer de torches, puis parlez au Maître mangeur de feu."}},
