[11791] = {"Avertir Arlos", {"Parlez au Général Arlos dans le Bastion intérieur au Donjon de la Bravoure."}},
[11792] = {"Les ennemis de la Lumière", {"Le Messager Vurenn vous demande d'aller combattre les Sectateurs nécrolytes près de Comté-lointaine au nord du Donjon de la Bravoure et de récupérer le Communiqué des sectateurs."}},
[11793] = {"Approfondir l'enquête", {"Apportez le Communiqué des sectateurs au Redresseur de torts Yaala à l'auberge du Donjon de la Bravoure."}},
[11794] = {"La chasse est ouverte", {"Le Redresseur de torts Yaala vous demande d'utiliser l'Oculus de l'Exorciste dans trois endroits du Donjon de la Bravoure pour percer à jour et abattre des membres du Culte des damnés."}},
[11795] = {"Protocole d'urgence : section 8.2, paragraphe C", {"Récupérez 6 Insignes de pilote de Spumelevier sur les corps des Pilotes de reconnaissance de Spumelevier et rapportez-les à Iggy \"la Vrille\" Virouage, dans les Bassins Brûlants."}},
[11796] = {"Protocole d'urgence : section 8.2, paragraphe D", {"Iggy \"la Vrille\" Virouage, dans les Bassins Brûlants, vous demande de détruire l'Épave à l'est, l'Épave au sud et l'Épave au nord-ouest."}},
[11797] = {"Le siège", {"Le sergent Martèlemont vous demande de tuer 6 Rampants des cryptes dans les Sables de Nasam à l'extérieur du donjon de la Bravoure."}},
[11798] = {"Le maître des rouages", {"Trouvez le Manuel du Maître des rouages Mécazod puis rapportez sa tête à Spumelevier Pleingaz."}},
