[11865] = {"Indignes de mourir", {"Zaza, au Campement de la SdPA dans la Toundra Boréenne, vous demande d'utiliser la Pile de fausses fourrures pour piéger 8 Trappeurs de Nesingwary."}},
[11866] = {"Les oreilles de nos ennemis", {"L'Archidruide Lathorius au Campement de la SdPA dans la Toundra Boréenne vous a promis une récompense si vous lui apportez 15 Oreilles de laquais de Nesingwary."}},
[11867] = {"Des oreilles en boucle…", {"L'Archidruide Lathorius à l'Avant-poste de la SdPA dans la Toundra Boréenne vous a promis une récompense si vous lui apportez 15 Oreilles de laquais de Nesingwary."}},
[11868] = {"La Sélectionneuse arrive", {"Zaza, au Campement de la SdPA dans la Toundra Boréenne, vous demande de tuer Karen la Sélectionneuse."}},
[11869] = {"« À la pêche aux palourdes, ils n'iront plus maman »", {"La Hiérophante Cénius, du Campement de la SdPA dans la Toundra Boréenne, veut que vous tuiez 10 Plongeurs fous de butin."}},
[11870] = {"Les confins Abandonnés", {"Allez voir la Hiérophante Liandra aux Confins Abandonnés."}},
[11871] = {"Pas de ça chez nous", {"La Hiérophante Liandra, des Confins Abandonnés de la Toundra Boréenne, veut que vous récupériez 12 Cargaisons de morceaux d’animaux."}},
[11872] = {"Le sinistre maître des palourdes…", {"Tuez le Maître des palourdes K puis retournez auprès de la Hiérophante Cénius au Campement de la SdPA."}},
[11873] = {"Informez Spumelevier", {"Partez pour la Piste d'atterrissage de Spumelevier et parlez à Spumelevier Pleingaz."}},
[11874] = {"Promo Upper Deck - Monture fusée", nil},
[11875] = {"Prendre l'avantage", {"Apportez 8 Résidus de Néant à l'Émissaire Mordin, à Shattrath."}},
[11876] = {"Aidez ceux qui ne peuvent pas s'aider eux-mêmes", {"L’archidruide Lathorius, au Campement de la SdPA dans la Toundra Boréenne, veut que vous utilisiez l’Écrase-piège de la SdPA pour libérer 8 Jeunes mammouths."}},
[11877] = {"Les plans d'attaque solfurie", {"Le Seigneur Torvos, à Shattrath, veut que vous vous empariez des Plans d'attaque solfurie."}},
[11878] = {"Khu'nok saura quoi faire", {"Remettez le Jeune mammouth orphelin à Khu'nok le Béhémoth, dans la Toundra Boréenne."}},
[11879] = {"Kaw le Destructeur de mammouths", {"Tuez Kaw le Destructeur de mammouths et récupérez la Hallebarde de guerre de Kaw, puis apportez-la à l’Archidruide Lathorius au Campement de la SdPA dans la Toundra Boréenne."}},
[11880] = {"L'examen multiphase", {"Le Messager Haronem, à Shattrath, veut que vous utilisiez les Lunettes spectrographiques multiphases pour prendre 6 Relevés multiphases."}},
[11881] = {"Charge la mule !", {"Aidez Jenny à se rendre du site de l'accident à Taunka'le, où elle retrouvera Fezzix Torduvrille, sans perdre tout son chargement."}},
[11882] = {"Jouer avec le feu", {"Parlez à un Maître mangeur de feu dans n'importe quelle capitale de l'Alliance."}},
[11883] = {"La danse de feu", {"Parlez à un Maître danseur de feu dans n'importe quelle capitale de la Horde."}},
[11884] = {"Ned, seigneur des rhinos…", {"Killinger le Garde-tanière, du Campement de la SdPA dans la Toundra Boréenne, veut que vous alliez tuer Nedar le Seigneur des rhinos et son compagnon « Musette »."}},
[11885] = {"Lignées antagonistes", {"Trouvez les Piles de crânes au milieu des cercles d'invocation de Skettis. Invoquez et vainquez chacun des descendants des adversaires de Terokk en utilisant 10 Parchemins perdus dans le temps à la Pile de crâne. Retournez ensuite auprès de Hazzik au Raie'odrome de Noirvent."}},
[11886] = {"Une activité inhabituelle", {"Rendez-vous au nouveau Camp du Crépuscule, au sud de l'entrée des Profondeurs de Brassenoire sur la grève de Zoram, dans Orneval, et récupérez la Correspondance du crépuscule sur les sectateurs. Puis appelez le Guide du Cercle terrestre en utilisant la Balise totémique."}},
[11887] = {"Fournitures d'urgence", {"Ramassez 7 Trousses d'urgence gnomes sur les corps de Pilotes de reconnaissance de Spumelevier accidentés et apportez-les à Fezzix Torduvrille à Taunka'le."}},
[11888] = {"Chevaucher jusqu’à Taunka’le", {"Parlez au Sage Terre-et-Ciel à Taunka’le."}},
[11889] = {"La mort vient d'en haut", {"Le Sergent Martèlemont veut que vous utilisiez le Filet renforcé pour tuer 6 Cracheurs de flammes flaellés, à l'extérieur du Donjon de la Bravoure."}},
[11890] = {"Que préparent-ils ?", {"Explorez la zone qui entoure la Station de pompage de Spumelevier. Ensuite, retournez voir le Sage Terre-et-Ciel à Taunka'le."}},
[11891] = {"Un déguisement innocent", {"Servez-vous de l'Orbe du clampant pour espionner le nouveau camp du Crépuscule, à l'ouest des Profondeurs de Brassenoire, sur la grève de Zoram, en Orneval. Ensuite, invoquez le Guide du Cercle terrestre à l'aide de la Balise totémique."}},
[11892] = {"L'assassinat de Harold Lane", {"L’archidruide Lathorius à l’Avant-poste de la SdPA dans la Toundra Boréenne veut que vous tuiez Harold Lane."}},
[11893] = {"La puissance des éléments", {"Dorain Sabot-de-givre, de Taunka'le, vous demande d'utiliser le Totem âmevent pour emmagasiner l'énergie de 10 Enragés de vapeur."}},
[11894] = {"Quelques réparations", {"Fezzix Torduvrille, à Taunka'le, vous demande de lui apporter 5 Peaux traitées à la vapeur."}},
[11895] = {"Maîtrisez l'orage", {"Terrassez la Tempête d'orage puis retournez voir le Sage Terre-et-Ciel à Taunka'le."}},
[11896] = {"Vulnérables à la foudre", {"Détruisez 15 unités robotisées (mais pas de mécagnomes) affaiblies par la Puissance de l'orage dans la zone des bassins qui entourent la Station de pompage de Spumelevier.","Ensuite, retournez voir le Sage Terre-et-Ciel à Taunka'le."}},
[11897] = {"Boucher les puisards", {"Le Sergent Martèlemont veut que vous utilisiez les Explosifs incendiaires pour faire s'effondrer les puisards qui se trouvent au sud-ouest et au nord-ouest du Donjon de la Bravoure."}},
[11898] = {"L'intrusion", {"Abattez le Prince Valanar et ses lieutenants, Vanthryn l'Impitoyable et Luthion l'Infâme, puis faites votre rapport au Chef Trombe-de-l'hiver à Taunka'le."}},
[11899] = {"Les âmes dé-maudites", {"Utilisez le Capturâme de la grande-mère pour emprisonner les âmes de 10 Mécagnomes de Spumelevier. Quand ce sera fait, retournez voir la Grande-mère Taiga à Taunka'le."}},
[11900] = {"Relever les compteurs", {"L'Archimage Bérinand au Bouclier Transitus vous a demandé de récupérer les relevés de ses appareils de mesure à Frimarra."}},
[11901] = {"L'armée, quelle armée ?", {"Gerald Green, de Comté-lointaine, veut que vous alliez dans la mine, au nord, et que vous découvriez ce qui est arrivé au détachement qui y a été envoyé."}},
[11902] = {"Une preuve pernicieuse", {"Retournez voir Gerald Green à Comté-lointaine."}},
[11903] = {"Il est temps d'agir", {"Gerald Green, à Comté-lointaine, vous demande de tuer 14 Charognards pestiférés."}},
[11904] = {"Les fruits de notre travail", {"Allez dans la mine de Comté-lointaine, prenez la Clé de déblocage du chariot au capitaine Jacobs et utilisez-la pour déverrouiller le Chariot à minerai. Retournez voir Gerald Green à Comté-lointaine quand ce sera fait."}},
[11905] = {"Retarder l'inévitable", {"L'Archimage Bérinand, au Bouclier Transitus, vous demande d'utiliser le Refabricateur interdimensionnel près de la faille du Nexus."}},
[11906] = {"Nettoyer les bassins", {"Ramassez 15 Pièces détachées de Spumelevier et rapportez-les à Yeux-de-fer à Taunka'le."}},
[11907] = {"Les sous-chefs", {"Désactivez Twonky, ED-210, Max Blasto et Le Broyeur, puis retournez voir le Sage Terre-et-Ciel à Taunka'le."}},
[11908] = {"Un ouvrage de référence", {"Jeremiah Hawning vous demande de récupérer le livre \"Chantiers, Usines et Ateliers\" dans le bâtiment en feu à côté de la mine de Comté-lointaine."}},
[11909] = {"Abattre le maître des rouages", {"Trouvez le Manuel du Maître des rouages Mécazod puis rapportez sa tête à la Grande-mère Taiga à Taunka'le."}},
[11910] = {"Les secrets des Anciens", {"L'Archimage Bérinand, au Bouclier Transitus, vous demande de collecter 3 Echardes glaciaires et 3 Echardes liées par magie sur les Anciens de Frimarra."}},
[11911] = {"Métamorphose", {"L'Archimage Bérinand, au Bouclier Transitus, vous demande de pénétrer dans le Nexus et de collecter 5 Echardes arcaniques sur les Protecteurs cristallins."}},
[11912] = {"Fou de baies", {"La Bibliothécaire Serrah, au Bouclier Transitus, vous demande de cueillir 10 Givrelles sur les Buissons de givrelles de Frimarra."}},
[11913] = {"Ne prenons pas de risques", {"Wendy Darren vous demande d'utiliser la Torche de Wendy pour brûler 8 sacs de Céréales de Comté-lointaine."}},
[11914] = {"Conserver le secret", {"La Bibliothécaire Serrah, au Bouclier Transitus, vous demande de tuer des Serpents arcaniques près du Nexus et de prendre 5 Essences de mana sur eux."}},
[11915] = {"Jouer avec le feu", {"Parlez à un Maître avaleur de flammes dans n'importe quelle capitale de la Horde."}},
[11916] = {"Le champion de Hurlenfer", {"Allez voir le chef Trombe-de-l’hiver à Taunka’le."}},
[11917] = {"Contre-attaquer", nil},
[11918] = {"Entraînement de base", {"Raelorasz, au Bouclier Transitus, vous demande de tuer 10 Tisse-sorts de Frimarra."}},
[11919] = {"La chasse au drake", {"Raelorasz vous a demandé d'utiliser la Lance de Raelorasz pour soumettre un Jeune drake du Nexus, et de le ramener au Bouclier Transitus."}},
[11920] = {"Des sectateurs parmi nous", {"Parlez au Capitaine Lougres \"le Gaucher\" sur le Brise-tempête au Donjon de la Bravoure."}},
[11921] = {"Lancer plus de torches", {"Gagnez le jeu de lancer de torches, puis parlez au Maître mangeur de feu."}},
[11922] = {"Lancer des torches", {"Gagnez le jeu de lancer de torches, puis parlez au Maître avaleur de flammes."}},
[11923] = {"Attraper des torches", {"Attrapez 4 torches de suite, puis parlez au Maître avaleur de flammes."}},
[11924] = {"Attraper plus de torches", {"Attrapez 10 torches de suite, puis parlez au Maître mangeur de feu."}},
[11925] = {"Attraper plus de torches", {"Attrapez 10 torches de suite, puis parlez au Maître avaleur de flammes."}},
[11926] = {"Lancer plus de torches", {"Gagnez le jeu de lancer de torches, puis parlez au Maître avaleur de flammes."}},
[11927] = {"La rumeur de la rue", {"Trouvez Leryssa à l'auberge du donjon de la Bravoure."}},
[11928] = {"Comté-lointaine", {"Partez pour Comté-lointaine dans la Toundra Boréenne et parlez à Gerald Green."}},
[11929] = {"La chute de Taunka'le", {"Parlez à Mère Tauranook à la sortie de Taunka'le."}},
[11930] = {"La traversée de Transborée", {"Escortez les Évacués de Taunka'le à travers Transborée jusqu’à Priseguerre Né-de-glace, à la frontière de la Désolation des dragons."}},
[11931] = {"Percer le code", {"Raelorasz, au Bouclier Transitus, vous demande de prendre le Codex azur à l'Inquisiteur Caleras et de récupérer 3 Runes chatoyantes sur ses Lieurs de sorts de Frimarra."}},
[11932] = {"Des lâches et des imbéciles", {"Parlez à Ataika à Kaskala, au nord-est du Donjon de la Bravoure."}},
[11933] = {"Voler la flamme de l'Exodar", {"Rapportez la Flamme de l'Exodar au Conteur de la fête."}},
[11935] = {"Voler la flamme de Lune-d'argent", {"Rapportez la Flamme de Lune-d'argent au Maître des traditions de la fête."}},
[11936] = {"L'éclosion d'un plan", {"Raelorasz, au Bouclier Transitus, vous a demandé de tuer des Wyrmides de Frimarra et d'utiliser leurs haches glacées pour casser 5 œufs de dragons bleus."}},
[11937] = {"MARQUEUR - Toutes les quêtes des torches sont terminées", nil},
[11938] = {"Gagner un peu de temps", {"Thassarian, à la Ziggourat Gémissante dans la Toundra Boréenne, vous demande d'infliger 20 pertes aux troupes du Fléau dans la Ville-temple d'En'kilah. Utilisez les Os de Lourid si vous avez besoin d'aide."}},
[11939] = {"?????", nil},
[11940] = {"La chasse au drake", {"Raelorasz vous demande d'utiliser la Lance à pointe sanglante pour soumettre un Jeune drake du Nexus et de le rapporter au Bouclier Transitus."}},
[11941] = {"Bizarre…", {"Apportez le Fragment scintillant à Raelorasz au Bouclier Transitus."}},
[11942] = {"Mots de pouvoir", {"Thassarian, à la Ziggourat Gémissante, vous demande d'aller à la Ville-temple d'En'kilah et de récupérer le Parchemin du grand prêtre Talet-Kha dans la Flèche de la Douleur, le Parchemin du grand prêtre Naferset dans la Flèche de la Décomposition et le Parchemin du grand prêtre Andorath dans la Flèche du Sang."}},
[11943] = {"La cellule", {"Raelorasz, au Bouclier Transitus, vous demande de vous procurer l'Armature de la prison sur le Général Céruléan, et le Noyau énergétique sur le Porteguerre Draksang."}},
[11944] = {"Encerclés !", {"Le Caporal Venn, au Séjour de la Mort, veut que vous tuiez 7 Défenseurs de la ziggourat."}},
[11945] = {"Se préparer au pire", {"Utaik vous demande de ramasser 8 Fournitures de Kaskala à Kaskala."}},
[11946] = {"Keristrasza", {"Keristrasza vous demande de lui parler quand vous serez prêt à préparer la confrontation avec Malygos.","Si vous perdez votre Prison arcanique améliorée, parlez à Raelorasz au Bouclier Transitus."}},
[11947] = {"Contre-attaquer", nil},
[11948] = {"Contre-attaquer", nil},
[11949] = {"Pas sans nous battre !", {"Ataika vous demande de tuer 12 Écumeurs kvaldirs à Kaskala."}},
[11950] = {"La sagesse de Muahit", {"Trouvez l'Ancien Muahit à Kaskala."}},
[11951] = {"Appâter le dragon", {"Keristrasza vous a demandé de collecter 10 Éclats de mana cristallisé dans les alentours de Frimarra.","Si vous perdez votre Prison arcanique améliorée, parlez à Raelorasz au Bouclier Transitus."}},
[11952] = {"Contre-attaquer", nil},
[11953] = {"Contre-attaquer", {"Tuez le Lieutenant glaciaire invoqué par la Pierre de glace située dans le défilé du Crépuscule, au nord de Silithus, puis retournez voir un Ancien du Cercle terrestre dans l'une des capitales."}},
[11954] = {"Contre-attaquer", {"Tuez le Templier glaciaire invoqué par la Pierre de glace située près de la Porte des ténèbres dans la Péninsule des flammes infernales, puis retournez voir un Ancien du Cercle terrestre dans l'une des capitales."}},
[11955] = {"Ahune, le seigneur du Givre", {"Voyagez jusqu'à l'Enclos aux esclaves, dans le Réservoir de Glissecroc, au Marécage de Zangar, et parlez à Numa Sœur-des-nuages."}},
[11956] = {"Trouver le phylactère", {"Allez au Séjour de la Mort, puis chevauchez Vespéran jusqu'à l'endroit où se trouve le Phylactère de Thanathal. Récupérez-le, puis retournez voir Thassarian dans la Ziggourat Gémissante."}},
[11957] = {"La fin de Saragosa", {"Keristrasza vous demande d'utiliser le Focalisateur de puissance pour piéger Saragosa, puis de la vaincre."}},
[11958] = {"Surtout, ne laissons rien perdre", {"Reprenez 6 lots de Marchandises moa’ki volées aux varleus, puis remettez-les au Chef Ko’nani, à Port-Moa’ki."}},
[11959] = {"Tuez Loguhn", {"Aspergez-vous du Sang de Loguhn, puis retournez voir le Chef Ko’nani à Port-Moa’ki."}},
[11960] = {"Préparer l'avenir", {"Récupérez 12 Louveteaux de la clairière de Tombeneige, puis remettez-les au Trappeur Mau’i à Port-Moa’ki."}},
[11961] = {"Que les esprits veillent sur nous", {"L'Ancien Muahit, dans la Baie du Souffle de Njord, vous demande de trouver le Cadavre d'Iruk dans l'eau et de récupérer le Totem d'Issliruk."}},
[11962] = {"Une dernière livraison", {"Apportez la Caisse de minerai de Comté-lointaine à Hilda Forge-pierre au Donjon de la Bravoure."}},
[11963] = {"Des armes pour Comté-lointaine", {"Retournez voir Gerald Green à Comté-lointaine."}},
[11964] = {"De l'encens pour les brûletins de l'été", {"Le maître des traditions de la fête vous demande d'apporter de l'Encens estival à un Brûletin d'été à l'extérieur de n'importe quelle ville de l'Alliance."}},
[11965] = {"Appel aux armes !", {"Allez dans le bâtiment de l'hôtel de ville de Comté-lointaine et faites sonner la cloche à l'intérieur."}},
[11966] = {"De l'encens pour les brûletins de la fête", {"Le conteur de la fête vous demande d'apporter de l'Encens estival à un Brûletin de la fête à l'extérieur de n'importe quelle ville de la Horde."}},
[11967] = {"Rassembler les rouges", {"Keristrasza vous a demandé de la conduire auprès de Raelorasz au Bouclier Transitus."}},
[11968] = {"Le vent tourne", {"L'Ancien Muahit vous a demandé de tuer Heigarr l'Horrible, dans la Baie du Souffle de Njord."}},
[11969] = {"Déclencher le piège", {"Raelorasz vous demande d'utiliser l'Étincelle de Raelorasz sur le feu de signal à Frimarra."}},
[11970] = {"Le maître des traditions estivales", {"Parlez au Maître des traditions de la fête dans n'importe quelle capitale de l'Alliance."}},
[11971] = {"Le conteur des légendes estivales", {"Parlez au Conteur de la fête dans n'importe quelle capitale de la Horde."}},
[11972] = {"Éclats d'Ahune", {"Apportez les Éclats de glace à Luma Mère-des-cieux."}},
[11973] = {"Prisonnière de guerre", {"Raelorasz, au Bouclier Transitus, vous demande de pénétrer dans le Nexus et de libérer Keristrasza."}},
[11975] = {"Et maintenant, quand je serai grande...", {"Conduisez votre orpheline, Salandria, à la promenade des Anciens de Lune-d'argent pour voir les Elite Tauren Chieftains. Si elle n'est pas présente lorsque vous serez sur place, n'oubliez pas de l'appeler."}},
[11976] = {"Éclats de glace", {"Apportez les Éclats de glace à Luma Mère-des-cieux."}},
[11977] = {"Une taurène parmi les taunkas", {"Allez trouver l'Émissaire Sabot-brillant au Camp de réfugiés de Ponevent."}},
[11978] = {"Il est temps de nous rejoindre", {"L’Émissaire Sabot-brillant, du Camp de réfugiés de Ponevent dans la Désolation des dragons, veut que vous récupériez 10 Armes de la Horde."}},
[11979] = {"Les taunkas et les taurens", {"Allez voir l’Émissaire Sabot-brillant au Camp de réfugiés de Ponevent."}},
[11980] = {"La fierté de la Horde", {"Le Garde de sang Roh’kill, du Camp de réfugiés de Ponevent dans la Désolation des dragons, veut que vous tuiez 15 Embusqués anub’ar."}},
[11981] = {"Trouvez Kurun !", {"Parlez à Kurun sur la colline qui domine Thor Modan."}},
[11982] = {"Que pleuve la destruction", {"Kurun, sur la colline qui domine Thor Modan, veut que vous interrompiez les efforts des nains de fer en jetant des rochers sur la partie exposée de Thor Modan."}},
[11983] = {"Le serment de sang de la Horde", {"Parlez aux taunkas du Camp de réfugiés de Ponevent et amenez 5 d’entre eux à prêter allégeance à la Horde.","Lorsque ce sera fait, rendez le Serment de sang de la Horde à l’Émissaire Sabot-brillant."}},
[11984] = {"Remplir les cages", {"Samir, des Sources de granit, veut que vous vous serviez de la Cage du chasseur de primes sur un troll drakkari qui a été étourdi par Budd."}},
[11985] = {"Dans la brèche", {"Kurun, sur la colline qui domine Thor Modan, veut que vous tuiez le Thane-de-fer Argrum."}},
[11986] = {"Le journal endommagé", {"Retrouvez les 8 Pages manquantes du journal, combinez-les avec le Journal incomplet, et remettez le journal complété à Torthen Fouillafond, au Campement de la brigade de la marche de l’Ouest."}},
[11987] = {"zzOLDLa carte porte-bonheur en argent", nil},
[11988] = {"La pierre-clé runique", {"Remettez 5 Fragments de pierre-clé runique à Torthen Fouillafond au Campement de la brigade de la marche de l’Ouest."}},
[11989] = {"Trêve ?", {"Drakuru, aux Sources de granit, veut que vous utilisiez le Couteau à découper émoussé, puis que vous lui parliez."}},
[11990] = {"La fiole aux visions", {"Drakuru, aux Sources de granit, veut que vous lui rapportiez une Fiole imprégnée, une Feuille d’élodée et 3 Feuilles de brume."}},
[11991] = {"Sujet à interprétation", {"Drakuru veut que vous récupériez 5 Mojos glacés sur les Trolls drakkari des Ruines de Drak’Zin. Lorsque ce sera fait, utilisez l’élixir au Brasero de Drakuru."}},
[11993] = {"Les prophéties runiques", {"Thorten Fouillafond, au Campement de la brigade de la marche de l’Ouest, veut que vous déchiffriez trois Plaques runiques."}},
[11994] = {"Test de drapeau de Juno", nil},
[11995] = {"Votre présence est demandée au Repos des étoiles", {"Parlez à l'Image de l'archimage Modera au Repos des étoiles."}},
[11996] = {"Votre présence est demandée au Marteau d'Agmar", {"Parlez à l'Image de l'archimage Aethas Saccage-soleil au Marteau d'Agmar."}},
[11997] = {"RÉUTILISER", {"Parlez au Capitaine Gryan Roidemantel au Campement de la brigade de la marche de l’Ouest."}},
[11998] = {"Adoucir le coup", {"Apportez à Brugar Cisaillepierre au Campement de la brigade de la marche de l’Ouest un bidon de Tord-boyaux du tonnerre."}},
[11999] = {"Fouillez les corps", {"Trouvez les Plans des jardins de Repos-de-Lune et rapportez-les à l'Image de l'archimage Aethas Saccage-soleil au Marteau d'Agmar."}},
[12000] = {"Fouillez les corps", {"Trouvez les Plans des jardins de Repos-de-Lune et rapportez-les à l'Image de l'archimage Modera au Repos des étoiles."}},
[12001] = {"zzOLDTarot de chasse : Fauves mineurs", {"Trouvez 12 Peaux déchirées, puis invoquez le Veneur avec le Tarot de chasse. Les peaux déchirées ne peuvent être obtenues que sur des bêtes de niveaux 5 ou supérieur."}},
[12002] = {"Frères d'armes", {"Tuer 10 Golems d’assaut runiques et retourner voir Brugar Cisaillepierre au Campement de la brigade de la marche de l’Ouest."}},
[12003] = {"À la découverte des tunnels", {"Brugar Cisaillepierre, au Campement de la brigade de la marche de l’Ouest, veut que vous exploriez le Bâtiment nord, le Bâtiment est et le Bâtiment sud de Thor Modan."}},
[12004] = {"Empêcher l'accord", {"Apportez les Restes du marchand des vents Mu'fah et les Écailles de Goramosh à l'Image de l'archimage Modera au Repos des étoiles."}},
[12005] = {"Empêcher l'accord", {"Apportez les Restes du marchand des vents Mu'fah et les Écailles de Goramosh à l'Image de l'archimage Aethas Saccage-soleil au Marteau d'Agmar."}},
[12006] = {"Vengez-nous de cette atrocité !", {"Tuez 15 des créatures suivantes, dans n’importe quelle combinaison : Chasseur de mages de la désolation des dragons, Traqueur de Repos-de-Lune, Sorcier du capteur tellurique et Sorcier focalisé. Lorsque ce sera fait, retournez voir Ethénial Lunombre aux jardins de Repos-de-Lune."}},
[12007] = {"Il faut savoir faire des sacrifices", {"Drakuru veut que vous apportiez l’Œil des prophètes au Brasero de Drakuru, à Zeb’Halak.","Vous aurez besoin de récupérer le mojo de Zim’bo pour y utiliser l’Elixir de Drakuru lorsque vous serez au piédestal."}},
[12008] = {"Le Marteau d'Agmar", {"Rendez-vous au Marteau d’Agmar, dans la Désolation des dragons, et parlez au Suzerain Agmar."}},
[12009] = {"Les pièges à crabes de Tua'kea", {"Collectez 8 des Pièges à crabes de Tua'kea et rapportez-les-lui à Port-Moa'ki."}},
[12010] = {"Le destin d'Orlond", {"Trouver le Géomètre Orlond dans Thor Modan."}},
[12011] = {"Signes de gros ennuis aquatiques", {"Parlez à Tua'kea à Port-Moa'ki du piège à crabes détruit que vous avez vu."}},
[12012] = {"Informez l'Ancien", {"Voyagez jusqu'à une capitale et parlez à un Ancien du Cercle terrestre."}},
[12013] = {"Abattre Arcanimus", {"Détruisez Arcanimus, puis retournez voir Ethénial Lunombre aux Jardins de Repos-de-Lune."}},
[12014] = {"Stable comme un roc ?", {"Retrouver le Sismographe portable et s’en servir pour mesurer la stabilité de Thor Modan, puis retourner voir Brugar Cisaillepierre au Campement de la brigade de la marche de l’Ouest."}},
[12015] = {"Quête de test pour Craig", {"Craig de Quelque part veut que vous rapportiez 5 Quelque chose."}},
[12016] = {"L'appât", {"Obtenir la Chair de « Deux-grosses-pinces » et l'apporter à Tua'kea à Port-Moa'ki."}},
[12017] = {"De la viande au bout de la ligne", {"Tuer Tu'u'gwar et retourner voir Tua'kea à Port-Moa'ki."}},
[12018] = {"La vengeance est un plat qui se mange froid", {"Récupérez 2 Morceaux de pierre de gargouille sur les Gargouilles putrides dans les Champs de la Charogne de l’est. Revenez voir le seigneur Bevis quand vous aurez terminé."}},
[12019] = {"Derniers sacrements", {"Allez dans la Ville-temple d'En'Kilah et trouvez l'Orbe de téléportation situé sous la citadelle flottante du Fléau de Naxxanar. Utilisez-le pour atteindre le sommet, et aidez Thassarian."}},
[12020] = {"Et une fois, quand j'étais ivre...", {"Parlez à Boxey Tournécrou au campement de la fête des Brasseurs."}},
[12021] = {"Une lettre pour la maison", {"Apportez la Lettre pour la maison à Dame Alustra dans les Champs de la Charogne de l’est."}},
[12022] = {"Buvez, éliminez !", {"Utilisez le Tastebière-cadeau pour toucher la C.E.R.V.O.I.S.E. 5 fois, puis revenez parler à Boxey Tournécrou."}},
[12023] = {"La vengeance est un plat qui se mange glacé", nil},
[12024] = {"L'enregistrement des morts", {"Fouillez les squelettes dans les Champs de la Charogne pour trouver des Certificats d’installation de l’Alliance."}},
[12025] = {"Une touche d'écarlate", {"Cherchez des preuves de la présence de la Croisade écarlate sur les Goules putrides dans les Champs de la Charogne de l’est. Retournez voir Dame Alustra avec tout ce que vous aurez trouvé."}},
[12026] = {"Le journal endommagé", {"Retrouvez les 8 Pages manquantes du journal, combinez-les avec le Journal incomplet, et remettez le journal complété à la Sage Paluna, au Camp Oneqwah."}},
[12027] = {"La grande aventure de monsieur Toumou", {"Aidez Emilie et Monsieur Toumou à regagner le Campement de la brigade de la marche de l’Ouest. Lorsque ce sera fait, allez parler à l’Écuyer Walter."}},
[12028] = {"Clairvoyance spirituelle", {"Parlez à Toalu'u le Mystique à Port-Moa'ki après avoir atteint la clairvoyance spirituelle."}},
[12029] = {"Fléau flambé", {"Mack, aux Sources de granit, veut que vous utilisiez son grog pour brûler 20 Trolls faméliques du Fléau à Drak’Tharon."}},
[12030] = {"L'Ancien Mana'loa", {"Parlez à la statue de l'Ancien Mana'loa à Indu'le."}},
[12031] = {"La liberté pour ceux qui ne peuvent partir", {"Donnez le repos à 15 habitants d'Indu'le, qu'il s'agisse de Pêcheurs, de Mystiques ou de Guerriers, puis retournez auprès de l'Ancien Mana'loa à Indu'le."}},
[12032] = {"Entretien avec les profondeurs", {"Communiez avec la Perle des profondeurs et faites ce que la déesse des mers Oacha'noa vous demandera.","Si vous survivez à cette épreuve, retournez voir Toalu'u le Mystique à Port-Moa'ki."}},
[12033] = {"Un message de l'ouest", {"Lisez la Lettre de Saurcroc, puis détruisez-la.","Parlez au Messager Torvus une fois votre tâche accomplie."}},
[12034] = {"La victoire approche…", {"Parlez au Sergent-chef Juktok au Marteau d’Agmar."}},
[12035] = {"Technologie reconvertie", {"Jeremiah Hawning, de Comté-lointaine, vous demande de détruire 5 Collecteurs des moissons et d'ensuite utiliser les Outils de Jeremiah pour modifier leurs circuits."}},
[12036] = {"Des profondeurs d'Azjol-Nérub", {"Explorez la Fosse de Narjun puis retournez voir le Sergent-chef Juktok au Marteau d'Agmar pour lui faire part de vos découvertes."}},
[12037] = {"Mission de sauvetage", {"Mack, des Sources de granit, veut que vous entriez dans Drak’Tharon pour découvrir ce qui est arrivé à Kurzel."}},
[12038] = {"Fléau flambé", {"Mack, aux Sources de granit, veut que vous utilisiez son grog pour brûler 30 Trolls faméliques du Fléau."}},
[12039] = {"Le sang noir de Yogg-Saron", {"Borus Torlefer, au Marteau d’Agmar dans la Désolation des dragons, veut que vous vous procuriez 10 Échantillons du sang noir de Yogg-Saron."}},
[12040] = {"Notre ennemi Arthas", {"Kilix le Démêleur, de la Fosse de Narjun dans la Désolation des dragons, vous a demandé de tuer 6 Seigneurs-du-dessous anub'ar."}},
[12041] = {"L'empire perdu", {"Remettez le Plan de bataille de Kilix au Suzerain Agmar, au Marteau d’Agmar, à l’est."}},
[12042] = {"Le Cœur des anciens", {"Drakuru veut que vous vous rendiez dans la partie la plus haute du Chantier d'abattage du Ciel bleu et que vous trouviez le Cœur des anciens."}},
[12043] = {"Défense de Rouilletuyère", {"Tuer 12 Terrassiers du désert et 1 Sous-chef du désert sur les sites de fouilles autour du Repos de Galakrond. Retourner voir Narf au Poste de Rouilletuyère lorsque la tâche est terminée."}},
[12044] = {"Approvisionnement", {"Rassemblez 8 tas de Minerai composite des fouilles autour du Repos de Galakrond et rapportez-les à Xink au Poste de Rouilletuyère."}},
[12045] = {"Glace pilée", {"Récolter 4 Grappes d'éclats de glace sur les Élémentaires de glace au nord et les rapporter à Zivlix au Poste de Rouilletuyère."}},
[12046] = {"Emballage délicat", {"Récoltez 12 Peaux d'animal légères sur les Jormungars tunneliers et les Condors os-de-dragon près du Poste de Rouilletuyère. Une fois que vous aurez récupéré les peaux, rapportez-les à Zivlix."}},
[12047] = {"Quelque chose qui ne fond pas", {"Cherchez 12 Morceaux d'os brisés sur les os de dragon à l'est du Poste de Rouilletuyère."}},
[12048] = {"Les armes du Fléau", {"Borus Torlefer, au Marteau d'Agmar dans la Désolation des dragons, veut que vous lui rapportiez 8 Armes du Fléau."}},
[12049] = {"Dur à avaler", {"Combattre un Jormungar trapu à l'est du Poste de Rouilletuyère, jusqu'à ce qu'il ouvre grand la gueule, et lui lancer une Charge explosive toute-puissante dans la gorge. Ramasser 6 morceaux de Viande de jormungar calcinée sur le sol après l'explosion du jormungar et les rapporter à Xink."}},
[12050] = {"Débitez-les !", {"Une fois arrivé aux nids des harpies, au nord, utilisez la Télécommande de déchiqueteur de Xink pour faire appel à un déchiqueteur. Rassemblez 50 Tas de bois dans la zone, puis retournez voir Xink au Poste de Rouilletuyère.","Si vous perdez la Télécommande de déchiqueteur de Xink, parlez à Xink au Poste de Rouilletuyère pour en obtenir une nouvelle."}},
[12051] = {"Volez-leur dans les plumes", {"Tuer 20 harpies au nord-ouest du Poste de Rouilletuyère pour Narf."}},
[12052] = {"Remettez-en une couche !", {"Tuez la Maîtresse du Vent-froid et 15 harpies du Vent-froid dans les montagnes au nord-ouest du Poste de Rouilletuyère. Retournez voir Narf une fois cette tâche accomplie.","Si vous avez besoin de la Télécommande de déchiqueteur de Xink, parlez à Xink au Poste de Rouilletuyère."}},
[12053] = {"La puissance de la Horde", {"Le Sergent-chef Juktok au Marteau d'Agmar dans la Désolation des dragons veut que vous utilisiez l'Étendard de bataille chanteguerre au village de Brume-glace, et que vous le défendiez contre les attaques.","Rendez l'Étendard de bataille chanteguerre lorsque vous aurez terminé."}},
[12054] = {"Déchiffrer le journal", {"Apportez un brin de Souffle de l’esprit à la Sage Paluna au Camp Oneqwah."}},
[12055] = {"Un appareil étrange", {"Apportez l'Étrange mécanisme de Goramosh à l'Image de l'archimage Modera au Repos des étoiles."}},
[12056] = {"Condamné à mort : le haut-sectateur Zangus", {"Tuez le haut-sectateur Zangus, puis remettez la Tête du haut-sectateur Zangus au Capitaine Gort, du Marteau d’Agmar dans la Désolation des dragons."}},
[12057] = {"Le tome relié de chair", {"Remettez le Tome relié de chair au Capitaine Gort au Marteau d’Agmar."}},
[12058] = {"Les prophéties runiques", {"La Sage Paluna, au Camp Oneqwah, veut que vous déchiffriez les trois Plaques runiques."}},
[12059] = {"Un appareil étrange", {"Apportez l'Appareil étrange de Goramosh à l'Image de l'archimage Aethas Saccage-soleil au Marteau d'Agmar."}},
[12060] = {"Projections et projets", {"Rapprochez-vous pour observer la projection sur le capteur tellurique qui flotte au-dessus des jardins de Repos-de-Lune.","Retournez ensuite voir l'Image de l'archimage Modera au Repos des étoiles pour lui communiquer les informations dont vous disposez."}},
[12061] = {"Projections et projets", {"Observez la projection sur le capteur tellurique qui flotte au-dessus des jardins de Repos-de-Lune.","Retournez ensuite voir l'Image de l'archimage Aethas Saccage-soleil au Marteau d'Agmar pour partager avec lui les informations dont vous disposez."}},
[12062] = {"Insulter Coren Navrebière", {"Parlez à Coren Navrebière."}},
[12063] = {"La force de Brume-glace", {"Trouvez Banthok Brume-glace au village de Brume-glace."}},
[12064] = {"Les chaînes des Anub'ar", {"Banthok Brume-glace, du village de Brume-glace de la Désolation des dragons, veut que vous lui rapportiez le Fragment de la clé d’Anok’ra, le Fragment de la clé de Tivax et le Fragment de la clé de Sinok."}},
[12065] = {"Le focalisateur sur la plage", {"Procurez-vous l'Anneau de contrôle des lignes telluriques et utilisez-le pour obtenir des informations sur le Focalisateur de lignes telluriques de la Grève Lumineuse.","Retournez ensuite auprès de l'Image de l'archimage Modera au Repos des étoiles."}},
[12066] = {"Le focalisateur sur la plage", {"Procurez-vous l'Anneau de contrôle des lignes telluriques et utilisez-le pour obtenir des informations sur le Focalisateur de lignes telluriques de la Grève Lumineuse.","Retournez ensuite auprès de l'Image de l'archimage Aethas Saccage-soleil au Marteau d'Agmar."}},
[12067] = {"Une lettre pour la maison", {"Apportez la Lettre du capitaine Malin au Commandant Saia Azuracier au Repos des étoiles."}},
[12068] = {"Les voix de la poussière", {"Drakuru veut que vous récupériez les Tablettes drakkari dans les Ruines de Drakil’jin et 5 Mojos sacrés sur des Oracles et des Protecteurs drakkari. Ensuite, vous devrez utiliser l’Elixir de Drakuru sur son Brasero dans les ruines pour invoquer son image."}},
[12069] = {"Le retour du grand chef", {"Libérez le Grand chef Brume-glace à l’aide de la Clé de la prison anub’ar, puis aidez-le à vaincre le Roi-d’en-bas Anub’et’kan.","Récupérez un Fragment de la carapace d’Anub’et’kan et présentez-le au Suzerain Agmar, au Marteau d’Agmar de la Désolation des dragons."}},
[12070] = {"Rallier les troupes", {"Utilisez l’Éclat de la terre pour rallier les Géants des Grisonnes qui se battent dans les Collines du Rocher, tuez tous les nains de fer qui s’opposent à vous, puis allez faire votre rapport à Kurun, sur la colline qui domine Thor Modan."}},
[12071] = {"Attaque aérienne !", {"Parlez à Valnok Ragevent au Marteau d'Agmar."}},
[12072] = {"Que les bêtes-chancreuses soient damnées !", {"Utilisez le Lance-fusée de Valnok pour appeler un Chevaucheur de guerre kor'kron. Montez sur son dos et utilisez-le pour tuer 25 Bêtes-chancreuses anub'ar !","Retournez voir Valnok Ragevent au Marteau d'Agmar si vous réussissez cette mission."}},
[12073] = {"Il faut battre le fer…", {"Tuez 10 nains de fer de n’importe quel type parmi les défenseurs de Thor Modan, puis allez faire votre rapport à l’Éclaireur Vor'takh au Camp Oneqwah."}},
[12074] = {"Un allié de circonstance", {"Parlez à Kurun sur la colline qui domine Thor Modan."}},
[12075] = {"La portion congrue", {"Collectez un Échantillon de chair rocheuse sur le cadavre du Géant des glaces à l'entrée de la caverne dans l'Étau de cristal, au nord de la position de Zort. Lorsque vous aurez terminé, retournez voir Zort près du centre du canyon."}},
[12076] = {"Une sale affaire", {"Collectez 2 Fioles de crachat corrosif de jormungar en utilisant le racloir sur vous. Rapportez ensuite les fioles à Zort à l'Étau de cristal."}},
[12077] = {"À appliquer deux fois par jour", {"Offrez l'élixir préparé par Zort à Ko'char l'Invaincu."}},
[12078] = {"Ver querelleur", {"Rendez-vous dans les cavernes sous l’Étau de cristal et capturez 3 Rejetons jormungars dans des caisses solides. Ne ramassez les caisses que lorsque les Rejetons jormungars seront piégés à l’intérieur !","Ensuite, apportez les Rejetons jormungars à Zort, à l’Étau de cristal."}},
[12079] = {"Terres piétinées", {"Entrez dans la caverne à l'extrême nord de l'Étau de cristal et tuez 8 Nourrisseurs jormungars du cœur de glace pour Ko'char l'Invaincu."}},
[12080] = {"Très grand ver", {"Entrez dans la Caverne du Cœur de glace et tuez Cliquettroue. S'il est toujours en votre possession, utilisez l'élixir protecteur de Zort pour vous protéger du crachat corrosif du jormungar.","Retournez auprès de Ko'char l'Invaincu, à l'Étau de cristal, lorsque vous aurez accompli votre tâche."}},
[12081] = {"Gavrock", {"Parlez à Gavrock aux Ruines de Téthys."}},
[12082] = {"Ta-ta da ta-Da da !", {"Accompagnez Harrison jusqu'à la sortie des Ruines de Drakil'jin, puis parlez à Harkor au Camp de Harkor."}},
[12083] = {"Là-haut dans la forêt", {"Procurez-vous l'Amulette de contrôle des lignes telluriques et utilisez-la pour obtenir des informations sur le Focalisateur de lignes telluriques de la Forêt de Lothalor.","Retournez ensuite auprès de l'Image de l'archimage Modera au Repos des étoiles."}},
[12084] = {"Là-haut dans la forêt", {"Procurez-vous l'Amulette de contrôle des lignes telluriques et utilisez-la pour obtenir des informations sur le Focalisateur de lignes telluriques de la Forêt de Lothalor.","Retournez ensuite auprès de l'Image de l'archimage Aethas Saccage-soleil au Marteau d'Agmar."}},
[12085] = {"Une lettre pour la maison", {"Apportez la Lettre du lieutenant Ta'zinni au Suzerain Agmar au Marteau d'Agmar."}},
[12086] = {"Le fils de Karkut", {"Aller au Séjour de la mort. C'est vers l'est, juste au-delà d'Unu'pe."}},
[12087] = {"Un peu d’aide ?", {"Le Caporal Venn, au Séjour de la Mort, veut que vous tuiez 8 Défenseurs de la ziggourat."}},
[12088] = {"Thassarian, le chevalier de la mort", {"Partez à la recherche de Thassarian dans la ziggourat Gémissante au nord du Séjour de la Mort."}},
[12089] = {"On recherche : magistère Keldonus", {"Tuer le Magistère Keldonus et retourner voir le Capitaine Gort au Marteau d'Agmar à la Désolation des dragons."}},
[12090] = {"On recherche : Gigantaure", {"Tuer Gigantaure et retourner voir le Capitaine Gort au Marteau d'Agmar à la Désolation des dragons."}},
[12091] = {"On recherche : Serreffroi", {"Tuer Serreffroi et retourner voir le Capitaine Gort au Marteau d'Agmar à la Désolation des dragons."}},
[12092] = {"Renforcer les Anciens", {"Utiliser l'Écorce des marcheurs pour renforcer 3 Anciens de Lothalor et retourner auprès de Sarendryana au Repos des étoiles."}},
[12093] = {"Les runes de contrainte", {"Gavrock, des Ruines de Téthys, veut que vous neutralisiez les quatre Runes de contrainte en triomphant du Surveillant Durval, du Surveillant Korgan, du Surveillant Lochli et du Surveillant Brunon."}},
[12094] = {"Une puissance latente", {"Chargez l’Éclat de Gavrock à la Première pierre des anciens, à la Deuxième pierre des anciens et à la Troisième pierre des anciens, puis retournez voir Gavrock aux Ruines de Téthys."}},
[12095] = {"Direction la Chute du dragon", {"Parler à Rokhan à la Chute du dragon dans la Désolation des dragons."}},
[12096] = {"Renforcer les Anciens", {"Utiliser l'Écorce des marcheurs pour renforcer 3 Anciens de Lothalor et retourner auprès du Garde-terre Grife au Marteau d'Agmar."}},
[12097] = {"Sarathstra, Fléau du nord", {"Parler à Rokhan pour qu'il invoque le wyrm de givre Sarathstra.","Tuer Sarathstra et remettre le Cœur glacé de Sarathstra au Capitaine Gort au Marteau d'Agmar."}},
[12098] = {"Fouillez Indu'le", {"Cherchez le commandant des forces des chasseurs de mages à Indu'le et au Lac Indu'le."}},
[12099] = {"Libres, enfin", {"Utilisez le Briserune de Gavrock pour libérer 4 Géants runifiés, puis retournez voir Gavrock aux Ruines de Téthys."}},
[12100] = {"Contenir la putréfaction", {"Essor Fureur-du-faucon au Marteau d'Agmar dans la Désolation des dragons veut que vous tuiez 15 Spécimens de la faune infectés et que vous trouviez un Organe résistant à la putréfaction."}},
[12101] = {"Le bon docteur…", {"Remettez l'Organe résistant à la putréfaction au Dr Maléfious au Marteau d'Agmar."}},
[12102] = {"À la recherche du lilas rubis", {"Le Docteur Sintar Maléfious au Marteau d'Agmar dans la Désolation des dragons veut que vous alliez chercher 1 Lilas rubis."}},
[12104] = {"Retour vers Essor", {"Apportez le vaccin de Sintar à Essor Fureur-du-faucon au Marteau d'Agmar."}},
[12105] = {"Descente dans les ténèbres", {"Remettez le Journal de Mikhail au Lieutenant Dumont, du Gîte Ambrepin."}},
[12106] = {"Fouillez Indu'le", {"Cherchez le commandant des forces des chasseurs de mages à Indu'le et au Lac Indu'le."}},
[12107] = {"Le bout de la ligne", {"Utilisez le Talisman de contrôle des lignes telluriques pour obtenir des informations du Focalisateur des lignes telluriques sous le Lac Indu'le. Ensuite, allez vers l'est au Sanctuaire draconique azur près du sud du centre de la Désolation des dragons et observez ce qu'il s'y passe depuis l'escarpement ouest.","Quand ce sera fait, retournez auprès de l'Image de l'archimage Modera au Repos des étoiles."}},
[12108] = {"PÉRIMÉ", {"Remettez la Note de Mikhail au Cartographe Tobias au Gîte Ambrepin."}},
[12109] = {"Aller voir Gryan Roidemantel… une fois de plus", {"Parlez au Capitaine Gryan Roidemantel au Campement de la brigade de la marche de l'Ouest."}},
[12110] = {"Le bout de la ligne", {"Utilisez le Talisman de contrôle des lignes telluriques pour obtenir des informations du Focalisateur des lignes telluriques sous le Lac Indu'le. Ensuite, allez vers l'est au Sanctuaire draconique azur près du sud du centre de la Désolation des dragons et observez ce qu'il s'y passe depuis l'escarpement ouest.","Quand ce sera fait, retournez auprès de l'Image de l'archimage Aethas Saccage-soleil au Marteau d'Agmar."}},
[12111] = {"Là où les monstres rôdent", {"Essor Fureur-du-faucon au Marteau d'Agmar dans la Désolation des dragons veut que vous utilisiez le Paquet de vaccins pour vacciner 5 Orignaux de Tombeneige et 5 Grizzlys arctiques."}},
[12112] = {"Des négociations difficiles", {"Trouver Zort au centre de l'Étau de cristal, au nord-est du Poste de Rouilletuyère."}},
[12113] = {"Vous aimez la viande des Grisonnes ?", {"Harkor, du Camp de Harkor, veut que vous collectiez 10 morceaux de Viande de worg fibreuse sur des Rôdeurs Hurlesoir et 10 Steaks de cornepelle sur des Ruminants long-sabot."}},
[12114] = {"Thérapie", {"Harkor, du Camp de Harkor, veut que vous tuiez 10 Protecteurs drakkari et 10 Oracles drakkari à l’extérieur des Ruines de Drakil’jin."}},
[12115] = {"Koltira et le langage de la mort", {"Apportez le Tome relié de chair à Koltira Tissemort."}},
[12116] = {"Il faut des tripes…", {"Kraz, du Camp de Harkor, veut que vous entriez dans les Ruines de Drakil’jin pour y récupérer 5 Vases canopes drakkari."}},
[12117] = {"En route pour Moa'ki", {"Rendez-vous à Port-Moa’ki, dans la Désolation des dragons, et parlez au Chef Ko’nani."}},
[12118] = {"En route pour Moa'ki", {"Rendez-vous à Port-Moa’ki, dans la Désolation des dragons, et parlez au Chef Ko’nani."}},
[12119] = {"Obtenir une audience", {"Apportez votre Lettre d'introduction à Tariolstrasz au Temple du Repos du ver."}},
[12120] = {"La massette de Drak'aguul", {"Kraz, du Camp de Harkor, veut que vous tuiez Drak’aguul, à l’extérieur des Ruines de Drakil’jin. Lorsque ce sera fait, retournez le voir avec la Massette de Drakil’jin."}},
[12121] = {"On se revoit de l'autre côté", {"Kraz, du Camp de Harkor, veut que vous utilisiez la Massette de Drakil’jin sur les gongs qui se trouvent à l’extérieur des Ruines de Drakil’jin. Lorsque vous aurez été tué, vous devrez entrer dans les ruines et parler à l’esprit de Gan’jo."}},
[12122] = {"Obtenir une audience", {"Apportez votre Lettre d'introduction à Tariolstrasz au Temple du Repos du ver."}},
[12123] = {"Informer la Reine", {"Apportez votre Lettre d'introduction à Alexstrasza la Lieuse-de-vie au Temple du Repos du ver."}},
[12124] = {"Informer la Reine", {"Apportez votre Lettre d'introduction à Alexstrasza la Lieuse-de-vie au Temple du Repos du ver."}},
[12125] = {"Au service du sang", {"Koltira Tissemort, au Marteau d'Agmar à la Désolation des dragons, veut que vous utilisiez la Gemme sanguine sur un Villageois d'Indu'le dérangé et que vous lui rapportiez une Gemme sanguine remplie."}},
[12126] = {"Au service de l'impie", {"Koltira Tissemort, au Marteau d'Agmar à la Désolation des dragons, veut que vous utilisiez la Gemme impie sur le Duc Vallenhal et que vous lui rapportiez une Gemme impie remplie."}},
[12127] = {"Au service du givre", {"Koltira Tissemort, au Marteau d'Agmar à la Désolation des dragons, veut que vous utilisiez la Gemme de givre sur des Revenants des glaces et que vous lui rapportiez une Gemme de givre remplie."}},
[12128] = {"Prendre des nouvelles de Raegar", {"Parlez à Raegar Cassefront à la Pointe du Prospecteur."}},
[12129] = {"Le plan parfait", {"Remettez les Plans du golem de guerre complétés à Raegar Cassefront à la Pointe du Prospecteur."}},
[12130] = {"Pourquoi fabriquer ce que vous pouvez faucher ?", {"Raegar Cassefront, de la Pointe du Prospecteur, veut 8 Pièces de golem de guerre"}},
[12131] = {"Nous avons l'énergie…", {"Remettez la Cellule énergétique de Durar et la Cellule énergétique de Kathorn à Raegar Cassefront, à la Pointe du Prospecteur."}},
[12132] = {"Le pouvoir de détruire", {"Koltira Tissemort, au Marteau d'Agmar à la Désolation des dragons, veut que vous entriez dans le Monde des ombres et que vous tuiez 6 Tourmenteurs ténébreux.","Parlez à Koltira Tissemort si vous avez besoin de retourner dans le monde des ombres."}},
[12133] = {"Ecraser la citrouille", {"Donnez le Symbole sacré brûlé à la Directrice d'orphelinat costumée."}},
[12134] = {"Sasha chasse", {"Sasha veut que vous tuiez 12 Chasseurs de Solstice à Solstice."}},
[12135] = {"« Que viennent les flammes ! »", {"La Directrice de l'orphelinat costumée veut que vous l'aidiez à éteindre les incendies du village quand le Cavalier sans tête les aura allumés. Quand ils seront éteints, retournez la voir."}},
[12136] = {"Le tome traduit", {"Remettre le Tome relié de chair traduit au Capitaine Gort au Marteau d'Agmar."}},
[12137] = {"Relax, mec", {"Gan’jo veut que vous récupériez de la Neige de léthargie éternelle dans son coffre, dans les Ruines de Drakil'jin.","Vous devrez ensuite utiliser la Neige sur les Anciens esprits drakkari des ruines, et rapporter leurs Particules spirituelles drakkari à Kraz, au Camp de Harkor."}},
[12138] = {"… ou peut-être pas, après tout", {"Chargez votre Golem de guerre avec l’énergie de 10 Factionnaires de foudre, puis retournez voir Raegar Cassefront à la Pointe du Prospecteur."}},
[12139] = {"« Que viennent les flammes ! »", {"La Directrice d'orphelinat masquée veut que vous l'aidiez à éteindre les incendies du village. Quand ils seront éteints, retournez la voir."}},
[12140] = {"Gloire à Roanauk !", {"Allez trouver Roanauk Brume-glace au Marteau d'Agmar et initiez-le à son rôle de membre et de chef des forces de la Horde.","Retournez voir le Suzerain Agmar avec le Serment de sang de la Horde lorsque vous aurez terminé cette tâche."}},
[12141] = {"Une mission diplomatique", {"Parlez à Karuk sur la Grève des Courcinglants, au sud-ouest du donjon de la Bravoure."}},
[12142] = {"Contrôle des nuisibles", {"Duane au Front de la 7e Légion vous demande de tuer 10 Frigbolds plaineige et 3 Magnataures de la Désolation des dragons dans les camps de magnataures près du Repos du ver."}},
[12143] = {"Course-poursuite dans le canyon", {"Duane veut que vous poursuiviez les Frigbolds dans le canyon au nord-est du Front de la 7e légion et que vous vous occupiez de leur chef."}},
[12144] = {"Contrôle des nuisibles", {"Kontokanis à la Chute du dragon vous a demandé de tuer 10 Frigbolds plaineige et 3 Magnataures dans les camps de magnataures près du Repos du ver."}},
[12145] = {"Course-poursuite dans le canyon", {"Kontokanis veut que vous poursuiviez les frigbolds dans le canyon au sud de la Chute du dragon et que vous vous occupiez de leur chef."}},
[12146] = {"Des implications troublantes", {"Apportez le Cor de bataille blasonné au Repos du ver et cherchez quelqu'un qui voudrait évaluer la menace potentielle."}},
[12147] = {"Des implications troublantes", {"Apportez le Cor de bataille blasonné au Repos du ver et cherchez quelqu'un qui voudrait évaluer la menace potentielle."}},
[12148] = {"Un spécimen unique", {"Trouvez et tuez Scinde-les-os dans le canyon au nord-ouest du Sanctuaire draconique azur ou aux alentours. Rapportez toute preuve que vous trouverez à Aurastrasza au temple du Repos du ver."}},
[12149] = {"Un magnataure puissant", {"Pourchassez et tuez Festin-de-sang, Briseglace et Drakentraille à l'est du Repos du ver.","Festin-de-sang réside au coin sud-est du Désert des Dragons, juste au nord du Sanctuaire draconique émeraude. Briseglace patrouille à l'extrémité nord du Miroir de l'aube. Drakentraille erre au pied des falaises à l'ouest du Sanctuaire draconique bronze.","Retournez voir Aurastrasza au Repos du ver lorsque vous aurez abattu les magnataures."}},
[12150] = {"Un maître des runes reclus", {"Localisez Dregmar Marquerune près de sa caverne sur la route des falaises surplombant le Miroir de l'aube. Défiez-le et découvrez le nom du chef magnataure par tous les moyens nécessaires. Retournez auprès d'Aurastrasza au Repos du ver lorsque vous aurez obtenu l'information."}},
[12151] = {"Un chef de guerre cruel", {"Voyagez jusqu'au cercle de torches au sud du Sanctuaire draconique azur et soufflez dans le Cor de bataille blasonné. Triomphez de Grom'thar le Portefoudre et rapportez sa tête à Aurastrasza au Repos du ver."}},
[12152] = {"La fin de Jin'arrak", {"Kraz, du Camp de Harkor, veut que vous récupériez l’Offrande sacrée drakkari dans les Ruines de Drakil’jin, et que vous lanciez la Poudre spirituelle drakkari dessus. Ensuite, utilisez l’Offrande drakkari infusée près des gongs, à l’extérieur des Ruines de Drakil’jin."}},
[12153] = {"Le thane de fer et son Enclume", {"Battez le Thane de fer Marteau-de-fureur grâce aux capacités de votre Golem de guerre. Ensuite, retournez voir Raegar Cassefront à la Pointe du Prospecteur."}},
[12154] = {"La grande panne", {"Servez-vous des Explosifs de Raegar pour détruire la Batterie runique à Dun Argol, puis allez faire votre rapport à Raegar Cassefront à la Pointe du Prospecteur."}},
[12155] = {"Ecraser la citrouille", {"Donnez le Symbole sacré brûlé à la Directrice d'orphelinat masquée."}},
[12156] = {"PÉRIMÉ", {"Escorter l’Envoyé de l’Alliance à Ruissargent. Lorsque ce sera fait, présentez-vous au lieutenant Dumont, au gîte Ambrepin."}},
[12157] = {"Le messager perdu", {"Parlez au Messager Lanson au Repos des étoiles, dans la Désolation des dragons."}},
[12158] = {"La mine de la Pierre creuse", {"Cherchez des indices dans la Mine de la Pierre creuse, au nord-ouest du Campement de la brigade de la marche de l’Ouest."}},
[12159] = {"Les âmes agitées", {"Petrov, de la Mine de la Pierre creuse, veut que vous utilisiez la Lanterne de mineur pour brûler les cadavres de 8 Mineurs de la Pierre creuse."}},
[12160] = {"Un nom surgi du passé", {"Retournez voir le Capitaine Gryan Roidemantel au Campement de la brigade de la marche de l’Ouest."}},
[12161] = {"Ruuna l'Aveugle", {"Parlez à Ruuna l’Aveugle, au Camp de Ruuna, près de la Passe de Vordrassil."}},
[12162] = {"Le village de Solstice", nil},
[12163] = {"La mal rôde dans les profondeurs", nil},
[12164] = {"L'heure du worg", {"Sasha, du comptoir du Pin blanc, veut que vous alliez sur l'île de la Lune sanguine pour y tuer Selas, Varlam, Gueulétripe et l'Ombre d'Arugal."}},
[12165] = {"Un plan tentant", {"Le Prospecteur Rokar, du Camp Oneqwah, veut que vous lui rapportiez les Plans du golem de guerre terminés."}},
[12166] = {"Le feu liquide d'Élune", {"La Gardienne Jodi Lunechant, au Repos des étoiles, vous a demandé d'utiliser le Feu liquide d'Élune pour purifier les cadavres de 6 Orignaux corrompus et de 6 Grizzlys enragés."}},
[12167] = {"Tuez les sectateurs", {"Collecter 5 Tenues de sectateur fonctionnelles pour la Gardienne Jodi Lunechant au Repos des étoiles."}},
[12168] = {"La faveur de Zangus", {"Rapportez la Faveur de Zangus à la Gardienne Jodi Lunechant au Repos des étoiles."}},
[12169] = {"Le haut-sectateur", {"La Gardienne Jodi Lunechant, au Repos des étoiles, vous a demandé d'abattre le Haut-sectateur Zangus."}},
[12170] = {"Baston sur la rivière Noire", {"Le Capitaine des écumeurs Kronn veut que vous tuiez 10 unités ou personnages-joueurs de l'Alliance au Camp de bûcherons de la rivière Noire."}},
[12171] = {"Traîtres et trahisons", {"Montrez la Missive de l’Alliance à Palena Nuée-argent, au Repos des étoiles."}},
[12172] = {"Harmonisation à Dalaran", {"L'Image de l'archimage Modera vous a demandé de vous Harmoniser avec les lignes telluriques du Norfendre."}},
[12173] = {"Harmonisation à Dalaran", {"L'Image de l'archimage Aethas Saccage-soleil, au Marteau d'Agmar, vous a demandé de vous Harmoniser avec les lignes telluriques du Norfendre."}},
[12174] = {"Le haut-commandant Halford Verroctone", {"Remettez la Missive de l’Alliance au Haut-commandant Halford Verroctone, au Donjon de Garde-hiver, dans l’est de la Désolation des dragons."}},
[12175] = {"Les peaux de worgs gris", {"Le Marchand de peaux Jun'ik, du bastion de la Conquête, veut que vous préleviez 3 Peaux de worg gris sur des Chasseurs brumegrise dans les bois du nord."}},
[12176] = {"Une substitution sans importance", {"Le Marchand de peaux Jun'ik veut que vous lui rapportiez 6 Peaux de grizzly."}},
[12177] = {"Les petites magouilles de Jun'ik", {"Le Marchand de peaux Jun'ik, du bastion de la Conquête des Grisonnes, a besoin d'un morceau de Charbon et de 5 mesures de Farine simple."}},
[12178] = {"Livraison à Krenna", {"Remettez le Tas de peaux à Krenna au Bastion de la Conquête."}},
[12179] = {"Specialisation 1 [PH]", {"JOURNAL : Accomplir cette quête pour la spécialisation 1."}},
[12180] = {"Les prospecteurs captifs", {"Libérez le Prospecteur Gann, le Prospecteur Torgan et la Prospectrice Varana, puis retournez voir le Montagnard Kilian à la Pointe du Prospecteur."}},
[12181] = {"Lui donner un nom", {"Montrez le Spécimen chancreux à Tobias Sarkhoff à la Nouvelle-Agamand."}},
[12182] = {"En route pour Vexevenin !", {"Livrez le Spécimen chancreux au Porte-peste en chef Mideton, à Vexevenin, à l’est de la Désolation des dragons."}},
[12183] = {"L'habit fait le moine", {"Apportez un Uniforme de surveillant au montagnard Kilian à la pointe du Prospecteur."}},
[12184] = {"Cultiver son image", {"Utilisez l'Appareil photographique de Kilian pour enregistrer les images de 8 Nains de fer à Dun Argol, puis retournez voir Kilian à la pointe du Prospecteur."}},
[12185] = {"Faire bon visage à Loken", {"Déguisez-vous en Surveillant Rune-de-fer, interceptez le message de Loken, puis faites votre rapport au Montagnard Kilian à la Pointe du Prospecteur."}},
[12186] = {"MARQUEUR : Gagnant", nil},
[12187] = {"MARQUEUR : Participant", nil},
[12188] = {"La flétrissure des Réprouvés et vous : petit manuel de survie", {"Le Porte-peste en chef Mideton, à Vexevenin dans la Désolation des dragons, veut que vous lui rapportiez 10 Résidus ectoplasmiques."}},
[12189] = {"Une bande d'imbéciles !", {"Voyager jusqu'à Vexevenin dans la Désolation des dragons et parler avec le Porte-peste en chef Mideton."}},
[12190] = {"Dis bonjour à mon p'tit pote", {"Dasnob veut que vous remettiez les Ingrédients de Harkor à Harkor, au Camp de Harkor."}},
[12191] = {"Buvez, éliminez !", {"Utilisez le Tastebière-cadeau pour toucher la C.E.R.V.O.I.S.E. 5 fois, puis revenez parler à Bizzle Soulevite."}},
[12192] = {"Et une fois, quand j'étais ivre...", {"Parlez à Bizzle Soulevite au campement de la fête des Brasseurs."}},
[12193] = {"Dites, il n'y aurait pas un souvenir cette année ?", {"Échangez le bon pour un bock à Belbi Coupecircuit dans le campement de la fête des Brasseurs."}},
[12194] = {"Dites, il n'y aurait pas un souvenir, cette année ?", {"Échangez le Bon pour un bock de la fête des Brasseurs à Blix Réparouage à l'arrière du campement de la fête des Brasseurs."}},
[12195] = {"Un « invité » inattendu", {"Apportez 5 Bois de cerf adulte à Tormak le Balafré au Camp Oneqwah."}},
[12196] = {"En partant de zéro", {"Apportez 8 Pièces de golem de guerre au prospecteur Rokar au Camp Oneqwah."}},
[12197] = {"Nous avons l'énergie…", {"Apportez la Cellule énergétique de Durar et la Cellule énergétique de Kathorn au Prospecteur Rokar au Camp Oneqwah."}},
[12198] = {"… ou peut-être pas, après tout", {"Chargez votre Golem de guerre avec l'énergie de 10 Factionnaires de foudre et revenez voir le Prospecteur Rokar au Camp Oneqwah."}},
[12199] = {"Renverser le thane-de-fer", {"À l'aide des capacités de votre Golem de guerre, vainquez le Thane de fer Marteau-de-fureur, puis retournez voir Tormak le Balafré au Camp Oneqwah."}},
[12200] = {"Les larmes de dragon émeraude", {"Le Porte-peste en chef Mideton, à Vexevenin dans la Désolation des dragons, vous demande de collecter 8 Larmes de dragon émeraude."}},
[12201] = {"L'ombre du surveillant", {"Apportez un Uniforme de surveillant à l'Éclaireur Vor'takh au Camp Oneqwah."}},
[12202] = {"Cultiver son image", {"Utilisez l'Appareil photographique de Rokar pour enregistrer les images de 8 Nains de fer à Dun Argol, puis retournez voir l'éclaireur Vor'takh au Camp Oneqwah."}},
[12203] = {"Les ordres de Loken", {"Déguisez-vous en Surveillant Rune-de-fer, interceptez le message de Loken, puis faites votre rapport à l'éclaireur Vor'takh au Camp Oneqwah."}},
[12204] = {"Au nom de Loken", {"Interrogez Hugues Leverre et Gavrock au sujet de Loken, puis faites part de vos découvertes à l'éclaireur Vor'takh au Camp Oneqwah."}},
[12205] = {"On recherche : l'Assaut écarlate", {"Tuez 20 membres de l'Assaut écarlate puis faites votre rapport au Grand exécuteur Ulcéré à Vexevenin."}},
[12206] = {"Les derniers rites de la flétrissure", {"L'Apothicaire Vicky Levine de Vexevenin vous demande de tester le Flacon de flétrissure sur le Prisonnier de l'Assaut écarlate."}},
[12207] = {"La chute de Vordrassil", {"La Voyante des vents Corne-grise, du bastion de la Conquête, veut que vous alliez à la Branche de Vordrassil, au Cœur de Vordrassil ou aux Larmes de Vordrassil, pour récupérer 6 Échantillons de gelée sur des Limons entropiques."}},
[12208] = {"Bonne chasse aux trolls !", {"Le Sergent Thurkin vous a demandé de parler à Samir, aux Sources de granit."}},
[12209] = {"Pillage de matériel", {"L'Intendant Bartlett de Vexevenin vous a demandé de voler 8 pièces d'Armures de l'Assaut écarlate et 8 Armes de l'Assaut écarlate."}},
[12210] = {"La chasse aux trolls est ouverte !", {"Le Lieutenant Dumont, du Gîte Ambrepin, veut que vous parliez à Samir aux Sources de granit."}},
[12211] = {"Qu'ils ne se relèvent pas !", {"L'Apothicaire Vicky Levine de Vexevenin vous a demandé d'utiliser sa Caisse de rats pour nettoyer les cadavres de 15 membres de l'Assaut écarlate à la Nouvelle-Âtreval."}},
[12212] = {"Regarnir les réserves", {"Le maître bûcheron Anderhol, du Gîte Ambrepin, veut que vous tuiez des Cerfs haute-ramure pour lui rapporter 8 morceaux de Venaison succulente."}},
[12213] = {"Les ténèbres du dessous", {"La Voyante des vents Corne-grise, du Bastion de la Conquête, veut que vous alliez à la Branche de Vordrassil, au Cœur de Vordrassil et aux Larmes de Vordrassil, et que vous utilisiez l’Orbe de géomancien dans les profondeurs sous la surface."}},
[12214] = {"Des montures fraîches", {"Hansel Bauer de Vexevenin vous a demandé de voler 3 Chevaux de guerre de l'Assaut et de les lui apporter en lui donnant leurs rênes."}},
[12215] = {"Eux ou nous !", {"Anderhol, du Gîte Ambrepin, veut que vous tuiez 6 Chasseurs brumegrises dans les Grisonnes."}},
[12216] = {"Ne flanchet pas !", {"Anderhol, du Gîte Ambrepin, veut que vous récupériez 8 Flanchets de grizzly sur les Ours des Grisonnes."}},
[12217] = {"Des yeux d'aigle", {"Anderhol, du Gîte Ambrepin, veut que vous tuiez 5 Aigles impériaux avant de revenir le voir."}},
[12218] = {"Répandez la bonne nouvelle", {"Le Porte-peste en chef Mideton, à Vexevenin dans la Désolation des dragons, veut que vous utilisiez la Bombe chancreuse de l'Épandeur de chancre réprouvé pour détruire 30 Morts affamés aux abords des Champs de la Charogne."}},
[12219] = {"L'arbre-monde manqué", {"La Hiérophante Thayreen, du Gîte Ambrepin, veut que vous alliez à la Branche de Vordrassil, au Cœur de Vordrassil ou aux Larmes de Vordrassil, pour récupérer 6 Échantillons de gelée sur des Limons entropiques."}},
[12220] = {"Une sombre influence", {"La Hiérophante Thayreen, du Gîte Ambrepin, veut que vous alliez au Cœur de Vordrassil, aux Larmes de Vordrassil et à la Branche de Vordrassil, et que vous utilisiez l’Orbe de géomancien dans les profondeurs sous la surface."}},
[12221] = {"La flétrissure des Réprouvés", {"Livrer la Flétrissure des Réprouvés au Docteur Sintar Maléfious au Marteau d'Agmar."}},
[12222] = {"Les secrets des lieuses de flammes", {"Le Bûcheron Drake, du Gîte Ambrepin, veut que vous lui rapportiez 3 Talismans imprégnés de flammes."}},
[12223] = {"Éclaircir les rangs", {"Tuez 12 Huscarls écorche-dragon, puis retournez voir le Bûcheron Drake au Gîte Ambrepin."}},
[12224] = {"L'avant-garde kor'kronne !", {"Présentez-vous à Saurcroc le jeune à l'Avant-garde kor'kronne."}},
[12225] = {"Mmmm… des ambregraines !", {"Parlez au Maître bûcheron Anderhol au Gîte Ambrepin."}},
[12226] = {"Elles ne font que passer", {"Anderhol, du Gîte Ambrepin, veut que vous lui rapportiez 5 Tiges d’euphraise."}},
[12227] = {"Faites votre devoir", {"Anderhol, du Gîte Ambrepin, veut que vous preniez le Cidre cathartique d’Anderhol et que vous vous en serviez dans les toilettes voisines, afin de récupérer les Ambregraines à moitié digérées."}},
[12228] = {"Reacquiring the Magic [PH]", nil},
[12229] = {"Un lien possible", {"La Voyante des vents Corne-grise, au Bastion de la Conquête, vous demande de vous procurer 8 échantillons de Sang de furbolg affolé sur les Furbolgs du Sanctuaire du Croc rageur et du Sanctuaire du Sang du cœur."}},
[12230] = {"Voler aux poliorcètes", {"Le Nécrogarde Meuledur de Vexevenin vous a demandé de récupérer 6 Bombes de poliorcète."}},
[12231] = {"La progéniture du dieu Ours", {"La Voyante des vents Corne-grise, au Bastion de la Conquête, vous demande de parler à Orsonn au Sanctuaire du Croc rageur et à Kodian au Sanctuaire du Sang du cœur."}},
[12232] = {"Bombarder les balistes", {"Le Nécrogarde Meuledur de Vexevenin vous a demandé d'utiliser les Bombes de poliorcète pour bombarder 5 Balistes de Nouvelle-Âtreval."}},
[12233] = {"[Périmé]Semez votre graine", nil},
[12234] = {"Besoin de savoir", {"La Maître-espionne Récrimine de Vexevenin vous a demandé de voler les Ordres quotidiens de l'Assaut écarlate : abbaye, les Ordres quotidiens de l'Assaut écarlate : caserne, les Ordres quotidiens de l'Assaut écarlate : plage."}},
[12235] = {"Naxxramas et la chute de Garde-hiver", {"Parlez au commandant des griffons Urik, au nid de griffons du Donjon de Garde-hiver."}},
[12236] = {"Ursoc, le dieu Ours", {"La Voyante des vents Corne-grise, au Bastion de la Conquête, vous demande de trouver Tur Ragepatte près de la tanière d'Ursoc et de vaincre Ursoc avec son aide. Utilisez les Cendres purifiées de Vordrassil sur le cadavre d'Ursoc quand ce sera fait."}},
[12237] = {"Le vol du défenseur de Garde-hiver", {"Sauvez 10 Villageois de Garde-hiver sans défense, et remettez-les au Commandant des griffons Urik au Donjon de Garde-hiver."}},
[12238] = {"La purification de Drak'Tharon", {"Drakuru veut que vous utilisiez l’Élixir de Drakuru au piédestal qui se trouve dans Drak’Tharon. Pour ce faire, vous aurez besoin de 5 Mojos endurcis."}},
[12239] = {"L'espionne de Nouvelle-Âtreval", {"Apportez les Ordres quotidiens de l'Assaut écarlate à l'Agent Skoulis à la Nouvelle-Âtreval."}},
[12240] = {"Un moyen pour une fin", {"L'Apothicaire Vicky Levine de Vexevenin vous a demandé d'utiliser les Termites de la famille Levine afin d'attirer le contremaître Kaleiki à l'extérieur et de le tuer."}},
[12241] = {"Détruisez l'arbrisseau", {"La Voyante des vents Corne-grise, au Bastion de la Conquête, veut que vous preniez la Torche verdoyante et que vous vous en serviez pour brûler l’Arbrisseau de Vordrassil. Ensuite, rapportez les Cendres de Vordrassil à la Voyante des vents Corne-grise."}},
[12242] = {"Les graines de Vordrassil", {"La Voyante des vents Corne-grise, du Bastion de la Conquête, veut que vous alliez à Grisegueule pour y récupérer 8 Graines de Vordrassil."}},
[12243] = {"Feu sur les eaux", {"L'Apothicaire Vicky Levine de Vexevenin vous a demandé d'utiliser son Eau brûlante de l'apothicaire afin de mettre le feu aux voiles de la Folie du pécheur.","Pendant que les matelots seront occupés à tenter d'éteindre l'incendie, descendez sous le pont et récupérez les Portulans du capitaine Shely sur celui-ci."}},
[12244] = {"Réparation de déchiqueteurs", {"Synipus, du Chantier d’abattage du Ciel bleu, veut que vous lui rapportiez 3 Déchiqueteurs en panne."}},
[12245] = {"Pas de pitié pour les captifs", {"La Maître-espionne Récrimine de Vexevenin vous a demandé de tuer le Nécrogarde Schneider, le Copiste en chef Kinnedius, l’Ingénieur Burke et le Chancelier Amai.","Quand vous aurez accompli cette tâche, allez faire part de votre succès au Grand exécuteur Ulcéré à Vexevenin."}},
[12246] = {"Un lien possible", {"La Hiérophante Thayreen, au Gîte Ambrepin, vous demande de vous procurer 8 échantillons de Sang de furbolg affolé sur les furbolgs du Sanctuaire du Croc rageur et du Sanctuaire du Sang du cœur."}},
[12247] = {"Les enfants d'Ursoc", {"La Hiérophante Thayreen, au Gîte Ambrepin, vous demande de parler à Orsonn au Sanctuaire du Croc rageur et à Kodian au Sanctuaire du Sang du cœur."}},
[12248] = {"L'arbrisseau de Vordrassil", {"La Hiérophante Thayreen, au Gîte Ambrepin, vous demande de prendre la Torche verdoyante et de l'utiliser pour brûler l'Arbrisseau de Vordrassil. Rapportez les Cendres de Vordrassil au Hiérophante Thayreen."}},
[12249] = {"Ursoc, le dieu Ours", {"La Hiérophante Thayreen, au Gîte Ambrepin, vous demande de trouver Tur Ragepatte près de la tanière d'Ursoc et de vaincre Ursoc avec son aide. Utilisez les Cendres purifiées de Vordrassil sur le cadavre d'Ursoc quand ce sera fait."}},
[12250] = {"Les graines de Vordrassil", {"La Hiérophante Thayreen, au Gîte Ambrepin, vous demande d'aller à Grisegueule et de vous procurer 8 Graines de Vordrassil."}},
[12251] = {"Retourner voir le haut-commandant", {"Parlez au Haut-commandant Halford Verroctone, au Donjon de Garde-hiver dans la Désolation des dragons."}},
[12252] = {"Torturer le tortionnaire", {"Le Grand exécuteur Ulcéré de Vexevenin vous demande d’utiliser 5 fois le Fer à marquer sur le Tortionnaire Alphonse pour obtenir de lui les informations désirées. Ensuite, tuez-le."}},
[12253] = {"Sauvés de la place du village", {"Le Haut-commandant Halford Verroctone, au Donjon de Garde-hiver, dans la Désolation des dragons, vous a demandé de sauver 6 Villageois de Garde-hiver piégés."}},
[12254] = {"Sans une prière", {"L'Agent Skoulis, à la Nouvelle-Âtreval, vous a demandé de vous procurer le Livre de prières de l'évêque Delarue."}},
[12255] = {"Le thane de Voldrune", {"Utilisez Porte-flamme pour vaincre le Thane Torvald Eriksson, puis faites votre rapport au Bûcheron Drake au Gîte Ambrepin."}},
[12256] = {"Les secrets des lieuses de flammes", {"Apportez 3 Talismans imprégnés de flammes au Sergent Nazgrim au Bastion de la Conquête."}},
[12257] = {"Une démonstration de force", {"Tuez 12 Huscarls écorche-dragon puis retournez voir le Sergent Nazgrim au Bastion de la Conquête."}},
[12258] = {"Le destin des morts", {"Le Commandant Lynore Frappevent, du Donjon de Garde-hiver dans la Désolation des dragons, veut que vous tuiez 15 Geists vengeurs."}},
[12259] = {"Le thane de Voldrune", {"Utilisez Porte-flamme pour vaincre le Thane Torvald Eriksson, puis faites votre rapport au Sergent Nazgrim au Bastion de la Conquête."}},
[12260] = {"Le déguisement parfait", {"L'Agent Skoulis, à Nouvelle-Âtreval, vous a demandé de voler l'image d'un Prêtre corbeau de l'Assaut. Rendez-lui le Miroir magique de la banshee lorsque vous aurez terminé."}},
[12261] = {"Aucune chance de fuite", {"Placez un Garde de destruction au bout du chemin permettant de sortir du Sanctuaire draconique obsidien à l'endroit où la route fait place à la neige. Protégez le garde jusqu'à ce qu'il soit chargé, puis retournez auprès de Sérinar."}},
[12262] = {"Personne ne viendra vous sauver", {"Tuez 6 Nécrolytes des profondeurs ardentes et 10 Squelettes fumants à l'extérieur du Sanctuaire draconique obsidien. Quand ce sera fait, retournez voir Sérinar."}},
[12263] = {"Les meilleures intentions", {"Attendez que Sérinar vous ait donné l'apparence d'un Nécrolyte des profondeurs ardentes, puis avancez vers le fond de la Gueule de Neltharion pour découvrir ce qu'y prépare le Culte des damnés. Retournez auprès de Sérinar lorsque vous aurez des détails sur leurs intentions."}},
[12264] = {"L'élimination des damnés", {"Tuer 3 Nécromanciens des profondeurs ardentes, 6 Assemblages fumants et 6 Geists fumants dans la Gueule de Neltharion, puis retourner auprès de Sérinar."}},
[12265] = {"Profaner les profanateurs", {"Localisez et détruisez 8 Runes nécromantiques dessinées sur le sol à l'intérieur de la Gueule de Neltharion. Une fois ceci fait, retournez auprès de Sérinar."}},
[12266] = {"Récits de destruction", {"Retourner au Repos du ver et annoncer à Nalice que la tâche de Sérinar est accomplie."}},
[12267] = {"La flamme de Neltharion", {"Aspergez la zone d'invocation à l'arrière de la Gueule de Neltharion avec la Flamme de Neltharion. Lorsque vous aurez attiré l'attention de Rothin le Putréfié, tuez-le, puis retournez voir Sérinar devant la caverne."}},
[12268] = {"Pour quelques bouts de ferraille de plus…", {"Pépingrenu, du Chantier d'abattage du Ciel bleu, vous demande de ramasser 4 Poulies à gorge, 3 Pignons ébréchés et 2 Ressorts de haute torsion."}},
[12269] = {"Pas dans notre mine", {"Le Généralissime Leoric von Zeldig, au Donjon de Garde-hiver dans la Désolation des dragons, veut que vous récupériez 10 Cartes de mineur de Garde-hiver."}},
[12270] = {"Déchiquetez l'Alliance", {"Grekk, du Chantier d'abattage du Ciel bleu, vous demande de vous procurer 3 Déchiqueteurs et de les lui rapporter."}},
[12271] = {"Le bâtonnet de contrainte", {"Apportez le Bâtonnet du tortionnaire au Grand exécuteur Ulcéré à Vexevenin."}},
[12272] = {"Le minerai sanguinolent", {"L'Ingénieur de siège Quatreflash, au Donjon de Garde-hiver dans la Désolation des dragons, veut que vous alliez récupérer 10 échantillons de Minerai étrange dans la Mine de Garde-hiver."}},
[12273] = {"La trahison", {"Le Grand exécuteur Ulcéré, à Vexevenin, vous a chargé d'utiliser le Bâtonnet de contrainte sur le Commandant Jordan, le Cannonier en chef Zierhut, le Forgeron Bonhomme et le Maître des écuries Mercer.","Lorsque vous aurez utilisé le bâtonnet sur eux, faites-leur payer leur trahison envers l'Assaut écarlate en les exécutant."}},
[12274] = {"Tombé en disgrâce", {"L'Agent Skoulis à Nouvelle-Âtreval vous a demandé de faire sonner la cloche de l'abbaye, puis d'aller parler avec le Grand abbé pour lui soutirer des informations."}},
[12275] = {"Le démo-gnome", {"Parler à l'Ingénieur de siège Quatreflash au Donjon de Garde-hiver dans la Désolation des dragons."}},
[12276] = {"À la recherche de Subrepte", {"Trouvez Subrepte le Démo-gnome dans la Mine de Garde-hiver. Utilisez le Robot-chercheur de Quatreflash si vous avez besoin d'aide pour localiser la mine."}},
[12277] = {"Ne laissez rien au hasard", {"Récupérez une Bombe de la mine de Garde-hiver et utilisez-la pour faire sauter le Puits de mine supérieur de Garde-hiver et le Puits de mine inférieur de Garde-hiver. Retournez faire votre rapport à l'Ingénieur de siège Quatreflash au Donjon de Garde-hiver dans la Désolation des dragons lorsque vous aurez terminé."}},
[12278] = {"Le club de la Bière du mois", {"Apporter le bulletin d'adhésion du club de la « Bière du mois » à Larkin Tonnebière à la taverne de Brûlepierre à Forgefer."}},
[12279] = {"Un appétit d'ours", {"Apportez 6 Saumons nordiques à Hugues Leverre au Comptoir du Cèdre."}},
[12280] = {"Procéder aux réparations", {"Lurz, du Chantier d'abattage du Ciel bleu, vous demande de ramasser 4 Poulies à gorge, 3 Pignons ébréchés et 2 Ressorts de haute torsion."}},
[12281] = {"Étudier les machines de guerre du Fléau", {"Livrez le Colis de Quatreflash au Haut-commandant Halford Verroctone au Donjon de Garde-hiver."}},
[12282] = {"L'empreinte du passé", {"Zelig le Visionnaire au Donjon de Garde-hiver dans la Désolation des dragons veut que vous récupériez son Orbe de divination."}},
[12283] = {"C'est la vérité, d'ailleurs", {"L'Agent Skoulis vous a demandé de voler le Journal du grand général Abbendis et de le remettre au Grand exécuteur Ulcéré à Vexevenin."}},
[12284] = {"Mettez-les sur les dents", {"Le Commandant Bargok vous demande de tuer 15 unités ou personnages-joueurs de l'Alliance au Chantier d'abattage du Ciel bleu."}},
[12285] = {"Œil pour œil", {"Le Grand exécuteur Ulcéré, à Vexevenin, vous a ordonné de revenir avec la Tête du grand général."}},
[12286] = {"Seau de bonbons", nil},
[12287] = {"Orik Cœur-vrai et la côte Oubliée", {"Allez demander de l’aide à Orik Cœur-vrai aux Confins de l’Aube, dans la Désolation des dragons."}},
[12288] = {"Débordée !", {"Aumana, au Chantier d'abattage du Ciel bleu, vous demande d'utiliser le Garrot de rénovation sur 10 Tirailleurs blessés."}},
[12289] = {"Frappez-les tant qu'ils sont à terre", {"Le Sergent Hartsman vous demande d'abattre 15 unités ou personnages-joueurs de la Horde au Chantier d'abattage du Ciel bleu."}},
[12290] = {"L'élixir de tourbeline", {"Orik Cœur-vrai aux Confins de l’Aube, dans la Désolation des dragons, veut que vous récupériez 5 Tourbelines dans les Hauts Surréels."}},
[12291] = {"Le récit oublié", {"Orik Cœur-vrai aux Confins de l’Aube, dans la Désolation des dragons, veut que vous écoutiez le récit d’un Paysan oublié, d’un Fusilier oublié, d’un Chevalier oublié et d’un Fantassin oublié."}},
[12292] = {"Le soutien local", {"Le Lieutenant Dumont, du Gîte Ambrepin, veut que vous alliez au Comptoir des Conifères et que vous récupériez le Coffre en cèdre."}},
[12293] = {"Sceller l'accord", {"Remettez le Coffre en cèdre à Ivan, au Poste de la Tremblaie."}},
[12294] = {"Un marché incertain", {"Retournez voir le Lieutenant Dumont au Gîte Ambrepin."}},
[12295] = {"Un exercice de diplomatie", {"Parlez à l'Envoyé Ducal à Ruissargent."}},
[12296] = {"La vie ou la mort", {"Rheanna, au Chantier d'abattage du Ciel bleu, vous demande d'utiliser le Bandage de rénovation sur 10 Soldats d'infanterie de la marche de l'Ouest blessés."}},
[12297] = {"Traîtres et trahisons", {"Le Capitaine Adams vous a demandé de montrer la Missive de l'Alliance à Greer Martel-à-minerai, au Donjon de la Garde de l’ouest."}},
[12298] = {"Le haut-commandant Halford Verroctone", {"Remettez la Missive de l’Alliance au Haut-commandant Halford Verroctone, au Donjon de Garde-hiver, à l’est de la Désolation des dragons."}},
[12299] = {"L'hospitalité du nord", {"Sergei de Ruissargent veut que vous tuiez 8 Maraudeurs du bastion de la Conquête."}},
[12300] = {"Montrez votre cran", {"Trouvez le Sergent Bris’os au nord-ouest de Ruissargent, et Montrez votre cran."}},
[12301] = {"La vérité nous libérera", {"Orik Cœur-vrai aux Confins de l’Aube, dans la Désolation des dragons, veut que vous utilisiez l’Orbe cristallin d’Orik dans les ruines situées sur la Côte Oubliée pour racheter l’âme des damnés.","Revenez voir Orik avec son Orbe cristallin quand vous aurez réussi."}},
[12302] = {"Un avertissement", {"Allez à l'extrémité nord-ouest de Ruissargent."}},
[12303] = {"Financer l'effort de guerre", {"L'intendant Bartlett de Vexevenin vous a demandé de récupérer 6 Trésors oubliés."}},
[12304] = {"Une propriété sur le front de mer", {"Le Géomètre Hansen, juste au nord de la Côte Oubliée, vous a demandé de tuer 20 Fantômes oubliés."}},
[12305] = {"Un adieu", {"Retournez voir Zelig le Visionnaire au Donjon de Garde-hiver dans la Désolation des dragons."}},
[12306] = {"Le club de la Bière du mois", {"Apporter le bulletin d'adhésion du club de la « Bière du mois » à Ray'ma au pavillon de Buissombre dans la vallée des Esprits d'Orgrimmar."}},
[12307] = {"La racine d'aconit", {"Katja de Ruissargent vous demande d'arracher 8 Racines d'aconit."}},
[12308] = {"L'évasion de Ruissargent", {"Ouvrez la Cage en bois et aidez l'Éclaireur de l'Alliance libéré à s'échapper de Ruissargent. Présentez-vous au Lieutenant Dumont quand vous aurez atteint le Gîte Ambrepin."}},
[12309] = {"Trouvez Durkon !", {"Trouvez le Cavalier Durkon à la Crypte de Garde-hiver, dans la Désolation des dragons."}},
[12310] = {"Une réponse rapide", {"Le Lieutenant Dumont vous a demandé d'aller au Poste de la Lisière et de tuer Vladek et 7 Chasseurs de Ruissargent."}},
[12311] = {"La crypte des nobles", {"Le Cavalier Durkon, au Donjon de Garde-hiver, veut que vous tuiez le Nécro-seigneur Amarion."}},
[12312] = {"Les secrets du Fléau", {"Remettez le Tome relié de chair au Cavalier Durkon, au Donjon de Garde-hiver dans la Désolation des dragons."}},
[12313] = {"[UNUSED] Sauvez la fête des Brasseurs !", {"Allez voir l’Espion de la fête des Brasseurs au Sinistre écluseur, dans les Profondeurs de Rochenoire."}},
[12314] = {"Mort au capitaine Zorna !", {"Tuez le Capitaine Zorna puis présentez-vous au Baron Lefranc dans la Baie de la KapitalRisk."}},
[12315] = {"Abattez le capitaine Brilleflot !", {"Tuez le Capitaine Brilleflot puis présentez-vous au Général Khazgar dans la Baie de la KapitalRisk."}},
[12316] = {"Ra-baie-ssez l'ennemi", {"Tuez 10 unités ou personnages-joueurs de la Horde dans la Baie de la KapitalRisk, puis retournez voir le Lieutenant Stuart."}},
[12317] = {"Ra-baie-ssez l'ennemi", {"Tuez 10 unités ou personnages-joueurs de l'Alliance dans la Baie de la KapitalRisk, puis retournez auprès du Centurion Kaggrum."}},
[12318] = {"Sauvez la fête des Brasseurs !", {"Parlez avec l'Espion de la fête des Brasseurs, au Sinistre écluseur dans les Profondeurs de Rochenoire."}},
[12319] = {"Le mystère du tome", {"Remettez le Tome relié de chair au haut-commandant Halford Verroctone, au Donjon de Garde-hiver dans la Désolation des dragons."}},
[12320] = {"Comprendre la langue des morts", {"Remettez le Tome relié de chair à l’Inquisiteur Hallard, dans la prison du donjon de Garde-hiver."}},
[12321] = {"Un sermon édifiant", {"Attendez que l’Inquisiteur Hallard ait terminé son Sermon édifiant, puis retournez voir le Haut-commandant Halford Verroctone au Donjon de Garde-hiver dans la Désolation des dragons avec les informations qui vous auront été communiquées."}},
[12323] = {"Enfumez-les", {"Utilisez la Bombe fumigène pour forcer à sortir de leurs bâtiments 20 Traînards de la KapitalRisk, puis faites votre rapport au Sergent Downey."}},
[12324] = {"Enfumez-les", {"Utilisez la Bombe fumigène pour forcer à sortir de leurs bâtiments 20 Traînards de la KapitalRisk, puis faites votre rapport au Garde de pierre Totem-de-rage."}},
[12325] = {"En territoire ennemi", {"Parlez au Commandant des griffons Urik pour prendre un vol à destination du Poste de Thorson. Lorsque vous serez au Poste de Thorson dans la Désolation des dragons, présentez-vous au duc Auguste Marteau-des-ennemis."}},
[12326] = {"Char à vapeur surprise", {"Utilisez un Char à vapeur de l'Alliance pour détruire 6 Chariots à peste et déposer les Soldats d'élite de la 7e Légion au Mausolée de Garde-hiver. Si vous réussissez, allez parler à Ambo Cash, à l'intérieur du Mausolée de Garde-hiver, dans la Désolation des dragons."}},
[12327] = {"Expérience extra-corporelle", {"Buvez la Potion vaporeuse près de la Boule de cristal de Ruuna et assistez à la Vision du passé."}},
[12328] = {"La demande de Ruuna", {"Ruuna l'Aveugle vous demande de vous procurer 4 mesures de Poudre vaporeuse."}},
[12329] = {"Destin et coïncidences", {"Parlez à Sasha au Comptoir du Pin blanc."}},
[12330] = {"Anatoly va parler", {"Sasha au Comptoir du Pin blanc vous demande de prendre la Fléchette tranquillisante et de l'utiliser sur Tatjana à Solstice. Ramenez ensuite Tatjana au Comptoir du Pin blanc."}},
