[12411] = {"Le serment d'une sœur", {"Cherchez Anya dans la Tanière des Hurlesoir près de Solstice."}},
[12412] = {"L'ami de mon ennemi", {"La Conquérante Krenna du Bastion de la Conquête vous demande de tuer 8 Chasseurs de Ruissargent ainsi que leur chef, Vladek, au poste de la Lisière."}},
[12413] = {"L'attaque de Ruissargent", {"La Conquérante Krenna du Bastion de la Conquête vous demande de tuer 8 Défenseurs de Ruissargent dans les Collines de Ruissargent."}},
[12414] = {"Monture guère solide", {"Chevauchez 5 Mustangs des hautes-terres jusqu'à l'Écuyer Percy, au Campement de la brigade de la marche de l'Ouest."}},
[12415] = {"L'homme qui hurlait à l'oreille des chevaux", {"Effrayez 15 Mustangs des hautes-terres puis retournez voir Soulok Furie-des-tempêtes au Camp Oneqwah."}},
[12416] = {"Une vive bataille", {"Aidez le Capitaine Iskandar à tenir les canyons en participant à la mort de 12 Goules algides attaquantes, 8 Geists algides attaquants et 1 Abomination algide attaquante. Retournez voir Ceristrasz lorsque vous aurez terminé."}},
[12417] = {"Retourner à la terre", {"Entrez dans le Sanctuaire draconique rubis par l'une des passes du nord ou du sud et cherchez des Glands de rubis. Utilisez les Glands de rubis sur les cadavres de dragons rouges pour rendre leur corps à la terre. Retournez auprès de Ceristrasz une fois votre tâche accomplie."}},
[12418] = {"À travers les champs de flammes", {"Entrez dans le Sanctuaire draconique rubis par l'une des passes du nord ou du sud, tuez 6 Nécromanciens algides et éliminez la source de la corruption sous le sanctuaire. Retournez voir Ceristrasz une fois votre tâche accomplie."}},
[12419] = {"Le destin du sanctuaire draconique rubis", {"Apportez la Broche de rubis de Dahlia Soletouche à Krasus, dans la Chambre du Conseil régnant au Repos du ver, et informez-le du destin de Dahlia."}},
[12420] = {"Le club de la Bière du mois", {"Apporter le bulletin d'adhésion du club de la « Bière du mois » à Larkin Tonnebière à la taverne de Brûlepierre à Forgefer."}},
[12421] = {"Le club de la Bière du mois", {"Apporter le bulletin d'adhésion du club de la « Bière du mois » à Ray'ma au pavillon de Buissombre dans la vallée des Esprits d'Orgrimmar."}},
[12422] = {"Clémence tactique", {"Apportez la Lettre de grâce à la Trappeuse capturée dans les Collines de Ruissargent."}},
[12423] = {"Le journal de Mikhail", {"Apportez le Journal de Mikhail à la Conquérante Krenna au Bastion de la Conquête."}},
[12424] = {"Gorgonna", {"Apportez le Journal de Mikhail à Gorgonna au Bastion de la Conquête."}},
[12425] = {"Ruuna l'Aveugle", {"Parlez à Ruuna l’Aveugle, au Camp de Ruuna, près de la Passe de Vordrassil."}},
[12426] = {"PÉRIMÉ", nil},
[12427] = {"La fosse des Conquérants : face à un ours !", {"Vainquez Cuiracier dans la fosse des Conquérants, puis parlez avec Vel'jen la Paperasse pour obtenir votre récompense."}},
[12428] = {"La fosse des Conquérants : le furbolg fou !", {"Vainquez Torgg Totem-de-tonnerre dans la fosse des Conquérants, puis parlez avec Vel'jen la Paperasse pour obtenir votre récompense."}},
[12429] = {"La fosse des Conquérants : sang et métal", {"Vainquez Rouillesang dans la fosse des Conquérants, puis parlez avec Vel'jen la Paperasse pour obtenir votre récompense."}},
[12430] = {"La fosse des Conquérants : mort probable", {"Vainquez Horgrenn Tranchenfer dans la fosse des Conquérants, puis parlez avec Vel'jen la Paperasse pour obtenir votre récompense."}},
[12431] = {"La fosse des Conquérants : l'affrontement final", {"Vainquez Krenna dans la fosse des Conquérants, puis parlez avec Gorgonna pour obtenir votre récompense."}},
[12432] = {"À cheval sur la fusée…", {"Le Général Gorlok, du phare de la Baie de la KapitalRisk, veut que vous détruisiez le Transport de bois de l’Alliance.","Le général Gorlok n'est présent au phare que si la Horde contrôle la baie."}},
[12433] = {"La chasse au dissolvant", {"Gurtor, de la base de la Horde de la Baie de la KapitalRisk, veut que vous lui rapportiez une boîte d’Élément 115."}},
[12434] = {"Le retour de la chasse au dissolvant", nil},
[12435] = {"Rapport au seigneur Devrestrasz", {"Parlez avec le seigneur Devrestrasz au Temple du Repos du ver."}},
[12436] = {"Un revenu supplémentaire", {"L’Approvisionneur Lorkran, du Bastion de la Conquête, veut que vous récupériez 5 morceaux de Venaison succulente sur les Cerfs haute-ramure des alentours."}},
[12437] = {"À cheval sur la fusée…", {"Le Commandant Howser, du phare de la Baie de la KapitalRisk, veut que vous couliez le chargement de bois de la Horde.","Le Commandant Howser n'est présent au phare que lorsque celui-ci est aux mains de l’Alliance."}},
[12438] = {"On recherche : Kreug le Parjure", {"Tuez Kreug le Parjure, puis allez voir le Généralissime Leoric von Zeldig au Donjon de Garde-hiver dans la Désolation des dragons pour toucher votre récompense."}},
[12439] = {"Une perturbation à l'ouest", {"Parlez à Rodney Wells au Donjon de Garde-hiver pour prendre un vol à destination du Repos des étoiles, dans l’ouest de la Désolation des dragons."}},
[12440] = {"En route pour le Repos des étoiles !", {"Parlez à l’Image de l’archimage Modera, au Repos des étoiles, dans l’ouest de la Désolation des dragons."}},
[12441] = {"On recherche : le Grand chaman Sangrepatte", {"Tuez le Grand chaman Sangrepatte, puis allez voir le Généralissime Leoric von Zeldig au Donjon de Garde-hiver dans la Désolation des dragons pour toucher votre récompense."}},
[12442] = {"On recherche : le commandant de l'Assaut Iustus", {"Tuez le Commandant de l'Assaut Iustus, puis allez voir le Généralissime Leoric von Zeldig au Donjon de Garde-hiver dans la Désolation des dragons pour toucher votre récompense."}},
[12443] = {"La chasse au dissolvant", {"Barbillonneur, de la base de l'Alliance de la Baie de la KapitalRisk, veut que vous lui rapportiez une boîte d’Élément 115."}},
[12444] = {"Escarmouches sur la rivière Noire", {"Le Capitaine des éclaireurs Carter veut que vous tuiez 10 unités ou personnages-joueurs de la Horde au Camp de bûcherons de la rivière Noire."}},
[12446] = {"Le retour de la chasse au dissolvant", nil},
[12447] = {"Le Sanctuaire draconique obsidien", {"Allez au Sanctuaire draconique obsidien au nord-ouest du Repos du ver et juste à l'ouest du Portail du Courroux. Retrouvez-y Sérinar et proposez-lui votre aide."}},
[12448] = {"Bataille ardente", {"Aidez le Capitaine Drayzen à tenir les canyons en participant à la mort de 12 Goules algides attaquantes, 8 Geists algides attaquants et 1 Abomination algide attaquante. Retournez auprès de Vargastrasz une fois cette tâche terminée."}},
[12449] = {"Retourner à la terre", {"Pénétrez dans le Sanctuaire draconique rubis par le passage sud et cherchez les Glands de rubis. Utilisez les Glands de rubis sur les dragons rouges décédés pour ramener leur corps à la vie. Retournez auprès de Vargastrasz une fois la tâche accomplie."}},
[12450] = {"À travers les champs de flammes", {"Entrez dans le Sanctuaire draconique rubis par la passe du sud, tuez 6 Nécromanciens algides et éliminez la source de la corruption sous le sanctuaire. Retournez voir Vargastrasz une fois votre tâche accomplie."}},
[12451] = {"En route pour le camp Oneqwah", {"Parlez à l'Éclaireur Vor'takh au Camp Oneqwah."}},
[12452] = {"zzOLD The Fate of the Ruby Dragonshrine", {"Apportez la Broche de rubis de Dahlia à Ceristrasz, et racontez-lui son destin."}},
[12453] = {"Vu d'en haut", {"La Voyante des vents Corne-grise veut que vous utilisiez la Plume argentée sur 6 Aigles impériaux dans les Grisonnes."}},
[12454] = {"Le cycle de la vie", {"Nishera la Gardienne du jardin au Sanctuaire draconique émeraude vous a demandé de tuer 5 Serres-du-ciel émeraudes."}},
[12455] = {"Dispersées aux quatre vents", {"Ambo Cash, du Mausolée de Garde-hiver dans la Désolation des dragons, veut que vous récupériez 8 Caisses de munitions de Garde-hiver."}},
[12456] = {"La plume d'Alystros", {"Nishera la Gardienne du jardin au Sanctuaire draconique émeraude vous a demandé d'obtenir La plume d'Alystros."}},
[12457] = {"Votre amie, la mitrailleuse", {"Ambo Cash, du Mausolée de Garde-hiver dans la Désolation des dragons, veut que vous sauviez 8 Soldats blessés de la 7e Légion."}},
[12458] = {"Des graines de flagellant", {"Le Seigneur Itharius, au sommet du Temple du Repos du ver, vous a demandé de lui rapporter 3 Graines de flagellant."}},
[12459] = {"Ce qui peut donner la vie peut également la reprendre", {"Le Seigneur Itharius, au sommet du Temple du Repos du ver, vous a demandé de détruire un Wyrm de givre réanimé affaibli, Turgid l'Infâme affaibli et le Surveillant Miremort affaibli."}},
[12460] = {"Rapport au sanctuaire draconique rubis", {"Allez faire votre rapport à Ceristrasz, sur la crête à l'est du Sanctuaire draconique rubis, à l'ouest du Repos du ver."}},
[12461] = {"Rapport au sanctuaire draconique rubis", {"Allez faire votre rapport à Vargastrasz, sur une crête à l'extérieur du Sanctuaire draconique rubis, juste à l'est du Marteau d'Agmar."}},
[12462] = {"Éclatons-nous un peu !", {"Yord « Calamité » Barbeglace, au Mausolée de Garde-hiver dans la Désolation des dragons, veut que vous tuiez le Nécro-seigneur X'avius, le Nécro-seigneur Horus et 15 membres du Fléau de Naxxramas."}},
[12463] = {"Il faut trouver Barbapille !", {"Ambo Cash, au Mausolée de Garde-hiver dans la Désolation des dragons, vous a demandé de retrouver Barbapille."}},
[12464] = {"Ma vieille ennemie", {"Rapportez la tête du Grand général au Commandant Eligor Portelaube au Donjon de Garde-hiver."}},
[12465] = {"Le journal de Barbapille", {"Récupérez les pages 4, 5, 6 et 7 du journal de Barbapille et donnez-les à Ambo Cash au Mausolée de Garde-hiver dans la Désolation des dragons."}},
[12466] = {"À la poursuite de Foudreglace : le front de la 7e Légion", {"Présentez-vous au Commandant de la Légion Tyralion au Front de la 7e Légion au centre de la Désolation des dragons."}},
[12467] = {"À la poursuite de Foudreglace : le phylactère de Thel'zan", {"Reprenez le Phylactère de Thel'zan à Foudreglace et rapportez-le au Haut-commandant Halford Verroctone au Donjon de Garde-hiver."}},
[12468] = {"Les exigences de la conquérante", {"Parlez au Sergent Nazgrim au Bastion de la Conquête."}},
[12469] = {"Retour à l'envoyeur", {"Suivez la crête vers l'est et livrez le paquet d'explosifs à Xink au Poste de Rouilletuyère dans le nord de la Désolation des dragons."}},
[12470] = {"Mystère de l'infini", {"Chromie, au sommet du Temple du Repos du ver, vous a demandé de vous rendre au Sanctuaire draconique bronze. Une fois à l'intérieur, posez le Sablier de l'Éternité au sol.","Protégez le sablier le temps qu'il se vide, puis retournez la voir avec toute information que vous aurez obtenue."}},
[12471] = {"La cruauté des kvaldirs", {"Parlez à Karuk du Prisonnier rohart capturé."}},
[12472] = {"Finalité", {"Apportez le Phylactère de Thel'zan au commandant de la Légion Yorik à l'intérieur du Mausolée de Garde-hiver de la Désolation des dragons."}},
[12473] = {"Une fin et un début", {"Vainquez Thel'zan le Portebrune et faites votre rapport au Haut-commandant Halford Verroctone au Donjon de Garde-hiver dans la Désolation des dragons."}},
[12474] = {"En route pour le bastion Fordragon !", {"Rendez-vous au bastion Fordragon dans la Désolation des dragons et parlez au généralissime Bolvar Fordragon."}},
[12475] = {"Les secrets que cachent les hommes", {"Zelig le Visionnaire au Donjon de Garde-hiver dans la Désolation des dragons veut que vous trouviez la Carte de l’Assaut et que vous la lui rapportiez."}},
[12476] = {"Le retour de la Croisade ?", {"Le Commandant Lynore Frappevent, du Donjon de Garde-hiver dans la Désolation des dragons, veut que vous tuiez 20 membres de l’Assaut écarlate au Camp de base de l’Assaut écarlate."}},
[12477] = {"Le Chemin de la rédemption", {"Remettez un exemplaire du « Chemin de la rédemption » au Haut-commandant Halford Verroctone, au Donjon de Garde-hiver dans la Désolation des dragons."}},
[12478] = {"La caverne de Deuillegivre", {"Zelig le Visionnaire, du donjon de Garde-hiver dans la Désolation des dragons, veut que vous utilisiez l’Orbe de divination de Zelig au fond de la Caverne de Deuillegivre. Découvrez les secrets du passé !"}},
[12479] = {"En route pour le Norfendre !", {"Parlez au Recruteur de l’Alliance au Donjon de la Bravoure, dans la toundra Boréenne ; ou à celui de Valgarde, dans le Fjord Hurlant."}},
[12480] = {"En route pour le Norfendre !", {"Parlez au Recruteur de la Horde du Bastion Chanteguerre, dans la toundra Boréenne, ou à celui de l’Accostage de la Vengeance, dans le Fjord Hurlant."}},
[12481] = {"Retourner le couteau dans la plaie", {"Lisez l’Insulte vrykule à Bjorn Halgurdsson à Torvheim, puis conduisez-le à l’embuscade de Lydell et participez à sa mise à mort."}},
[12482] = {"Contre Nifflevar", {"Le capitaine des forestiers Areiel vous demande d'aller à Nifflevar et d'y tuer 5 Guerriers écorche-dragon, 4 Voyants des runes écorche-dragon et 4 Chiens de chasse écorche-dragon."}},
[12483] = {"Le ragoût de chapeneiges", {"Dasnob, aux Sources de granit, veut que vous récoltiez 5 Chapeneiges chatoyants et 5 Yeux de serpents des glaces dans les Ruines de Drak'Zin, et 5 brins de Réglisse à Zeb'Halak."}},
[12484] = {"Kébab de Fléau", {"Dasnob, des Sources de granit, veut que vous remettiez une Carcasse de troll momifié à Mack Froussen et que vous la jetiez au feu."}},
[12485] = {"Howling Fjord: aa - A - LK FLAG", nil},
[12486] = {"À l'avant-poste de Bor'gorok, vite !", {"Allez voir la spirite Grognecroc à l'Avant-poste de Bor'gorok."}},
[12487] = {"Au bastion de la Conquête, mais avec prudence !", {"Présentez-vous à la Conquérante Krenna au Bastion de la Conquête dans les Grisonnes."}},
[12488] = {"Le grand exécuteur a besoin de vous", {"Parlez au Grand exécuteur Ulcéré à Vexevenin, dans la Désolation des dragons."}},
[12489] = {"Bienvenue dans le bassin de Sholazar !", {"Suivez le chemin au nord-ouest pour rejoindre le camp que Total a mentionné."}},
[12490] = {"La vengeance de Veehja", {"Veehja, au sanctuaire des Écailles, vous demande de tuer y 6 Mariniers skadir, 3 Tisse-brume skadir et 2 Lanceurs de runes skadir."}},
[12491] = {"La navre-bière de Navrebière", {"Donnez la Navre-bière de Navrebière à Ipfelkofer Baril-de-fer au camp de la fête des Brasseurs près de Forgefer."}},
[12492] = {"La navre-bière de Navrebière", {"Donnez la navre-bière de Navrebière à Perce Fauchebaril au camp de la fête des Brasseurs près d'Orgrimmar."}},
[12493] = {"Test PvP", {"Tuez 10 méchants."}},
[12494] = {"DRAPEAU: publicité du maître de monte (20)", nil},
[12495] = {"Une audience auprès de la reine dragon", {"Trouvez Alexstrasza la Lieuse-de-vie au Temple du Repos du ver à la Désolation des dragons."}},
[12496] = {"Une audience auprès de la reine dragon", {"Trouvez Alexstrasza la Lieuse-de-vie au Temple du Repos du ver, à la Désolation des dragons."}},
[12497] = {"Galakrond et le Fléau", {"Parlez à Torastrasza au Temple du Repos du ver de la Désolation des dragons."}},
[12498] = {"Sur des ailes de rubis", {"Détruisez 30 Charognards du désert et récupérez la faux d'Antiok. Retournez auprès d'Alexstrasza la Lieuse-de-vie au Temple du Repos du ver si vous parvenez à accomplir cette tâche."}},
[12499] = {"Retour à Angrathar", {"Parlez au Généralissime Bolvar Fordragon au Bastion Fordragon et rapportez-lui votre victoire contre le Fléau."}},
[12500] = {"Retour à Angrathar", {"Parlez à Saurcroc le Jeune à l'Avant-garde kor'kronne et rapportez-lui votre victoire contre le Fléau."}},
[12501] = {"Patrouille anti-trolls", {"Le Commandant Kunz du Séjour d'Argent vous a demandé de vous rendre aux quatre postes de la Croisade d'argent, de suivre les ordres de leurs capitaines et de revenir ensuite lui faire un rapport.","Le capitaine Brandon et le capitaine Rupert se trouvent à Drak'Sotra, le capitaine Grondel à Drak'Agal, et l'alchimiste Finklestein à Heb'Valok."}},
[12502] = {"Patrouille anti-trolls : hissez les étendards !", {"Le Capitaine Brandon vous a demandé de placer la Bannière de la Croisade d'argent sur les Piédestaux drakkari à Drak'Sotra"}},
[12503] = {"Séjour de paix", {"Le Commandant Falstaav vous a demandé de détruire 10 créatures du Fléau dans la cour du Séjour d'Argent."}},
[12504] = {"Croisade d'argent, parés au départ !", {"Le Sergent Stockemarteau, à l'Autel de Sseratus, vous demande de parler avec 10 de ses Soldats d'argent et de leur dire de revenir au rapport."}},
[12505] = {"De nouveaux ordres pour le sergent Stockemarteau", {"Le Commandant Kunz vous a demandé de remettre les Nouveaux ordres de déploiement au Sergent Stockemarteau à l'Autel de Sseratus."}},
[12506] = {"Ennuis d'autel", {"Le Maléficieur Ubungo, du Séjour d'Argent, vous a demandé d'explorer le bâtiment principal de l'autel à l'Autel de Sseratus."}},
[12507] = {"Étrange mojo", {"Apportez le Mojo étrange au Maléficieur Ubungo au Séjour d'Argent."}},
[12508] = {"Passer la serpillère", {"Le Caporal Maga, à l'Autel de Sseratus, veut que vous tuiez 10 fidèles de Sseratus parmi les Champions de Sseratus, les Prêtres de Sseratus et les Charmeurs de serpents drakkari."}},
[12509] = {"Patrouille anti-trolls : un peu de courage !", {"Le Capitaine Rupert, à Drak'Sotra, vous demande de redonner du courage aux Recrues de la Croisade."}},
[12510] = {"Des fluides élémentaires précieux", {"Le Maléficieur Ubungo, au Séjour d'Argent, veut que vous obteniez 3 Fluides élémentaires précieux."}},
[12511] = {"Les Grisonnes ont besoin de nous", {"Présentez-vous au Lieutenant Dumont, au Gîte Ambrepin, dans l'ouest des Grisonnes."}},
[12512] = {"Ne laissez personne derrière vous", {"Le Dr Rogers à Drak'Sotra veut que vous trouviez le Croisé Lamoof, le Croisé Jonathan et la Croisée Joséphine. Utilisez le Bandage de croisé sur eux, puis mettez-les en sécurité auprès du Dr Rogers."}},
[12513] = {"Un joli chapeau...", {"Don Carlos vous a, par inadvertance, chargé de défier et vaincre son jeune alter ego de la Hautebrande d'antan. Lorsque ce sera fait, apportez le Chapeau de don Carlos à Don Carlos, à Tanaris, comme preuve de votre victoire."}},
[12514] = {"Un cocktail aux champignons", {"Récupérez 10 Champignons chapebrouilles et rapportez-les au Maléficieur Ubungo au Séjour d'Argent."}},
[12515] = {"Un joli chapeau...", {"Don Carlos vous a, par inadvertance, chargé de défier et vaincre son jeune alter ego de la Hautebrande d'antan. Lorsque ce sera fait, apportez le Chapeau de don Carlos à Don Carlos, à Tanaris, comme preuve de votre victoire."}},
[12516] = {"Il ne faut pas abuser des bonnes choses", {"Tuez le Prophète troublé de Sseratus et retournez auprès du Maléficieur Ubungo au Séjour d'Argent."}},
[12517] = {"Suite de Voleurs", {"Utilisez la Suite de Voleurs pour invoquer une Diseuse de bonne aventure de la foire de Sombrelune et donnez-lui la suite."}},
[12518] = {"Suite de Mages", {"Utilisez la Suite de Mages pour invoquer une Diseuse de bonne aventure de la foire de Sombrelune et donnez-lui la suite."}},
[12519] = {"Patrouille anti-trolls : vous voulez quoi ? Une médaille ?", {"Le Capitaine Grondel à l'extérieur du Colisée vous demande de récupérer 7 Médailles drakkari sur les Cadavres drakkari des alentours."}},
[12520] = {"La maîtrise des rhinos : l'épreuve", {"Tuez 15 Rhinocéros cornéclats et retournez voir Hemet Nesingwary au Camp de base de Nesingwary."}},
[12521] = {"Où est passé Hemet Nesingwary ?", {"Vous effectuez cette quête"}},
[12522] = {"Besoin d'un moteur ? Servez-vous !", {"Weslex Viveclef, au Camp de base de Nesingwary, veut que vous lui rapportiez un Moteur de machine volante."}},
[12523] = {"Pièces détachées à gogo", {"Apportez 7 Pièces détachées de la KapitalRisk à Weslex Viveclef au Camp de base de Nesingwary."}},
[12524] = {"La mésaventure de la KapitalRisk", {"Debaar, au Camp de base de Nesingwary, veut que vous tuiez 15 membres de la KapitalRisk. Vous pouvez tuer des Forbans de la KapitalRisk ou des Excavateurs de la KapitalRisk, au choix."}},
[12525] = {"Effacez-moi ce sourire de son visage", {"Tuez le Contremaître Ricarnaque et Tourte-au-rôt, puis retournez auprès de Debaar au Camp de base de Nesingwary."}},
[12526] = {"La maîtrise des rhinos : la traque", {"Parlez à l'Oracle Soo-fiett aux Os de Nozronn."}},
[12527] = {"Rôdeurs gloutons", {"Apportez 5 Cristaux de basilic à l'Alchimiste Finklestein dans le sanctuaire de Heb'Valok."}},
[12528] = {"Jouer le jeu", {"Présentez-vous au Haut-chaman Rakjak à la Colline de Frénécœur en tant que nouvel « esclave »."}},
[12529] = {"L'esclave du chasseur de singes", {"Tuez 8 Fourrageurs poings-durs et 6 Fonceurs poings-durs pour le Haut-chaman Rakjak à la Colline de Frénécœur.","Si vous perdez les Chaînes de Goregek, parlez à l'Ancien Harkek sur la Colline de Frénécœur."}},
[12530] = {"Asticoter les Poings-doux", {"Goregek vous a donné un Asticoteur à poings-doux. Utilisez-le pour piquer les poings-doux jusqu'à ce que la Matriarche poing-dur sorte de sa cachette.","Tuez la Matriarche poing-dur, et retournez ensuite voir le Haut-chaman Rakjak à la Colline de Frénécœur.","Si vous perdez les Chaînes de Goregek, parlez à l'Ancien Harkek à la Colline de Frénécœur."}},
[12531] = {"La menace souterraine", {"Trouvez Serfex dans les sables de la rivière sud, tuez-le et rapportez sa Griffe au Haut-chaman Rakjak à la Colline de Frénécœur.","Si vous perdez la Craie usée de Dajik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12532] = {"Il y a un couac !", {"Capturez 12 Poulets évadés pour l'Ancien Harkek.","Si vous perdez la Craie usée de Dajik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12533] = {"L'apprenti du Chasse-guêpe", {"Tuez 6 Guêpes de la ruche Saphir et 9 Bourdons de la ruche Saphir pour le Haut-chaman Rakjak à la colline de Frénécœur.","Si vous perdez la Craie usée de Dajik, allez parler à l'Ancien Harkek sur la Colline de Frénécœur."}},
[12534] = {"La reine de saphir", {"Rapportez le Dard de la reine de saphir au Haut-chaman Rakjak à la Colline de Frénécœur.","Si vous perdez la Craie usée de Dajik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12535] = {"Des bêtises", {"Récupérez 8 Grappes de cristaux pour le Haut-chaman Rakjak à la Colline de Frénécœur."}},
[12536] = {"Une dure chevauchée", {"Chevauchez le Crocilisque captif jusqu'au Refuge de Murmebrume et parlez ensuite à Zepik pour obtenir de nouvelles instructions.","Si vous perdez le Cor de chasse de Zepik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12537] = {"La foudre frappe vraiment deux fois", {"Détruisez les Grappes de cristaux des Confins-du-ciel, en face du Sanctuaire du temps Murmebrume, pour le compte de Zepik.","Si vous perdez le Cor de chasse de Zepik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12538] = {"La brume n'écoute pas", {"Tuez 12 Gorlocs murmebrumes pour Zepik.","Si vous perdez le Cor de chasse de Zepik, allez parler à l'Ancien Harkek à la Colline de Frénécœur."}},
[12539] = {"À pattes", {"Retrouvez votre chemin jusqu'à la Colline de Frénécœur, puis parlez au Haut-chaman Rakjak."}},
[12540] = {"Je n'ai fait que suivre les ordres…", {"Trouvez un Oracle parlepluie blessé pour le Haut-chaman Rakjak à la Colline de Frénécœur."}},
[12541] = {"Patrouille anti-trolls : l'apprenti alchimiste", {"L'Alchimiste Finklestein de Heb'Valok vous demande de l'aider dans la fabrication d'un sérum de vérité.","Quand vous serez prêt à commencer, allez lui parler et suivez ses instructions."}},
[12542] = {"L'appel de la Croisade", {"Faites votre rapport au Croisé Valus à la Confiance de Lumière, dans la Désolation des dragons."}},
[12543] = {"Une offrande à Soo-fiett", {"Rapportez 5 Crocs de cobras intacts à l'Oracle Soo-fiett, près des Os de Nozronn."}},
[12544] = {"Les os de Nozronn", {"Utilisez l'encens de Soo-fiett près des Os de Nozronn, puis retournez voir Hemet Nesingwary au camp de base de Nesingwary avec l'information obtenue."}},
[12545] = {"La purification de Jintha'kalar", {"Le Croisé Valus de la Confiance de Lumière, à la Désolation des dragons, vous a demandé de tuer 15 membres du Fléau de Jintha'kalar."}},
[12546] = {"La reconquête", {"Allez à l'Avalanche dans le Bassin de Sholazar et utilisez la Rune Oméga pour déployer l'Etymidian. Utilisez-le pour tuer 200 Serviteurs du Fléau, Bythius le Sculpte-chair, Urgreth des Mille Tombeaux et Morguegrêle.","Parlez à l'Avatar de Freya dans le Bassin de Sholazar quand vous aurez accompli cette tâche."}},
[12547] = {"La rune d'activation", {"Obtenez la Rune Oméga à l'intérieur de la Terrasse du Façonneur et rapportez-la à Etymidian."}},
[12548] = {"L'Etymidian", {"Traversez le Portail à l'extrémité sud du Bassin de Sholazar et cherchez l'arme connue sous le nom de l'Etymidian qui se trouve de l'autre côté."}},
[12549] = {"La maîtrise des sabres-d'effroi : devenir un prédateur", {"Tuez 15 Sabres-d'effroi et retournez voir Buck Bellamble au Camp de base de Nesingwary."}},
[12550] = {"La maîtrise des sabres-d'effroi : traquer sa proie", {"Buck Bellamble, au Camp de base de Nesingwary, vous a demandé d'identifier 3 Empreintes de Shango parmi les empreintes de sabres-d'effroi, près du lac de Flot-amer."}},
[12551] = {"La maîtrise des crocilisques : l'épreuve", {"Tuez 15 Crocilisques de la mangrove et retournez voir Debaar au Camp de base de Nesingwary."}},
[12552] = {"Mort aux nécromagi", {"Le Sergent Tesson-de-lune à Drak'Sotra vous a demandé de détruire 8 Nécromagi hath'ar."}},
[12553] = {"Filières de frôleurs", {"La Spécialiste Roudenté à Drak'Sotra vous a demandé de récupérer 5 Filières de frôleurs intactes."}},
[12554] = {"Malas le Corrupteur", {"Le Sergent Tesson-de-lune à Drak'Sotra vous a demandé de revenir avec la Tête du Corrupteur."}},
[12555] = {"Un écheveau enchevêtré", {"La Spécialiste Roudenté à Drak'Sotra vous a demandé d'utiliser le Lance-écheveau enchevêtré pour détruire 5 Pulvérisateurs de peste."}},
[12556] = {"La maîtrise des rhinos : la mise à mort", {"Rapportez la Corne de Farunn à Hemet Nesingwary au Camp de base de Nesingwary."}},
[12557] = {"Un travail de laboratoire", {"L'Alchimiste Finklestein à Heb'Valok veut que vous lui rapportiez un Asticot fangeux, une Aile de chauve-souris desséchée, une Ambregraine et un peu de Mucus de serpent glacé dans son laboratoire."}},
[12558] = {"La maîtrise des sabres-d'effroi : prêt à bondir", {"Rapportez la Peau de Shango à Buck Bellamble au Camp de base de Nesingwary."}},
[12559] = {"Activation du portail d'accès : le perchoir du Faiseur", {"L'Avatar de Freya, aux Rapides Parlepluie, veut que vous alliez au Perchoir du Faiseur et que vous utilisiez l'Interrupteur d'activation Gamma."}},
[12560] = {"La maîtrise des crocilisques : le plan", {"Rapportez 5 feuilles de Fougère des sables à Debaar, au Camp de base de Nesingwary."}},
[12561] = {"Un problème de confiance", {"Obtenez la confiance de Freya en détruisant 6 Cadavres chancreux et 10 Ravageurs de la faucheuse d'os dans les Terres Perdues."}},
[12562] = {"Pas besoin que les Drakkari aient des élémentaires d'eau !", {"Le Sous-lieutenant Jax au Séjour d'Argent vous a demandé de tuer 10 Lieurs d'eau drakkari."}},
[12563] = {"Patrouille anti-trolls", {"Le Commandant Kunz du Séjour d'Argent vous a demandé de vous rendre aux quatre postes de la Croisade d'argent, de suivre les ordres de leurs capitaines puis de revenir lui faire votre rapport.","Le Capitaine Brandon et le Capitaine Rupert sont en poste à Drak'Sotra, le Capitaine Grondel à Drak'Agal, et l'Alchimiste Finklestein à Heb'Valok."}},
[12564] = {"Patrouille anti-trolls: un remède contre la douleur", nil},
[12565] = {"La bénédiction de Zim'Abwa", {"Le Maléficieur Ubungo au Séjour d'Argent veut que vous présentiez 10 Offrandes drakkari à la statue de Zim'Abwa."}},
[12566] = {"De l'aide pour le camp Sabot-d'hiver", {"Parler au Chef Totem-de-frêne au Camp Sabot-d'hiver."}},
[12567] = {"Bénédiction de Zim'Abwa", nil},
[12568] = {"Patrouille anti-trolls: morts incinérés", nil},
[12569] = {"La maîtrise des crocilisques : l'embuscade", {"Rapportez la Mâchoire de Broussard à Debaar, au Camp de base de Nesingwary."}},
[12570] = {"Un heureux malentendu", {"Escortez l'Oracle parlepluie blessé jusqu'à la Canopée Parlepluie et allez parler au Haut-oracle Soo-pape."}},
[12571] = {"Faire partir le méchant serpent", {"Le Haut-oracle Soo-pape à la Canopée Parlepluie vous a demandé de tuer Pointevenin et 10 Cobras empereurs.","Si vous perdez le Sac d'insectes de Lafoo, allez parler au Haut-oracle Soo-pape à la Canopée Parlepluie."}},
[12572] = {"Les dieux aiment le qui-brille", {"Récupérez 6 des trésors déterrés par Lafoo et rapportez-les au Haut-oracle Soo-pape à la Canopée Parlepluie.","Si vous perdez le Sac d'insectes de Lafoo, allez parler au Haut-oracle Soo-pape à la Canopée Parlepluie."}},
[12573] = {"Faire la paix", {"Le Haut-oracle Soo-pape à la Canopée Parlepluie vous a demandé de donner l'Offrande de paix au Chaman Vekjik, puis de revenir lui donner sa réponse.","Si vous perdez le Sac d'insectes de Lafoo, parlez au Haut-oracle Soo-pape à la Canopée Parlepluie."}},
[12574] = {"Déjà de retour ?", {"Rendez-vous au Refuge de Murmebrume et parlez au Mandebrume Soo-flet.","Si vous perdez le Cristal favori de Jaloot, allez parler au Haut-oracle Soo-pape à la Canopée Parlepluie."}},
[12575] = {"Le trésor perdu de Murmebrume", {"Tuez le Seigneur de guerre Tartek, puis rapportez le Trésor de Murmebrume au Mandebrume Soo-flet au Refuge de Murmebrume.","Si vous perdez le Cristal favori de Jaloot, parlez au Haut-oracle Soo-pape dans la Canopée Parlepluie."}},
[12576] = {"Nous veut pas…", {"Tuez 8 Porte-épieux frénécœurs et 6 Charognards frénécœurs pour le Mandebrume Soo-flet au Refuge de Murmebrume.","Si vous perdez le Cristal favori de Jaloot, parlez au Haut-oracle Soo-pape dans la Canopée Parlepluie."}},
[12577] = {"Il est temps de rentrer !", {"Retournez à la Canopée Parlepluie."}},
[12578] = {"Le gorloc furieux", {"Rendez-vous à Marchemousse et parlez à Moudel lorsque vous serez arrivé.","Si vous perdez la Balle anti-stress de Moudel, parlez au Haut-oracle Soo-pape dans la Canopée Parlepluie."}},
[12579] = {"Le sang-de-vie du sanctuaire marchemousse", {"Moudel vous a demandé de récolter 10 Éclats sang-de-vie, mais d'éviter de vous rendre dans la grotte à l'intérieur du pilier.","Si vous perdez la Balle anti-stress de Moudel, parlez au Haut-oracle Soo-pape dans la Canopée Parlepluie."}},
[12580] = {"Le sauveur des Marchemousses", {"Moudel vous a demandé de sauver 6 Victimes marchemousses à Marchemousse, même s'il sera sans doute trop tard pour certaines d'entre elles.","Si vous perdez la Balle anti-stress de Moudel, parlez au Haut-oracle Soo-pape dans la Canopée Parlepluie."}},
[12581] = {"Le fardeau d'un héros", {"Tuez Artruis le Sans-cœur.","Il vaudrait mieux avoir de l'aide, car Moudel ne pourra pas vous accompagner pour cette tâche."}},
[12582] = {"Le champion frénécœur", nil},
[12583] = {"Le pulvérisateur pulvérisé", {"La Spécialiste Roudenté à Drak'Sotra vous a demandé de collecter des Pièces du pulvérisateur de peste."}},
[12584] = {"Le mal à l'état pur", {"Le Capitaine Rupert vous a demandé de collecter 10 Morceaux de saronite et de les apporter à Eitrigg au Séjour d'Argent."}},
[12585] = {"Patrouille anti-trolls: une bonne flambée", nil},
[12586] = {"À la recherche d'un plus gros gibier", {"Parlez à <x> à <x>."}},
[12587] = {"Patrouille anti-trolls", {"Le Commandant Kunz du Séjour d'Argent vous a demandé de vous rendre aux quatre postes de la Croisade d'argent, de suivre les ordres de leurs capitaines et de revenir ensuite lui faire votre rapport.","Le Capitaine Brandon et le Capitaine Rupert sont en poste à Drak'Sotra, le Capitaine Grondel est en poste à Drak'Agal, et l'Alchimiste Finklestein est en poste à Heb'Valok."}},
[12588] = {"Patrouille anti-trolls : et que ça creuse !", {"Le Capitaine Brandon, à Drak'Sotra, vous a demandé d'utiliser la Bêche en acier sur les Monticules de terre anciens alentours pour récupérer la Relique drakkari ancienne."}},
[12589] = {"Du recul ? Quel recul ?", {"Avec la Carabine RJR, tirez sur la pomme placée sur le crâne de Wilhelm le Verni, puis parlez à Drostan."}},
[12590] = {"Blahblah [PH]", nil},
[12591] = {"Patrouille anti-trolls : attention en dessous !", {"Le Capitaine Rupert à Drak'Sotra vous a demandé d'utiliser des Grenades à percussion près de 5 Cratères néthurbiens."}},
[12592] = {"Le défi du grand chasseur", {"Tuez 60 têtes de Gibier pour aider Hemet et ses compagnons pendant la chasse. Lorsque vous aurez tué vos 60 proies, retournez voir Drostan au Camp de base de Nesingwary pour comparer vos chiffres."}},
[12593] = {"Au service du roi-liche", {"Présentez-vous à l’instructeur Razuvious au Cœur d'Achérus."}},
[12594] = {"Patrouille anti-trolls : je m'en fiche totalement !", {"Le Capitaine Grondel vous a demandé de tuer 7 Chambardeurs moussus dans Drak'Agal."}},
[12595] = {"À la recherche d'un plus gros gibier", {"Parlez à Dorian Traquedrake, à l'Avant-poste de Dorian, au Bassin de Sholazar."}},
[12596] = {"L'organisation des pays producteurs d'épais trolls", {"Le Commandant Kunz au Séjour d'Argent vous a demandé de vous rendre aux quatre postes de la Croisade d'argent, de suivre les ordres de leurs chefs et de revenir lui faire votre rapport.","Le Capitaine Brandon et le Capitaine Rupert sont en poste à Drak'Sotra, au sud-est.","Le Capitaine Grondel est en poste à Drak'Agal, à l'est.","L'Alchimiste Finklestein est en poste à Heb'Valok, au nord."}},
[12597] = {"Un remède contre la douleur", {"Le Capitaine Brandon de Drak'Sotra vous a demandé de récolter 5 Pavots aquatiques à maturité dans les champs de Drak'Sotra."}},
[12598] = {"Attention en dessous !", {"Le Capitaine Rupert à Drak'Sotra vous a demandé d'utiliser des Grenades à percussion près de 5 Cratères nérubiens."}},
[12599] = {"Une bonne flambée", {"Le Capitaine Grondel de Drak'Agal vous a demandé de lui rapporter 20 morceaux de Bois d'épineux mort."}},
[12600] = {"Promotion Upper Deck - Ours de monte", nil},
[12601] = {"L'apprenti alchimiste", {"L'Alchimiste Finklestein de Heb'Valok vous demande de l'aider dans la fabrication d'un sérum de vérité.","Quand vous serez prêt à commencer, allez lui parler et suivez ses instructions."}},
[12602] = {"L'apprenti alchimiste", {"L'Alchimiste Finklestein de Heb'Valok vous demande de l'aider dans la fabrication d'un sérum de vérité.","Quand vous serez prêt à commencer, allez lui parler et suivez ses instructions."}},
[12603] = {"Aiguisez vos griffes", {"Tuez 6 Drakes primordiaux puis retournez voir Dorian Traquedrake, à l'Avant-poste de Dorian."}},
[12604] = {"Félicitations !", nil},
[12605] = {"Se procurer l'appât", {"Apportez 6 Jeunes drakes primordiaux à Dorian Traquedrake, à l'Avant-poste de Dorian."}},
[12606] = {"Les cocons, c'est à ça qu'on les reconnaît.", {"Le Capitaine Rupert vous a demandé d'ouvrir les Cocons nérubiens de Drak'Sotra et de revenir le voir quand vous aurez libéré 3 Fantassins emprisonnés."}},
[12607] = {"À pied, à cheval et en mammouth…", {"Ramenez un Mammouth fracasse-défense à Zazoutille, à l'Avant-poste de Dorian."}},
[12608] = {"L'incursion des sectateurs", {"Parlez à l'Avatar de Freya aux Rapides Parlepluie."}},
[12609] = {"Regarnir les étagères", {"Le Capitaine Arnath à Heb'Valok vous a demandé de trouver 7 Ichors d'araignée frais sur les Rampantes des trappes à Zul'Drak."}},
[12610] = {"Leur couper les ailes", {"Le Capitaine Arnath à Heb'Valok vous a demandé de lui trouver 7 Ailes de chauve-souris impeccables provenant de Chauve-souris de Zul'Drak."}},
[12611] = {"Rendu au centuple", {"L'Avatar de Freya vous demande de vaincre Thalgran Portechancre en utilisant le Gardien de Freya pour lui renvoyer ses Traits de mort au centuple."}},
[12612] = {"Le pilier effondré", {"Allez au Pilier Sang-de-vie et cherchez des indices sur les raisons de sa destruction."}},
[12613] = {"Activation du portail d'accès : le surplomb du Faiseur", {"L'avatar de Freya vous demande de vous diriger vers le Surplomb du Faiseur, à l'est du Bassin de Sholazar, et d'utiliser l'Interrupteur d'activation Thêta."}},
[12614] = {"Agression post-partum", {"Tuez la Mère des couvées Slivina et retournez voir Dorian Traquedrake, à l'Avant-poste de Dorian."}},
[12615] = {"La bénédiction de Zim'Torga", {"Le Féticheur Khufu vous a demandé de présenter 10 Offrandes drakkari à la statue de Zim'Torga."}},
[12616] = {"La chambre des secrets", {"L'Émissaire de l'Aube d'argent vous a demandé de fouiller la chambre de Tenris Bourbassang, dans le Quartier des domestiques de Karazhan."}},
[12617] = {"Exterminer les intrus", {"L'Avatar de Freya aux Rapides Parlepluie veut que vous alliez au Campement de la Main de Mort, au nord-ouest, et que vous tuiez 8 Sectateurs infiltrés."}},
[12618] = {"Bénédiction de Zim'Torga", nil},
[12619] = {"La lame runique blasonnée", {"Cherchez une Épée détériorée sur les râteliers d'armes du Fort d'Ébène et utilisez-la sur une Forgerune pour créer une Épée à lame runique.","Remettez l'Épée à lame runique à l'Instructeur Razuvious au Fort d'Ébène."}},
[12620] = {"Le courroux de la Gardienne de la vie.", {"L'Avatar de Freya, aux Rapides Parlepluie, veut que vous utilisiez le Cor de Freya au sommet du Pilier Scintillant."}},
[12621] = {"Le pacte de Freya", {"Écoutez ce que l'Avatar de Freya veut vous dire."}},
[12622] = {"Les chefs de Jin'Alai", {"Le Scalpeur Ahunae à Zim'Torga vous a demandé de récupérer les Trésors de Kutube'sa, de Gawanil et de Chulo le Fol."}},
[12623] = {"Rendez-vous chez le féticheur", {"Allez parler au Féticheur Khufu à Zim'Torga."}},
[12624] = {"Il pourrait être n'importe où !", {"Recherchez l'Anneau de fiançailles en or sur les différentes créatures du Bassin de Sholazar."}},
[12625] = {"Dominer Achérus", {"Présentez-vous au Maître poliorcète Corvus à l'Étage de la garnison du Fort d'Ébène."}},
[12627] = {"Interférences à Jin'Alai", {"Le Féticheur Khufu à Zim'Torga vous a demandé de perturber le Chaudron bleu, le Chaudron vert, le Chaudron violet et le Chaudron rouge."}},
[12628] = {"Une discussion avec Har'koa", {"Parlez à Har'koa à l'Autel de Har'koa."}},
[12629] = {"Oh, vous pouvez courir…", {"Stefan au Guet d'Ébène vous a demandé d'aller au Reliquaire d'agonie et de récupérer 5 Entrailles d'abominations putrescentes sur des Abominations putrescentes et 5 Baves de goule collantes."}},
[12630] = {"L'engeance tous risques", {"Stefan, du Guet d'Ébène, vous a demandé d'utiliser Nass pour recueillir 10 Échantillons de cheveux de Trolls flétris.","Si vous perdez Nass, retournez voir Stefan pour le récupérer."}},
[12631] = {"Une invitation, en quelque sorte…", {"Apportez le Sautoir non-vivant à Stefan au Guet d'Ébène."}},
[12632] = {"Ma progéniture d'abord", {"Har'koa vous a demandé d'utiliser un poil de sa moustache sur les corps de ses enfants pour les libérer et les ramener à la vie. Retournez la voir à l'Autel de Har'koa une fois que ce sera fait."}},
[12633] = {"L'appel des ténèbres", {"Apportez le Sautoir putride à Stefan au Guet d'Ébène."}},
[12634] = {"Certains font de la limonade, d'autres de la liqueur", {"Rapportez 1 Orange, 2 Régimes de bananes et 1 Papaye à Tristevin Tonnebière au Camp de base de Nesingwary."}},
[12635] = {"Les reliques de la déesse léopard des neiges", {"Le Chroniqueur To'kini à Zim'Torga vous demande de récupérer 10 Reliques har'koanes."}},
[12636] = {"L'œil d'Achérus", {"Présentez-vous au Roi-liche dans le Fort d'Ébène."}},
[12637] = {"Manqué de justesse", {"Stefan au Guet d'Ébène vous demande d'apporter le Sautoir non-vivant à Datura Rose-sang."}},
[12638] = {"Pas passé loin", {"Stefan au Guet d'Ébène vous demande d'apporter le Sautoir putride à Datura Rose-sang."}},
[12639] = {"Les terres gelées", {"La Dompteuse des éléments Dagoda à Zim'Torga veut que vous collectiez 7 Essences de terre gelée."}},
[12640] = {"Sceller les failles", {"Le Scalpeur Ahunae à Zim'Torga vous a demandé de sceller 7 Failles élémentaires."}},
[12641] = {"La mort vient de l'œil", {"Le Roi-liche vous ordonne d'utiliser l'Œil d'Achérus pour analyser le Fort écarlate, la Forge de la Nouvelle-Avalon, l'Hôtel de ville de la Nouvelle-Avalon et la Chapelle de la Flamme cramoisie.","Contrôlez l'Œil d'Achérus en utilisant le mécanisme de contrôle de l'Œil d'Achérus."}},
[12642] = {"L'esprit de Rhunok", {"Parler avec l'Esprit de Rhunok à l'Autel de Rhunok."}},
[12643] = {"Le côté positif", {"Stefan au Guet d'Ébène vous a demandé d'aller au Reliquaire d'agonie pour récupérer 5 Entrailles d'abominations putrescentes sur les Abominations putrescentes et 5 Baves de goules collantes."}},
[12644] = {"Une recette alambiquée", {"Menez le procédé de distillation à son terme et rapportez le Punch de la jungle de Tonnebière à Tristevin Tonnebière, au Camp de base de Nesingwary."}},
[12645] = {"Le test de dégustation", {"Proposez un Échantillon de punch de la jungle de Tonnebière à Hemet Nesingwary, Hadrius Harlowe et Tamara Tremblerouage, puis retournez voir Tristevin Tonnebière au Camp de base de Nesingwary."}},
[12646] = {"Mon prophète, mon ennemi", {"L'Esprit de Rhunok à l'Autel de Rhunok vous a demandé de récupérer son Mojo du dieu ours arctique."}},
[12647] = {"La fin de la souffrance", {"L'Esprit de Rhunok vous a demandé d'utiliser l'Encens du tourmenteur pour réveiller son corps. Vous devez ensuite tuer Rhunok en utilisant le pouvoir que cet esprit vous a donné.","Si vous perdez le Mojo de Rhunok, retournez voir l'Esprit de Rhunok."}},
[12648] = {"Soirée déguisée", {"Stefan au Guet d'Ébène veut que vous utilisiez le Sautoir ensorcelé pour appliquer le déguisement du Fléau.","Ainsi déguisé, vous devez acheter du Plasma amer à Carnentraille.","Si vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12649] = {"En costume !", {"Stefan au Guet d'Ébène veut que vous utilisiez le Sautoir ensorcelé pour appliquer le Déguisement du Fléau.","Ainsi déguisé, vous devez acheter du Plasma amer à Carnentraille.","Si vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12650] = {"Se piller soi-même", {"Le Chroniqueur To'kini à Zim'Torga vous demande de récupérer 7 Reliques rhunokiennes."}},
[12651] = {"Le terrain d'atterrissage de Rive-du-lac", {"Parlez à Tamara Tremblerouage au Terrain d'atterrissage de Rive-du-lac."}},
[12652] = {"La goule au pot", {"Carnentraille au Reliquaire d'agonie veut que vous utilisiez la Fracassée de boyaux à la cervelle près de 10 Goules putréfiées."}},
[12653] = {"Retour auprès de Har'koa", {"Parlez à Har'koa à l'Autel de Har'koa."}},
[12654] = {"La chasseresse à temps partiel", {"Rapportez les Restes de Poix à Tamara Tremblerouage au Terrain d'atterrissage de Rive-du-lac."}},
[12655] = {"La bénédiction de Zim'Rhuk", {"Le Féticheur Khufu vous a demandé de présenter 10 Offrandes drakkari à la statue de Zim'Rhuk."}},
[12656] = {"Bénédiction de Zim'Rhuk", nil},
[12657] = {"La puissance du Fléau", {"Le Roi-liche vous ordonne d'apporter ses ordres au Généralissime Darion Mograine au Fort d'Ébène.","Utilisez le transporteur lumineux derrière le roi-liche pour vous rendre auprès du Généralissime Darion Mograine dans la Salle de commandement."}},
[12658] = {"Mon roc familier", {"Rapportez 7 Œufs de roc à Zazoutille, à l'Avant-poste de Dorian."}},
[12659] = {"Des scalps !", {"Le Scalpeur Ahunae à Zim'Torga vous demande d'utiliser le Couteau d'Ahunae pour scalper 10 Trolls d'Heb'Drakkar.","Le Scalpeur vous rappelle que vous devez d'abord récupérer le butin qu'ils ont sur eux."}},
[12660] = {"Les armes de destruction", {"L'Avatar de Freya vous demande d'aller au Campement de la Main de Mort, au nord-ouest, pour y détruire 4 Explosifs instables."}},
[12661] = {"Infiltrer Voltarus", {"Stefan veut que vous utilisiez le Sautoir ensorcelé pour pénétrer dans Voltarus et faire tout ce que le Commandant du Fléau sur place vous demande.","Si vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12662] = {"Heb'Jin toxique", {"La Dompteuse des éléments Dagoda à Zim'Torga vous a demandé de tuer Heb'Jin."}},
[12663] = {"Retrouvailles", nil},
[12664] = {"Sombre horizon", {"Drakuru, à Voltarus, veut que vous parliez à Sakassang et que vous visitiez Zul'Drak."}},
[12665] = {"Ça sent les problèmes", {"Har'koa vous a demandé de découvrir ce qui est arrivé à Quetz'lun. Allez lui parler pour pouvoir chevaucher un de ses enfants et vous rendre discrètement à l'Autel de Quetz'lun.","Lorsque ce sera fait, retournez la voir à l'Autel de Har'koa."}},
[12666] = {"Préparation pour un voyage au cœur du monde souterrain", {"Har'koa vous demande de récupérer 8 Parures sacrées et de les lui rapporter à l'Autel de Har'koa."}},
[12667] = {"À la recherche de la déesse serpent du vent", {"Recherchez l'Esprit de Quetz'lun à l'Autel de Quetz'lun."}},
[12668] = {"Les bases de la vengeance", {"L'esprit de Quetz'lun vous a demandé de tuer 12 trolls, soit des Adorateurs de Quetz'lun, soit des Berserkers touchés par le serpent, près de ses Fontaines d'âmes.","Ce n'est qu'une fois que vous aurez accompli cette tâche que vous pourrez retourner la voir à l'Autel de Quetz'lun."}},
[12669] = {"Pour l'instant, tout va mal", {"Stefan au Guet d'Ébène veut que vous retourniez auprès de Drakuru et que vous accomplissiez les tâches qu'il vous donne.","Pendant que vous serez au Reliquaire de souffrance, versez le Diluant dans 5 Chaudrons de chancre.","Si vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12670] = {"La moisson écarlate", {"Présentez-vous au Prince Valanar à Brèche-de-Mort."}},
[12671] = {"Un vol de reconnaissance", {"Aidez le Pilote Vic à effectuer un Vol de reconnaissance."}},
[12672] = {"Mise en place", {"L'Esprit de Quetz'lun vous a demandé de récupérer 10 Fragments de pouvoir du monde souterrain. Retournez la voir à l'Autel de Quetz'lun une fois la tâche effectuée."}},
[12673] = {"Là-bas tout en bas", {"Le Seigneur Drakuru à Voltarus veut que vous utilisiez le Sceptre de suggestion sur les Geists chancreux et que vous leur ordonniez ensuite de récolter 7 Cristaux chancreux."}},
[12674] = {"L'enfer a une furie", {"L'Esprit de Quetz'lun à l'Autel de Quetz'lun vous a demandé d'utiliser la Badine maléficieuse de Quetz'lun sur le Grand prêtre Mu'funu, la Grande prêtresse Tua-Tua et le Grand prêtre Hawinni. Lorsque ce sera fait, tuez-les."}},
[12675] = {"Une dernière chose", {"Apportez les Fragments de pouvoir du monde souterrain à Har'koa et suppliez-la de vous donner une portion de son esprit. Vous la trouverez à l'Autel de Har'koa."}},
[12676] = {"Sabotage", {"Stefan au Guet d'Ébène vous a demandé d'effectuer une autre tâche pour Drakuru.","De plus, vous devez utiliser les Charges explosives sur 5 Chariots du Fléau au Reliquaire de souffrance.","Si vous perdez le Sautoir ensorcelé, allez voir Stefan au Guet d'Ébène, il vous en donnera un autre."}},
[12677] = {"Matières dangereuses", {"Stefan au Guet d'Ébène vous a demandé d'aller à Voltarus et d'exécuter les ordres de Drakuru. Une fois sur place, vous devez voler 5 Cristaux chancreux récoltés.","Si vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12678] = {"Si le chaos règne, laissons la souffrance tenir les rênes", {"Le Prince Valanar à Brèche-de-Mort vous ordonne de tuer 10 Croisés écarlates ou Paysans écarlates et 10 Citoyens de Havre-comté."}},
[12679] = {"Ce soir nous dînons à Havre-comté", {"Orithos l'Assombrisseur des cieux à Brèche-de-Mort veut que vous récupériez 15 Flèches en saronite."}},
[12680] = {"Voleurs de chevaux", {"Salanar le Cavalier vous a demandé de voler un cheval dans l'Écurie de Havre-comté et de le lui ramener à Brèche-de-Mort."}},
[12681] = {"Expert en composants", {"Apportez 5 Griffes de roc déformées à Colvin Norrington, à l'Avant-poste de Dorian."}},
[12682] = {"En territoire inconnu (PÉRIMÉ)", nil},
[12683] = {"Une envie brûlante d'aider", {"Colvin Norrington, à l'Avant-poste de Dorian, vous a demandé de récupérer 5 Échantillons de glaires d'hydre et de tuer 5 Hydres Flot-amer."}},
[12684] = {"Le sang d'un dieu mort", {"Har'koa vous a demandé de récupérer 7 doses de Sang de Mam'toth. Rapportez-les-lui à l'Autel de Har'koa."}},
[12685] = {"On récolte ce qu'on sème", {"Tuez le Prophète de Quetz'lun épuisé, puis allez voir le Féticheur Khufu à Zim'Torga."}},
[12686] = {"Tolérance zéro", {"Drakuru à Voltarus veut que vous alliez au Reliquaire de souffrance et que vous utilisiez le Sceptre de renforcement sur un Serviteur de Drakuru. Vous devez alors utiliser ses capacités pour battre Darmuk."}},
[12687] = {"Dans le royaume des ombres", {"Salanar le Cavalier, à Brèche-de-Mort, vous a mis au défi de tuer le Cavalier noir d'Achérus et de lui prendre son Destrier de la mort d'Achérus.","Si vous réussissez, ramenez le Destrier de la mort d'Achérus à Brèche-de Mort et utilisez l'Appel du cavalier pour invoquer Salanar."}},
[12688] = {"Orchestrer un désastre", {"Aidez l'Ingénieur Hélice à s'enfuir du Site de fouilles de Ricarnaque, puis parlez avec Hemet Nesingwary au Camp de base de Nesingwary."}},
[12689] = {"La main des oracles", nil},
[12690] = {"Alimenter le feu", {"Drakuru à Voltarus veut que vous utilisiez le Sceptre de commandement sur des Abominations boursouflées et que vous utilisiez leurs capacités dans la Brèche algide pour tuer 60 Brise-tête drakkari et attirer 3 Chefs drakkari.","Les chefs apparaîtront lorsque vous aurez tué suffisamment de Brise-têtes."}},
[12691] = {"Un coffre vétuste", {"Obtenez une Énorme clé en pierre d'un des gardiens de Sholazar à proximité, et rapportez-la au Coffre vétuste au Surplomb du Faiseur."}},
[12692] = {"Le retour du chasseur de liches", {"Retournez à la Colline de Frénécœur et parlez à l'ancien Harkek."}},
[12693] = {"Suivi du choix de faction (varleu)", nil},
[12694] = {"Suivi du choix de faction (oracle)", nil},
[12695] = {"Le retour du peau-sèche gentil", {"Retournez à la Canopée Parlepluie et parlez au Haut-oracle Soo-pape."}},
[12696] = {"Surveillance aérienne", {"Le Professeur Calvert veut que vous preniez le vol pour le Terrain d'atterrissage de Rive-du-lac, et que vous parliez au Pilote Vic une fois rendu là-bas."}},
[12697] = {"Gothik le Moissonneur", {"Parler avec Gothik le Moissonneur à Brèche-de-Mort."}},
[12698] = {"Le don continu", {"Gothik le Moissonneur à Brèche-de-Mort vous a remis le Don du moissonneur pour que vous l'utilisiez contre les Mineurs écarlates. Créez 5 Goules écarlates avec l'appareil et rapportez-les à Gothik."}},
[12699] = {"C'est un peu embarrassant…", {"Cherchez les Clés de Vic dans le lac du Cœur du fleuve."}},
[12700] = {"Une occasion d'attaquer", {"Parlez au Prince Valanar à Brèche-de-Mort."}},
[12701] = {"Massacre à la halte de la Lumière", {"Le Prince Valanar à Brèche-de-Mort vous a ordonné de massacrer 100 Défenseurs écarlates."}},
[12702] = {"Des poulets et des varleus", {"L'Ancien Harkek à la Colline de Frénécœur vous a demandé de capturer 12 Poulets évadés."}},
[12703] = {"Le massacre de Kartak", {"Trouvez un échantillon pur de Sang d'oracle et apportez-le à l'autel de Kartak pour invoquer Kartak.","Avec l'aide des pouvoirs de Kartak, massacrez 50 Gorlocs touchétincelles pour le Chaman Jakjek au Bastion de Kartak."}},
[12704] = {"Apaiser la pierre à Pluie toute-puissante", {"Le Haut-oracle Soo-pape de la Canopée Parlepluie vous a demandé de récupérer 6 Trésors parmi ceux déterrés par votre compagnon.","Allez parler au Haut-oracle Soo-pape à la Canopée Parlepluie pour obtenir l'aide d'un de vos anciens compagnons."}},
[12705] = {"La volonté des titans", {"Récupérez un Cristal corrompu sur les varleus du Havre Touchétincelle. Rapportez-le à la Pierre à foudre toute-puissante pour demander l'aide de Soo-palonion. Grâce aux pouvoirs de Soo-palonion, tuez 50 Varleus frénécœurs. Une fois la tâche achevée, parlez au Mandefoudre Soo-briquet au Havre Touchétincelle."}},
[12706] = {"Victoire à Brèche-de-Mort !", {"Livrez le Rapport du prince Valanar au Généralissime Mograine dans Achérus : le fort d'Ébène."}},
[12707] = {"Justice laineuse", {"Le Scalpeur Ahunae à Zim'Torga vous a demandé d'utiliser le Médaillon de Mam'toth pour calmer et chevaucher un Mammouth enragé. Vous devez ensuite piétiner à mort 12 Disciples de Mam'toth."}},
[12708] = {"Guerriers tiki enchantés", {"La Dompteuse des éléments Dagoda à Zim'Torga vous a demandé de détruire 12 Guerriers tiki enchantés."}},
[12709] = {"Les caches ensorcelées", {"Le Chroniqueur To'kini à Zim'Torga vous a demandé d'obtenir 12 Caches du bastion de Zol'Maz."}},
[12710] = {"Révélation", {"Drakuru à Voltarus veut que vous vous téléportiez jusqu'à la chambre supérieure."}},
[12711] = {"Du courrier abandonné", nil},
[12712] = {"La clé du seigneur de guerre Zol'Maz", {"Har'koa à Zim'Torga vous a demandé d'obtenir la Clé du Seigneur de guerre Zol'Maz."}},
[12713] = {"Trahison", {"Stefan au Guet d'Ébène vous a demandé de vaincre Drakuru.","Si vous perdez le Sautoir ensorcelé, retournez voir Stefan au Guet d'Ébène."}},
[12714] = {"La volonté du roi-liche", {"Retournez à Brèche-de-Mort et faites votre rapport au Prince Valanar."}},
[12715] = {"La crypte du Souvenir", {"Présentez-vous au Prince Keleseth dans la Crypte du Souvenir."}},
[12716] = {"La requête du porte-peste", {"Noth le Porte-peste vous a demandé de lui rapporter 1 Chaudron vide, 1 Chaîne de fer et 10 Crânes de croisés."}},
[12717] = {"La cuvée spéciale de Noth", {"Placez la Pile de crânes de croisés dans le Chaudron de peste."}},
[12718] = {"Plus de crânes pour la cuvée", nil},
[12719] = {"Nul lieu pour fuir ou se cacher", {"Le Prince Keleseth, dans la Crypte du Souvenir, vous a ordonné de tuer le Maire Quimby et de rapporter le Registre de la Nouvelle-Avalon."}},
[12720] = {"Comment se faire des amis et influencer ses ennemis", {"Le Prince Keleseth à la Crypte du Souvenir vous a ordonné de découvrir la vérité à propos de l'« Aube cramoisie ».","Enlevez les Éloquentes de Keleseth de la Boîte à bijoux ouvragée et utilisez-les pour rendre la Croisade écarlate « éloquente »."}},
[12721] = {"Saccage", {"Har'koa à Zim'Torga vous a demandé de libérer Akali et d'en informer ensuite le Féticheur Khufu à Zim'Torga."}},
[12722] = {"Comme des agneaux à l'abattoir", {"Le Baron Vaillefendre à la Crypte du Souvenir vous a ordonné de tuer 10 Soldats de la Croisade écarlate et 15 Habitants de la Nouvelle-Avalon."}},
[12723] = {"Derrière les lignes écarlates", {"Présentez-vous à Orbaz Sangreplaie à la Taverne écarlate de la Nouvelle-Avalon."}},
[12724] = {"Le chemin du croisé vertueux", {"Orbaz Sangreplaie, à la Taverne écarlate de la Nouvelle-Avalon, vous a ordonné de voler le Plan de route des patrouilles de la Nouvelle-Avalon dans le Fort écarlate."}},
[12725] = {"Frères dans la mort", {"Cherchez Koltira Tissemort dans le Fort écarlate."}},
[12726] = {"Le chant du Vent et de l'Eau", {"Soo-premm veut que vous utilisiez les Tambours de la tempête à la Saillie de Forgetempête et que vous aidiez Haiphoon à dévorer 3 Revenants des tempêtes et 3 Esprits aqueux."}},
[12727] = {"Une issue sanglante", {"Tuez le Grand inquisiteur Valroth et rapportez la Tête de Valroth à Thassarian à la Taverne écarlate à la Nouvelle-Avalon."}},
[12728] = {"Surveiller la faille : les cavernes des Ailerons-d'hiver", {"Le Bibliothécaire Garren, à l'Escarpement d'Ambre, vous a demandé d'utiliser l'Arcanomètre dans les Cavernes des Ailerons-d'hiver."}},
[12729] = {"Les dieux ont parlé", {"Livrez le Cœur du gardien impeccable et l'Essence venteuse du gardien à Har'koa, dans Zim'Torga."}},
[12730] = {"Convocation à Zol'Heb", {"Har'koa à Zim'Torga vous a demandé de tuer le Prophète d'Akali."}},
[12732] = {"La force du sang du cœur", {"Apportez une fiole de Sang du cœur Solegrâce à Rejek, à la Colline de Frénécœur."}},
[12733] = {"Le défi de la mort", {"Olrun le Mande-guerre vous a ordonné de gagner 5 duels contre d'autres chevaliers de la mort.","Vous pouvez affronter des initiés chevaliers de la mort ou des joueurs chevaliers de la mort."}},
[12734] = {"Rejek : le premier sang", {"Avec l'Épée de Rejek, tuez une Guêpe de la ruche Saphir, un Fonceur poing-dur, et 3 Membres de la tribu murmebrume. Vous pouvez tuer des Oracles murmebrumes ou des Guerriers murmebrumes, au choix. Retournez voir Rejek à la Colline de Frénécœur quand vous aurez effectué cette tâche."}},
[12735] = {"Un chant de purification", {"Soo-premm, à la Canopée Parlepluie, vous demande de jouer du Carillon purificateur au lac Flot-amer, au fleuve le Joug-d'hiver et au Cœur du fleuve, et de tuer les esprits maléfiques qui polluent leurs eaux."}},
[12736] = {"Un chant de réflexion", {"Soo-premm, à la Canopée Parlepluie, vous a demandé d'utiliser le Didjeridou de contemplation au sommet du Pilier Scintillant, du Pilier Solegrâce, du Pilier Mousselume et du Pilier Confins-du-ciel."}},
[12737] = {"Un chant de fécondité", {"Soo-premm, à la Canopée Parlepluie, vous demande d'aller à la Saillie de Forgetempête, au nord-ouest du Perchoir du Faiseur, et d'utiliser la Corne de fécondité près de 8 Monticules de terre fertile trempés."}},
[12738] = {"Vengeance !", {"Présentez-vous au Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie."}},
[12739] = {"Une surprise spéciale", {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Malar Corne-brave, un prisonnier de l'Aube d'argent à l'intérieur de la prison à proximité."}},
[12740] = {"Parachute argenté", {"Le Commandant Falstaav au Séjour d'Argent vous a demandé d'équiper 10 Croisés d'argent ou Porte-boucliers d'argent qui se trouvent dans la cour de parachutes."}},
[12741] = {"La puissance de la tempête", {"Rassemblez 3 Essences de la mousson et 3 Essences de la tempête, et apportez-les au Sanctuaire de la tempête. Retournez voir Rejek à la Colline de Frénécœur avec le Véritable pouvoir de la tempête."}},
[12742] = {"Une surprise spéciale", {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Ellen Perrepont, une prisonnière de l'Aube d'argent à l'intérieur de la prison à proximité."}},
[12743] = {"Une surprise spéciale", {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Yazmina Roncechêne, une prisonnière de l'Aube d'argent à l'intérieur de la prison à proximité."}},
[12744] = {"Une surprise spéciale", {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Donovan Tirgivre, un prisonnier de l'Aube d'argent à l'intérieur de la prison à proximité."}},
[12745] = {"Une surprise spéciale", {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Goby Explonheimer, un prisonnier de l'Aube d'argent à l'intérieur de la prison à proximité."}},
[12746] = {"Une surprise spéciale", {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Valok le Vertueux, un prisonnier de l'Aube d'argent à l'intérieur de la prison à proximité."}},
[12747] = {"Une surprise spéciale", {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Dame Eonys, une prisonnière de l'Aube d'argent à l'intérieur de la prison à proximité."}},
[12748] = {"Une surprise spéciale", {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Kug Gueule-de-fer, un prisonnier de l'Aube d'argent à l'intérieur de la prison à proximité."}},
[12749] = {"Une surprise spéciale", {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Iggy Sombrebroche, un prisonnier de l'Aube d'argent à l'intérieur de la prison à proximité."}},
[12750] = {"Une surprise spéciale", {"Le Chevalier-commandant Poing-du-Fléau à la Chapelle de la Flamme cramoisie vous a ordonné d'exécuter Antoine Brack, un prisonnier de l'Aube d'argent à l'intérieur de la prison à proximité."}},
[12751] = {"Un retour au pays... en quelque sorte", {"Retournez voir Thassarian dans la Taverne écarlate à la Nouvelle-Avalon et rapportez-lui la nouvelle de votre victoire."}},
[12752] = {"Recherche désespérée", nil},
[12753] = {"Une alliance désespérée", nil},
[12754] = {"Embuscade au surplomb", {"Orbaz Sangreplaie à la Taverne écarlate à la Nouvelle-Avalon vous a ordonné de tuer le Courrier écarlate et de récupérer les Possessions du courrier écarlate, ainsi que le Message du courrier écarlate."}},
[12755] = {"Une rencontre avec le destin", {"Orbaz Sangreplaie vous a ordonné de livrer le Message du courrier écarlate au Grand général Abbendis à Port-royal."}},
[12756] = {"L'Assaut écarlate émerge", {"Donnez le Chemin de la rédemption à Orbaz Sangreplaie dans la Taverne écarlate à la Nouvelle-Avalon."}},
[12757] = {"L'approche des armées écarlates", {"Donnez le Chemin de la rédemption au Généralissime Darion Mograine à Achérus : le fort d'Ébène."}},
[12758] = {"Un couvre-chef de héros", {"Récupérez des Explosifs de la KapitalRisk sur n'importe quelle unité de la KapitalRisk, au Site de fouilles de Ricarnaque, et utilisez-les pour obtenir la Tête d'un Guette-tempête. Apportez-la ensuite à Rejek à la Colline de Frénécœur."}},
[12759] = {"De drôle d'outils de guerre", {"Récupérez certains des Pièges de Zepik dans son trésor au Bastion de Kartak. Utilisez-les pour tuer 50 Gorlocs touchétincelles, puis retournez voir le Chaman Jakjek au Bastion de Kartak."}},
[12760] = {"La puissance secrète des Frénécœurs", {"Utilisez la boisson secrète frénécœur pour tuer 30 Gorlocs touchétincelles autour du Bastion de Kartak."}},
[12761] = {"La maîtrise des cristaux", {"Récupérez les Défenses de cristal au Havre Touchétincelle et utilisez-les pour tuer 50 Varleus frénécœurs. Une fois la tâche effectuée, retournez voir le Mandefoudre Soo-briquet au Havre Touchétincelle."}},
[12762] = {"Le pouvoir des Tout-puissants", {"Chargez le Cristal dormant poli au Pilier Sang-de-vie découvert. Rendez-vous au Havre Touchétincelle et utilisez le pouvoir contenu dans le cristal pour massacrer 30 Varleus frénécœurs, puis parlez au Mandefoudre Soo-briquet au Havre Touchétincelle."}},
[12763] = {"Changement de priorités", {"L'Éclaireur Vor'takh veut que vous vous rendiez à Zul'Drak et que vous vous présentiez au sergent Riannah à la Brèche de Lumière.","Makki Trombe-de-l'hiver, au Camp Onequah, vous aidera à vous y rendre."}},
