[12767] = {"Parler avec l'ambassadeur", {"Parler avec Golluck Poing-roc au Temple du Repos du ver."}},
[12768] = {"Le régisseur du temple du Repos du ver", {"Parler à Tariolstrasz au Temple du Repos du ver."}},
