[12778] = {"L'apocalypse écarlate", {"Cherchez le Roi-liche à Brèche-de-Mort."}},
[12779] = {"La fin de toutes choses…", {"Le Roi-liche vous a ordonné d'éradiquer la Croisade écarlate.","Utilisez le Cor des Couvegivres pour invoquer un Vainqueur couvegivre. Avec son aide, tuez 150 Soldats écarlates et détruisez 10 Balistes écarlates. Évitez les projectiles en sortant de leur ligne de tir !"}},
[12780] = {"PÉRIMÉ>>L'ennemi de notre ennemi", {"Le Sergent Riannah à la Brèche de Lumière veut que vous parliez à Stefan au Guet d'Ébène et que vous lui offriez votre aide."}},
[12781] = {"Bienvenue !", {"Apportez le Bon-cadeau du Fort d'Ébène à Siouxsie la banshee au Fort d'Ébène."}},
