[12789] = {"Dans la brèche !", {"Le Croisé Valus de la Confiance de Lumière veut que vous vous présentiez auprès du Sergent Riannah à la Brèche de Lumière, dans Zul'Drak."}},
[12790] = {"Apprendre à partir et revenir : le chemin magique", {"L'Archimage Célindra à la Porte Pourpre à Dalaran vous a demandé d'utiliser le Téléporteur vers le Cristal du Séjour pourpre et le Téléporteur vers le Cristal de Dalaran."}},
[12791] = {"Le royaume magique de Dalaran", {"Donnez la Chevalière du Kirin Tor à l'Archimage Célindra à la Porte Pourpre à Dalaran."}},
[12792] = {"Chaque chose en son temps", {"Le Commandant Kunz au Séjour d'Argent vous a demandé d'aller rejoindre le Sergent Riannah à la Brèche de Lumière."}},
[12793] = {"De la fumée à l'horizon", {"Khufu, dans Zim'Torga, veut que vous vous rendiez à la Brèche de Lumière et que vous vous présentiez au Sergent Riannah.","Maaka vous fournira le transport."}},
[12794] = {"Le royaume magique de Dalaran", {"Donnez la Chevalière du Kirin Tor à l'Archimage Célindra à la Porte Pourpre à Dalaran."}},
[12795] = {"Passons au séjour", {"Datura Rose-sang au Guet d'Ébène veut que vous alliez parler au Commandant Falstaav au Séjour d'Argent."}},
[12796] = {"Le royaume magique de Dalaran", {"Donnez la Chevalière du Kirin Tor à l'Archimage Célindra à la Porte Pourpre à Dalaran."}},
[12797] = {"Retour par le portail d'accès", {"Apportez la Rune Oméga à l'Avatar de Freya aux Rapides Parlepluie, dans le Bassin de Sholazar."}},
[12798] = {"Suite d'Épées", {"Utilisez la Suite d'Épées pour invoquer une Diseuse de bonne aventure de la foire de Sombrelune et donnez-lui la suite."}},
[12799] = {"Récupération d'esprits", {"Le Capitaine Arnath à Heb'Valok vous demande de récupérer 5 Ectoplasmes anciens d'Esprits drakkari égarés aux Bassins de Zha'Jin."}},
[12800] = {"Le dernier ordre du roi-liche", {"Faites votre rapport au Commandant du Fléau Thalanor à la Scierie de Browman."}},
[12801] = {"La Lumière de l'Aube", {"Battez les forces de l'Aube d'argent et découvrez la Lumière de l'Aube.","Si vous survivez, faites votre rapport au Généralissime Darion Mograine."}},
[12802] = {"Mon cœur est entre tes mains", {"Drakuru vous a demandé de lui rapporter le Cœur des anciens au Passage de Drak'atal.","Vous allez devoir récupérer 5 Mojos désespérés pour utiliser l'élixir sur place."}},
[12803] = {"Une force de la nature", {"Rendez-vous à la Ligne de la désolation, entre les Rapides Parlepluie et les Terres Perdues du Bassin de Sholazar, et cherchez l'Avatar de Freya."}},
[12804] = {"Un steak digne d'un chasseur", {"Apportez 5 Steaks de Longs-cous à Korg le Fendeur, au Camp de base de Nesingwary."}},
[12805] = {"La préservation de la force vitale", {"L'Avatar de Freya veut que vous alliez à l'Éclat scindé au sud-est, et que vous utilisiez la Gemme de force vitale sur 8 Élémentaires sang-de-vie détruits."}},
[12806] = {"Se lever en toute hâte !", {"Faites votre rapport au Seigneur-commandant Arete à la Cime de la Mort, dans la Couronne de glace."}},
[12807] = {"Résumé des épisodes précédents…", {"Écoutez l'histoire du Seigneur-commandant Arete à la Cime de la Mort."}},
[12808] = {"Une alliance désespérée", nil},
[12809] = {"Forgefer", nil},
[12810] = {"Du sang dans l'eau", {"Le Seigneur-commandant Arete, à la Cime de la Mort,vous a demandé de remplir la Vessie de sang avec le sang de 10 Dents voraces de la mer."}},
[12811] = {"Recherche désespérée", nil},
[12812] = {"Orgrimmar", nil},
[12813] = {"Réanimez leurs cadavres !", {"Setaal Soignobscur à la Cime de la Mort vous a donné la Teinture du sombre réparateur. Appliquez-la sur les cadavres de 10 unités de l'Assaut écarlate trouvées au Port de l'Assaut."}},
[12814] = {"Vous aurez besoin d'un griffon", {"Amenez un Griffon de l'Assaut à Uzo Mandemort à la Cime de la Mort."}},
[12815] = {"Zone d'exclusion aérienne", {"Uzo Mandemort, à la Cime de la Mort, vous demande de tuer 10 Chevaucheurs de griffons de l'Assaut."}},
[12816] = {"Enquêter au sujet du Fléau à Lune-d'argent", {"Récupérez 3 Pierres nécrotiques pâles du Fléau à l'extérieur de Lune-d'argent et enquêtez à propos des Cercles runiques brillants près de leur campement."}},
[12817] = {"Enquêter au sujet du Fléau à l'Exodar", {"Récupérez 3 Pierres nécrotiques pâles sur les membres du Fléau à l'extérieur de l’Exodar et enquêtez sur les Cercles runiques brillants près de leur campement. Puis retournez voir le Lieutenant Kregor."}},
[12818] = {"Nettoyage", {"Gouaille Grilledouille à K3 vous a demandé 10 morceaux de Débris calcinés."}},
[12819] = {"Juste à côté", {"Gouaille Grilledouille, à K3, aimerait récupérer ses Outils de Grilledouille oubliés dans son champ de mines."}},
[12820] = {"Une délicate attention", {"Ricket, à K3, veut que vous utilisiez les Mines améliorées à Plaie-de-Garm pour repousser 12 Attaquants de Garm. Les Envahisseurs de Garm et les Suivants Brûleglace comptent comme attaquants."}},
[12821] = {"Le tango du pénitencier", {"Ricket à K3 voudrait que vous localisiez la Batterie de télétransporteur et que vous l'utilisiez pour activer la Plate-forme de téléportation à la Cime de Garm."}},
[12822] = {"N'ayez pas peur", {"Ricket à K3 vous demande de tuer 6 Guetteurs de Garm et 8 Fidèles brûleglace."}},
[12823] = {"Un plan sans accroc", {"Gino à la Cime de Garm vous a demandé de placer le Paquet d'explosifs durcis près de l'Autel de Givrebide, de tuer Tormar Givrebide puis de vous échapper avant que la caverne ne s'effondre sur vous."}},
[12824] = {"Artificier extraordinaire", {"Retournez voir Ricket à K3."}},
[12825] = {"Une preuve de gratitude", {"[PLACEHOLDER] Utilisez les mini avions pour jouer les kamikazes sur le chef gnoll."}},
[12826] = {"Légèrement instable", {"Apportez les Mines améliorées de Gouaille à Ricket à K3."}},
[12827] = {"Vol de rations", {"Ricket à K3 vous demande de récupérer 16 Rations gnolles séchées sur les Gnolls de la colline Sauvage, qui se trouvent non loin."}},
[12828] = {"Une grande inspiration", {"Ricket, à K3, veut que vous récupériez un T.E.M.O.R. Utilisez-le pour faire sauter un mammouth et récupérez 8 Viandes de mammouth copieuses."}},
[12829] = {"Installation", {"Tore Grondelevier à l'extérieur de la Caverne de la Toile cristalline vous a demandé de tuer 12 Araignées de la Toile cristalline."}},
[12830] = {"Récupération de minerai", {"Tore Grondelevier à l'extérieur de la Caverne de la Toile cristalline vous a demandé de récupérer 5 échantillons de Minerai de saronite impur."}},
[12831] = {"Seulement à moitié oublié", {"Un Mineur gobelin blessé à la Caverne de la Toile cristalline vous a demandé de lui apporter une Glande à venin pointeglace trouvée sur une Rampante pointeglace."}},
[12832] = {"Un départ amer", {"Escortez le Mineur gobelin blessé hors de la Caverne de la Toile cristalline, puis parlez à Ricket à K3."}},
[12833] = {"Surplus", {"Ricket, à K3, veut que vous utilisiez les Mines améliorées à Plaie-de-Garm pour repousser les attaquants de Garm. Les Envahisseurs de Garm et les Suivants Brûleglace comptent comme attaquants."}},
[12834] = {"Ni la pluie, ni la neige, ni un avion qui explose", {"[PLACEHOLDER] Test bombardement temps froid sur géant."}},
[12835] = {"Déjà grand", {"[PLACEHOLDER] Test Jormungar adulte sur géant"}},
[12836] = {"Une preuve de gratitude", {"Ricket à K3 veut que vous tuiez Peau-noueuse."}},
[12837] = {"Tore a les fournitures", {"[PLACEHOLDER] Faites votre rapport à Tore"}},
[12838] = {"Trouver des informations", {"Morneplaie des aurochs, à la Cime de la Mort, vous a demandé de récupérer 5 Documents de renseignement de l'Assaut."}},
[12839] = {"Le grand plan de l'amiral", {"Remettez la Note du Grand Amiral au seigneur-commandant Arete à la Cime de la Mort."}},
[12840] = {"Que ça reste entre nous", {"Le Seigneur-commandant Arete, à la Cime de la Mort, vous a demandé d'extirper l'information des capitaines Welsington et Hartford."}},
[12841] = {"La proposition de la mégère", {"Lok'lira la Mégère à l'intérieur de la Mine Lugubre vous a demandé de récupérer les Runes des Yrkvinn que détient la Surveillante Syra."}},
[12842] = {"La runeforge : se préparer à la bataille", {"L’Instructeur Razuvious du Fort d’Ébène, vous ordonne de vous rendre à une runeforge, et de vous servir de votre talent Runeforge pour graver une rune de votre choix sur votre Lame d’âme runique.","Vous pourrez la remplacer n’importe quand, tant que vous serez à proximité d’une runeforge."}},
[12843] = {"Elles ont pris nos hommes !", {"Gretchen Pfutincelle vous demande d'aller à Sifreldar et de secourir 5 Prisonniers gobelins."}},
[12844] = {"Récupération de l'équipement", {"Gretchen Pfutincelle vous a demandé d'obtenir 8 Equipements K3 à Sifreldar, au nord de K3."}},
[12845] = {"Drapeau du cristal de téléportation à Dalaran", nil},
[12846] = {"Le gobelin manquant", {"Trouvez l'entrée de la Mine Lugubre au nord du Village Sifreldar et cherchez des indices sur l'endroit où se trouve Zeev Pfutincelle."}},
[12847] = {"Secondes chances", {"Tuez le Grand abbé Langrain, puis invoquez la Porte d'Arete en présence de son cadavre. Parlez au Seigneur-commandant Arete à l'intérieur de la Cathédrale cramoisie une fois qu'il aura questionné l'esprit du grand abbé."}},
[12848] = {"La faim sans fin", {"L'Instructeur Razuvious à Fort d'Ébène vous a demandé de libérer un Initié indigne puis de tuer l'Initié indigne de sang-froid !","C'est votre vie ou la sienne !"}},
[12849] = {"Le pouvoir du sang, du givre et de l'impie", nil},
[12850] = {"Rapport au commandant du Fléau Thalanor", {"Faites votre rapport au Commandant du Fléau Thalanor sur le balcon au premier étage du Fort d'Ébène."}},
[12851] = {"La pro de l’ours", {"Brijana à Brunnhildar vous a demandé de chevaucher Croc-de-glace et d'abattre 7 Worgs de givre et 15 Géants du givre dans la Vallée des Anciens hivers."}},
[12852] = {"L'amiral à découvert", {"Le Seigneur-commandant Arete vous a demandé de tuer le Grand amiral Ponevent. Retournez voir le Seigneur-commandant à la Cime de la Mort quand la tâche est accomplie."}},
[12853] = {"Une escapade luxueuse !", {"Allez voir Gouaille Grilledouille à K3, aux Pics Foudroyés."}},
[12854] = {"Sur les traces de Brann", {"Récupérez la Note enveloppée dans de la toile au bivouac de Brann et rapportez-la à l'Archéologue Andorin à Fort du Givre."}},
[12855] = {"Renifler le coupable", {"Utilisez le Collier de molosse du givre au Camp abandonné pour suivre la piste des objets de Brann. Quand vous les aurez récupérés, utilisez l'appareil de communication et contactez qui vous pouvez."}},
[12856] = {"Brisons la glace", {"Brijana, à l'est de Brunnhildar, veut que vous voliez jusqu'à Dun Niffelem, que vous libériez 3 Proto-drakes captifs et que vous sauviez 9 Prisonnières de Brunnhildar."}},
[12857] = {"Avis de recherche : Nageoire de Crin-de-rage", {"Récupérez la Nageoire de Crin-de-rage et rapportez-la au Chef Grifferage à la Brèche de Lumière à Zul'Drak."}},
[12858] = {"Les pièces du puzzle", {"Rassemblez les 6 Fragments du disque de l'inventeur et utilisez-les pour assembler le Disque de l'inventeur."}},
[12859] = {"Nouvelle neuve : le feu ça brûle !", {"L'Ancien chaman Moky à la Brèche de Lumière à Zul'Drak vous demande d'utiliser l'Extincteur grifferage pour éteindre 15 Feux de hutte à la Tanière grifferage."}},
[12860] = {"À la recherche de données", {"Utilisez le disque de l'inventeur pour récupérer 7 Fragments de données cachées dans les Banques de données."}},
[12861] = {"Les trolls ils ont tombé sur la tête !", {"Le Chef Grifferage à la Brèche de Lumière à Zul'Drak vous demande d'utiliser les Ouvre-serrures de Drakuru trouvés sur différents trolls Drakuru pour libérer 8 Grifferages capturés."}},
[12862] = {"Quand tout le reste a échoué…", {"Parlez à Ricket pour utiliser l'Engin de livraison à propulsion D16 de Ricket afin de pénétrer au cœur des Pics Foudroyés."}},
[12863] = {"Des remerciements", {"Apportez la caisse de Fournitures médicales à Glorthal Barberêche à Fort du givre."}},
[12864] = {"Les éclaireurs manquants", {"Glorthal, au Fort du Givre, vous a demandé d'explorer la vallée du Souffle de Bor et la Chute de Bor en quête d'une trace des Éclaireurs givre-nés disparus."}},
[12865] = {"Des compagnons loyaux", {"Fjorlin Front-de-givre, au Fort du Givre, veut que vous récupériez 8 tranches de Viande fraîche de rhinocéros des glaces, et que vous les utilisiez pour nourrir les Aigles foudrecrête qui nichent au-dessus de la ville."}},
[12866] = {"Contenir les agresseurs", {"Glorthal Barberêche au Fort du Givre veut que vous tuiez 8 Hurleuses givreplumes et 8 Sorcières givreplumes."}},
[12867] = {"Voleurs de bébés", {"Fjorlin Front-de-givre au Fort du Givre vous a demandé de lui rapporter 15 Œufs d'aigles foudrecrêtes."}},
[12868] = {"Sirana Hurleglace", {"Glorthal Barberêche au Fort du Givre veut que vous éliminiez Sirana Hurleglace."}},
[12869] = {"Trop loin", {"Fjorlin Front-de-givre veut que vous chevauchiez un Aigle foudrecrête et que vous tuiez 16 Wyrms foudrepics dans le Pied-mont."}},
[12870] = {"Reliques anciennes", {"Rapportez 10 Reliques d'Ulduar à Rork Menton-pointu au Fort du Givre."}},
[12871] = {"De l'aide de la Ligue des explorateurs", {"Apportez la Coque de Norgannon à Lagnus, au Fort du Givre."}},
[12872] = {"La coque de Norgannon", {"Récupérez la Coque de Norgannon auprès de l'Archiviste Mécaton."}},
[12873] = {"Le roi des givre-nés", {"Parlez au Roi Yorg Foudrecœur au Fort du Givre."}},
[12874] = {"La ferveur du givre-né", {"Yorg Foudrecœur, au Fort du givre, vous a demandé de parler à Fjorlin Front-de-givre pour récupérer une monture et passer votre épreuve."}},
[12875] = {"Un guide expérimenté", {"Localiser Drom Poignegivre."}},
[12876] = {"Des invités indésirables", {"Fjorlin Front-de-givre au Fort du givre vous a demandé de tuer 10 Envahisseurs forge-foudre."}},
[12877] = {"Le guetteur solitaire", {"Reprenez la Chevalière de Poignegivre des mains d'un Observateur nain de fer et apportez-la à Creteus dans le Pied-mont."}},
[12878] = {"La relique cachée", {"Creteus, dans le Pied-mont, vous a donné des informations qui vous permettent de localiser le Noyau de Norgannon."}},
[12879] = {"La fureur du roi givre-né", {"Rapportez le noyau de Norgannon à Creteus au Pied-mont."}},
[12880] = {"L'explorateur en chef", {"Rapportez le noyau de Norgannon à Brann Barbe-de-bronze au Moteur des Faiseurs."}},
[12881] = {"Les frères Barbe-de-bronze", nil},
[12882] = {"Reliques anciennes", {"Vous effectuez cette quête"}},
[12883] = {"Ordres de Drakuru", {"Rapportez les Ordres de Drakuru au Seigneur croisé Lantinga à la Brèche de Lumière à Zul'Drak."}},
[12884] = {"Le guet d'Ébène", {"Portez les Ordres de Drakuru à Stefan Vadu au Guet d'Ébène à Zul'Drak."}},
[12885] = {"Les exilés d'Ulduar", {"Parlez à Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12886] = {"Le Drakkensryd", {"Utilisez le Harpon hyldnir pour vaincre 10 Chevaucheuses de drake du Hyldsmeet au Temple des Tempêtes. Utilisez le Harpon hyldnir sur un Ornement de colonne pour quitter le Drakkensryd et parler à Thorim une fois la tâche accomplie."}},
[12887] = {"Qu'est-ce qu'on se marre", {"Détruisez l'Oculaire et faites votre rapport au Baron Lesquille au Caveau des Ombres."}},
[12888] = {"FERRAILL-E", {"Apportez la Carte d'accès de FERRAILL-E, ainsi que 4 Poignées de boulons en cobalt et 5 Condensateurs surchargés à FERRAILL-E à la Bibliothèque de l'inventeur."}},
[12889] = {"La console prototype", {"Utilisez la Console prototype à la Bibliothèque de l'inventeur."}},
[12890] = {"Si la taille était importante", nil},
[12891] = {"J'ai une idée mais d'abord…", {"Le Baron Lesquille, au Caveau des Ombres, vous a demandé de trouver un Bâtonnet de sectateur, un Crochet d'abomination, une Corde de Geist et 5 Essences du Fléau."}},
[12892] = {"Qu'est-ce qu'on se marre", {"Détruisez l'Oculaire puis faites votre rapport au Baron Lesquille au Caveau des Ombres."}},
[12893] = {"Libérez votre esprit", {"Le Baron Lesquille, au Caveau des Ombres, vous demande d'utiliser le Bâtonnet souverain sur les cadavres de Vil, Dame Bois-de-nuit et Le Bondisseur."}},
[12894] = {"Le camp avancé des croisés", {"Allez trouver le Croisé MacKellar au Camp avancé des croisés à Zul'Drak."}},
[12895] = {"Le Barbe-de-bronze manquant", {"Vous effectuez cette quête"}},
[12896] = {"S'il ne peut pas être détourné…", {"Tuez le Général Plaie-de-lumière puis allez rapporter votre succès à Thassarian à bord de la canonnière Brise-ciel."}},
[12897] = {"S'il ne peut pas être détourné…", {"Tuez le Général Plaie-de-lumière puis allez rapporter votre succès auprès de Koltira Tissemort à bord de la canonnière, le Marteau d'Orgrim."}},
[12898] = {"Le caveau des Ombres", {"Présentez-vous au Baron Lesquille au Caveau des Ombres."}},
[12899] = {"Le caveau des Ombres", {"Présentez-vous au Baron Lesquille au Caveau des Ombres."}},
[12900] = {"Fabriquer un harnais", {"Astrid Bjornrittar à Brunnhildar veut que vous rapportiez 3 Peaux de yéti crins-de-glace."}},
[12901] = {"Système D", {"L’Ingénieur Reed, du Camp avancé des croisés à Zul’Drak, vous a demandé de récupérer 10 Morceaux de ferraille du Fléau."}},
[12902] = {"En quête de réponses", {"Fouillez la Tanière Grifferage à la recherche d’indices sur l’origine de ceux qui, ou de ce qui, a attaqué les varleus Grifferage."}},
[12903] = {"Les amis sont là pour ça…", {"Le Croisé MacKellar, au Camp avancé des croisés, vous a demandé de trouver le Croisé Dargath, ainsi que Gerk et Burr."}},
[12904] = {"La Lumière ne m'accorde pas la vengeance", {"Gerk, à la Fin de Thrym à Zul’Drak, veut que vous tuiez 15 Varguls."}},
[12905] = {"Mildred la Cruelle", {"Parlez à Mildred la Cruelle à la Mine Lugubre."}},
[12906] = {"Discipline", {"Mildred la Cruelle à la Mine Lugubre vous a demandé d'utiliser le Bâton de discipline sur 6 Vrykuls épuisés."}},
[12907] = {"Faire des exemples", {"Mildred la Cruelle à la Mine Lugubre vous a demandé de tuer Garhal."}},
[12908] = {"La bravache et la prisonnière", {"Apportez la Clé de Mildred à Lok'lira la Mégère à la Mine Lugubre."}},
[12909] = {"Le nez sait", {"Parlez à Khaliisi au pied de la montagne au Point d'impact de Grom'arsh."}},
[12910] = {"Renifler le coupable", {"Chevauchez Morsure-de-givre et amenez-la jusqu'à la source de l'odeur de la note de Brann. Puis, retrouvez le Communicateur de Brann et utilisez-le pour entrer en contact avec Brann Barbe-de-bronze."}},
[12911] = {"Test de crédit de mort", {"/signe au test de crédit de mort"}},
[12912] = {"Une grande tempête approche", {"Gymer, le roi des géants des tempêtes, vous a demandé de trouver un moyen de le libérer de sa cage. Allez voir l’Ingénieur Reed, au Camp avancé des croisés."}},
[12913] = {"Vous ne pouvez pas parler orc comme tout le monde ?", {"Parlez à Moteha Né-du-vent, au Point d'impact de Grom'arsh."}},
[12914] = {"Le salut de Gymer", {"L’Ingénieur Reed, du Camp avancé des croisés à Zul’Drak, a besoin de 6 Essences de banshees et de 6 Diatomites."}},
[12915] = {"Rentrer dans les bonnes grâces", {"Thorim au Temple des Tempêtes vous a demandé de tuer Fjorn et 5 Géants de fer forge-foudre à l'Enclume de Fjorn, à l'est de Dun Niffelem.","Utilisez le Charme de terre de Thorim pour invoquer ses Terrestres qui se battront à vos côtés. Vous aurez besoin d'un Rocher de granit près de l'Enclume de Fjorn pour que le charme fonctionne."}},
[12916] = {"Notre seul espoir", {"Utilisez la Charge d’hydroglycérine éléphantesque pour détruire l’Enclos du Fléau qui retient Gymer prisonnier. Lorsque ce sera fait, parlez à Gymer, à la Fin de Thrym à Zul’Drak."}},
[12917] = {"Parler avec la voix du vent", {"Apportez 5 Voix du vent à Moteha Né-du-vent, au Point d'impact de Grom'arsh."}},
[12918] = {"La perfection des gemmes", {"Ounhulo, à Valgarde, a besoin de deux Énormes citrines, de deux Jades sombres et de deux Cristaux ombreux."}},
[12919] = {"La vengeance du roi des tempêtes", {"Parlez à Gymer pour commencer.","Lorsque vous serez sur son dos, détruisez 100 créatures du Fléau, et tuez Algar l’Élu, Thrym et le Prince Navarius. Si vous réussissez, allez faire votre rapport au Croisé MacKellar, au Camp avancé des croisés."}},
[12920] = {"Retrouver Brann", {"Utilisez le Communicateur de Brann pour contacter Brann Barbe-de-bronze et parlez-lui, puis parlez à Boktar Rougefurie au Point d'impact de Grom'arsh.","Si vous perdez le Communicateur de Brann, allez parler à Moteha Né-du-vent au Point d'impact de Grom'arsh."}},
[12921] = {"Changer de décor", {"Retrouvez Lok'lira la Mégère à Brunnhildar."}},
[12922] = {"Le feu du raffineur", {"Récupérez 10 Étincelles furieuses sur des Revenants vengeurs au Lac du Champ-gelé, puis utilisez l'enclume à l'Enclume de Fjorn."}},
[12924] = {"Forger une alliance", {"Rendez-vous à Dun Niffelem et demandez au Roi Jokkum de permettre que l'Armure de Thorim soit reforgée. Après avoir terminé la tâche de Jokkum, allez parler à Njormeld à Dun Niffelem."}},
[12925] = {"Aberrations", {"Thyra Kvinnshal à Brunnhildar vous demande de vous rendre à Valkyrion et d'obtenir des Fioles d'huile glaciale des Aspirantes de Valkyrion. Utilisez les Fioles d'huile glaciale pour détruire 30 Oeufs de Proto-drakes pestiférés."}},
[12926] = {"Les pièces du puzzle", {"Utilisez le Communicateur de Brann pour contacter Brann Barbe-de-bronze afin de savoir que faire ensuite."}},
[12927] = {"Collecte de données", {"Utilisez le Disque de l'inventeur pour retrouver 7 fragments de Données cachées dans les Banques de données."}},
[12928] = {"La coque de Norgannon", {"Récupérez le Noyau de Norgannon auprès de l’Archiviste Mécaton."}},
[12929] = {"Les Terrestres d'Ulduar", {"Parlez à Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12930] = {"Une terre rare", {"Utilisez la Pioche terrestre pour extraire 7 mottes de Terre enchantée. Apportez la Terre enchantée et 5 Etoffes de tisse-givre à Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12931] = {"Contre-attaquer", {"Rochecombe le Sculpteroc veut que vous tuiez 10 Attaquants forge-foudre et que vous retourniez le voir au Refuge de Rochecombe. Vous pouvez tuer soit des Ecumeurs forge-foudre soit des Saccageurs forge-foudre."}},
[12932] = {"L'Amphithéâtre de l'Angoisse : Yggdras !", {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez d’Yggdras ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12933] = {"L'Amphithéâtre de l'Angoisse : magnataure !", {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez de Barbapue ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12934] = {"L'Amphithéâtre de l'Angoisse : de l'au-delà !", {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez de l’un des seigneurs élémentaires ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12935] = {"L'Amphithéâtre de l'Angoisse : rohart-maggedon !", {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez d’Orinoko Brise-défense ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12936] = {"L'Amphithéâtre de l'Angoisse : Korrak le Ragesang !", {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez de Korrak le Ragesang ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12937] = {"Soulager les blessés", {"Utilisez le Cataplasme tellurique pour soigner 8 Défenseurs terrestres tombés au combat, puis retournez voir Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12938] = {"Le duc", {"Parlez au Duc Lankral au Caveau des Ombres."}},
[12939] = {"Un défi pour l'honneur", {"Le Duc Lankral, au Caveau des Ombres, vous a demandé d'utiliser le Drapeau de défi pour défier et vaincre 6 Combattants de Mjordin."}},
[12940] = {"Seau de bonbons", nil},
[12941] = {"Seau de bonbons", nil},
[12942] = {"Qu'on leur coupe leurs ailes noires !", {"Iva la Vengeresse à Brunnhildar vous demande de vous rendre à Valkyrion et de tuer 12 Val'kyrs naissantes."}},
[12943] = {"Le décret du caveau des Ombres", {"Le Duc Lankral, au Caveau des Ombres, vous a ordonné de lire le Décret du caveau des Ombres en présence du Thane Ufrang le Puissant, puis de le tuer."}},
[12944] = {"Seau de bonbons", nil},
[12945] = {"Seau de bonbons", nil},
[12946] = {"Seau de bonbons", nil},
[12947] = {"Seau de bonbons", nil},
[12948] = {"Le Champion de l'Angoisse", {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez de Vladof le Boucher ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12949] = {"Récupérer la clé", {"Vaelen l'Écorché dans la Salle d'Ufrang vous a demandé de récupérer la Clé des chaînes de Vaelen."}},
[12950] = {"Seau de bonbons", nil},
[12951] = {"Informer le baron", {"Parlez au Baron Lesquille au Caveau des Ombres."}},
[12952] = {"La perfection des gemmes", {"Vous effectuez cette quête"}},
[12953] = {"Brûle, Valkyrion, brûle !", {"Utilisez les Lance-harpons de Valkyrion pour brûler 6 Meules de foin sèches."}},
[12954] = {"L'Amphithéâtre de l'Angoisse : Yggdras !", {"Entrez dans l’arène de l’Amphithéâtre de l’Angoisse et triomphez d’Yggdras ! Ensuite, allez voir Wodin le Serviteur-troll pour toucher votre récompense."}},
[12955] = {"Éliminez la concurrence", {"Le Bondisseur, au Caveau des Ombres, vous demande d'affronter en duel et de vaincre Sigrid Née-de-glace, Efrem le Fidèle, Onu'zun et Bricole Sifflemèche."}},
[12956] = {"Une faible lueur d'espoir", {"Vous devez apporter le Minerai resplendissant raffiné à Thorim au Temple des tempêtes."}},
[12957] = {"Les esclaves des Forge-foudre", {"Rochecombe le Sculpteroc au Refuge de Rochecombe veut que vous libériez 6 Mécagnomes captifs et que vous tuiez 3 Sous-chefs forge-foudre."}},
[12958] = {"Livraison : Amulette en jade de sang", {"Combinez une Amulette vrykule, un Jade sombre et une Pierre de sang pour créer une Amulette en jade de sang et l'apporter ensuite à Timothy Jones à Dalaran.","Vous trouverez une Amulette vrykule sur n'importe quel vrykul du Norfendre."}},
[12959] = {"Livraison : Figurine luminescente en ivoire", {"Combinez de l’Ivoire nordique, une Calcédoine et un Cristal ombreux pour créer une Figurine luminescente en ivoire et apportez-la à Timothy Jones à Dalaran. Vous trouverez de l’Ivoire nordique sur n'importe quel Brochepelle ou Mammouth du Norfendre."}},
[12960] = {"Livraison : Broche du soleil pernicieux", {"Combinez une Broche des nains de fer, une Énorme citrine et un Cristal soleil pour créer une Broche du soleil pernicieux et apportez-la ensuite à Timothy Jones à Dalaran.","Vous trouverez une Broche des nains de fer sur n'importe quel Nain de fer du Norfendre."}},
[12961] = {"Livraison : Figurine complexe en os", {"Combinez un Os de proto-dragon, un Cristal soleil et un Jade sombre pour créer une Figurine complexe en os et l'apporter à Timothy Jones à Dalaran.","Vous trouverez un Os de proto-dragon sur n'importe quel Proto-dragon du Norfendre."}},
[12962] = {"Livraison : Relique de l'armure éclatante", {"Combinez un Morceau d'armure des éléments, une Pierre de sang et une Enorme citrine pour créer une Relique de l'armure éclatante et l'apporter à Timothy Jones à Dalaran.","Vous trouverez un Morceau d'armure des éléments sur n'importe quel Revenant du Norfendre."}},
[12963] = {"Livraison : Bibelot du soleil changeant", {"Combinez un Bibelot du Fléau, un Cristal soleil et un Cristal ombreux pour créer un Bibelot du soleil changeant et l'apporter à Timothy Jones à Dalaran.","Vous trouverez un Bibelot du Fléau sur n'importe quel créature du Fléau en Norfendre."}},
[12964] = {"Le minerai sombre", {"Apportez 5 Echantillons de minerai à Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12965] = {"Les cadeaux de Loken", {"Détruisez la Fureur de Loken, le Pouvoir de Loken et la Faveur de Loken, puis retournez voir Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12966] = {"Vous ne pourrez pas le rater", {"Le Roi Jokkum, à Dun Niffelem, veut que vous rejoigniez Njormeld à l'Enclume de Fjorn."}},
[12967] = {"Affronter les éléments", {"Njormeld veut que vous accompagniez Snorri à l'Enclume de Fjorn et que vous l'aidiez à tuer 10 Revenants vengeurs."}},
[12968] = {"La folie de Yulda", {"Iva la Vengeresse à Brunnhildar vous a demandé de tuer Yulda la Parlorage à Valkyrion."}},
[12969] = {"Il est à vous, ce gobelin ?", {"Défiez Agnetta Tyrsdottar afin de sauver Zeev Pfutincelle. Retournez auprès de Lok'lira la Mégère à Brunnhildar une fois la tâche accomplie."}},
[12970] = {"Le Hyldsmeet", {"Écoutez la proposition de Lok'lira la Mégère."}},
[12971] = {"Accepter tous les défis", {"Lok'lira la Mégère à Brunnhildar vous a demandé de vaincre 6 Prétendantes victorieuses."}},
[12972] = {"Vous aurez besoin d'un ours", {"Parlez à Brijana à l'extérieur de Brunnhildar."}},
[12973] = {"Les frères Barbe-de-bronze", {"Accompagnez Brann Barbe-de-bronze et sa Machine volante au Fort du Givre."}},
[12974] = {"L'appel du champion !", {"Vous effectuez cette quête"}},
[12975] = {"In Memoriam", {"Le Roi Jokkum à Dun Niffelem veut que vous récupériez 8 Fragments de cor à Chute-tonnerre."}},
[12976] = {"Un monument à ceux qui sont tombés", {"Le Roi Jokkum veut que vous apportiez les Fragments du Cor de Hodir à Njormeld à Dun Niffelem."}},
[12977] = {"L’appel de Hodir", {"Vous devez vaincre 5 Ancêtres niffelem et 5 Givre-nés sans repos à Chute-tonnerre. Vous devez ensuite souffler dans le Cor de Hodir au-dessus de leurs cadavres, puis rapportez le Cor de Hodir à Dun Niffelem."}},
[12978] = {"Face à la tempête", {"Bruor Plaie-du-fer au Refuge de Rochecombe veut que vous assassiniez 10 Forge-foudre nidavelirs. Vous pouvez tuer des Champions forge-foudre, des Magi forge-foudre, ou des Golems de guerre forge-foudre."}},
[12979] = {"L'armure des ténèbres", {"Apportez 4 Echantillons d'armure sombre à Bruor Plaie-du-fer au Refuge de Rochecombe."}},
[12980] = {"Les secrets de l'armure", {"Emportez la Plaque d'armure sombre à l'Atelier de Mimir et demandez au Domestique Tock de l'examiner. Quand ce sera fait, retournez auprès de Bruor Plaie-du-fer au Refuge de Rochecombe."}},
[12981] = {"Chaud et froid", {"Vous devez vous rendre au Lac du Champ-gelé pour y tuer les Revenants vermoulus et utiliser ensuite l'Essence de glace sur les Ferrailles fumantes à l'Enclume de Fjorn. Vous devrez alors récupérer 6 Morceaux de fer gelé et retourner à l'Enclume de Fjorn à Dun Niffelem."}},
[12982] = {"Les prisonniers de la Lame d'ébène", {"Vaelen l'Écorché, au Caveau des Ombres, vous a demandé de libérer 8 Prisonniers de la Lame d'ébène. Ils se trouvent dans des cages partout dans la région de Jotunheim."}},
[12983] = {"La dernière de son espèce", {"Astrid Bjornrittar à Brunnhildar vous a demandé de sauver une Matriarche glacegueule à la Caverne de l'Hibernation."}},
[12984] = {"Valduran Issu-des-tempêtes", {"Utilisez le Cor de guerre de Rochecombe quand vous aurez trouvé Valduran Issu-des-tempêtes. Battez Valduran Issu-des-tempêtes et retournez voir Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12985] = {"Forger une tête", {"Njormeld à Dun Niffelem a besoin de vous pour utiliser la Pioche à pointe de diamant sur les Géants de fer morts au lac du Champ-gelé, puis retournez le voir quand vous aurez récupéré 8 Yeux de Forge-foudre."}},
[12986] = {"Le destin des titans", {"Creteus dans le Pied-mont vous a demandé de vous rendre au Temple de l'Innovation, au Temple de l'Hiver, au Temple de la Vie et au Temple de l'Ordre, et d'utiliser la Banque de données pour examiner chaque zone."}},
[12987] = {"Placer le heaume de Hodir", {"Njormeld vous demande de voler vers le sommet des Pointes de glace du Nord à Dun Niffelem et d'utiliser les Tablettes de déclaration."}},
[12988] = {"Détruisez les forges !", {"Détruisez la Forge de foudre du nord, la Forge de foudre centrale et la Forge de foudre du sud avec les Bombes de Rochecombe, puis retournez voir Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12989] = {"Les ténèbres rampantes", {"Astrid Bjornrittar vous a demandé de tuer 8 Jormungars voraces à la Caverne de l'Hibernation à l'est de Brunnhildar."}},
[12990] = {"Le tunnel spatiotemporel", {"Apportez à Jon LeCraft les matériaux qu'il recherche pour éviter une mort rapide et obtenir un nouveau sort génial."}},
[12991] = {"Tapez là où ça fait mal", {"Tuez 10 Artificiers forge-foudre, puis retournez voir Bruor Plaie-du-fer au Refuge de Rochecombe."}},
[12992] = {"Écrasez les vrykuls !", {"Vil, au Caveau des Ombres, veut que vous assassiniez 15 Vrykuls dans la région de Jotunheim."}},
[12993] = {"Une menace colossale", {"Récupérez le Plan des caractéristiques offensives du colosse et le Plan des caractéristiques défensives du colosse, puis rapportez-les à Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[12994] = {"À la chasse aux espions", {"Vous devez vaincre 3 Espions forge-foudre dans la Vallée des Anciens hivers.","Utilisez le Croc de worg éthérien sur place pour invoquer un Chasseur éthérien qui débusquera les ennemis cachés. Vous devez retourner auprès de la Mère de la tanière worg de givre à Dun Niffelem une fois la vallée nettoyée de ces espions."}},
[12995] = {"Laisser notre marque", {"Le Baron Lesquille, au Caveau des Ombres, veut que vous plantiez 15 Bannières de la Lame d'ébène près des cadavres de Vrykuls trouvés dans la région de Jotunheim."}},
[12996] = {"Tour de chauffe", {"Astrid Bjornrittar à Brunnhildar veut que vous utilisiez les Rênes de matriarche ours de guerre pour vaincre Kirgaraak."}},
[12997] = {"Dans la fosse", {"Astrid Bjornrittar à Brunnhildar veut que vous utilisiez les Rênes de matriarche ours de guerre dans la Fosse du croc et que vous battiez 6 Ours de guerre du Hyldsmeet."}},
[12998] = {"Le Cœur de la tempête", {"Rochecombe le Sculpteroc au Refuge de Rochecombe veut que vous alliez à Uldis pour essayer d'empêcher que le Surveillant Narvir n'obtienne le Cœur de la tempête."}},
[12999] = {"La sorcière des ossements", {"Parlez à la Sorcière des ossements dans la caverne, à Njorndar."}},
[13000] = {"Mesures d'urgence", {"Vous effectuez cette quête"}},
[13001] = {"Forger la lance de Hodir", {"Le Gardien du savoir Randvir à Dun Niffelem vous a demandé de récupérer 3 Éclats permagivre des profondeurs de la Caverne de l'hibernation et 3 Peaux de mammouth stoïque dans la Vallée des Anciens hivers."}},
[13002] = {"La perfection des gemmes", {"Geba'li, du Bastion Chanteguerre, a besoin de deux Enormes citrines, de deux Jades sombres et de deux Cristaux ombreux."}},
[13003] = {"L’art de bien tuer son dragon", {"Vous devez utiliser la Lance de Hodir pour défier un Wyrm sauvage dans la Vallée des Anciens hivers. Une fois que c'est fait, vous devez vous battre à mort."}},
[13004] = {"La perfection des gemmes", {"Alestros, du Donjon de la Bravoure, a besoin de deux Énormes citrines, de deux Jades sombres et de deux Cristaux ombreux."}},
[13005] = {"Le serment des Terrestres", {"Thorim, à la Terrasse des Faiseurs, vous demande d'utiliser le Cor des pics pour appeler les Terrestres, et vaincre 20 Nains de fer et 7 Sentinelles de fer."}},
[13006] = {"Nettoyage à suc", {"Vous devez aller à la Caverne de l'hibernation à l'ouest de Dun Niffelem pour récupérer 5 unités d'Huile visqueuse sur les créatures du même nom sur place.","Vous devez ensuite retourner à Dun Niffelem pour appliquer l'huile sur le Heaume de Hodir."}},
[13007] = {"Le colosse de fer", {"Utilisez un Jormungar apprivoisé pour vaincre le Colosse de fer, puis retournez voir Rochecombe le Sculpteroc au Refuge de Rochecombe."}},
[13008] = {"Les tactiques du Fléau", {"Le Commandant de la croisade Entari, à l'Avant-garde d'Argent dans la Couronne de glace, vous a demandé de libérer 8 Croisés entoilés."}},
[13009] = {"Un nouveau départ", {"Njormeld veut que vous apportiez l'Armure reforgée à Thorim au Temple des tempêtes."}},
[13010] = {"Krolmir, le marteau des tempêtes", {"Thorim vous demande d'aller parler au Roi Jokkum à Dun Niffelem et de découvrir ce qu'il sait à propos de Krolmir."}},
[13011] = {"Éliminer Jorcuttar", {"Le Roi Jokkum à Dun Niffelem vous demande de tuer Jorcuttar à la Caverne de l’hibernation."}},
