[13034] = {"Le témoin et le héros", {"Xarantaur, au Camp Tunka'lo, veut que vous lisiez le Premier parchemin de l'histoire, le Second parchemin de l'histoire et le Troisième parchemin de l'histoire."}},
[13035] = {"Les laquais de Loken", {"Thorim vous demande de tuer Eisenfaust dans le Hall du Façonneur, et Halefnir l'Enfant du vent et Duronn le Formerune à la Terrasse des Faiseurs."}},
[13036] = {"L'honneur avant tout", {"Présentez-vous au Commandant de la croisade Entari à l'Avant-garde d'Argent, dans la Couronne de glace."}},
[13037] = {"Souvenirs de Sabot-tempête", {"Parlez au Chef Vivelance au Gouffre du Sérac, puis retournez voir Xarantaur au Camp Tunka'lo."}},
[13038] = {"Distortions temporelles", {"Utilisez le Totem du chef pour fermer 4 Failles du Sérac, puis rapportez le Totem à Xarantaur au Camp Tunka'lo."}},
[13039] = {"Défendre l'avant-garde", {"Le Seigneur croisé Dalfors, à l'Avant-garde d'Argent dans la Couronne de glace, vous a demandé de tuer 15 Nérubiens des profondeurs oubliées."}},
[13040] = {"Soigner l'insoignable", {"Le Père Gustav, à l'Avant-garde d'Argent dans la Couronne de glace, vous a demandé de lui apporter 10 Glandes à venin des profondeurs oubliées."}},
[13041] = {"Terminer la livraison", {"Apportez une Calcédoine à Timothy Jones à Dalaran."}},
[13042] = {"Dans les profondeurs des Basses-salles", {"La Sorcière des ossements à Njorndar vous a demandé de battre l'Apprenti Osterkilgr afin d'obtenir des informations concernant ce que prépare le Dr Terrible.","Vous devez ensuite tuer l'Apprenti."}},
[13043] = {"Le tout est plus grand que la somme des parties", {"Montez Nergeld et prenez-en le contrôle. Défendez-le contre les habitants de la Salle des Horreurs jusqu'à ce que le Dr Terrible se montre.","Utilisez Nergeld pour tuer le docteur, puis retournez voir la Sorcière des ossements à Njorndar."}},
[13044] = {"S'il reste des survivants...", {"Parlez à Penumbrius à l'Avant-garde d'Argent dans la Couronne de glace."}},
[13045] = {"Ça va tanguer dans la verdure !", {"Sauvez 3 Croisés d'argent capturés à Fléaulme et amenez-les à l'Infirmerie de l'avant-garde au centre de l'Avant-garde d'Argent. Allez voir le Généralissime Tirion Fordring si vous réussissez."}},
[13046] = {"Caler la dent creuse d'Arngrim", {"Vous devez donner à manger à Arngrim 5 Jormungars désincarnés puis retourner auprès de l'image gelée d'Arngrim à Dun Niffelem.","Rendez-vous dans la Vallée des Anciens hivers et utilisez la Dent d'Arngrim sur les Jormungars mobiles qui s'y trouvent. Une fois qu'ils sont désincarnés, vous devez les combattre jusqu'à ce qu'ils soient affaiblis."}},
[13047] = {"La rétribution", {"Rencontrez Thorim près du Temple de la Sagesse. Racontez l'issue du combat au Roi Jokkum à Dun Niffelem."}},
[13048] = {"Lorsque le temps tourne court", {"Utilisez le Marteau de la connaissance sur les Reliques taunkas pour le mettre en phase avec l'époque de Sabot-tempête. Une fois que vous l'aurez mis en phase avec 6 Reliques de l'époque de Sabot-tempête, retournez voir Xarantaur au Camp Tunka'lo."}},
[13049] = {"Les armes du héros", {"Retrouvez la Lance de Sabot-tempête et la Cotte de maille de Sabot-tempête, et rapportez-les à Xarantaur au Camp Tunka'lo."}},
[13050] = {"Veranus", {"Thorim, au Temple des tempêtes, veut que vous obteniez 5 Petits œufs de proto-drake sur les pics proches de Brunnhildar."}},
[13051] = {"Violation de territoire", {"Placez les Oeufs de proto-dragon volés au sommet du Nid de la mère des couvées, puis retournez voir Thorim au Temple des tempêtes."}},
[13052] = {"Surveillance aérienne", {"Le Professeur Calvert veut que vous preniez le vol pour le Terrain d'atterrissage de Rive-du-lac et que vous parliez au Pilote Vic, une fois rendu là-bas."}},
[13053] = {"À la recherche de survivants", {"Aidez Zak Tremblerouage à réussir le Vol d'essai."}},
[13054] = {"Le pisteur disparu", {"Vous effectuez cette quête"}},
[13055] = {"Remède de caverne", {"Le Traqueur Val'zij, à la tanière de Gimorak, vous a demandé de récupérer 1 Glande à toxine jormungar et 8 Champignons de la caverne."}},
[13056] = {"La vengeance est un plat qui se mange à toute heure", {"Le Traqueur Val'zij vous demande de tuer Gimorak dans la Tanière de Gimorak."}},
[13057] = {"La terrasse des Faiseurs", {"Trouvez Thorim à la limite sud-est de la Terrasse des Faiseurs, au nord-ouest de Dun Niffelem."}},
[13058] = {"Changer le sens du vent", {"Utilisez le Marteau de la connaissance pour remonter le temps et affronter le Vent du nord sous les traits de Sabot-tempête. Quand Sabot-tempête perd, récupérez le Cor de fureur élémentaire et achevez le Vent du nord, puis retournez voir Xarantaur au Camp Tunka'lo."}},
[13059] = {"Vengeance pour les varguls", {"Bethod Feigr, aux Basses-salles, vous demande de défier le Thane Illskar en jetant son épée, puis de le tuer au Cercle de Souffrance."}},
[13060] = {"Quand tout le reste a échoué…", {"Parlez à Ricket pour utiliser l'Engin de livraison à propulsion D16 de Ricket afin de pénétrer au cœur des Pics Foudroyés."}},
[13061] = {"Préparation pour la gloire", {"Parlez à Lok'lira la Mégère à Brunnhildar."}},
[13062] = {"Cadeau d'adieu de Lok'lira", {"Parlez à Gretta la Médiatrice à Brunnhildar."}},
[13063] = {"Digne de valeur", {"Brijana veut que vous alliez à Brunnhildar pour parler avec Astrid Bjornrittar."}},
[13064] = {"Rivalité fraternelle", {"Thorim veut que vous écoutiez son histoire."}},
