[5241] = {"Tio Carlin", {"Encontre Carlin Trilharrubra."}},
[5242] = {"O golpe final", {"Mate Moora e Salia, e leve a Cabeça do Lorde das Sombras Fel'dan para Greta Musgo no Casco em Selva Maleva."}},
[5243] = {"Casas do sagrado", {"Viaje para Stratholme, ao norte. Procure os caixotes de suprimentos que cobrem a cidade e recupere 5 Águas Bentas de Stratholme. Fale novamente com Leonid Bartolomeu, o Venerado, quando coletar fluido abençoado suficiente."}},
[5244] = {"Ruínas de Kel'Theril", {"Fale com Jaron Moldapedra na Aldeia Chuva Estelar."}},
[5245] = {"Espíritos atormentados de Kel'Theril", {"Use a Picareta do Jaron para encontrar os quatro Fragmentos da Relíquia dos Altaneiros. Leve-os para Aurora Clamacéus nas Terras Pestilentas Orientais."}},
[5246] = {"Fragmentos do passado", {"Encontre os Escritos Sagrados dos Altaneiros no Templo de Zin-Malor, em Azshara, e leve-os para Aurora Clamacéus, perto da Torre do Passo Norte nas Terras Pestilentas Orientais."}},
[5247] = {"Fragmentos do passado", {"Leve uma Barra de Tório Encantado, 5 Restaurações de Cristal e uma Ampola de Água Hedionda para Aurora Clamacéus perto da Torre do Passo Norte nas Terras Pestilentas Orientais."}},
[5248] = {"Atormentada pelo passado", {"Leve o Cristal de Zin-Malor ao Lago Kel'Theril, em Hibérnia, e encontre o espírito de um altaneiro que fale com você."}},
[5249] = {"Rumo a Hibérnia!", {"Encontre a Aldeia Chuva Estelar, em Hibérnia, e fale com Eol Correnoite."}},
[5250] = {"Chuva Estelar", {"Encontre a Chuva Estelar, em Hibérnia, e fale com Eol Correnoite."}},
[5251] = {"O arquivista", {"Viaje até Stratholme e encontre o Arquivista Galford da Cruzada Escarlate. Destrua-o e queime o Arquivo Escarlate."}},
[5252] = {"Altaneira arrependida", {"Fale com Eol Correnoite na Aldeia Chuva Estelar e pergunte qual é o lugar mais apropriado para o Cristal de Zin-Malor."}},
[5253] = {"O cristal de Zin-Malor", {"Leve o Cristal de Zin-Malor para o Arquidruida Fandral Guenelmo em Darnassus."}},
[5261] = {"Enzo Peleteiro", {"Fale com Enzo Peleteiro."}},
[5262] = {"A verdade desaba sobre todos", {"Leve a Cabeça do Balnazzar para o Duque Nicolau Zverenhoff nas Terras Pestilentas Orientais."}},
[5263] = {"Acima e além", {"Desbrave Stratholme e destrua o Barão Rivendare. Corte sua cabeça e leve-a de volta para o Duque Nicolau Zverenhoff.\r\n"}},
[5264] = {"Lorde Maximiliano Tyrosus", {"Fale com o Lorde Maximiliano Tyrosus. Ele está dentro da igreja."}},
[5265] = {"O Castelo Violeta", {"Abra o Castelo Violeta e reivindique sua recompensa."}},
[5281] = {"As almas inquietas", {"Encontre Egan. Ele foi visto pela última vez nos arredores de Stratholme."}},
[5282] = {"As almas inquietas", {"Use o Canhão de Ectoplasma do Egan nos moradores fantasmagóricos e espectrais de Stratholme. Depois de livrá-los da forma de fantasma, use o canhão de novo e BLAU: liberdade!","","Liberte 15 Almas Inquietas, depois fale novamente com Egan."}},
[5283] = {"A arte de forjar armaduras", {"Para virar Ferreiro de Armaduras, tu vai ter que forjar os seguintes itens e levá-los pro Grumnus: quatro Elmos Ornados de Mithril, duas Botas Ornadas de Mithril e um Peitoral Ornado de Mithril."}},
[5284] = {"O caminho do armeiro", {"Para te tornares Armeiro, tu terás que forjar os seguintes itens e trazer para o Ironus: 4 Espadas Largas de Lunaço, 4 Machados Sólidos de Ferro, 2 Machados Pesados de Mithril e 2 Grandes Maças Pretas.\r\n"}},
[5301] = {"A arte de forjar armaduras", {"Para virar Ferreiro de Armaduras, você vai ter que forjar os seguintes itens e levá-los para Okothos: quatro Elmos Ornados de Mithril, duas Botas Ornadas de Mithril e um Peitoral Ornado de Mithril."}},
[5302] = {"O caminho do armeiro", {"Para se tornar um Armeiro, você precisa fazer os seguintes itens e levá-los para Borgosh: 4 Espadas Largas de Lunaço, 4 Machados Sólidos de Ferro, 2 Machados Pesados de Mithril e 2 Grandes Maças Pretas.\r\n"}},
[5303] = {"<UNUSED>", nil},
[5305] = {"Doce serenidade", {"Vá para Stratholme e mate o Forjador de Martelos Carmesim. Recolha o Avental do Forjador de Martelos Carmesim e leve-o para Lilith.\r\n"}},
[5306] = {"A litofídia da caçadora sombria", {"Vá para o Pico da Rocha Negra e mate a Caçadora Sombria Vosh'gajin. Recolha a Litofídia de Vosh'gajin e fale novamente com Kilram."}},
[5307] = {"Corrupção", {"Encontre o Cuteleiro da Guarda Negra em Stratholme e destrua-o. Recolha a Insígnia da Guarda Negra e fale novamente com Seril Flagelicida."}},
[5321] = {"A adormecida despertou", {"Escolte Kerlonian Perenumbra até Liladris Luneflúvia no Entreposto de Maestra, situado no Vale Gris."}},
[5341] = {"A fortuna da família Barov", {"Desbrave Scolomântia e recupere a fortuna da família Barov. A fortuna é composta por quatro escrituras: A Escritura de Castro das Flechas, A Escritura de Montalvo, A Escritura da Serraria Tarren e A Escritura de Costa Sul. Fale novamente com Alexei Barov quando tiver terminado a tarefa."}},
[5342] = {"O último Barov", {"Vá para o Acampamento Ventogelante, no território da Aliança, e assassine Weldon Barov. Recolha a cabeça dele e fale novamente com Alexei Barov.","","Esteja avisado: Weldon provavelmente estará muito bem protegido pela escória da Aliança."}},
[5343] = {"A fortuna da família Barov", {"Desbrave Scolomântia e recupere a fortuna da família Barov. A fortuna é composta por quatro escrituras: A Escritura de Castro das Flechas, a Escritura de Montalvo, a Escritura da Serraria Tarren e a Escritura de Costa Sul. Fale novamente com Weldon Barov quando tiver terminado a tarefa."}},
[5344] = {"O último Barov", {"Vá para o Baluarte, no território da Horda, e assassine Alexei Barov. Recolha a cabeça dele e fale novamente com Weldon Barov.","","Esteja avisado: Alexei provavelmente estará muito bem protegido pela escória da Horda."}},
[5361] = {"Árvore genealógica", {"Leve o Relatório de Vigia do Rochedo Chifre Longo para Nataoka Chifre Longo no Entreposto do Espírito que Anda, situado em Desolação."}},
[5381] = {"Mão da Iruxos", {"Recolha a Caixa Demoníaca na Fortaleza Machado do Trovão e leve-a, junto com a Picareta do Diabo, para Taiga Crina Sábia na Aldeia Pescassombra.\r\n"}},
[5382] = {"Doutor Theolen Krastinov, o Carniceiro", {"Encontre o Doutor Theolen Krastinov em Scolomântia. Destrua-o, depois queime os Restos Mortais de Eva Sarkhoff e os Restos Mortais de Lucien Sarkhoff. Fale novamente com Eva Sarkhoff depois que tiver terminado a tarefa.\r\n"}},
[5383] = {"Saco de Horrores de Krastinov", {"Leve o Saco de Horrores para Eva Sarkhoff, em Castro das Flechas."}},
[5384] = {"Kirtonos, o Arauto", {"Volte a Scolomântia com o Sangue dos Inocentes em mãos. Encontre a varanda e derrame o Sangue dos Inocentes no braseiro. Kirtonos virá na intenção de devorar sua alma.","","Lute com bravura, não dê nenhuma brecha! Destrua Kirtonos e fale novamente com Eva Sarkhoff.\r\n"}},
