[6183] = {"Em honra aos mortos", {"Fale com Mathias Shaw novamente se desejar aceitar esta tarefa."}},
[6184] = {"Flint Umbramais", {"Vá até o Acampamento Ventogelante, nas Terras Pestilentas Ocidentais, e encontre seu contato, Flint Umbramais.\r\n"}},
[6185] = {"As Pestes do Leste", {"Vasculhe as Terras Pestilentas Orientais em busca de pistas sobre o \"Arauto da Praga\" e os agentes da AVIN desaparecidos. Ser encontrar alguma Insígnia da AVIN, leve de volta para Flint Umbramais, no Acampamento Ventogelante."}},
[6186] = {"A ascensão do Arauto da Praga", {"Return to Stormwind and inform King Varian Wrynn of the fate of Nathanos Marris."}},
[6187] = {"A restauração da ordem", {"Reúna um exército e siga até as Terras Pestilentas Orientais. Lance um ataque maciço contra Nathanos Arauto da Praga e qualquer lixo da Horda que tente protegê-lo.","","Mantenha-se sempre alerta, Nekoquestie. Os membros da Horda vão defender o lorde patrulheiro com as próprias vidas."}},
[6221] = {"Lenha Morta do norte", {"Nafien quer que você mate 5 Vigias do Covil de Lenha Morta, 5 Vingadores de Lenha Morta e 5 Xamãs de Lenha Morta."}},
[6241] = {"Atividade Invernosa", {"Salfa quer que você mate 6 Totêmicos Invernosos, 6 Vigias do Covil dos Invernosos e 6 Desbravadores Invernosos."}},
