[7342] = {"Flechas são pra molengas", nil},
[7367] = {"Anulando a ameaça", {"Encontre o Especialista em Explosivos dos Lobo do Gelo e mate-o. Fale novamente com o Especialista em Explosivos dos Lançatroz quando completar a missão."}},
[7368] = {"Anulando a ameaça", {"Encontre o Especialista em Explosivos dos Lançatroz e mate-o. Fale novamente com o Especialista em Explosivos dos Lobo do Gelo quando completar a missão."}},
[7383] = {"Coroa da Terra", {"Encha o Frasco de Ametista e leve-o de volta para Corithras Lunafúria em Dolanaar."}},
