[7701] = {"PROCURA-SE: Feitor Maltorius", {"Nas profundezas do Fosso de Lava do Caldeirão, na Garganta Abrasadora, você encontrará o anão traidor dos Ferro Negro chamado Feitor Maltorius.","","Mate o Feitor Maltorius, arranque a cabeça dele e leve-a para o Capitão-vigia Lolo Longalcance na Garganta Abrasadora para receber sua recompensa."}},
[7702] = {"Mate-os de sono", {"Lá no fundo do Fosso de Lava do Caldeirão, você vai encontrar os dormitórios dos anões Ferro Negro.","","Vá até lá e roube 20 Travesseiros de Ferro Negro. Fale novamente com Evonice Fumarenta na Garganta Abrasadora quando tiver terminado a tarefa."}},
[7703] = {"Negócios pendentes", {"Encontre as Manoplas do Poder de Gordok e devolva-as para o Capitão Kebrapaw no Gládio Cruel.","","De acordo com Kebrapaw, a \"história de ancião\" diz que Tortheldrin — um elfo \"medonho\" que se diz príncipe — roubou as manoplas de um dos reis Gordok."}},
[7704] = {"Olha o tamanho disso!", {"Leve o Travesseiro da Camareira Alma Fada para Evonice Fumarenta, na Garganta Abrasadora."}},
[7721] = {"Bota dez aí, chefia!", {"Colete 10 Âmagos dos Elementais de Água dos Elementais do Mar e dos Borrifos do Mar ao longo da Costa Esquecida de Feralas, e leve-os para Zorbin Cegasvista, próximo às docas."}},
[7722] = {"Segue o fluxo", {"Em algum lugar do Fosso de Lava do Caldeirão você encontra as Instruções Secretas: Fluxo Ígneo que o Feitor Maltorius roubou. Encontre as instruções e devolva-as ao Mestre Ferreiro Queimatar na Garganta Abrasadora.\r\n"}},
[7723] = {"Malditos dedos gordos", {"Hansel Mão-pesada quer que você mate 20 Golens de Guerra Pesados.","","Os Golens de Guerra Pesados perambulam pelas áreas em torno do Caldeirão, na Garganta Abrasadora. Quando tiver matado a quantidade necessária, fale novamente com Hansel na Garganta Abrasadora."}},
[7724] = {"Toco cru pegando fogo", {"Hansel Mão-pesada quer que você mate 20 Grandes Aranhas de Lava na Garganta Abrasadora. Você pode encontrá-las nos platôs e poços de lava do local.","","Depois de matar as Grandes Aranhas de Lava, fale novamente com Hansel Mão-pesada na Garganta Abrasadora."}},
[7725] = {"Querida, encolhi mais gigantes", {"Use o Ultra-encolhedor de Zorbin para deixar qualquer tipo de gigante de Feralas com um tamanho mais compacto e manejável. Colete 10 Resíduos de Miniaturização encontrados nos gigantes encolhidos e leve para Zorbin Cegasvista, que fica nas docas da Costa Esquecida, em Feralas."}},
[7726] = {"Completa o tanque, chefia!", {"Colete 6 Âmagos dos Elementais de Água dos Elementais do Mar e dos Borrifos do Mar ao longo da Costa Esquecida de Feralas e leve-os para Zorbin Cegasvista, próximo às docas."}},
[7727] = {"Incendossauro? É de comer?", {"Hansel Mão-pesada quer que você mate 20 Incendossauros.","","Eles ocupam as poças dentro do Fosso de Lava. O Fosso de Lava fica no Caldeirão da Garganta Abrasadora.","","Depois de matar os Incendossauros, fale novamente com Hansel Mão-pesada na Garganta Abrasadora."}},
[7728] = {"OBJETOS ROUBADOS: Alcaraviz de Ferraria e Luneta do Espia", {"Encontre e devolva o Alcaraviz de Ferraria e a Luneta do Espia ao Capataz Scrange na Garganta Abrasadora.","","A única informação que você tem sobre esses itens é a seguinte: com certeza foram roubados por anões Ferro Negro. O Alcaraviz de Ferraria é uma ferramenta usada por ferreiros e a Luneta do Espia é um aparelho de monitoramento inestimável para vigias. "}},
[7729] = {"TEMOS VAGAS: desbancando a concorrência", {"Mate 15 Capatazes Ferro Negro e 15 Feitores Ferro Negro. Depois, fale novamente com Capataz Scrange, na Garganta Abrasadora."}},
[7730] = {"Infestação Zukk'ash", {"Traga 20 Carapaças de Zukk'ash para Hadoken Passo Veloz na Aldeia Mojache, que fica em Feralas."}},
[7731] = {"Cravaçoite", {"Derrote Cravaçoite na Profundeza Atormentada, leve as glândulas para Hadoken Passo Veloz na Aldeia Mojache, que fica em Feralas."}},
[7732] = {"O relatório dos Zukk'ash", {"Entregue o Relatório sobre Zukk'ash da Aldeia Mojache para Zilzibin Tamborleiro. Ele fica no Bazar de Orgrimmar."}},
[7733] = {"Melhoria de qualidade", {"Leve 10 Pelegos de Yeti Cortifúria para Pradal McGrubben no Domínio de Plumaluna, em Feralas."}},
