[8733] = {"Erânicos, Tirano dos Sonhos", {"Vá para o continente de Teldrassil e encontre o agente de Malfurion perto das muralhas de Darnassus.\r\n"}},
[8734] = {"Tyrande e Remulos", {"Vá à Clareira da Lua e fale com o Guardião Remulos. "}},
[8735] = {"A Corrupção do Pesadelo", {"Vá até os quatro portais do Sonho Esmeralda em Azeroth e obtenha um Fragmento da Corrupção do Pesadelo de cada um. Quando tiver cumprido essa tarefa, fale novamente com o Guardião Remulos na Clareira da Lua.\r\n"}},
[8736] = {"Os pesadelos manifestos", {"Defenda o Refúgio Noturno de Erânicos. Impeça que o Guardião Remulos pereça. Não mate Erânicos. Defenda-se. Espere Tyrande.\r\n"}},
[8737] = {"Templário Lazúli", {"Evoque e mate um Templário Lazúli e fale novamente com Bor Juba Agreste no Forte Cenariano. Você também precisa entregar as Instruções Táticas I para concluir a missão."}},
[8738] = {"Relatório do Batedor de Colme'Régia", {"Contate o Batedor Cenariano Landion no interior da Colme'Régia e entregue o Relatório do Batedor de Colme'Régia ao Arauto do Vento Chifre Altivo no Forte Cenariano. Você também precisa entregar as Instruções Táticas VII para concluir a missão."}},
[8739] = {"Relatório do Batedor de Colme'Ashi", {"Contate a Batedora Cenariana Jalia no interior da Colme'Ashi e entregue o Relatório do Batedor de Colme'Ashi ao Arauto do Vento Chifre Altivo no Forte Cenariano. Você também precisa entregar as Instruções Táticas VIII para concluir a missão."}},
[8740] = {"Os espoliadores do Crepúsculo", {"Mate a Espoliadora do Crepúsculo Mirna e 2 Espoliadores do Crepúsculo. Apresente-se ao Arauto do Vento Chifre Altivo depois que tiver concluído a tarefa. Você também precisa entregar as Instruções Táticas IX para concluir a missão."}},
[8741] = {"O campeão está de volta", {"Leve o Estilhaço de Cetro Verde a Anacronos, que está nas Cavernas do Tempo, em Tanaris.\r\n"}},
