[8778] = {"A Brigada de Altaforja vai botar pra explodir!", {"Leve 6 Óleos de Imolação, 5 Combustíveis de Foguete Goblínico e 10 Pós Explosivos Densos para a Arcanista Molabico, que está perto de Colme'Zora, em Silithus. Você também precisa entregar as Instruções de Logística IV para completar esta tarefa."}},
[8779] = {"Matérias-primas de vidência", {"Leve 1 Estilhaço Brilhante Grande, 1 Estilhaço Radiante Grande e 1 Esmeralda Enorme para a Geóloga Matarriso, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística V para concluir esta missão."}},
[8780] = {"Kits de armadura para o campo", {"Leve 8 Kits de Armadura Rústica e 8 Kits de Armadura Pesada para Jenila Fortemalho no Posto Avançado da Brigada de Altaforja, perto da Colme'Zora, em Silithus. Você também precisa entregar as Instruções de Logística VII para concluir a missão."}},
[8781] = {"Armas para o campo", {"Leve 2 Espadas Largas de Lunaço para Jenila Fortemalho no Posto Avançado da Brigada de Altaforja, perto da Colme'Zora. Você também precisa entregar as Instruções de Logística VI para concluir a missão."}},
[8782] = {"Suprimentos de uniformização", {"Leve 1 Lunatrama, 2 Peças de Runatrama e 1 Seda de Aranha Ferroteia para o Arauto do Vento Chifre Altivo, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística VIII para concluir esta missão."}},
[8783] = {"Materiais extraordinários e onde se encontram", {"Leve 2 Barras de Tório Encantado e 2 Couros Encantados para Vargus, no Forte Cenariano, em Silithus. Você também precisa entregar as Instruções de Logística IX para concluir a missão."}},
[8784] = {"Segredos dos Qiraji", {"Leve o Artefato Qiraji Antigo aos dragões escondidos perto da entrada do templo."}},
[8785] = {"A Legião de Orgrimmar precisa de mojo!", {"Leve 6 Mojos Poderosos, 6 Frascos de Mojo Grande e 8 Óleos de Imolação para a Sacerdotisa Sombria Shai, que fica perto de Colme'Régia, em Silithus. Você também precisa entregar as Instruções de Logística IV para completar esta tarefa."}},
[8786] = {"Armas para o campo", {"Leve 3 Machados Sólidos de Ferro para Merok Passo Longo no acampamento da Legião de Orgrimmar, do lado de fora da Colme'Régia. Você também precisa entregar as Instruções de Logística VI para concluir a missão."}},
[8787] = {"Kits de armadura para o campo", {"Leve 8 Kits de Armadura Rústica e 8 Kits de Armadura Pesada para Merok Passo Longo perto de Colme'Régia. Você também precisa entregar as Instruções de Logística VII para concluir a missão."}},
