[8789] = {"Armamentos Imperiais Qiraji", {"Arygos, no Templo de Ahn'Qiraj, criará Armamentos Infundidos em Elemêntio para você. Para tanto, entregue-lhe Armamentos Imperiais Qirajis e 3 Minérios de Elemêntio."}},
[8790] = {"Paramentos Imperiais Qiraji", {"Merithra do Sonho, no Templo de Ahn'Qiraj, criará Armamentos Infundidos em Elemêntio para você. Para tanto, entregue-lhe Paramentos Imperiais Qiraji e 3 Minérios de Elemêntio.\r\n"}},
