[9144] = {"Perdido na Terra Fantasma", {"Siga a estrada que parte da Vila de Brisabela rumo ao sul e procure o Mensageiro Albatrilha quando se aproximar da Terra Fantasma."}},
[9145] = {"Ajude a patrulheira Valanna!", {"Encontre a Patrulheira Valanna e o Tenente Tomathren às margens do Lago Elrendar, que fica na Terra Fantasma perto da Vila Corona Solar."}},
[9146] = {"Apresente-se ao capitão Helios", {"Fale com o Capitão Helios no Enclave dos Andarilhos, que fica na margem sul do Lago Elrendar na Terra Fantasma."}},
[9147] = {"O mensageiro caído", {"A Boticária Thereza na Floresta do Canto Eterno quer que você colete 4 Amostras de Sangue Pestilento das feras da Terra Fantasma."}},
[9148] = {"Entrega para Tranquillien", {"Leve a Carta de Luaprata para o Arcanista Vandril em Tranquillien, que fica na Terra Fantasma."}},
[9149] = {"A costa infecta", {"Leve 6 Espinhas Pestilentas de Murloc para o Boticário Reinaldo na Terra Fantasma."}},
[9150] = {"Resgatando o passado", {"O Magíster Darenis quer que você vá para o Sacrário Lunar, a oeste de Tranquillien, e recupere 8 Essências de Mana Cristalizadas dos Devoradores Arcanos e Deslocadores de Mana que habitam a estrutura."}},
[9151] = {"O sacrário solar", {"Fale com o Magíster Idonis no Sacrário Solar, que fica na Terra Fantasma."}},
[9152] = {"Suprimentos de Raposo", {"Recupere os Suprimentos de Maurício Raposo e devolva-os para ele em Tranquillien, que fica na Terra Fantasma."}},
[9153] = {"Sob a sombra", {"Verifique seu mapa para encontrar uma região sob ataque do Flagelo. Vá até lá e derrote os membros do Flagelo em torno da necrópole, a fim de danificá-la. Em seguida, entregue 10 Runas Necróticas para o Comandante Tomás Helleran na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais."}},
[9154] = {"Capela Esperança da Luz", {"Entregue o Anúncio do Chamado às Armas para o Guardião dos Pergaminhos na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais."}},
[9155] = {"Na trilha da morte", {"Mate 10 Famélicos Reanimados e 10 Canibais Tortos na Trilha da Morte. Fale novamente com o Sicário Raniério em Tranquillien para receber sua recompensa."}},
[9156] = {"Procura-se: Putripunho e Luzran", {"Vasculhe a Terra Fantasma e encontre Putripunho e Luzran. Leve a cabeça deles para o Sicário Raniério em Tranquillien, para receber sua recompensa."}},
[9157] = {"Rituais esquecidos", {"Leve 8 Medalhões das Ondas para Geranis Matinalba na Terra Fantasma."}},
[9158] = {"Vetores da praga", {"Mate 10 Linces Garralma para a Andarilha Sedina no Enclave dos Andarilhos, que fica na Terra Fantasma."}},
[9159] = {"Enfraquecendo a praga", {"Mate 8 Tocaieiras Fusaracna e 10 Brumorcegos Vampíricos para a Andarilha Sedina no Enclave dos Andarilhos, que fica na Terra Fantasma."}},
[9160] = {"Investigue An'daroth", {"Investigue a área de An'daroth, mate 12 Espiãs Sentinelas e depois apresente-se à Dama Aurífera em Tranquillien, que fica na Terra Fantasma."}},
[9161] = {"A sombra do traidor", {"Vá para a Torre Aurestela na Terra Fantasma e procure pistas sobre o passado de Dar'Khan."}},
[9162] = {"Vestígios do passado", {"Leve o Diário de Dar'Khan para a Patrulheira Vynna no Enclave dos Andarilhos."}},
[9163] = {"Em território ocupado", {"Roube o Plano Noctiélfico: An'daroth, o Plano Noctiélfico: An'owyn e o Plano Noctiélfico: Divinação dos Sin'dorei e leve-os para Dama Aurífera em Tranquillien, que fica na Terra Fantasma."}},
[9164] = {"Prisioneiros da Cidadela da Morte", {"Leve o Trago Restaurador de Reinaldo até a Cidadela da Morte e resgate o Aprendiz Varnis, a Boticária Edite e o Patrulheiro Vedoran. Fale com a Arcanista Janeda no Sacrário Solar para receber sua recompensa."}},
[9165] = {"Salvo-conduto", {"Entregue o Salvo-conduto assinado para o Comandante de Expedição Metz na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais."}},
[9166] = {"Entregue os planos para An'telas", {"Entregue os Planos Noctiélficos para o Magíster Salystor em An'telas, que fica na Terra Fantasma."}},
[9167] = {"A destruição do traidor", {"Mate Dar'Khan Drathir na Cidadela da Morte e leve sua cabeça para o Magíster Idonis no Sacrário Solar, que fica na Terra Fantasma."}},
[9168] = {"Heart of Deatholme", {"Destroy 8 Eyes of Dar'Khan and report back to Magister Idonis at the Sanctum of the Sun in the Ghostlands."}},
[9169] = {"Desative An'owyn", {"Recupere o Orbe Controlador do Cristal e use-o para desativar o Cristal da Lua Noctiélfico em An'owyn. Depois, apresente-se ao Magíster Salystor em An'telas, que fica na Terra Fantasma."}},
[9170] = {"Os tenentes de Dar'Khan", {"O Magíster Idonis quer que você vá para a Cidadela da Morte e mate Masophet, o Negro, Juron, o Enganador, Borgoth, o Dessangrador e Mirdoran, o Caído. Apresente-se a ele no Sacrário Solar, que fica na Terra Fantasma, quando completar a missão."}},
[9171] = {"Comida crocante", {"Colete 5 Pernas Crocantes de Aranha e entregue-as para o Mestre Chef Mouldier em Tranquillien, que fica na Terra Fantasma."}},
[9172] = {"Apresente-se ao magíster Kaendris", {"Leve o Diário de Dar'Khan para o Magíster Idonis no Sacrário Solar, que fica na Terra Fantasma."}},
[9173] = {"Reconquistando o Pico dos Correventos", {"Mate 8 Acólitos da Cidadela da Morte e 10 Patrulheiras Decaídas e depois fale novamente com o Alto-executor Malta em Tranquillien, que fica na Terra Fantasma."}},
[9174] = {"Derrote Aquantion", {"Geranis Matinalba quer que você nade até o fundo do lago que fica a leste da Vila Corona Solar e use o Feixe de Medalhões no Altar da Maestria das Ondas. Evoque e destrua o elemental conhecido como Aquantion e fale novamente com Geranis."}},
[9175] = {"O colar da Dama Sombria", {"Leve o Colar da Dama Sombria ao Alto-executor Malta em Tranquillien, que fica na Terra Fantasma."}},
[9176] = {"Os zigurates gêmeos", {"Siga para o Zigurate Sangrento e para o Zigurate Uivante. Recupere a Pedra de Luz e a Pedra de Chamas. Fale novamente com o Magíster Kaendris no Sacrário Solar quando recuperar os itens."}},
[9177] = {"Jornada para a Cidade Baixa", {"Entregue o Colar da Dama Sombria para a Grande Dama Sylvana Correventos no Distrito Real da Cidade Baixa."}},
[9178] = {"Escritura do artífice – contrapeso denso", {"Leve 120 Contrapesos Densos e a Escritura do Artífice – Contrapeso Denso – para o Caravaneiro Esmagapedra na Capela Esperança da Luz, que fica nas Terras Pestilentas Orientais."}},
