[9423] = {"Fale novamente com Obaden", {"Fale novamente com o Anacoreta Obaden, no Templo de Telhamat, na Península Fogo do Inferno, e lhe informe sobre a morte de Sedai."}},
[9424] = {"A vingança de Makuru", {"Makuru, no Templo de Telhamat, na Península Fogo do Inferno, quer que você lhe traga 10 colares de Contas Ancestrais de Mag'har."}},
[9425] = {"Apresente-se a Serraria Tarren", {"Viaje até Serraria Tarren, no Contraforte de Eira dos Montes, e fale com a Conselheira Crepax."}},
[9426] = {"Os Poços de Aggonar", {"Amaan, o Sábio, quer que você vá até os Poços de Aggonar e mate 6 Terríferos e 6 Putrefações Virulentas."}},
[9427] = {"Purificando as águas", {"Amaan, o Sábio, quer que você vá até os Poços de Aggonar e use a Ampola Purificante no cadáver de Aggonar. Fale novamente com Amaan, o Sábio, quando a água estiver livre da essência de Aggonar."}},
[9428] = {"Apresente-se ao Posto Machadada", {"Vá até o Posto Machadada no Vale Gris e fale com o Conselheiro Jurassolar."}},
[9429] = {"Viajar para a Vila Sombria", {"Vá até a Vila Sombria, na Floresta do Crepúsculo, e fale com o Anacoreta Delan."}},
[9430] = {"Relíquias de Sha'naar", {"Amaan, o Sábio, no Templo de Telhamat, que fica na Península Fogo do Inferno, quer que você viaje para as Ruínas de Sha'naar e colete 10 Relíquias de Sha'naar."}},
[9431] = {"A Different Approach", {"Pegue 2 punhados de Terra Purificadora de Penedos Trovejantes e leve tudo para Vizlo Limpajunta no Posto Confinalvo."}},
[9432] = {"Viajar para Astranaar", {"Vá até Astranaar, no Vale Gris, e fale com a Vindicadora Palanaar."}},
[9433] = {"Um mergulho no poço lunar", {"Use o controle do Robotron quando estiver perto do Console de Comando Escondido num pequeno amontoado de arbustos na agulha mais próxima de Thalanaar para assumir o comando do Robotron 3000. Pilote o robô até o extremo oeste das Mil Agulhas, onde fica o posto avançado da Aliança de Thalanaar. Use o robô para coletar uma amostra de água do poço lunar e leve isso para Vizlo Limpajunta no Posto Confinalvo."}},
[9434] = {"O teste do tônico", {"Entregue o Caixote de Reagentes para a Magistra Elosai na Aldeia Vento Livre."}},
[9435] = {"Desvio de cristais", {"Recupere o Carregamento de Cristais Raros dos Renegados e devolva-o para Huraan, na Costa Sul."}},
[9436] = {"Conhecimento dos Sanguescalpo", {"Nemeth Olho de Falcão no Acampamento Grom'gol quer que você adquira um Totem Sanguescalpo de um Xamã Sanguescalpo."}},
[9437] = {"O crepúsculo do mensageiro da aurora", {"Yalandar Estelúmen, na Aldeia Muralha Verde, quer que você reivindique a Carga do Mensageiro da Aurora e que liberte a primeira oficial Itânia das forças de Theramore na Torre Norte."}},
[9438] = {"Mensagem para Thrall", {"Vá até Orgrimmar e entregue a Carta de Nazgrel para Thrall."}},
[9439] = {"Achados e roubados", {"Recupere a Bolsa do Conselheiro e o Florete do Conselheiro no lugar onde estão escondidos na Fortaleza Angor. Depois, devolva tudo para o Conselheiro Sarophas em Karrath."}},
[9440] = {"Pequenos petiscos", {"Alimente os animais capturados dos Perdido com Petiscos Maculados. Depois, devolva as sobras para Cersei Cantocaso em Pedregal, no Pântano das Mágoas."}},
[9441] = {"Enviado aos Mag'har", {"Thrall quer que você fale novamente com Gorkan Punho de Sangue no Posto Mag'har, na Península Fogo do Inferno, e ofereça seus serviços a ele."}},
[9442] = {"Uma doença debilitante", {"Arauto da Terra Ryga, no Posto Mag'har, na Península Fogo do Inferno, quer que você lhe leve 1 Cogumelo Secampânulo; o cogumelo se encontra em uma caverna na Grande Fenda."}},
[9443] = {"A suposta marca do Arauto da Luz", {"Consiga a Marca do Arauto da Luz e leve-a para Mehlar Aurolume no Baluarte, nas Terras Pestilentas Ocidentais."}},
[9444] = {"Profanação de tumba", {"Profane a Tumba de Uther e fale novamente com Mehlar Aurolume no Baluarte, nas Terras Pestilentas Ocidentais."}},
[9445] = {"<NYI><TXT>Placeholder: A Worthy Offering", {"Obtain Uther's Blessing from the top of the guard tower near the entrance to Hearthglen and return it to <NPC> at Chillwind Point."}},
[9446] = {"Tumba do Arauto da Luz", {"Escolte o Anacoreta Truuen até a Tumba de Uther, nas Terras Pestilentas Ocidentais. Depois, fale com a Alta-sacerdotisa Donélia no Acampamento Ventogelante, nas Terras Pestilentas Ocidentais."}},
[9447] = {"Aplicando o unguento", {"Arauto da Terra Ryga quer que você aplique Unguento Curativo em 10 Brutos Mag'har Enfraquecidos perto do Posto Mag'har, que fica na Península Fogo do Inferno."}},
[9448] = {"Misericórdia pelos amaldiçoados", {"Mate 3 Perdidos Amaldiçoados e, depois, fale novamente com o Anacoreta Avuun no Refúgio do Pântano das Mágoas."}},
[9449] = {"Call of Earth", {"Fale com o Espírito do Vale no Bosque Sagrado, no Vale Ammen."}},
[9450] = {"Call of Earth", {"Mate 4 Elementos da Terra Inquietos e, em seguida, fale novamente com o Espírito do Vale no Bosque Sagrado, no Vale Ammen."}},
[9451] = {"Call of Earth", {"Entregue o Cristal da Terra para Firmanvaar no Ponto de Impacto, que fica no Vale Ammen."}},
[9452] = {"Hum... delicinha de pargo-vermelho!", {"Use a Rede de Pesca Draeneica para pescar 10 Pargos-vermelhos. Entregue os peixes e a rede para Diktynna na Ilha Névoa Lazúli após cumprir a tarefa."}},
[9453] = {"Encontrar Acteon!", {"Entregue o Caixote de Pargos-vermelhos a Acteon no Entreposto Lazúli."}},
[9454] = {"A grande caçada de pastolunas", {"Leve 6 peças de Lombo de Cervo Pastoluna para Acteon no Entreposto Lazúli."}},
[9455] = {"Descobertas intrigantes", {"Entregue o Cristal Fracamente Faiscante ao Exarca Menelaus no Entreposto Lazúli."}},
[9456] = {"Extermínio de espreitanoites, Ilha 2...", {"O Exarca Menelaus, no Entreposto Lazúli, quer que você mate 8 Espreitanoites Raquíticos Infectados."}},
[9457] = {"Um patrono incomum", {"Use a Dádiva de Naias perto do Altar de Naias para evocá-lo. Depois, derrote-o e leve o seu Coração para Nemeth Olho de Falcão no Acampamento Grom'gol."}},
