[9461] = {"Call of Fire", {"Procure o Profeta Velen na Cripta das Luzes em Exodar."}},
[9462] = {"Call of Fire", {"Fale com Tuluun no Entreposto Lazúli, que fica na Ilha Névoa Lazúli."}},
[9463] = {"Propriedades medicinais", {"A Anacoreta Fateema, no Entreposto Lazúli, quer que você lhe leve 8 Ramos de Enrediça."}},
[9464] = {"Call of Fire", {"Fale com Temper no Campo das Cinzas, que fica na Ilha Névoa Lazúli."}},
[9465] = {"Call of Fire", {"Recupere a Tocha Ritual e entregue-a para Temper no Campo das Cinzas, que fica na Ilha Névoa Lazúli."}},
[9466] = {"Procura-se: Garranegra, a Selvagem", {"Traga as Garras de Garranegra para o Capitão-patrulheiro Venn'ren, na Vigília do Falcão, que fica na Península Fogo do Inferno."}},
[9467] = {"Call of Fire", {"Recupere as Cinzas do Arrogante e entregue-as, junto com a Tocha Ritual, para Temper no Campo das Cinzas, que fica nas Ilha Névoa Lazúli. Lembre-se de que você pode usar o Orbe do Retorno para se teletransportar de volta para Temper quando tiver as cinzas."}},
[9468] = {"Call of Fire", {"Entregue as Cinzas do Arrogante para Tuluun no Entreposto Lazúli, que fica na Ilha Névoa Lazúli."}},
[9469] = {"Apoio de Barbapena", {"Vá até a Casa de Barbapena e faça uma visita ao anão, se ele estiver em casa. Se não, procure indícios do paradeiro dele."}},
