[9473] = {"Uma alternativa à alternativa", {"Daedal, no Entreposto Lazúli, quer que você leve 5 Bulbos de Boca-de-leão Lazúli para ele."}},
[9474] = {"A Marca do Arauto da Luz", {"Consiga a Marca do Arauto da Luz e entregue-a para o Anacoreta Truuen no Acampamento Ventogelante, nas Terras Pestilentas Ocidentais."}},
[9475] = {"Retomada dos ovos", {"Colete 5 Ovos de Grifo e leve tudo para o Embaixador Rualeth no Ninho da Águia."}},
