[10556] = {"Arranhões", {"Use o Punhado de Plumas no Círculo de Feitiço dos Lashh'an."}},
[10557] = {"Voo de teste: capacitório de zefírio", {"Fale com Netto Desastraz e use o Capacitório de Zefírio."}},
[10558] = {"Reverenciado pela Fortaleza da Honra", {"O Comandante-chefe Danath Matatroll, na Fortaleza da Honra, que fica na Península Fogo do Inferno, quer que você se torne reverenciado pela Fortaleza da Honra, lutando com os orcs vis nos Salões Despedaçados da Cidadela Fogo do Inferno."}},
[10559] = {"Reverenciado em Thrallmar", {"Nazgrel, em Thrallmar, na Península Fogo do Inferno, quer que você se torne reverenciado em Thrallmar, combatendo os orcs vis dentro dos Salões Despedaçados da Cidadela Fogo do Inferno."}},
[10560] = {"Reverenciado pelos Sha'tar", {"A'dal, em Shattrath, quer que você se torne reverenciado com os Sha'tar, derrotando as forças de Kael'thas que estão na Bastilha da Tormenta."}},
[10561] = {"Reverenciado pelos Defensores do Tempo", {"Andormu, dentro das Cavernas do Tempo em Tanaris, quer que você se torne Reverenciado pelos Defensores do Tempo, protegendo os eventos da Antiga Eira dos Montes e do Lamaçal Negro dentro das Cavernas do Tempo."}},
[10562] = {"Cercados!", {"Comandante de Ala Nuainn, do lado de fora da Fortaleza Martelo Feroz, quer que você mate 10 Atacantes Infernais."}},
[10563] = {"Contra a Cidadela da Legião", {"Rume para a Cidadela da Legião e encontre o Dispositivo de Comunicação da Legião. Enquanto estiver ao lado do dispositivo comunicador, use o disfarce e descubra os planos da Legião. Fale novamente com Comandante de Ala Nuainn na Fortaleza Martelo Feroz."}},
[10564] = {"Os infernais que se explodam!", {"Use a Bomba Vil em cima do glifo verde na Cidadela da Legião, e em seguida se apresente ao Comandante de Ala Nuainn na Fortaleza Martelo Feroz."}},
[10565] = {"As pedras de Vekh'nir", {"Colete e use um Cristal Vekh'nir no Círculo de Feitiço Vekh'nir para criar um Cristal Vekh'nir Carregado."}},
[10566] = {"Tentativa e erro", {"Teste cada varinha de Dertrok em um ogro escravo dos arakkoas. Cada varinha precisará de um Cristal Vekh'nir Infuso para ser usada."}},
[10567] = {"Criação do pingente", {"Colete 6 Garras de Ruuan'ok e as use no Círculo do Oráculo Ruann'ok para evocar um Emissário do Corvo. Mate-o para coletar o Pingente do Emissário."}},
[10568] = {"Tabuletas de Baa'ri", {"A Anacoreta Ceyla no Altar de Sha'tar quer que você colete 12 Tabuletas Baa'ri no chão e com os Trabalhadores Grislíngua nas Ruínas de Baa'ri.","","Completar missões para os Aldor reduzirá sua reputação junto aos Áugures."}},
[10569] = {"As Ruínas de Sketh'lon", {"Consiga as Páginas 1, 2 e 3 do Diário do Comandante de Sketh'lon com os arakkoas do Conclave Negro nas Ruínas de Sketh'lon e as entregue ao Cavalga-grifo Kieran."}},
[10570] = {"Um cardólatra da pesada", {"Borak, Filho de Oronok, que está na ponte perto da Ponta do Eclipse, no Vale da Lua Negra, quer que você recupere a Missiva de Tempesfúria."}},
[10571] = {"Oronu, o Ancião", {"A Anacoreta Ceyla no Altar de Sha'tar quer que você obtenha as Ordens de Akama que estão com Oronu, o Ancião, nas Ruínas de Baa'ri.","","Completar missões para os Aldor reduzirá sua reputação junto aos Áugures."}},
[10572] = {"Bomba la bomba", {"Obtenha um Núcleo de Força de Aníquilus e uma Placa de Armadura de Aníquilus, e leve-os ao Comandante de Ala Nuainn, na Fortaleza Martelo Feroz."}},
[10573] = {"A Forja da Morte", {"Apresente-se a Ewan Asalouca, o Arrojado, na Torre da Forja da Morte."}},
[10574] = {"Os corruptores Grislíngua", {"Obtenha os quatro fragmentos de medalhão com Haalum, Eykenen, Lakaan e Uylaru e procure a Anacoreta Ceyla no Altar de Sha'tar, que fica no Vale da Lua Negra.","","Completar missões para os Aldor reduzirá sua reputação com os Áugures."}},
[10575] = {"A Jaula do Carcereiro", {"A Anacoreta Ceyla quer que você entre na Jaula do Carcereiro, ao sul das Ruínas de Baa'ri e interrogue Sanoru quanto à localização de Akama.","","Completar missões para os Aldor reduzirá sua reputação junto aos Áugures."}},
[10576] = {"O golpe Lua Negra", {"Borak, Filho de Oronok, que está na ponte perto da Ponta do Eclipse, no Vale da Lua Negra, quer que você recupere 6 peças de Armadura Eclipsion."}},
[10577] = {"O que Illidan quer, Illidan consegue...", {"Borak, Filho de Oronok, que está na ponte perto da Ponta do Eclipse, no Vale da Lua Negra, quer que você entregue a Mensagem de Illidan ao Grão-comandante Ruusk na Ponta do Eclipse."}},
[10578] = {"A Cifra da Danação: a missão de Borak", {"Borak, Filho de Oronok, que está na ponte perto da Ponta do Eclipse, no Vale da Lua Negra, quer que você recupere o Terceiro Fragmento da Cifra da Danação com Ruul, o Obscurecente.","","Nesta missão, será melhor ir em grupo."}},
[10579] = {"A Cifra da Danação: terceiro fragmento recuperado", {"Leve o Cofre de Borak para Oronok Coração-partido na Fazenda de Oronok, no Vale da Lua Negra."}},
[10580] = {"Cadê o gnomo que estava aqui?", {"Investigue a área sul da Serra dos Espigões nas Montanhas da Lâmina Afiada para encontrar algum indício do paradeiro de Tocha e dos outros gnomos."}},
[10581] = {"Siga as migalhas de pão", {"Siga a trilha de peças na ladeira para o Espinhaço Cortante. Fale com Tocha na Estação do Tocha, nas Montanhas da Lâmina Afiada."}},
[10582] = {"Lacaios do Concílio das Sombras", {"Ewan Asalouca, o Arrojado, na Torre da Forja da Morte, quer que você mate 10 Guardiões da Forja da Morte e 5 Evocadores da Forja da Morte."}},
[10583] = {"O destino de Flanis", {"Ewan Asalouca, o Arrojado, na Torre da Forja da Morte, quer que você vasculhe a Forja da Morte em busca de Flanis Celerasa e recupere a Mochila de Flanis."}},
[10584] = {"Recuperando os conversores", {"Use o Coletor de Magneto Protovoltaico para coletar 5 Eletromentais."}},
[10585] = {"A câmara de evocação", {"Obtenha um Deslocador Elemental com um Ferreiro da Forja da Morte ou um Engenhoqueiro da Forja da Morte. Use o dispositivo para interromper o ritual na câmara de evocação. Apresente-se a Ewan Asalouca, o Arrojado, na Torre da Forja da Morte, depois de completar a missão."}},
[10586] = {"Acabe com o Armipotente!", {"Derrote o Armipotente Razuun, recolha as Ordens de Razuun, e apresente-se ao Comandante de Ala Nuainn na Fortaleza Martelo Feroz, no Vale da Lua Negra."}},
[10587] = {"Campo de treinamento Karabor", {"A Exarca Onaala, no Altar de Sha'tar, quer que você obtenha 8 Glaives Solfúria dos Caçadores de Demônios Suplicantes e Caçadores de Demônios Iniciados nas Ruínas de Karabor, a sudeste do Altar de Sha'tar.","","Cumprir missões para os Áugures fará com que a sua reputação entre os Aldor diminua."}},
[10588] = {"A Cifra da Danação", {"Use a Cifra da Danação no Altar da Danação para evocar Cyrukh, o Senhor do Fogo.","","Destrua Cyrukh, o Senhor do Fogo, e então fale com o Reparador da Terra Torlok no Altar da Danação."}},
[10589] = {"Obtendo acesso", {"Traga um Controle do Teleportador da Legião ao Comandante de Ala Nuainn na Fortaleza Martelo Feroz, no Vale da Lua Negra. Os Armeiros Mo'args na Cidadela da Legião costumam carregar os dispositivos."}},
[10590] = {"Prove seu ódio", {"Mehlar Aurolume, no Baluarte, quer que você leve 20 Pedras do Flagelo do Lacaio para ele."}},
[10592] = {"Sabedoria da Rainha Banshee", {"Leve os Fragmentos de Pedra do Flagelo para a Grande Dama Sylvana Correventos, na Cidade Baixa."}},
[10593] = {"Ancient Evil", {"Desvende os segredos do Templo de Atal'Hakkar para libertar Atal'alarion e arrancar a Trepadeira Pútrida da carne dele. Fale com Mehlar no Baluarte depois que tiver feito isso."}},
[10594] = {"Medindo a frequência de ressonância", {"Coloque os Receptores de Frequências Oscilantes de modo que você seja atingido por 5 campos de Oscilação sobrepostos quando estiver no meio deles."}},
[10595] = {"Cercados!", {"O Guarda-de-sangue Gulmok, do lado de fora da Aldeia Lua Negra, quer que você mate 10 Atacantes Infernais."}},
[10596] = {"Contra a Cidadela da Legião", {"Vá até a Cidadela da Legião, encontre o Dispositivo de Comunicação da Legião, e descubra os planos da Legião enquanto estiver disfarçado. Fale novamente com o Guarda-de-Sangue Gulmok na Aldeia Lua Negra quando a missão estiver cumprida."}},
[10597] = {"Bomba la bomba", {"Obtenha um Núcleo de Força de Aníquilus e uma Placa de Armadura de Aníquilus, e leve-os ao Guarda-de-Sangue Gulmok na Aldeia Lua Negra."}},
[10598] = {"Os infernais que se explodam!", {"Use a Bomba Vil em cima do glifo verde na Cidadela da Legião para destruir os infernais, e em seguida apresente-se ao Guarda-de-Sangue Gulmok na Aldeia Lua Negra."}},
