[11043] = {"Por um grifo melhor", {"Fale com Bruno Barbachama na Fortaleza Martelo Feroz, que fica no Vale da Lua Negra."}},
[11044] = {"Visões da destruição", {"Fale com o Comandante de Ala Nuainn, na Fortaleza Martelo Feroz."}},
[11045] = {"Zorus, o Judicante", {"Fale com Zorus, o Judicante, na Fortaleza Martelo Feroz."}},
