[11049] = {"A grande caçada de ovos da Asa Etérea", {"Yarzill, o Mercenário, no Campo-base Presa do Dragão do Vale da Lua Negra, quer que você recupere um Ovo da Asa Etérea."}},
[11050] = {"Aceitamos todos os ovos", nil},
