[11066] = {"Segura o laço!", {"Use o Laço para capturar 5 Arraias Etéreas."}},
[11067] = {"Corrida da Presa do Dragão: Trope, o Arrota-lixo", {"Siga Trope, o Arrota-lixo, enquanto tenta permanecer montado. Caso vença Trope, o Arrota-lixo, relate a vitória a Ja'y Nosliw no Campo-base Presa do Dragão, que fica no Vale da Lua Negra."}},
[11068] = {"Corrida da Presa do Dragão: Veterano Corlok", {"Siga o Veterano Corlok enquanto tenta permanecer montado. Caso você vença o Veterano Corlok, relate a vitória a Ja'y Nosliw no Campo-base Presa do Dragão, que fica no Vale da Lua Negra."}},
[11069] = {"Corrida da Presa do Dragão: Comandante de Ala Ichman", {"Siga o Comandante de Ala Ichman enquanto tenta permanecer montado. Caso você vença Comandante de Ala Ichman, relate a vitória a Ja'y Nosliw no Campo-base Presa do Dragão, que fica no Vale da Lua Negra."}},
