[11071] = {"Corrida da Presa do Dragão: Capitão Rasga-céus", {"Siga o Capitão Rasga-céus enquanto tenta permanecer montado. Caso você vença o Capitão Rasga-céus, relate a vitória a Ja'y Nosliw no Campo-base Presa do Dragão, que fica no Vale da Lua Negra."}},
[11072] = {"Sangue adversário", {"Encontre as Pilhas de Crânios no meio dos círculos de evocação em Skettis. Evoque e derrote cada um dos descendentes usando 10 Pergaminhos do Tempo Perdido na Pilha de Crânios. Fale novamente com Hazzik no Campo de Pouso Ventonegro ao obter as marcas de cada um."}},
[11073] = {"A queda de Terokk", {"Leve a Oferenda do Tempo Perdido preparada por Hazzik para a Pilha de Crânios, no centro de Skettis. Evoque e derrote Terokk. Após concluir a tarefa, fale novamente com o Comandante Aéreo Adaris."}},
[11074] = {"Marcas dos descendentes", {"Recolha Pergaminhos do tempo perdido dos arakkoas perdidos no tempo em Skettis e leve-os para as Pilhas de Crânios nos círculos de evocação em Skettis. Evoque e derrote os descentes dos adversários de Terokk. Fale novamente com Hakkiz ao obter a Garra de Akkarai, o Espinhaço de Karrog, a Escama de Vakkiz e as Garras de Gezzarak."}},
[11075] = {"As minas da Asa Etérea", {"Procure a Senhora das Minas na Plataforma da Asa Etérea."}},
[11076] = {"Catando cavaco", {"A Senhora das Minas da Asa Etérea, no Vale da Lua Negra, quer que você recupere 15 caixotes de Carga de Mina Etérea."}},
[11077] = {"Os dragões são o nosso menor problema", {"O Encarregado Presa do Dragão nas Minas da Asa Etérea do Vale da Lua Negra quer que você mate 15 Esfola-pedras da Mina Etérea e 5 Assoladores da Mina Etérea."}},
[11078] = {"Para dominar os céus", {"Recolha os Dentes de Dragão."}},
[11079] = {"Um chicote vil para Gahk", {"Colete um Chicote Vil."}},
