[11081] = {"A grande revolta dos Sangue-de-lodo", {"Entregue o Plano de Fuga Sangue-de-lodo à Senhora das Minas."}},
[11082] = {"Em busca da verdade", {"Encontre um Feitor Sangue-de-lodo e o interrogue. Fale novamente com a Senhora das Minas quando tiver a informação dos Sangue-de-lodo e a Mão do Feitor."}},
[11083] = {"Ah, eu tô maluco", {"Ronag, o Feitor, nas Minas da Asa Etérea do Vale da Lua Negra quer que você mate o Encarregado Sangue-de-lodo Enlouquecido e 5 Mineradores Sangue-de-lodo Enlouquecidos."}},
[11084] = {"Cabeça erguida, Capitão!", {"Fale com o Suserano Mor'ghor, do Campo-base Presa do Dragão no Vale da Lua Negra."}},
[11085] = {"Fuga de Skettis", {"Escolte o Prisioneiro da Guarda Aérea durante a fuga de Skettis e, a seguir, apresente-se ao Sargento Aéreo Doryn."}},
[11086] = {"Atrapalhando o Portal do Crepúsculo", {"O Suserano Mor'ghor, do Campo-base Presa do Dragão, no Vale da Lua Negra, quer que você mate 20 Agentes Umbramorte."}},
[11088] = {"<DEPRECATED>Unravelling Your Training", {"Bring Jonathan LeCraft 10 pieces of Spellfire cloth."}},
[11089] = {"O Canhão das almas de Reth'hedron", {"O Lorde Illidari Balthas, no Campo-base Presa do Dragão, que fica no Vale da Lua Negra, quer que você lhe traga 2 Barras de Açovil, 1 Estrutura de Adamantita, 1 Núcleo de Força de Kório e 1 Essência Arcana Impecável."}},
[11090] = {"Domine o Dominador", {"O Lorde Illidari Balthas no Campo-base Presa do Dragão, do Vale da Lua Negra, lhe conferiu a tarefa de usar o Canhão das Almas contra Reth'hedron, o Dominador, em Nagrand.","","É necessário devolver o Canhão das Almas."}},
