[11252] = {"Para Utgarde!", {"O Defensor Mordun incumbiu você de executar Ingvar, o Ladravaz, que mora em Utgarde.","","Em seguida, entregue a cabeça de Ingvar para o Vice-almirante Castro."}},
[11253] = {"Farejar o inimigo", {"Use a Correia de Cão Pestilento próximo às jaulas de cães pestilentos em Porto Vendeta e procure pistas aonde o Cão Pestilento levar você."}},
[11254] = {"O mapa de pele de dragão", {"Entregue o Mapa de Pele de Dragão para o alto-executor Anselmo no Porto Vendeta."}},
