[11260] = {"Pare a ascensão!", {"Leve o Pergaminho Vraikal de Ascensão à pira para evocar e matar o Thane. Isto feito, você tem certeza que a anciã Ankha, da Aldeia Casco Invernal, se interesserá pelo pergaminho, devido à natureza do ritual."}},
[11261] = {"O conquistador de Skorn!", {"Fale novamente com o Chefe Totem de Cinzas na Aldeia Casco Invernal."}},
[11262] = {"Ingvar tem que morrer!", {"A Patrulheira Sombria Marrah pediu para você matar Ingvar, o Ladravaz, na Bastilha Utgarde, e levar a cabeça dele ao Alto-executor Anselmo, em Porto Vendeta."}},
[11263] = {"Dando cabo de Gjalerbron", {"O Chefe Totem de Cinzas quer que você mate 15 Guerreiros de Gjalerbron, 8 Lança-runas de Gjalerbron e 8 Guarda-sonhos de Gjalerbron. Em seguida, fale novamente com ele na Aldeia Casco Invernal."}},
[11264] = {"Necrolorde supremo Mezhen", {"Mate o Necrolorde Supremo Mezhen. Em seguida, fale novamente com o Chefe Totem de Cinzas na Aldeia Casco Invernal."}},
[11265] = {"De chaves e jaulas", {"A Anciã Ankha quer que você pegue Chaves da Jaula de Gjalerbron em quantidade suficiente para libertar 10 Prisioneiros de Gjalerbron. Fale novamente com ela, na Aldeia Casco Invernal, após terminar a tarefa."}},
