[11537] = {"A batalha não pode parar", {"O Emissário Inuuro quer que você mate 6 Demônios da Legião Ardente e o Emissário do Ódio na Praça da Aurora. Use o Estandarte Sol Partido para empalar o cadáver do Emissário do Ódio."}},
[11538] = {"Batalha pela Armaria de Beirassol", {"O Emissário Inuuro quer que você mate 6 Demônios da Legião Ardente e o Emissário do Ódio na Praça da Aurora ou na Armaria de Beirassol. Use o Estandarte Sol Partido para empalar o cadáver do Emissário do Ódio."}},
[11539] = {"A conquista do porto", {"O Magíster Ilastar, na Armaria de Beirassol, quer que você mate 6 Evocadores Aurolumes, 6 Cavaleiros Sangrentos Aurolumes e 3 Atiradores Peritos Aurolumes."}},
