[11789] = {"Um soldado em apuros", {"Socorrista Antonioni, do lado de fora da Bastilha Valentia, quer que você vá até o interior do Rompe-procelas e recupere o Antipeçonha do Antonioni de uma caixa de Suprimentos de Primeiros Socorros."}},
[11790] = {"Sectários entre nós", {"Fale com Capitão \"Canhoto\" Velabaixa acima do Rompe-procelas, na Bastilha Valentia."}},
[11791] = {"Avisem Arlos", {"Fale com o General Arlos dentro da Fortaleza Interior, na Bastilha Valentia."}},
[11792] = {"Inimigos da Luz", {"O Emissário Vurenn quer que você lute contra Necrólitos Sectários perto de Algures, ao norte da Bastilha Valentia, e recupere o Comunicado do Sectário deles."}},
[11793] = {"Mais investigações", {"Leve o Comunicado do Sectário para a Vindicante Yaala, dentro da estalagem na Bastilha Valentia."}},
[11794] = {"A caçada começou", {"Vindicante Yaala quer que você use o Óculus do Exorcista em três diferentes locais na Bastilha Valentia para desmascarar e derrotar membros da Seita dos Malditos."}},
[11795] = {"Protocolo de emergência: Seção 8.2, parágrafo C", {"Recupere 6 Insígnias de Piloto do Biela dos corpos dos Pilotos de Reconhecimento do Biela e volte a falar com Renê Fuzuê, nos Poços Escaldantes."}},
[11796] = {"Protocolo de emergência: Seção 8.2, parágrafo D", {"Renê Fuzuê quer que você inutilize os Destroços do Leste, os Destroços do Sul e os Destroços do Noroeste."}},
[11797] = {"O cerco", {"Sargento Martelmonte quer que você mate 6 Rastejantes da Cripta, nas Areias de Nasam, fora da Bastilha Valentia."}},
[11798] = {"O Engrenochefe", {"Investigue o Manual do Engrenochefe e traga a Cabeça do Mecanozod para Biela Velotroll."}},
