[11865] = {"A morte é pouco", {"Zazá no Acampamento da DruiPA, que fica na Tundra Boreana, quer que você use a Pilha de Peles Falsas para prender 8 Coureadores de Rosarães."}},
[11866] = {"Faça-os ouvir!", {"Arquidruida Lathorius no Acampamento da DruiPA, que fica na Tundra Boreana, disse que recompensará quem trouxer 15 Orelhas de Lacaios de Rosarães."}},
[11867] = {"Não vão me ouvir reclamar!", {"Arquidruida Lathorius no Acampamento da DruiPA, que fica na Tundra Boreana, disse que recompensará quem trouxer 15 Orelhas de Lacaio de Rosarães."}},
