[11885] = {"Sangue adversário", {"Encontre as Pilhas de Crânios no meio dos círculos de evocação em Skettis. Evoque e derrote cada um dos descendentes usando 10 Pergaminhos do tempo perdido na Pilha de Crânios. Fale novamente com Hazzik no Campo de Pouso Ventonegro."}},
[11886] = {"Atividade incomum", {"Vá para o novo Acampamento do Crepúsculo, ao sul da Entrada das Profundezas Negras, na Praia de Zoram - a qual fica no Vale Gris - e pegue a Correspondência do Crepúsculo dos sectários. Depois, convoque o Guia da Harmonia Telúrica usando a Lanterna Totêmica."}},
[11887] = {"Equipamentos de emergência", {"Consiga 7 Equipamentos de Emergência Gnômicos dos corpos dos Pilotos de Reconhecimento do Biela e leve-os para Fazzix Girarroda na Aldeia Taunka'le."}},
[11888] = {"Viagem à Aldeia Taunka'le", {"Fale com o Sábio Terra e Céu na Aldeia Taunka'le."}},
[11889] = {"A morte vem de cima", {"Sargento Martelmonte quer que você use a Rede Reforçada para aprisionar e matar 6 Cospe-chamas Flagelados, fora da Bastilha Valentia."}},
[11890] = {"O que estão tramando?", {"Investigue a área nos arredores da Estação Bombeadora do Biela. Depois, fale novamente com o Sábio Terra e Céu, na Aldeia Taunka'le."}},
[11891] = {"Um disfarce inocente", {"Use o Orbe do Caranguejo para espionar o novo Acampamento do Crepúsculo a oeste da Entrada das Profundezas Negras na Praia de Zoram do Vale Gris. Depois, evoque o Guia da Harmonia Telúrica usando a Lanterna Totêmica."}},
