[12022] = {"Caneco no boneco!", {"Use a Amostra de Cortesia da CervaFest para acertar o B.I.R.I.T.A. 5 vezes. Em seguida, fale com Caxias Girafuso."}},
[12024] = {"Recording the Fallen", {"Search the skeletons in the Carrion Fields for Alliance Settlement Documents."}},
[12025] = {"A Scarlet Among Us", {"Search for evidence of the Scarlet Crusade on the freshly risen Carrion Ghouls in the Eastern Carrion Fields. Return to Lady Alustra with anything you find."}},
[12026] = {"O diário danificado", {"Recupere 8 Páginas Perdidas do Diário , combine-as com o Diário Incompleto e entregue o diário completo à Sábia Paluna na Aldeia Oneqwah."}},
[12027] = {"As Perigosas Peripécias do Sr. Pinote", {"Ajude Emília e o Sr. Pinote a voltar ao Acampamento da Brigada de Cerro Oeste, e então fale com o Escudeiro Walter."}},
[12028] = {"Iluminação espiritual", {"Fale com Toalu'u, o Místico, no Porto Moa'ki, quando obtiver a iluminação espiritual."}},
[12029] = {"Churrasco de Flagelo", {"Mack, nas Fontes Graníticas, quer que você use o grogue dele para queimar 20 Trolls Esfomeados do Flagelo em Drak'Tharon."}},
[12030] = {"Ancião Mana'loa", {"Fale com a estátua do Ancião Mana'loa, na Aldeia Indu'le."}},
[12031] = {"Liberdade para os espíritos", {"Dê o descanso final a 15 aldeões Indu'le, dentre Pescadores, Místicos e Guerreiros. Então fale com o Ancião Mana'loa na Aldeia Indu'le."}},
[12032] = {"Conversando com as profundezas", {"Invoque Oacha'noa com a Pérola das Profundezas e faça o que a deusa do mar ordenar.","","Se você sobreviver à prova, fale novamente com Toalu'u, o Místico, no Porto Moa'ki."}},
[12033] = {"Mensagem do oeste", {"Leia a Carta de Saurfang e, depois, destrua-a.","","Fale com o Mensageiro Torvus ao completar essa tarefa."}},
[12034] = {"A vitória se aproxima", {"Fale com o Primeiro-Sargento Juktok, que fica no Martelo de Agmar."}},
[12035] = {"Tecnologia reobjetivada", {"Jeremias Ranieri, em Algures, quer que você destrua 5 Golens Colheiteiros e use as Ferramentas do Jeremias para reprogramá-los."}},
[12036] = {"Das profundezas de Azjol-Nerub", {"Explore o Fosso de Narjun e reporte suas descobertas para o Primeiro-Sargento Juktok no Martelo de Agmar."}},
[12037] = {"Busca e resgate", {"Mack, nas Fontes Graníticas, quer que você entre em Drak'Tharon e descubra o que houve com Kurzel."}},
[12038] = {"Churrasco de Flagelo", {"Mack, nas Fontes Graníticas, quer que você use o grogue dele para queimar 30 Trolls Esfomeados do Flagelo."}},
[12039] = {"O sangue negro de Yogg-Saron", {"Borus Dobraferro, no Martelo de Agmar, no Ermo das Serpes, quer que você recupere 10 Amostras de Sangue Negro de Yogg-Saron."}},
[12040] = {"Inimigo comum", {"Kilix, o Desfiador, no Fosso de Narjun, no Ermo das Serpes, quer que você mate 6 Sublordes Anub'ar."}},
[12041] = {"O Império perdido", {"Entregue o Plano de Batalha de Kilix ao Lorde Supremo Agmar no Martelo de Agmar, no Ermo das Serpes."}},
[12042] = {"Coração dos antigos", {"Drakuru quer que você vá à câmara mais alta da Madeireira Céu Azul e encontre o Coração dos Antigos."}},
[12043] = {"Em Defesa do Posto Ferrugem", {"Mate 12 Escavadores dos Ermos e 1 Capataz dos Ermos nas escavações próximas do Repouso de Galakrond. Fale novamente com Narf, no Posto Ferrugem, quando completar a tarefa."}},
[12044] = {"Farmácia particular", {"Colete 8 montes de Minério Composto dos buracos próximos ao Repouso de Galakrond, e leve-os para Xink, no Posto Ferrugem."}},
[12045] = {"Estilhaços de gelo", {"Colete 4 Drusas de Estilhaços de Gelo dos Elementais de Gelo ao norte e leve-as para Zivlix, no Posto Ferrugem."}},
[12046] = {"Invólucro macio", {"Colete 12 Pelegos Animais Finos dos Escavadores Yormungar ou dos Condores Dracósseos perto do Posto Ferrugem. Quando conseguir os pelegos, leve-os para Zivlix."}},
[12047] = {"Algo que não derreta", {"Procure por 12 Pedaços de Osso Estilhaçado nos ossos de dragão a leste do Posto Ferrugem."}},
[12048] = {"Armamentos do Flagelo", {"Borus Dobraferro, no Martelo de Agmar, no Ermo das Serpes, quer que você colete 8 Armamentos do Flagelo."}},
[12049] = {"Duro de engolir", {"Lute com um Yormungar Imponente a leste do Posto Ferrugem até que ele abra a boca, então jogue uma Carga Explosiva Potente na garganta do verme. Quando o verme explodir, colete 6 nacos de Carne de Yormungar Queimada do chão e leve-os de volta para Xink."}},
[12050] = {"Madeeeira!", {"Use o Dispositivo de Controle de Retalhador do Xink para convocar um Retalhador, quando você chegar aos Ninhos das Harpias, ao Norte. Colete 50 montes de Madeira das árvores da área, e então fale novamente com Xink, no Posto Ferrugem.","","Se você perder o Dispositivo de Controle de Retalhador, peça outro para Xink, no Posto Ferrugem."}},
[12051] = {"Uma harpia incomoda muita gente", {"Narf, no Posto Ferrugem, quer que você mate 20 Harpias a Noroeste do Posto Ferrugem."}},
[12052] = {"Não passarão, passarinhos!", {"Mate a Senhora do Vento Frio e 15 Harpias do Vento Frio nas montanhas a noroeste do Posto Ferrugem. Fale novamente com Narf quando completar a tarefa.","","Se precisar do Dispositivo de Controle de Retalhador, peça para Xink no Posto Ferrugem."}},
[12053] = {"A força da Horda", {"O Primeiro-Sargento Juktok, no Martelo de Agmar, no Ermo das Serpes, quer que você finque o Estandarte de Batalha do Brado Guerreiro na Aldeia Bruma Boreal, e o defenda dos agressores.","","Devolva o Estandarte de Batalha do Brado Guerreiro quando terminar a missão."}},
[12054] = {"Decifrando o diário", {"Traga um ramo de Halispírito para Sábia Paluna na Aldeia Oneqwah."}},
[12055] = {"Um dispositivo estranho", {"Entregue o Dispositivo Estranho de Goramosh para a Imagem da Arquimaga Modera no Recanto das Estrelas."}},
[12056] = {"Marcado para Morrer: Grão-sectário Zangus", {"Mate o Grão-sectário Zangus e leve a Cabeça do Grão-sectário Zangus para o Capitão Gort no Martelo de Agmar, que fica no Ermo das Serpes."}},
[12057] = {"O tomo encadernado com carne", {"Leve o Tomo Encadernado com Carne para o Capitão Gort, no Martelo de Agmar."}},
[12058] = {"As profecias rúnicas", {"A Sábia Paluna, na Aldeia Oneqwah, quer que você decifre as três Placas Rúnicas."}},
[12059] = {"Um dispositivo estranho", {"Entregue o Dispositivo Estranho de Goramosh para a Imagem do Arquimago Aethas Fendessol no Martelo de Agmar."}},
[12060] = {"Projeções e planos", {"Aproxime-se da Agulha de Mana flutuando acima dos Jardins Lua Serena e observe a projeção.","","Então, relate o que descobrir para a Imagem da Arquimaga Modera, no Recanto das Estrelas."}},
[12061] = {"Projeções e planos", {"Aproxime-se da Agulha de Mana flutuando acima dos Jardins Lua Serena e observe a projeção.","","Então relate o que descobrir para a Imagem do Arquimago Aethas Fendessol, no Martelo de Agmar."}},
[12062] = {"Insulte Coren Cervatroz", {"Fale com Coren Cervatroz."}},
[12063] = {"O poder da Bruma Boreal", {"Encontre Banthok Bruma Boreal, na Aldeia Bruma Boreal."}},
[12064] = {"Correntes dos Anub'ar", {"Banthok Bruma Boreal, na Aldeia Bruma Boreal, que fica no Ermo das Serpes, quer que você leve para ele a Parte da Chave de Anok'ra, a Parte da Chave de Tivax e a Parte da Chave de Sinok."}},
[12065] = {"O foco na praia", {"Obtenha o Anel de Controle do Foco da Linha do Meridiano e use-o para conseguir informações do Foco da Linha do Meridiano na Praia Reluzente.","","Então, fale novamente com a Imagem da Arquimaga Modera no Recanto das Estrelas."}},
[12066] = {"O foco na praia", {"Obtenha o Anel de Controle do Foco da Linha do Meridiano e use-o para conseguir informações do Foco da Linha do Meridiano na Praia Reluzente.","","Então, fale novamente com a Imagem do Arquimago Aethas Fendessol no Martelo de Agmar."}},
[12067] = {"Uma carta para casa", {"Entregue a Carta da Capitão Malin para a Comandante Saia Azulaço, no Recanto das Estrelas."}},
[12068] = {"Pergunte ao pó", {"Drakuru quer que você colete as Tabuletas Drakkari das Ruínas de Drakil'jin, e 5 Mojos Sagrados dos Oráculos Drakkari e Protetores Drakkari da região. Em seguida, use o Elixir de Drakuru no Braseiro de Drakuru nas ruínas para evocar a imagem dele."}},
[12069] = {"O retorno do Chefe Supremo", {"Liberte o Chefe Supremo Bruma Boreal usando a Chave da Prisão Anub'ar e ajude-o a derrotar o Baixo-Rei Anub'et'kan.","","Leve um Fragmento da Casca de Anub'et'kan para o Lorde Supremo Agmar, no Martelo de Agmar, que fica no Ermo das Serpes."}},
[12070] = {"Incentivo às tropas", {"Use o Estilhaço da Terra para incentivar os Gigantes da Serra Gris lutando nos Montes dos Penedos, mate quaisquer anões de ferro que se opuserem, e então apresente-se a Karun no morro acima de Thor Modan."}},
[12071] = {"Ataque aéreo!", {"Apresente-se ao Furivento Valnok no Martelo de Agmar."}},
[12072] = {"Pro inferno com as mangraferas!", {"Use a Pistola Sinalizadora de Valnok para chamar uma Mantícora de Batalha Kor'kron, na Aldeia Bruma Boreal. Monte na Mantícora de Batalha Kor'kron e use-a para matar 25 Mangraferas Anub'ar!","","Fale novamente com Furivento Valnok, no Martelo de Agmar, ao completar a missão."}},
[12073] = {"Marretando o ferro", {"Mate 10 Anões de Ferro de qualquer tipo que estiverem defendendo Thor Modan e, depois, apresente-se ao Batedor Vor'takh na Aldeia Oneqwah."}},
[12074] = {"Um aliado oportuno", {"Fale com Karun no morro sobre Thor Modan."}},
[12075] = {"Amostras de morte", {"Colete uma amostra de carne pétrea do cadáver do Gigante do Gelo na entrada da caverna na Fenda Cristalina, ao norte de onde Zort está. Quando completar essa tarefa, volte a falar com Zort perto do centro do desfiladeiro."}},
[12076] = {"Negócio sujo", {"Colete 2 Ampolas de Cuspe Corrosivo dos Yormungares, usando a raspadeira em você mesmo. Leve as ampolas para Zort, na Fenda Cristalina, quando completar a tarefa."}},
[12077] = {"Aplique duas vezes ao dia", {"Ofereça o elixir que Zort preparou para Ko'char, o Inquebrável."}},
[12078] = {"O encantador de vermes", {"Entre nas cavernas da Fenda Cristalina e capture 3 Crias de Yormungar usando os caixotes resistentes. Só pegue os caixotes de volta depois que os filhotes estiverem presos lá dentro!","","Leve as Crias de Yormungar de volta para Zort na Fenda Cristalina."}},
[12079] = {"Reconquistando a caverna", {"Penetre na caverna na parte norte da Fenda Cristalina e mate 8 Yormungares Caçadoras Gelocórdias para Ko'char, o Inquebrável."}},
[12080] = {"Verme enorme", {"Entre na Caverna Gelocórdia e mate Cascavadora. Se você ainda tiver, use o Elixir Protetor de Zort contra a cusparada corrosiva da Yormungar.","","Fale novamente com Ko'char, o Inquebrável, quando completar a tarefa."}},
[12081] = {"Gavrocha", {"Fale com Gavrocha nas Ruínas de Tethys."}},
[12082] = {"Ta-na-na-náá!", {"Escolte Harrison das Ruínas de Drakil'jin, e em seguida fale com Harkor no Acampamento do Harkor."}},
[12083] = {"No alto dos bosques", {"Obtenha o Amuleto de Controle do Foco da Linha do Meridiano e use-o para conseguir informações do Foco da Linha do Meridiano nos bosques de Lothalor.","","Então, fale novamente com a Imagem da Arquimaga Modera no Recanto das Estrelas."}},
[12084] = {"No alto dos bosques", {"Obtenha o Amuleto de Controle do Foco da Linha do Meridiano e use-o para conseguir informações do Foco da Linha do Meridiano nos bosques de Lothalor.","","Então, fale novamente com a Imagem do Arquimago Aethas Fendessol no Martelo de Agmar."}},
[12085] = {"Uma carta para casa", {"Entregue a Carta do Tenente Ta'zinni ao Lorde Supremo Agmar no Martelo de Agmar."}},
[12086] = {"O filho de Karkut", {"Vá para o Campo da Morte, depois de Unu'pe."}},
[12087] = {"A Little Help Here? DEPRECATED", {"Corporal Venn at Death's Stand wants you to kill 8 Ziggurat Defenders."}},
[12088] = {"Thassarian, o Cavaleiro da Morte", {"Procure por Thassarian dentro do Zigurate dos Lamentos, ao norte do Campo da Morte."}},
[12089] = {"Procura-se: Magíster Keldonus", {"Mate o Magíster Keldonus e fale com o Capitão Gort, no Martelo de Agmar, que fica no Ermo das Serpes."}},
[12090] = {"Procura-se: Gigantauro", {"Mate Gigantauro e fale novamente com o Capitão Gort no Martelo de Agmar, que fica no Ermo das Serpes."}},
[12091] = {"Procura-se: Garra Horrenda", {"Mate Garra Horrenda e fale com o Capitão Gort, no Martelo de Agmar, que fica no Ermo das Serpes."}},
[12092] = {"Fortaleça os ancientes", {"Use a Casca dos Andarilhos para fortalecer 3 Ancientes de Lothalor e então fale novamente com Sarendryana no Recanto das Estrelas."}},
[12093] = {"Runas de compulsão", {"Gavrocha, nas Ruínas de Tethys, quer que você neutralize as quatro Runas de Compulsão derrotando o Feitor Durval, o Feitor Korgan, o Feitor Lochli e o Feitor Brunon."}},
[12094] = {"Poder latente", {"Energize o Estilhaço de Gavrocha no Primeiro Marco Ancestral, no Segundo Marco Ancestral e no Terceiro Marco Ancestral, e em seguida fale novamente com Gavrocha nas Ruínas de Tethys."}},
[12095] = {"Para a Aldeia do Dragão Arruinado", {"Fale com Rokhan, na Aldeia do Dragão Arruinado, que fica no Ermo das Serpes."}},
[12096] = {"Fortaleça os ancientes", {"Use a Casca dos Andarilhos para fortalecer 3 Ancientes de Lothalor e então fale novamente com o Guarda da Terra Grife no Martelo de Agmar."}},
[12097] = {"Sarathstra, Flagelo do norte", {"Peça para Rokhan chamar a serpe gélida Sarathstra.","","Mate Sarathstra e entregue o Coração Gelado de Sarathstra para o Capitão Gort no Martelo de Agmar."}},
[12098] = {"Vasculhe a Aldeia Indu'le", {"Procure o comandante das tropas de caça-magos na Aldeia Indu'le e no Lago Indu'le."}},
[12099] = {"Liberdade, liberdade", {"Use o Quebra-runas de Gavrocha para libertar 4 Gigantes Rúnicos, e então fale novamente com Gavrocha nas Ruínas de Tethys."}},
[12100] = {"Contendo a putrefação", {"Pairante Fúria da Águia, no Martelo de Agmar, no Ermo das Serpes, quer que você mate 15 Animais Selvagens Infectados e ache um Órgão Resistente à Putrefação."}},
[12101] = {"O bom doutor...", {"Entregue o Órgão Resistente à Putrefação ao Dr. Síntar C. Málius, no Martelo de Agmar."}},
[12102] = {"Em busca da lilás-rubi", {"O Doutor Síntar C. Málius, no Martelo de Agmar, no Ermo das Serpes, quer que você colete 1 Lilás-rubi."}},
[12104] = {"Falar com Pairante", {"Entregue a Vacina de Síntar a Pairante Fúria da Águia no Martelo de Agmar."}},
[12105] = {"Descida às trevas", {"Entregue o Diário de Mikhail ao Tenente Dumont no Chalé do Pinho Âmbar."}},
[12106] = {"Vasculhe a Aldeia Indu'le", {"Procure o comandante das tropas de caça-magos na Aldeia Indu'le e no Lago Indu'le."}},
[12107] = {"O fim da linha", {"Use o Talismã de Controle do Foco da Linha do Meridiano para conseguir informações do Foco da Linha do Meridiano sob o Lago Indu'le. Então, siga na direção leste para o Santuário Dragônico Lazúli, perto da parte centro-sul do Ermo das Serpes, e, do parapeito ocidental, observe o que está acontecendo lá.","","Depois, fale novamente com a Imagem da Arquimaga Modera, no Recanto das Estrelas."}},
[12108] = {"DEPRECATED", {"Bring Mikhail's Note to Cartographer Tobias at Amberpine Lodge."}},
[12109] = {"Apresente-se a Miguel Mantoforte... de novo", {"Fale com o Capitão Miguel Mantoforte no Acampamento da Brigada de Cerro Oeste."}},
[12110] = {"O fim da linha", {"Use o Talismã de Controle do Foco da Linha do Meridiano para conseguir informações do Foco da Linha do Meridiano sob o Lago Indu'le. Então, siga na direção leste para o Santuário Dragônico Lazúli, perto da parte centro-sul do Ermo das Serpes, e observe o que está acontecendo lá, do parapeito ocidental.","","Depois, fale novamente com a Imagem do Arquimago Aethas Fendessol, no Martelo de Agmar."}},
[12111] = {"Onde moram os bichos selvagens", {"Pairante Fúria da Águia, no Martelo de Agmar, no Ermo das Serpes, quer que você use o Pacote de Vacinas para vacinar 5 Cervos Nevados e 5 Ursos Árticos."}},
[12112] = {"Negociações rígidas", {"Encontre Zort no centro da Fenda Cristalina, a nordeste do Posto Ferrugem."}},
[12113] = {"A marvada carne", {"Harkor, no Acampamento do Harkor, quer que você colete 10 peças de Carne Fibrosa de Worg dos Predadores Uivo da Noite, e 10 Bifes de Hastepás dos Ruminantes Cascolongo."}},
[12114] = {"Terapia", {"Harkor, no Acampamento do Harkor, quer que você mate 10 Protetores Drakkari e 10 Oráculos Drakkari do lado de fora das Ruínas de Drakil'jin."}},
[12115] = {"Koltirus e a linguagem da morte", {"Leve o Tomo Encadernado com Carne para Koltirus Tecemorte."}},
[12116] = {"Das tripas coração", {"Kraz, no Acampamento do Harkor, quer que você entre nas Ruínas de Drakil'jin e colete 5 Vasos Canópicos Drakkari."}},
[12117] = {"Viagem para Porto Moa'ki", {"Vá até Porto Moa'ki no Ermo das Serpes e fale com o Ancião Ko'nani."}},
[12118] = {"Viagem para Porto Moa'ki", {"Vá até Porto Moa'ki no Ermo das Serpes e fale com o Ancião Ko'nani."}},
[12119] = {"Requerimento de audiência", {"Entregue sua Carta de Apresentação ao Templo do Repouso das Serpes para Tariolstrasz, no Templo do Repouso das Serpes."}},
[12120] = {"A marreta de Drag'akaal", {"Kraz, no Acampamento do Harkor, quer que você mate Drag'akaal do lado de fora das Ruínas de Drakil'jin e volte com a Marreta Drakil'jin."}},
[12121] = {"A gente se vê no aquém do além", {"Kraz, no Acampamento do Harkor, quer que você use a Marreta Drakil'jin nos gongos do lado de fora das Ruínas de Drakil'jin. Uma vez assassinado, entre nas ruínas e fale com o espírito de Gan'jo enquanto estiver morto."}},
[12122] = {"Requerimento de audiência", {"Entregue sua Carta de Apresentação ao Templo do Repouso das Serpes para Tariolstrasz, no Templo do Repouso das Serpes."}},
[12123] = {"Informando a rainha", {"Entregue sua Carta de Apresentação ao Templo do Repouso das Serpes a Alexstrasza, a Mãe da Vida, no Templo do Repouso das Serpes."}},
[12124] = {"Informando a rainha", {"Entregue sua Carta de Apresentação ao Templo do Repouso das Serpes a Alexstrasza, a Mãe da Vida, no Templo do Repouso das Serpes."}},
[12125] = {"A serviço do sangue", {"Koltirus Tecemorte, no Martelo de Agmar, no Ermo das Serpes, quer que você use a Gema Sangrenta em um Aldeão Indu'le Demente. Se apresente novamente a ele com uma Gema Sangrenta Preenchida."}},
[12126] = {"A serviço do profano", {"Koltirus Tecemorte, no Martelo de Agmar, no Ermo das Serpes, quer que você use a Gema Profana no Duque Vallenhal e se apresente novamente a Koltirus Tecemorte com uma Gema Profana Preenchida."}},
[12127] = {"A serviço do gelo", {"Koltirus Tecemorte, no Martelo de Agmar, no Ermo das Serpes, quer que você use a Gema Gélida nos Elementais Vingativos Gélidos e se apresente novamente a ele com uma Gema Gélida Preenchida."}},
[12128] = {"Conferindo a situação de Raegar", {"Fale com Raegar Quebracelha no Mirante do Minerador."}},
[12129] = {"O plano perfeito", {"Traga o Diagrama do Golem de Guerra completo para Raegar Quebracelha no Mirante do Minerador."}},
[12130] = {"Para que fabricar se você pode confiscar?", {"Traga 8 Peças de Golem de Guerra para Raegar Quebracelha no Mirante do Minerador."}},
[12131] = {"Nós temos a força", {"Traga a Pilha de Durar e a Pilha de Kathorn para Raegar Quebracelha no Mirante do Minerador."}},
[12132] = {"O poder de destruir", {"Koltirus Tecemorte, no Martelo de Agmar, que fica no Ermo das Serpes, quer que você entre no mundo das sombras e derrote 6 Atormentadores Sombrios.","","Fale com Koltirus Tecemorte se precisar entrar novamente no reino das sombras."}},
[12133] = {"Esmague a abóbora", {"Dê o Símbolo Sagrado Chamuscado para a Matriarca Fantasiada."}},
[12134] = {"A caçada de Sasha", {"Sasha quer que você mate 12 Caçadores do Solstício na Aldeia Solstício."}},
[12135] = {"\"Que venha o fogo!\"", {"A Matriarca Fantasiada quer que você ajude a apagar os focos de incêndio na vila assim que o Cavaleiro Sem Cabeça atear fogo. Quando acabar, fale novamente com a Matriarca Fantasiada."}},
[12136] = {"O tomo traduzido", {"Leve o Tomo Encadernado com Carne Traduzido para o Capitão Gort, no Martelo de Agmar."}},
[12137] = {"Fica frio, camarada", {"Gan'jo quer que você colete a Neve do Sono Eterno no baú nas Ruínas de Drakil'jin.","","Você então deverá usar a Neve nos Antigos Espíritos Drakkari de lá e levar as Partículas Espirituais Drakkari de volta a Kraz no Acampamento do Harkor."}},
[12138] = {"... Ou talvez a gente não tenha", {"Carregue seu Golem de Guerra com a energia de 10 Sentinelas Relâmpago e fale novamente com Raegar Quebracelha no Mirante do Minerador."}},
[12139] = {"\"Que venha o fogo!\"", {"A Matriarca Mascarada quer que você ajude a apagar os incêndios da vila. Quando acabar, fale novamente com a Matriarca Mascarada na cidade."}},
[12140] = {"Salve Roanauk!", {"Procure por Roanauk Bruma Boreal no Martelo de Agmar, e oficie sua iniciação como membro e líder das tropas da Horda.","","Fale novamente com o Lorde Supremo Agmar com o Juramento de Sangue da Horda quando você completar a missão."}},
[12141] = {"Uma missão diplomática", {"Fale com Karuk, no Areal Quebramar, sudoeste da Bastilha Valentia."}},
[12142] = {"Controle de Pragas", {"Dúnio, do Front da 7ª Legião, quer que você mate 10 Nevolds da Planície Nevada e 3 Magnatauros do Ermo das Serpes, nos acampamentos de magnatauros ao redor do Templo do Repouso das Serpes."}},
[12143] = {"Perseguição no desfiladeiro", {"Dúnio quer que você persiga os nevolds pelo desfiladeiro a nordeste do Front da 7ª Legião, e que você dê um jeito no líder deles."}},
[12144] = {"Controle de pragas", {"Kontokanis, na Aldeia do Dragão Arruinado, quer que você mate 10 Nevolds da Planície Nevada e 3 Magnatauros do Ermo das Serpes, nos acampamentos de magnatauros ao redor do Templo do Repouso das Serpes."}},
[12145] = {"Perseguição no desfiladeiro", {"Kontokanis quer que você persiga os nevolds pelo desfiladeiro a sul da Aldeia do Dragão Arruinado, e que você dê um jeito no líder deles."}},
[12146] = {"Implicações perturbadoras", {"Leve a Corneta de Batalha Brasonada para o Templo do Repouso das Serpes e procure alguém interessado em avaliar a ameaça potencial."}},
[12147] = {"Implicações perturbadoras", {"Leve a Corneta de Batalha Brasonada para o Templo do Repouso das Serpes e procure alguém interessado em avaliar a ameaça potencial."}},
