[12460] = {"Apresente-se no Santuário Dragônico Rubi", {"Apresente-se a Ceristrasz na cordilheira a leste do Santuário Dragônico Rubi, a oeste do Templo do Repouso das Serpes."}},
[12461] = {"Apresente-se no Santuário Dragônico Rubi", {"Apresente-se a Vargastrasz no penhasco próximo ao Santuário Dragônico Rubi, a leste do Martelo de Agmar."}},
[12462] = {"Um pouco de ação", {"Yord Barbagelo, o Calamidade, no Mausoléu Invergarde, no Ermo das Serpes, quer que você mate o Necrolorde X'avius, o Necrolorde Hórus e 15 Flagelos de Naxxramas."}},
[12463] = {"Barbapilha tem de ser encontrado!", {"Amgo Cash, no Mausoléu Invergarde, no Ermo das Serpes. quer que você encontre Barbapilha."}},
