[12469] = {"Devolver ao remetente", {"Siga a cordilheira para o leste e entregue o pacote de explosivos para Xink, no Posto Ferrugem, na parte norte do Ermo das Serpes."}},
[12470] = {"O mistério do infinito", {"Crona, no topo do Templo do Repouso das Serpes, quer que você vá até o Santuário Dragônico Bronze. Uma vez lá dentro, posicione a Ampulheta da Eternidade no chão.","","Proteja a ampulheta pelo tempo que for necessário, e então informe Crona de suas descobertas."}},
[12471] = {"A Crueldade dos Kvaldir", {"Fale com Karuk sobre o Prisioneiro Morsano Capturado."}},
[12472] = {"Agora só falta você", {"Entregue o Filactério de Thel'zan para o Comandante de Legião Jorge, dentro do Mausoléu Invergarde, no Ermo das Serpes."}},
[12473] = {"Um final e um começo", {"Derrote Thel'zan, o Noctífero, e apresente-se ao Alto-comandante Haraldo Serpecida na Bastilha Invergarde, no Ermo das Serpes."}},
[12474] = {"Para o Forte Fordragon!", {"Vá para o Forte Fordragon, que fica no Ermo das Serpes, e fale com o Grão-lorde Bolvar Fordragon."}},
[12475] = {"Os segredos que os homens encobrem", {"Zelig, o Visionário, na Bastilha Invergarde, no Ermo das Serpes, quer que você encontre o Mapa da Ofensiva e o devolva para ele."}},
[12476] = {"O retorno da Cruzada?", {"A Comandante Lynore Golpevento, na Bastilha Invergarde, no Ermo das Serpes, quer que você mate 20 membros da Ofensiva Escarlate, na Base da Ofensiva."}},
[12477] = {"O Caminho da Redenção", {"Leve o \"Caminho da Redenção\" para o Alto-comandante Haraldo Serpecida na Bastilha Invergarde, que fica no Ermo das Serpes."}},
[12478] = {"Caverna Gélido Lamento", {"Zelig, o Visionário, na Bastilha Invergarde, no Ermo das Serpes, quer que você use o Orbe de Vidência de Zelig no fundo da Caverna Gélido Lamento. Descubra os segredos do passado!"}},
[12479] = {"Para Nortúndria!", {"Fale com o Recrutador da Aliança na Bastilha Valentia, que fica na Tundra Boreana, ou com o Recrutador da Aliança que fica no Porto Vendeta no Fiorde Uivante."}},
[12480] = {"Para Nortúndria!", {"Fale com o Recrutador da Horda na Fortaleza Brado Guerreiro, que fica na Tundra Boreana, ou com o Recrutador da Horda que fica no Porto Vendeta no Fiorde Uivante."}},
[12481] = {"Botando lenha na fogueira", {"Leia o Insulto Vraikal para Bjorn Halgurdsson em Baleheim, e então atraia-o até a Cilada de Lídio e ajude a matá-lo."}},
[12482] = {"Contra Niffelvar", {"A Capitão-patrulheira Areiel quer que você vá até Niffelvar e mate 5 Guerreiros Esfola-dragão, 4 Leitores de Runas Esfola-dragão e 4 Cães de Caça Esfola-dragão."}},
[12483] = {"Ensopado de Brilhacampa", {"Prigmon, nas Fontes Graníticas, quer que você colete 5 Campaneves Tremeluzentes e 5 Olhos de Serpente do Gelo das Ruínas de Drak'Zin, e 5 Raízes-doces de Zeb'Halak."}},
[12484] = {"Espetinho de Flagelo", {"Prigmon, nas Fontes Graníticas, quer que você leve uma Carcaça Mumificada de Troll a Mack Fearsen e que a coloque no fogo."}},
[12486] = {"Para o Posto Avançado Bor'gorok, rápido!", {"Fale com a Porta-voz Espiritual Rosnapresas no Posto Avançado Bor'gorok."}},
[12487] = {"Para o Forte da Conquista, mas cuidado!", {"Apresente-se à Conquistadora Krenna no Forte da Conquista, na Serra Gris."}},
[12488] = {"O Alto-executor precisa de você", {"Fale com o Alto-executor Iracundo em Perfídia, que fica no Ermo das Serpes."}},
[12489] = {"Boas-vindas à Bacia Sholazar", {"Siga a trilha para noroeste para chegar ao acampamento mencionado por Múncio."}},
[12490] = {"A vingança de Veehja", {"Veehja, no Altar das Escamas, quer que você mate 6 Marinheiros Skadir, 3 Tecelões de Brumas Skadir e 2 Lança-runas Skadir."}},
[12491] = {"A Cerva Atroz do Cervatroz", {"Entregue a Cerva Atroz do Cervatroz para Ipfelkofer Barrildechope na CervaFest, perto de Altaforja."}},
[12492] = {"A Cerva Atroz do Cervatroz", {"Entregue a Cerva Atroz do Cervatroz para Torneiro Barrilantra na CervaFest, perto de Orgrimmar."}},
[12493] = {"Tese de JxJ", {"Mate 10 oponentes."}},
[12495] = {"Audiência com a Rainha Dragonesa", {"Fale com Alexstrasza, a Mãe da Vida, no Templo do Repouso das Serpes, que fica no Ermo das Serpes."}},
[12496] = {"Audiência com a Rainha Dragonesa", {"Fale com Alexstrasza, a Mãe da Vida, no Templo do Repouso das Serpes, que fica no Ermo das Serpes."}},
[12497] = {"Galakrond e o Flagelo", {"Fale com Torastrasza no Templo do Repouso das Serpes, que fica no Ermo das Serpes."}},
[12498] = {"Em asas de rubi", {"Destrua 30 Catadores dos Ermos e recupere a Foice de Antiok. Fale com Alexstrasza, a Mãe da Vida, no Templo do Repouso das Serpes, após cumprir a tarefa."}},
[12499] = {"Retorno a Angrathar", {"Fale com o Grão-lorde Bolvar Fordragon, no Forte Fordragon, e conte a ele da sua vitória sobre o Flagelo."}},
[12500] = {"Retorno a Angrathar", {"Fale com Saurfang, o Jovem, na Vanguarda Kor'kron, e conte a ele sobre a derrota do Flagelo."}},
[12501] = {"Patrulha troll", {"O Comandante Kunz, na Fortaleza Argêntea, quer que você vá até os quatro postos da Cruzada Argêntea, siga as ordens dos capitães de cada um deles e, depois, apresente-se novamente a ele.","","O Capitão Brandão e o Capitão Ruperto estão em Drak'Sotra, o Capitão Grondel está em Draz'Agal e o Alquimista Bacamarte está em Heb'Valok."}},
[12502] = {"Patrulha troll: padrões elevados", {"O Capitão Brandão quer que você coloque o Estandarte da Cruzada Argêntea nos Pedestais Drakkari, que ficam em Drak'Sotra."}},
[12503] = {"Defenda a Fortaleza", {"O Comandante Falstaav lhe conferiu a tarefa de destruir 10 servos quaisquer do Flagelo no pátio, na Fortaleza Argêntea."}},
[12504] = {"Cruzada Argêntea, estamos partindo!", {"O Sargento Malhomonte, no Altar de Sseratus, quer que você diga a 10 dos Soldados Argênteos para se apresentarem a ele."}},
[12505] = {"Novas ordens para o Sargento Malhomonte", {"O Comandante Kunz quer que você entregue as Novas Ordens de Posicionamento ao Sargento Malhomonte no Altar de Sseratus."}},
[12506] = {"Complicações no Altar de Sseratus", {"O Bagateiro Ubungo, na Fortaleza Argêntea, pediu para você investigar o prédio principal do altar no Altar de Sseratus."}},
[12507] = {"Mojo estranho", {"Entregue o Mojo Estranho ao Bagateiro Ubungo na Fortaleza Argêntea, em Zul'Drak."}},
[12508] = {"Passando o rodo", {"A Cabo Magha, no Altar de Sseratus, quer que você mate qualquer combinação de 10 Campeões de Sseratus, Sacerdotes de Sseratus ou Encantadores de Cobra Drakkari."}},
[12509] = {"Patrulha troll: fortitude intestinal", {"O Capitão Ruperto, em Drak'Sotra, quer que você recupere a coragem dos Recrutas da Cruzada covardes."}},
[12510] = {"Fluidos elementais preciosos", {"O Bagateiro Ubungo, na Fortaleza Argêntea, quer que você obtenha 3 Fluidos Elementais Preciosos."}},
[12511] = {"Para as montanhas!", {"Apresente-se ao Tenente Dumont no Chalé do Pinho Âmbar na parte oeste da Serra Gris."}},
[12512] = {"Ninguém fica para trás", {"O Dr. Rogers, em Drak'Sotra, quer que você encontre o Cruzado Lamoof, o Cruzado Jonas e a Cruzada Josefina. Use a Bandagem do Cruzado neles e acompanhe-os até o Dr. Rogers."}},
[12513] = {"Belo chapéu...", {"Dom Ramón desafiou você dizendo que você não é capaz de derrotá-lo no passado, na Antiga Eira dos Montes. Depois disso, traga o Chapéu do Dom Ramón para ele mesmo, como prova, em Tanaris."}},
[12514] = {"Misturador de cogumelo", {"Colete 10 Fungos do Pileque e entregue-os ao Bagateiro Ubungo na Fortaleza Argêntea."}},
[12515] = {"Belo chapéu...", {"Dom Ramón desafiou você dizendo que você não é capaz de derrotá-lo no passado, na Antiga Eira dos Montes. Depois disso, traga o Chapéu do Dom Ramón para ele mesmo, como prova, em Tanaris."}},
[12516] = {"Colhendo bons frutos", {"Assassine o Profeta de Sseratus Embriagado e depois fale novamente com o Bagateiro Ubungo na Fortaleza Argêntea."}},
[12517] = {"Baralho de Ladinos", {"Use o Baralho de Ladinos para evocar uma Cartomante de Negraluna e entregue o baralho a ela."}},
[12518] = {"Baralho de Magos", {"Use o Baralho de Magos para evocar uma Cartomante de Negraluna e entregue o baralho a ela."}},
[12519] = {"Patrulha troll: vai uma medalha?", {"O Capitão Grondel, na entrada do Coliseu, quer que você reúna 7 Medalhões Drakkari dos Cadáveres Drakkari próximos."}},
[12520] = {"Maestria dos rinocerontes: o teste", {"Mate 15 Rinocerontes Estilhastes e fale novamente com Rosarães Guima, no Acampamento do Rosarães."}},
[12521] = {"Onde diabos está Rosarães Guima?", {"Fale com o Arquimago Pentarus quando você estiver pronto para partir para a Bacia Sholazar. Ao chegar lá, fale com Múncio Tirambaço no Mangue Verdejante da Bacia Sholazar."}},
[12522] = {"Precisa de um motor? Pega aí!", {"Veslex Chaveloz, no Acampamento do Rosarães, quer que você lhe traga o Motor de Máquina Voadora."}},
[12523] = {"Tem uma peça? Me dê uma!", {"Traga 7 Peças Sobressalentes da Empreendimentos S.A. para Veslex Chaveloz no Acampamento do Rosarães."}},
[12524] = {"Acidente da Empreendimentos S.A.", {"Debaar, no Acampamento do Rosarães, quer que você mate 15 membros da Empreendimentos S.A.. Você pode matar tanto Rufiões quanto Escavadores da Empreendimentos S.A."}},
[12525] = {"Tira aquele sorriso da cara dele", {"Mate o Encarregado Vigarício e o Bolofo e, em seguida, fale novamente com Debaar no Acampamento do Rosarães."}},
[12526] = {"Maestria dos rinocerontes: a caçada", {"Fale com o Oráculo Soo-rahm, nos Ossos de Nozronn."}},
[12527] = {"Tocaieiros glutões", {"Entregue 5 Cristais de Basilisco ao Alquimista Bacamarte no santuário, em Heb'Valok."}},
[12528] = {"Cooperando", {"Apresente-se ao Sumo Xamã Rakjak, no Morro dos Feralma, e diga que você é o novo \"escravo\"."}},
[12529] = {"O escravo do caçador de gorilas", {"Mate 8 Colhedores Falanjões e 6 Agressores Falanjões para o Sumo Xamã Rakjak, no Morro dos Feralma.","","Se perder os Grilhões de Goregek, fale com o Ancião Harkek, no Morro dos Feralma."}},
[12530] = {"Atormentando os falanginhos", {"Goregek te deu um Cutucador de Falanginhos. Use-o para cutucar os Falanginhos e, assim, chamar a atenção da Matriarca dos Falanjões, tirando-a do esconderijo. Mate a Matriarca dos Falanjões e informe o Sumo Xamã Rakjak sobre a morte dela. Você pode encontrar Rakjak no Morro dos Feralma.","","Se perder os Grilhões de Goregek, fale com o Ancião Harkek no Morro dos Feralma."}},
[12531] = {"A ameaça subterrânea", {"Encontre Serfex sob as areias do rio ao sul, mate-o e leve as garras dele para o Sumo Xamã Rakjak, no Morro dos Feralma.","","Se perder o Giz Usado de Dajik, fale com o Ancião Harkek no Morro dos Feralma."}},
[12532] = {"A fuga das galinhas!", {"Capture 12 Galinhas Desgarradas para o Ancião Harkek.","","Se perder o Giz Usado de Dajik, fale com o Ancião Harkek, no Morro dos Feralma."}},
[12533] = {"O aprendiz do caçador de vespas", {"Mate 6 Vespas Safiras e 9 Zangões Safiras para o Sumo Xamã Rakjak, no Morro dos Feralma.","","Se perder o Giz Usado de Dajik, fale com o Ancião Harkek no Morro dos Feralma."}},
[12534] = {"A rainha safira", {"Traga o Aguilhão da Rainha Safira para o Sumo Xamã Rakjak, no Morro dos Feralma.","","Se perder o Giz Usado de Dajik, fale com o Ancião Harkek no Morro dos Feralma."}},
[12535] = {"Preparando os danos", {"O Sumo Xamã Rakjak, no Morro dos Feralma, quer que você colete 8 Veios de Cristal do Pilar Beira-céu."}},
[12536] = {"Andar a crocolisco", {"Monte no crocolisco capturado e traga-o para o Refúgio Brumurmúria. A seguir, fale com Zepik para obter mais instruções.","","Se perder a Trompa de Caça de Zepik, fale com o Ancião Harkek, no Morro dos Feralma."}},
[12537] = {"O raio cai duas vezes no mesmo lugar", {"Zepik quer que você esmague os Veios de Cristal do Pilar Beira-céu em frente ao santuário do tempo de Brumurmúria.","","Se perder a Trompa de Caça de Zepik, fale com o Ancião Harkek, no Morro dos Feralma."}},
[12538] = {"A névoa não está escutando", {"Zepik quer que você mate 12 Gorlocs Brumurmúria.","","Se perder a Trompa de Caça de Zepik, fale com o Ancião Harkek, no Morro dos Feralma."}},
[12539] = {"Gastando sola", {"Volte para o Morro dos Feralma e fale com o Sumo Xamã Rakjak."}},
[12540] = {"Apenas seguindo ordens", {"Encontre o Oráculo Voz-da-chuva Ferido para o Sumo Xamã Rakjak no Morro dos Feralma."}},
[12541] = {"Patrulha troll: o aprendiz do alquimista", {"O alquimista Bacamarte, em Heb'Valok, quer que você o ajude a criar um soro da verdade.","","Quando você estiver pronto para começar, fale com ele e siga as instruções."}},
[12542] = {"O chamado da Cruzada", {"Apresente-se ao Cruzado Valus na Guarda da Luz, no Ermo das Serpes."}},
[12543] = {"Um presente para Soo-rahm", {"Leve 5 Presas de Naja Intactas para o Oráculo Soo-rahm, perto dos Ossos de Nozronn."}},
[12544] = {"Ossos de Nozronn", {"Use o Incenso de Soo-rahm perto dos Ossos de Nozronn e retorne a Rosarães Guima, no Acampamento do Rosarães, com as informações que obtiver."}},
[12545] = {"A purificação de Jintha'kalar", {"O Cruzado Valus, na Guarda da Luz, no Ermo das Serpes, quer que você mate 15 Flagelos de Jintha'kalar."}},
[12546] = {"Reivindicação", {"Vá ao Avalanche, na Bacia Sholazar, e use a Runa Ômega para chamar o Etimidiano. Use-o para matar 200 Lacaios do Flagelo, Bythius, o Molda-carne, Urgreth das Mil Tumbas e Escarnizus.","","Quando concluir a tarefa, fale com o Avatar de Freya, na Bacia Sholazar."}},
[12547] = {"A runa de ativação", {"Pegue a Runa Ômega no Terraço do Moldador e leve-a ao Etimidiano."}},
[12548] = {"O Etimidiano", {"Atravesse o pórtico ao sul da Bacia Sholazar e procure a arma conhecida como O Etimidiano no outro lado."}},
[12549] = {"Maestria dos deimossabres: tornando-se um predador", {"Mate 15 Deimossabres e fale novamente com Buck Cantwell, no Acampamento do Rosarães."}},
[12550] = {"Maestria dos deimossabres: espreitando a presa", {"Buck Cantwell, no Acampamento do Rosarães, quer que você identifique 3 Pegadas de Shango, partindo das Pegadas de Deimossabres perto do Lago Maremarga."}},
[12551] = {"Maestria dos crocoliscos: a prova", {"Mate 15 Crocoliscos do Mangue e fale novamente com Debaar, no Acampamento do Rosarães."}},
[12552] = {"Morte aos necromagi", {"O Sargento Lunarmento, em Drak'Sotra, pediu para você matar 8 Necromagi Hath'ar."}},
[12553] = {"Fiandeiras das raspadeiras", {"Especialista Engreneide, em Drak'Sotra, pediu para você coletar 5 Fiandeiras das Raspadeiras Intactas."}},
[12554] = {"Malash, o Corruptor", {"O Sargento Lunarmento insiste que você volte com a Cabeça do Corruptor."}},
[12555] = {"Um novelo emaranhado", {"A Especialista Engreneide, em Drak'Sotra, pediu para você usar o Arremessador de Novelo Emaranhado para causar a destruição de 5 Pulverizadores de Peste."}},
[12556] = {"Maestria dos rinocerontes: o abate", {"Leve o Chifre de Farunn para Rosarães Guima, no Acampamento do Rosarães."}},
[12557] = {"Trabalho de laboratório", {"O Alquimista Bacamarte, em Heb'Valok, quer que você encontre um Verme de Lama Lodosa, uma Asa Seca de Morcego, uma Semente Âmbar e um pouco de Muco Gelado de Serpente no laboratório dele."}},
[12558] = {"Maestria dos deimossabres: pronto para atacar", {"Leve a Pelagem de Shango para Buck Cantwell, no Acampamento do Rosarães."}},
[12559] = {"Energizando o pórtico: o Ninho do Criador", {"O Avatar de Freya, nas Cachoeiras de Voz-da-chuva, quer que você vá ao Ninho do Criador e use o Interruptor Gama."}},
[12560] = {"Maestria dos crocoliscos: o plano", {"Leve 5 Samambaias-da-areia para Debaar, no Acampamento do Rosarães."}},
[12561] = {"Uma questão de confiança", {"Obtenha a confiança de Freya destruindo 6 Cadáveres Empesteados e 10 Assoladores Ceifa-ossos nas Terras Perdidas."}},
[12562] = {"Os Drakkari não precisam de elementais da água!", {"A Subtenente Jax, na Fortaleza Argêntea, pediu para você matar 10 Magos d'Água Drakkari."}},
[12563] = {"Patrulha troll", {"O Comandante Kunz, na Fortaleza Argêntea, quer que você vá até os quatro postos da Cruzada Argêntea, siga as ordens dos capitães de cada um deles e, depois, apresente-se novamente a ele.","","O Capitão Brandão e o Capitão Ruperto estão em Drak'Sotra, o Capitão Grondel está em Draz'Agal e o Alquimista Bacamarte está em Heb'Valok."}},
[12565] = {"A bênção de Zim'Abwa", {"O Bagateiro Ubungo, na Fortaleza Argêntea, quer que você leve 10 Oferendas Drakkari à estátua de Zim'Abwa."}},
[12566] = {"Ajuda à Aldeia Casco Invernal", {"Fale com o Chefe Totem de Cinzas na Aldeia Casco Invernal."}},
[12567] = {"Bênção de Zim'Abwa", nil},
[12569] = {"Maestria dos crocoliscos: a emboscada", {"Leve a Mandíbula do Atacamato para Debaar, no Acampamento do Rosarães."}},
[12570] = {"Bom mal-entendido", {"Escolte o Oráculo Voz-da-chuva Ferido até as Frondes Voz-da-chuva e fale com o Sumo Oráculo Soo-say."}},
[12571] = {"Faz a cobra malvada ir embora", {"O Sumo Oráculo Soo-say, nas Frondes Voz-da-chuva, quer que você mate Venena e 10 Najas Imperatrizes.","","Se perder a Sacola de Insetos do Lafu, fale com o Sumo Oráculo Soo-say, nas Frondes Voz-da-chuva."}},
[12572] = {"Deuses gostam de coisas brilhantes", {"Colete 6 tesouros desenterrados por Lafu e leve-os ao Sumo Oráculo Soo-say, nas Frondes Voz-da-chuva.","","Se perder a Sacola de Insetos do Lafu, fale com o Sumo Oráculo Soo-say, nas Frondes Voz-da-chuva."}},
[12573] = {"Fazendo as pazes", {"O Sumo Oráculo Soo-say, nas Frondes Voz-da-chuva, quer que você entregue uma proposta de paz ao Xamã Vekjik e retorne com a resposta dele.","","Se perder a Sacola de Insetos do Lafu, fale com o Sumo Oráculo Soo-say, nas Frondes Voz-da-chuva."}},
[12574] = {"Já de volta?", {"Vá até o Refúgio Brumurmúria e fale com o Chamabruma Soo-gan.","","Se perder o Cristal Favorito de Jaloot, fale com o Sumo Oráculo Soo-say nas Frondes Voz-da-chuva."}},
[12575] = {"O tesouro perdido de Brumurmúria", {"Mate o Senhor da Guerra Tartek e recupere o Tesouro de Brumurmúria para o Chamabruma Soo-gan, no Refúgio Brumurmúria.","","Se perder o Cristal Favorito de Jaloot, fale com o Sumo Oráculo Soo-say nas Frondes Voz-da-chuva."}},
[12576] = {"Dando um basta", {"Mate 8 Lanceiros Feralma e 6 Catadores Feralma para o Chamabruma Soo-gan, no Refúgio Brumurmúria.","","Se perder o Cristal Favorito de Jaloot, fale com o Sumo Oráculo Soo-say nas Frondes Voz-da-chuva."}},
[12577] = {"Hora de voltar para casa!", {"Retorne às Frondes Voz-da-chuva."}},
[12578] = {"O furioso Gorloc", {"Vá até a Aldeia Mofâmbulo e fale com Mútli.","","Se perder a Bola de Relaxamento do Mútli, fale com o Sumo Oráculo Soo-say nas Frondes Voz-da-chuva."}},
[12579] = {"Sangue vital do santuário Mofâmbulo", {"Mútli quer que você colete 10 Estilhaços de Sangue Vital, mas fique longe da caverna que fica dentro do pilar.","","Se perder a Bola de Relaxamento do Mútli, fale com o Sumo Oráculo Soo-say nas Frondes Voz-da-chuva."}},
[12580] = {"O salvador dos Mofâmbulos", {"Mútli quer que você resgate 6 Mofâmbulos Vitimados na Aldeia Mofâmbulo. No entanto, alguns deles podem não estar mais vivos para serem salvos.","","Se perder a Bola de Relaxamento do Mútli, fale com o Sumo Oráculo Soo-say, nas Frondes Voz-da-chuva."}},
[12581] = {"O fardo de um herói", {"Mate Artruis, o Nefando.","","Apesar de recomendarmos fortemente garantir a ajuda de amigos, Mútli não poderá acompanhá-lo nesta tarefa."}},
