[13035] = {"Lacaios de Loken", {"Thorim quer que você mate Eisenfaust no Salão do Criador e Halefnir, o Filho do Vento, e Duronn, o Forjado em Runas, no Terraço dos Criadores."}},
[13036] = {"Honra acima de tudo", {"Apresente-se ao Comandante Cruzado Entari na Vanguarda Argêntea, que fica na Coroa de Gelo."}},
[13037] = {"As memórias de Casco Feroz", {"Fale com o Chefe Lança Veloz na Caverna Flotagelo e fale novamente com Xarantauro na Aldeia Tunka'lo."}},
[13038] = {"Distorções no tempo", {"Use o Totem do Chefe para fechar 4 Fendas de Flotagelo e leve o totem a Xarantauro na Aldeia Tunka'lo."}},
[13039] = {"Defendendo a Vanguarda", {"O Lorde Cruzado Dalfors, na Vanguarda Argêntea, que fica na Coroa de Gelo, quer que você mate 15 Nerubianos das Profundezas Abandonadas."}},
[13040] = {"Curar o incurável", {"O Padre Gustav, da Vanguarda Argêntea, na Coroa de Gelo, quer que você colete 10 Vesículas de Peçonha das Profundezas Abandonadas."}},
[13041] = {"Toque final na encomenda", {"Entregue uma calcedônia para Falcão Joiado em Dalaran."}},
[13042] = {"Nas entranhas dos salões subterrâneos", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você arranque informações à força do Aprendiz Osterkilgr a respeito do que o Dr. Terrível está armando.","","Em seguida, mate o aprendiz."}},
[13043] = {"A soma das partes altera o produto", {"Monte em Nergeld, assumindo o controle sobre ele. Defenda-o dos habitantes do Salão dos Horrores até que o Dr. Terrível apareça.","","Use Nergeld para matar o doutor e, em seguida, fale novamente com a Bruxa dos Ossos, na Vila Njorndar."}},
[13044] = {"Se houver sobreviventes...", {"Fale com Penumbrius na Vanguarda Argêntea, que fica na Coroa de Gelo."}},
[13045] = {"Em direção à devastação verdejante", {"Resgate 3 Cruzados Argênteos Capturados no Forte do Flagelo e leve-os para a Enfermaria da Vanguarda, no meio da Vanguarda Argêntea. Apresente-se ao Grão-lorde Tirion Fordring após cumprir a missão."}},
[13046] = {"Alimentando Arngrim", {"Alimente Arngrim com 5 Yormungares Desencarnados e volte à imagem congelada de Arngrim em Dun Niffelem.","","Vá ao Vale dos Antigos Invernos e use o Dente de Arngrim nos Yormungares Errantes. Uma vez que eles tenham se tornado desencarnados, você deverá lutar com eles até que estejam enfraquecidos."}},
[13047] = {"O Juízo", {"Encontre Thorim perto do Templo da Sabedoria. Informe o resultado da luta ao Rei Iokkum em Dun Niffelem."}},
[13048] = {"Onde o tempo se extraviou", {"Use o Martelo da História nos Artefatos Taunka para sintonizá-lo para o tempo do Casco Feroz. Depois de sintonizá-lo com 6 relíquias do tempo do Casco Feroz, fale novamente com Xarantauro na Aldeia Tunka'lo."}},
[13049] = {"As armas do herói", {"Reaveja a Lança e a Cota de Malha de Casco Feroz e devolva-os a Xarantauro na Aldeia Tunka'lo."}},
[13050] = {"Veranes", {"Thorim, no Templo das Tempestades, quer que você consiga 5 Ovos Pequenos de Protodracos dos picos perto da Aldeia Brunnhildar."}},
[13051] = {"Invasão de território", {"Ponha os Ovos Roubados de Protodraco no Ninho da Prolemadre e fale novamente com Thorim no Templo das Tempestades."}},
[13052] = {"Aerial Surveillance", {"Professor Calvert wants you to take the flight to Lakeside Landing and speak with Pilot Vic there."}},
[13053] = {"Encontrando sobreviventes", {"Ajude Zak Rodafrouxa a concluir o Voo de Teste."}},
[13054] = {"O rastreador desaparecido", {"Procure o Rastreador Val'zij no Covil de Gimorak, a sudeste do Local da Queda de Grom'arsh."}},
[13055] = {"Remédios da caverna", {"O Rastreador Val'zij, no Covil de Gimorak, quer que você consiga 1 Glândula de Toxina de Yormungar e 8 Cogumelos da Caverna."}},
[13056] = {"A vingança é um prato que se come frio", {"O Rastreador Val'zij quer que você mate Gimorak no Covil de Gimorak."}},
[13057] = {"O Terraço dos Criadores", {"Encontre Thorim no extremo sudeste do Terraço dos Criadores, a noroeste de Dun Niffelem."}},
[13058] = {"Mudando a direção do vento", {"Use o Martelo da História para voltar no tempo e enfrentar o Vento Norte como Casco Feroz. Quando Casco Feroz tombar e soltar a Trompa da Fúria Elemental, use-a para dar cabo do Vento Norte e fale novamente com Xarantauro na Aldeia Tunka'lo."}},
[13059] = {"Vingança para os Vargul", {"Bethod Feigr, nos Salões Subterrâneos, quer que você use sua espada para desafiar e matar o Thane Illska, no Círculo dos Sofrimentos."}},
[13060] = {"Quando tudo mais falha", {"Fale com Ricket para usar seu Dispositivo Propelido de Entrega D16 e adentrar os Picos Tempestuosos."}},
[13061] = {"Prepare-se para a glória", {"Fale com Lok'lira, a Velha, na Aldeia Brunnhildar."}},
[13062] = {"O presente de despedida de Lok'lira", {"Fale com Gretta, a Árbitra, na Aldeia Brunnhildar."}},
[13063] = {"Considerada digna", {"Brijana quer que você vá à Aldeia Brunnhildar e fale com Astrid Bjornrittar."}},
