[13116] = {"Mostardos-quentes!", {"Abasteça a Cesta de Piquenique Vazia com 4 Rinocerontes-quentes, preparados por você com sua perícia em culinária, e 4 Mostardas Selvagens colhidas dos gramados de Dalaran.","","Entregue a Cesta de Piquenique para o Arquimago Pentarus na Plataforma de Krasus, em Dalaran."}},
[13117] = {"De onde vêm eles?", {"Investigue um Altar de Evocação na parte leste da Usina de Carne e, em seguida, apresente-se novamente à Cavaleira das Trevas Arly, na Guarda Negra."}},
[13118] = {"A purgação do Forte do Flagelo", {"O Vigia de Ébano, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você mate 3 Sumos Sacerdotes das Profundezas Abandonadas, 3 Hipomonarcas das Profundezas Abandonadas e 8 Cruzados Reanimados."}},
[13119] = {"A destruição dos altares", {"Mate o Mestre-evocador Zarod e use o Cajado do Evocador Mestre para destruir o Primeiro, o Segundo, o Terceiro e o Quarto Altares de Evocação. Quando terminar a tarefa, apresente-se à Cavaleira das Trevas Arly, na Guarda Negra."}},
[13120] = {"O olhar da morte", {"Use o Orbe Olhar da Morte para colocar orbes no Apoio de Orbe do Laboratório de Abominação, no Apoio de Orbe do Laboratório de Gigante de Carne e no Apoio de Orbe da Área do Caldeirão. Em seguida, fale novamente com a Cavaleira das Trevas Arly, na Guarda Negra."}},
[13121] = {"Através do olho", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você use o Olho do Lich Rei para obter informações."}},
[13122] = {"A Pedra do Flagelo", {"O Vigia de Ébano, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você lhe traga 15 Pedras do Flagelo."}},
[13124] = {"O esforço perdura", {"Raelorasz quer que você entre no Óculus e resgate Belgaristrasz e seus companheiros."}},
[13125] = {"Silêncio no forte", {"O Vigia de Ébano, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você mate Salranax, o Lacerador de Carnes, o Hipomonarca Talonox e o Sumo Sacerdote Yath'amon."}},
[13126] = {"Uma frente unificada", {"Belgaristrasz quer que você destrua 10 Constructos Centrífugos e derrube o escudo de Varos. Então, derrote Varos Galganuvens."}},
[13127] = {"Lorde-mago Urom", {"Belgaristrasz quer que você derrote o Lorde-mago Urom dentro de Óculus."}},
[13128] = {"Asas e preces", {"Belgaristrasz quer que você mate Éregos dentro do Óculus e, em seguida, informe Raelorasz no Escudo de Transitus, que fica em Gelarra."}},
[13129] = {"Brincando de morto", {"Kurzel quer que você use o Trapo da Blusa de Kurzel no cadáver de Novos, o Evocador, e leve o Pano Manchado de Icor a Mack."}},
[13130] = {"A rocha que iniciou uma revolução", {"O Cruzado-arquiteto Silas, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você recupere 10 troncos de Cerne Cristalino e 10 peças de Maçonaria Élfica Antiga."}},
[13131] = {"Sucata no meu baú", {"Brigg, no Pináculo Utgarde, quer que você encontre 5 Barras de Prata Imaculadas, 3 Miçangas Brilhosas, 2 Cálices Dourados e uma Estátua de Jade."}},
[13132] = {"Que a vingança seja minha!", {"Brigg, no Pináculo Utgarde, quer que você mate o Rei Ymiron."}},
[13133] = {"Encontre o herói ancestral", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você traga Iskalder até ela."}},
[13135] = {"Perigo iminente", {"O Cruzado-engenheiro Patricuspe, no Vale dos Ecos, que fica na Coroa de Gelo, quer que você recupere 8 Energias Cristalizadas."}},
[13137] = {"Combate não muito justo", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você derrote Iskalder no Pináculo Chaga de Guerra."}},
[13139] = {"No coração congelado de Nortúndria", {"Entregue o Relatório do Padre Gustav ao Grão-lorde Tirion Fordring na Vanguarda Argêntea, que fica na Coroa de Gelo."}},
[13141] = {"A batalha pelo Pináculo dos Cruzados", {"Vá para o Pináculo dos Cruzados e finque o Estandarte Abençoado da Cruzada em cima da Pilha de Crânios de Cruzados. Defenda o estandarte contra os ataques do Flagelo até que a terra no pináculo tenha sido santificada. Em seguida, fale com o Padre Gustav no Vale dos Ecos, que fica na Coroa de Gelo."}},
[13142] = {"A vingança da banshee", {"A Bruxa dos Ossos, na Vila Njorndar, quer que você a vingue desafiando e derrotando o Sumo Thane Balargarde."}},
[13148] = {"Conserto de colar", {"Conserte o colar com uma Calcedônia e devolva-o para Falcão Joiado na Loja dos Joalheiros, na Área Comercial Magus, em Dalaran."}},
[13149] = {"Dissipando ilusões", {"Crona quer que você use o Interruptor Arcano nos caixotes suspeitos em Stratholme do passado e, então, fale com ela perto da entrada de Stratholme."}},
[13150] = {"ZZOLDLost: Widget", {"Repair the whatsit and return it to Timothy Jones at the Jewelcrafting shop in Dalaran."}},
[13151] = {"Uma escolta real", {"Crona pediu a você que acompanhe Arthas no Expurgo de Stratholme. Você deve falar com ela novamente quando Mal'Ganis for derrotado."}},
[13154] = {"Ossos e flechas", {"O Arqueiro Ranulfo na Fortaleza Invérnia quer que você tome 10 Aljavas de Flechas de Osso de Dragão de jogadores da Horda na Floresta das Sombras."}},
[13156] = {"Uma erva extraordinária", {"A Anacoreta Tessa na Fortaleza Invérnia quer que você tome 10 Bolsas de Herborismo da Horda de jogadores da Horda na Estepe da Vida."}},
[13157] = {"O Pináculo dos Cruzados", {"Procure o Grão-lorde Tirion Fordring no Pináculo dos Cruzados, que fica na Coroa de Gelo."}},
[13158] = {"Discrição é tudo", {"Rhonin quer que você vá ao Castelo Violeta em Dalaran e fale com o Carcereiro Alturas."}},
[13159] = {"Contenção", {"O Carcereiro Alturas quer que você entre no Castelo Violeta e dê cabo da força de invasão dos dragões azuis. Apresente-se a ele novamente quando Cyanigosa estiver morta."}},
[13165] = {"A retomada de Áquerus", {"Use seu feitiço Portão da Morte para retornar a Áquerus: a Fortaleza de Ébano. Apresente-se ao Grão-lorde Dárion Mograine quando chegar."}},
[13166] = {"A batalha pela Fortaleza de Ébano", {"Mate 10 membros do Flagelo e ajude a matar o Retalhoso. Fale novamente com o Grão-lorde Dárion Mograine, se tiver sucesso."}},
[13167] = {"Morte ao rei traidor", {"Kilix, o Desfiador, no Fosso de Narjun, deu a você a missão de vencer Anub'arak em Azjol-Nerub. Você deve falar novamente com Kilix tendo a Carapaça Quebrada de Anub'arak em mãos."}},
