[246] = {"Подсчет врагов", nil, {"Убейте 10 полукровок из Красногорья и 6 браконьеров из Красногорья и сообщите об этом заместителю судьи Фелдону в Приозерье."}},
[247] = {"Завершение охоты", nil, nil},
[248] = {"Пронизывающий взор", nil, {"Поднимитесь на уцелевшую башню рядом с крепостью Каменной Стражи в Красногорье.\n\nОтыщите подходящее место и спрячьте там амулет Азоры."}},
[249] = {"Моргант", nil, {"Победите Морганта.\n\nПринесите его подвеску Тени Теокриту в башню Азоры в Элвиннском лесу."}},
[250] = {"Прямая и черная угроза", nil, {"Осмотрите область вокруг восточной части Дамбы и отыщите для главного инженера Плотиннера улику, указывающую на замыслы дворфов Черного Железа."}},
[251] = {"Перевод записки Аберкромби", nil, {"Отнесите письмо, адресованное Элло, Шире Фон-Инди."}},
[252] = {"Перевод для Элло", nil, {"Отнесите переведенное письмо лорду Элло Чернодреву."}},
[253] = {"Невеста Бальзамировщика", nil, {"Отыщите могилу Элизы. Извлеките из нее сердце Бальзамировщика и вернитесь к Элло Чернодреву."}},
[254] = {"Копание земли", nil, nil},
[255] = {"Наемники", nil, {"Мировой судья Тупонос просит вас убить 4 огров из клана Мо'грош, 4 громил из клана Мо'грош и 4 головорезов из клана Мо'грош."}},
[256] = {"Разыскивается: Чок'сул", nil, {"Убейте Чок'сула и принесите его голову мировому судье Тупоносу в Телcамар."}},
[257] = {"Похвальба охотника", nil, {"Убейте 6 горных канюков и вернитесь к Дэрилу Юному в Приют странников. На выполнение задания отводится 15 минут."}},
[258] = {"Вызов охотнику", nil, {"Убейте 5 старых горных вепрей и вернитесь к Дэрилу Юному в Приют странников. На выполнение задания отводится 12 минут."}},
[259] = {"<UNUSED>", nil, {"Talk to Kelt Thomasin."}},
[261] = {"По пути Алого ордена", nil, {"Убейте 30 опустошителей-отрекшихся и вернитесь к брату Антону на Высоте Найджела."}},
[262] = {"Таинственный незнакомец", nil, {"Отнесите книгу, обнаруженную на ферме Свена, в Темнолесье и покажите ее горожанам."}},
[263] = {"Защита королевских земель", nil, {"Горный пехотинец Стенолом просит вас убить 10 шаманов из племени Камнедробов и 10 костегрызов из племени Камнедробов."}},
[264] = {"Покуда смерть не разлучит нас", nil, {"Положите подвеску Кларисы на могилу Юрива в Серебряном бору."}},
[265] = {"Продолжение поисков незнакомца", nil, {"Поговорите с горожанами о таинственном незнакомце."}},
[266] = {"Расспросы в таверне", nil, {"Найдите в таверне улики, связанные с таинственным незнакомцем."}},
[267] = {"Троггская угроза", nil, {"Принесите 8 каменных зубов троггов капитану Ругельфассу."}},
[268] = {"Возвращение к Свену", nil, {"Отнесите Свену законченные записи Трясунчика."}},
[269] = {"В поисках мудрости", nil, {"Отдайте законченные записи Трясунчика епископу Фартингу в Штормграде."}},
[270] = {"Обреченный флот", nil, {"Отправляйтесь в Гавань Менетилов в Болотине и поговорите с дворфом по имени Глорин Стальной Лоб."}},
[271] = {"Месть Вайрины", nil, {"Убейте Старину Черношкура и принесите его голову Дэрилу Юному в Приюте странников."}},
[272] = {"Испытание Морского Льва", nil, {"Найдите половинку подвески ловкости водяного облика и половинку подвески выносливости водяного облика. Поговорите с жителями Лунной поляны, чтобы узнать, где могут находиться эти предметы.\n\nСложите из половинок подвеску морского льва. Для этого вам надо находиться рядом со святилищем Ремула.\n\nОтнесите собранную подвеску Дендриту Блеску Звезд в деревню Ночная Гавань, что на Лунной поляне."}},
[273] = {"Снабжение экспедиции", nil, {"Поговорите с Хальдаром."}},
[274] = {"Прямая и черная угроза", nil, {"Ашлан Камнехмыл велел вам вернуться к главному инженеру Плотиннеру в Лок Модан и поделиться с ним открытием."}},
[275] = {"Нарывы на теле земли", nil, {"Убейте 8 трясинных ползунов и вернитесь к Ретилю Стражу Природы в Болотину."}},
[276] = {"Вытоптанные луга", nil, {"Убейте 15 гноллов  из стаи Мохошкуров и 10 полукровок из стаи Мохошкуров. Вернитесь к Ретилю Стражу Природы в Болотину."}},
[277] = {"Запрет на огонь", nil, {"Принесите Ретилю Стражу Природы 9 необработанных кремней."}},
[278] = {"Прямая и черная угроза", nil, {"Принесите паучий яд, мо'грошский кристалл и слезу кроколиска главному инженеру Плотиннеру."}},
[279] = {"Угроза из глубин", nil, {"Убейте 12 мурлоков из племени Синежабрых.\n\nПрикончите Жруна и принесите его голову Карлу Борану в Гавань Менетилов."}},
[280] = {"Прямая и черная угроза", nil, {"Проплывите по реке к основанию дамбы, отыщите бочку с порохом и влейте в нее нейтрализующую смесь, чтобы предотвратить взрыв."}},
[281] = {"Украденные товары", nil, {"Обыщите сундуки, бочки и ящики в поселениях мурлоков на побережье."}},
[282] = {"Наблюдения Сенира", nil, {"Поговорите с горным пехотинцем Талосом."}},
[283] = {"Прямая и черная угроза", nil, {"Кажется, нейтрализующая смесь сработала. Вернитесь к главному инженеру Плотиннеру – принесите ему добрые вести."}},
[284] = {"Поиски продолжаются", nil, {"Найдите статуэтку Менетила."}},
[285] = {"Поиски в поселениях мурлоков", nil, {"Продолжите поиски статуэтки Менетила в поселении мурлоков."}},
[286] = {"Возвращение статуэтки", nil, {"Верните статуэтку Менетила Карлу Борану. Его можно найти в Гавани Менетилов, что в Болотине."}},
[287] = {"Форт Мерзлогривов", nil, {"Обследуйте форт Мерзлогривов и убейте 5 охотников за головами из племени Мерзлогривов по просьбе Сенира Белоборода из Караноса."}},
[288] = {"Третья флотилия", nil, {"Купите первому помощнику Фитсиммонсу кружку медовухи."}},
[289] = {"Проклятая команда", nil, {"Убейте 13 проклятых матросов, 5 проклятых стражей бухты и первого помощника Снеллига. Принесите табакерку Снеллига первому помощнику Фитсиммонсу в Гавань Менетилов."}},
[290] = {"Очищение", nil, {"Возьмите ключ от сейфа на \"Отважном\" у капитана Халиндора.\n\nОткройте сейф на \"Отважном\"."}},
[291] = {"Донесения", nil, {"Отнесите донесение Сенира сенатору Бэрину Краснокамню в Стальгорн."}},
[292] = {"Око Палета", nil, {"Отнесите проклятое Око Палета Глорину Стальному Лбу. Его можно найти в Гавани Менетилов, что в Болотине."}},
[293] = {"Проклятое Око", nil, {"Отнесите Око Палета архиепископу Бенедикту. Его можно найти в Соборе Света в Штормграде."}},
[294] = {"Месть Ормера", nil, {"Убейте 10 крапчатых ящеров и 10 крапчатых крикунов."}},
[295] = {"Месть Ормера", nil, {"Убейте 10 крапчатых смертехватов и 10 крапчатых острозубов. Вернитесь к Ормеру Сталеплету на раскоп Вельгара."}},
[296] = {"Месть Ормера", nil, {"Убейте Зарлозуба и принесите Ормеру Сталеплету один из когтей чудовища."}},
[297] = {"Сбор идолов", nil, {"Принесите Магмару Рудоколу 8 резных каменных идолов."}},
[298] = {"Отчет о ходе раскопок", nil, {"Отнесите отчет о ходе работ Джерну Шлеморогу в Телcамар."}},
[299] = {"Тайны прошлого", nil, {"Обыщите раскопки и найдите четыре недостающих фрагмента скрижали: Адос, Модр, Голм и Неру. Принесите их геологу Вельгару."}},
[301] = {"Отчет в Стальгорн", nil, {"Отнесите отчет Сталекрута о ходе работ геологу Грозовой Вершине."}},
[302] = {"Порох для Сталекрута", nil, {"Поговорите с Джерном Шлеморогом в Телcамаре, что в Лок Модане"}},
[303] = {"Война с Черным Железом", nil, {"Убейте 10 дворфов, 5 проходчиков, 5 диверсантов и 5 подрывников из клана Черного Железа."}},
[304] = {"Мрачное поручение", nil, {"Убейте Балгараса Нечистого и принесите его ухо Длиннокосу Мрачному в Дун Модр."}},
[305] = {"Пропавшие ученые", nil, {"Таррел Камнепряд просит отыскать Меррин Камнепряд на раскопках."}},
[306] = {"Пропавшие ученые", nil, {"Отнесите записку от Меррин Таррелу Камнепряду. Его можно найти в Гавани Менетилов, что в Болотине."}},
[307] = {"Грязные лапы", nil, {"Принесите из рудника у Серебряного ручейка 4 ящика оборудования горному пехотинцу Грозовой Вершине."}},
[308] = {"Отвлечь Ярвена", nil, nil},
[309] = {"Охрана груза", nil, {"Убедитесь, что Миран и его груз добрались до раскопа, и сообщите об этом геологу Сталекруту."}},
[310] = {"Злейшие соперники", nil, {"Замените один бочонок эля Громоваров бочонком жгучего пива Ячменоваров в подвале \"Громоварки\" в Караносе."}},
[311] = {"Возвращение к Марлетте", nil, {"Принесите бочонок громового эля Марлетте Ячменовар в Поселок Пивоваров."}},
[312] = {"Ограбление тайника Тундры Макгранна", nil, {"Добудьте из пещеры Старого Ледоборода вяленое мясо из краденого ларя Тундры Макгранна."}},
[313] = {"Серая берлога", nil, {"Принесите 8 грив вендиго пилоту Камнекожцу."}},
[314] = {"Защита стада", nil, {"Убейте Вагаша и принесите его клык Рудре Янтарлен на Бараний выгон."}},
[315] = {"Всем портерам портер", nil, {"Принесите 6 порций звездолиста Регольду Ячменовару из Поселка Пивоваров."}},
[316] = {"<UNUSED 1>", nil, nil},
[317] = {"Заготовка продовольствия", nil, {"Принесите 4 куска кабанины и 2 толстых медвежьих шкуры пилоту Брюхинсу в Поселок Сталежара."}},
[318] = {"\"Вечное сияние\"", nil, {"Возьмите бочонок \"Вечного сияния\" у Регольда Ячменовара в Поселке Пивоваров."}},
[319] = {"Услуга за \"Вечное сияние\"", nil, {"Убейте 6 медведей-ледолапов, 8 старых скальных вепрей и 8 снежных леопардов. Вернитесь к Регольду Ячменовару в Поселок Пивоваров."}},
[320] = {"Возвращение к Толстопузу", nil, {"Отдайте бочонок \"Вечного сияния\" пилоту Толстопузу в Поселке Сталежара."}},
[321] = {"Светлосталь", nil, {"Найдите груз с затонувшего корабля \"Орлик\" в Болотине."}},
[322] = {"Достойное оружие", nil, {"Отнесите сундук со слитками светлостали Гриманду Элмору в Штормград."}},
[323] = {"Доказательство силы", nil, {"Убейте 15 скелетов-налетчиков, 3 скелетов-лекарей и 3 скелетов-хранителей и вернитесь к Свену."}},
[324] = {"Утерянные слитки", nil, {"Соберите 5 слитков светлостали и вернитесь к Глорину Стальному Лбу в Болотину."}},
[325] = {"Вооружен и особо опасен", nil, {"Принесите Проклятье Морбента Свену в Сумеречный лес."}},
[328] = {"Спрятанный ключ", nil, {"Откройте сейф букмекера Ирода маленьким латунным ключом."}},
[329] = {"Шпион найден!", nil, {"Отнесите грязную записку лейтенанту Дорену в Лагерь повстанцев."}},
[330] = {"Расписание дежурств", nil, {"Поговорите с капралом Сетманом."}},
[331] = {"Доклад Дорену", nil, {"Поговорите с лейтенантом Дореном в Лагере повстанцев."}},
[332] = {"Реклама винного магазина", nil, {"Отправляйтесь на винокурню Галлины и принесите Сюзетте Галлине талон на вино, чтобы получить бутылку вина бесплатно."}},
[333] = {"Харлан нуждается в помощи", nil, {"Отправляйтесь в магазин одежды в районе каналов и принесите Реме Шнайдер заказ на ткани от Харлана Багли."}},
[334] = {"Посылка для Турмана", nil, {"Отправляйтесь в суконную лавку Ларсона в Квартал Магов Штормграда, и передайте Турману Шнайдеру его набор для шитья."}},
[335] = {"Благородный напиток", nil, {"Принесите Зардету Черному Когтю слезу Тиллоа из Темнолесья и мускусный корень из Болотины."}},
[336] = {"Благородный напиток", nil, {"Отнесите портер \"Черный коготь\" лорду Вишлоку в Зал просителей."}},
[337] = {"Старый учебник истории", nil, {"Вернуть книгу \"История Штормграда\" в библиотеку Штормграда."}},
[338] = {"Зеленые холмы Тернистой долины", nil, {"Соберите все недостающие страницы романа \"Зеленые холмы Тернистой долины\". Когда у вас на руках окажутся все четыре главы, верните их Барнилу."}},
[339] = {"Глава I", nil, {"Принесите 1, 4, 6 и 8 страницы книги \"Зеленые холмы Тернистой долины\" Барнилу Каменному Горшку, чтобы собрать всю первую главу."}},
[340] = {"Глава II", nil, {"Принесите 10, 11, 14 и 16 страницы книги \"Зеленые холмы Тернистой долины\" Барнилу Каменному Горшку, чтобы собрать всю вторую главу."}},
[341] = {"Глава III", nil, {"Принесите 18, 20, 21 и 24 страницы книги \"Зеленые холмы Тернистой долины\" Барнилу Каменному Горшку, чтобы собрать всю третью главу."}},
[342] = {"Глава IV", nil, {"Принесите 25, 26 и 27 страницы книги \"Зеленые холмы Тернистой долины\" Барнилу Каменному Горшку, чтобы собрать всю четвертую главу."}},
[343] = {"Кстати, о стойкости духа", nil, {"Отправляйтесь в Королевскую библиотеку Крепости Штормграда и поговорите с Милтоном Снопом. Он даст вам книгу по металлургии, которая интересует брата Кристофа."}},
[344] = {"Брат Пакстон", nil, {"Поговорите с братом Пакстоном из Аббатства Североземья."}},
[345] = {"Запас чернил", nil, {"Поговорите со штейгером Ослоу из Приозерья и расспросите его о ретбанской руде."}},
[346] = {"Возвращение к Кристофу", nil, {"Вернитесь к брату Кристофу на Соборную площадь. Отдайте ему книгу \"Как закалялось железо\"."}},
[347] = {"Ретбанская руда", nil, {"Добудьте 5 кусков ретбанской руды и принесите их брату Пакстону в библиотеку Североземского аббатства."}},
[348] = {"Лихорадка Тернистой долины", nil, {"Найдите знахаря Унбагва и попросите его вызвать Мокка Дикаря. Принесите сердце Мокка Фину Горемыку."}},
[349] = {"Лихорадка Тернистой долины", nil, {"temp text 02 - log"}},
[350] = {"Помощь старого друга", nil, {"Поговорите с Иллингом Триасом в Штормграде."}},
[351] = {"Найди КПХ-17/TN!", nil, {"Отнесите аварийный маяк к автономному роботу Карлса."}},
[352] = {"<UNUSED>", nil, nil},
[353] = {"Посылка для Грозовой Вершины", nil, {"Доставьте посылку горному пехотинцу Грозовой Вершине в Лок Модан."}},
[354] = {"Смерти в семье", nil, {"Принесите останки Грегора, останки Ниссы и останки Турмана Колману Фартингу в Брилл."}},
[355] = {"Разговор с Севреном", nil, {"Поговорите с мировым судьей Севреном в Брилле."}},
[356] = {"Патруль в арьергарде", nil, {"Убейте 8 кровоточащих ужасов и 8 странствующих духов, а потом доложите о своих успехах Линнее, ожидающей вас в своем лагере."}},
[357] = {"Личность лича", nil, {"Принесите книгу лича Бетору Ледострелу в Подгород."}},
[358] = {"Грабители могил", nil, {"Убивайте грабителей могил и полукровок из стаи Гнилошкуров.\n\nПринесите мировому судье Севрену 8 образцов бальзамирующей лимфы."}},
[359] = {"Обязанности Отрекшихся", nil, {"Поговорите со стражем смерти Линнеей."}},
[360] = {"Возвращение к мировому судье", nil, {"Вернитесь к мировому судье Севрену в Брилл."}},
[361] = {"Недоставленное письмо", nil, {"Найдите Иветту Фартинг и вручите ей письмо от Турмана Агамонда."}},
[362] = {"Мельницы с привидениями", nil, {"Убейте Девлина Агамонда и принесите его останки Колману Фартингу в Брилл."}},
[363] = {"Резкое пробуждение", nil, {"Поговорите с жрецом Тьмы Сарвисом."}},
[364] = {"Безмозглые твари", nil, {"Жрец Тени Сарвис хочет, чтобы вы убили 5 безмозглых зомби и 5 презренных вурдалаков."}},
[365] = {"Поля горя", nil, {"Украдите 10 тыкв с западной фермы, к северу от Могильника. Потом отнесите эти тыквы аптекарю Юхану в Брилл."}},
[366] = {"Книга Гюнтера", nil, {"Верните Гюнтеру книгу заклинаний. Его можно найти на острове, в приюте Гюнтера."}},
[367] = {"Новая чума", nil, {"Принесите аптекарю Юхану 5 склянок крови псов тьмы."}},
