[736] = {"Звезда, Рука и Сердце", nil, {"Прочитайте записку Бел'дугура. Принесите перечисленные в записке предметы Бел'дугуру в  Подгород."}},
[737] = {"Запретное знание", nil, {"Отнесите книгу Йагина Тельдарину."}},
[738] = {"Найти Эгмонда", nil, {"Найдите Эгмонда."}},
[739] = {"Мурдалок", nil, {"Уничтожьте убийцу Эгмонда, Мурдалока. \n\nУбейте 12 костегрызов из племени Каменного Свода. \n\nСообщите геологу Сталекруту в Лок Модане обо всем, что произошло."}},
[741] = {"Рассеянный геолог", nil, {"Отвезите таинственную окаменелость главному археологу Седоусу в Дарнас."}},
[742] = {"Большая охота", nil, {"Поговорите с Сенани Громовое Сердце на заставе Расщепленного Дерева в Ясеневом лесу."}},
[743] = {"Соседство со стаей Неистовства Ветра", nil, {"Принесите 8 когтей гарпий из стаи Ярости Ветра Руулу Орлиному Когтю в деревню Кровавого Копыта."}},
[744] = {"Подготовка к церемонии", nil, {"Добудьте 6 лазурных перьев и 6 бронзовых перьев, и принесите их Эйану Орлиному Когтю в Громовой утес."}},
[745] = {"Недобрые соседи", nil, {"Убейте 10 дубильщиков, 8 шкуродеров и 5 браконьеров из стаи Бледногривов и возвращайтесь к Бейну Кровавому Копыту в деревню Кровавого Копыта."}},
[746] = {"Дворфийские делишки", nil, {"Соберите 5 кирок геолога.\n\nСломайте их в кузнице. \n\nОтнесите 5 сломанных инструментов Бейну Кровавому Копыту в деревню Кровавого Копыта."}},
[747] = {"Охота начинается", nil, {"Принесите 7 перьев равнинных долгоногов и 7 кусков мяса равнинных долгоногов Груллу Соколиному Ветру в Лагерь Нараче."}},
[748] = {"Отравленная вода", nil, {"Принесите 6 лап волков прерий и 4 когтя равнинного долгонога Муллу Громовому Рогу в деревню Кровавого Копыта."}},
[749] = {"Разграбленный караван", nil, {"Морин Ловец Облаков попросил вас изучить содержимое ящиков с припасами в разграбленном караване."}},
[750] = {"Охота продолжается", nil, {"Принесите 10 шкур горных пум Груллу Соколиному Ветру."}},
[751] = {"Разграбленный караван", nil, {"Отнесите бумаги Торговой Компании Морину Ловцу Облаков, которого можно найти неподалеку от Деревни Кровавого Копыта."}},
[752] = {" Скромная просьба", nil, {"Поищите матушку вождя Соколиный Ветер у источника к юго-востоку от Лагеря Нараче."}},
[753] = {"Скромная просьба", nil, {"Наберите кувшин воды из колодца.\n\nОтнесите кувшин вождю Соколиному Ветру в Лагерь Нараче, который находится к северо-западу от источника."}},
[754] = {"Очищение колодца Заиндевевшего Копыта", nil, {"Очистите колодец Заиндевевшего Копыта с помощью тотема очищения и возвращайтесь к Муллу Громовому Рогу."}},
[755] = {"Обряды Матери-Земли", nil, {"Отправьтесь к провидцу Серой Пасти, который живет к югу от Лагеря Нараче."}},
[756] = {"Тотем Громового Рога", nil, {"Принесите 6 когтей охотников прерий и 6 когтей пум плоскогорья Муллу Громовому Рогу."}},
[757] = {"Обряд Силы", nil, {"Убивайте дыбогривов в Ежевичной лощине и принесите 8 их поясов вождю Соколиному Ветру в Лагерь Нараче."}},
[758] = {"Очищение колодца Громового Рога", nil, {"Очистите колодец Громового Рога на севере от деревни с помощью тотема очищения и возвращайтесь к Муллу Громовому Рогу."}},
[759] = {"Тотем Буйногривых", nil, {"Отнесите 8 зубов могучего волка прерий Муллу Громовому Рогу в Деревню Кровавого Копыта."}},
[760] = {"Очищение колодца Буйногривых", nil, {"Очистите колодец Буйногривых с помощью тотема очищения и возвращайтесь к Муллу Громовому Рогу."}},
[761] = {"Охота на перепелятников", nil, {"Принесите 8 трофейных перьев перепелятника Харкену Тотему Ветра в Деревне Кровавого Копыта."}},
[762] = {"Посланник зла", nil, {"Убейте посла Инфернуса в Крепости Ангор и принесите доказательство его смерти советнику Белграму в Стальгорн."}},
[763] = {"Обряды Матери-Земли", nil, {"Отнесите тотем Соколиного Ветра Бейну Кровавому Копыту в Деревню Кровавого Копыта. Следуйте по дороге из Лагеря Нараче."}},
[764] = {"Торговая Компания", nil, {"Убейте 14 рабочих Торговой Компании и 6 бригадиров Торговой Компании по поручению Морина Ловца Облаков из Деревни Кровавого Копыта."}},
[765] = {"Бригадир Шумовик", nil, {"Убейте бригадира Шумовика и принесите его планшет Морину Ловцу Облаков в Деревню Кровавого Копыта."}},
[766] = {"Маззранач", nil, {"Принесите сердце койота, бедренную кость пумы плоскогорья, чешую долгонога и потроха перепелятника Мауру Заклинателю Дождя в деревню Кровавого Копыта."}},
[767] = {"Обряд прозрения", nil, {"Поговорите с Зарельманом Две Луны в Деревне Кровавого Копыта."}},
[768] = {"Добывание шкур", nil, {"Принесите 12 кусков тонкой кожи Верену Долгоногу в Громовой Утес."}},
[769] = {"Сумка из шкуры кодо", nil, {"Принесите 4 куска легкой кожи и 4 мотка грубой нити Верену Высокому Страннику в Громовой Утес."}},
[770] = {"Поцарапанный демоном плащ", nil, {"Отыщите кого-нибудь, кто слышал о Призрачном Вое."}},
[771] = {"Обряд прозрения", nil, {"Принесите 2 камня, лежащих у источника, и 2 янтарных семени Зарельману Две Луны в Деревню Кровавого Копыта."}},
[772] = {"Обряд прозрения", nil, {"Выпейте воду Провидцев перед костром племени в Деревне Кровавого Копыта и следуйте за видением."}},
