[850] = {"Главари племени Колкар", nil, {"Принесите Регтару Вратам Смерти, живущему на западе от Перекрестка, голову Барака."}},
[851] = {"Верог Дервиш", nil, {"Принесите Регтару Вратам Смерти, живущему на западе от Перекрестка, голову Верога."}},
[852] = {"Хэзрул Кровавая Отметина", nil, {"Принесите голову Хэзрула Регтару Врата Смерти, живущему к западу от Перекрестка."}},
