[2751] = {"Варварские доспехи", nil, {"Принесите 2 украшенных узором бронзовых наруча, 2 бронзовых длинных меча и 2 острых когтя Орокку Омошу в Оргриммар."}},
[2752] = {"За железными наплечниками", nil, {"Принесите Орокку Омошу в Оргриммар 4 бронзовых боевых топора и 4 бронзовых боевых молота."}},
[2753] = {"Тяжелая поступь", nil, {"Принесите 4 зеленых железных шлема, 4 пары зеленых железных наручей и 2 пары зеленых железных поножей Орокку Омошу в Оргриммар."}},
[2754] = {"Рога безумия", nil, {"Принесите Орокку Омошу в Оргриммар 2 прочных железных кувалды, 2 пары посеребренных бронзовых сапог и 2 пары посеребренных бронзовых рукавиц."}},
[2755] = {"Радости Омоша", nil, {"Научитесь танцу Радости Омошей."}},
[2756] = {"Старые обычаи", nil, {"Принесите 4 стальные кирасы и 4 стальных латных шлема Атурку Наковальне в Оргриммар."}},
[2757] = {"Вперед, в Пиратскую Бухту!", nil, {"Поговорите с Макгеваном в Пиратской Бухте."}},
[2758] = {"Происхождение кузнечного искусства", nil, {"Принесите 6 пар золотых чешуйчатых наручей Хэнку Молоту в Штормград."}},
[2759] = {"В поисках Гальвана", nil, {"Поговорите с Макгеваном в Пиратской Бухте."}},
[2760] = {"Орден Мифрила", nil, {"Поговорите с Гальваном Древним в Тернистой долине."}},
[2761] = {"Плавим, не плавим, плавим, не плавим…", nil, {"Принесите сорок мифриловых слитков и сорок железных слитков Гальвану Древнему из Тернистой долины."}},
[2762] = {"Обманное серебро", nil, {"Принесите сорок мифриловых слитков и пять слитков истинного серебра Гальвану Древнему из Тернистой долины."}},
[2763] = {"Могущество камней", nil, {"Принесите сорок мифриловых слитков и 4 цитрина Гальвану Древнему из Тернистой долины."}},
[2764] = {"Лучший ученик Гальвана", nil, {"Поговорите с Трентоном Молотом Света. Его можно найти в Прибамбасске, что в Танарисе."}},
[2765] = {"Кузнец-умелец!", nil, {"Получите награду у Гальвана Древнего!"}},
[2766] = {"Найти ККX-22/FE!", nil, {"Отнесите аварийный маяк к автономному роботу Карлса, в пещеру рядом с Долиной Свирепого Утеса."}},
[2767] = {"Спасти ККX-22/FE!", nil, {"Проводите ККX-22/FE в безопасное место, затем сообщите об этом Карлсу Пропеллерзу в Пиратской Бухте."}},
[2768] = {"Изыскательский жезл", nil, {"Принесите изыскательский жезл главному инженеру Чепухастеру в Прибамбасск."}},
[2769] = {"Братья Медноштиф", nil, {"Поговорите с Виззлом Медноштифом на Мерцающей равнине."}},
[2770] = {"Газ'рилла", nil, {"Принесите искрящую чешую Газ'риллы Виззлу Медноштифу на Мерцающую равнину."}},
[2771] = {"Голова на плечах", nil, {"Принесите два мифриловых капюшона и изысканный мифриловый наплечник Трентону Молоту Света."}},
[2772] = {"Мир у твоих ног", nil, {"Принесите две пары тяжелых мифриловых сапог и изысканные мифриловые штаны Трентону Молоту Света."}},
[2773] = {"Мифриловый парень", nil, {"Принесите две тяжелых мифриловых кирасы и изысканные мифриловые перчатки Трентону Молоту Света."}},
[2781] = {"Разыскивается: Калиф Жало Скорпида", nil, {"Принесите голову Калифа Жало Скорпида главному инженеру Чепухастеру в Прибамбасск."}},
[2782] = {"Секрет Рин'джи", nil, {"Отнесите табличку Оран Змеиные Кольца в Квартал Магов Подгорода. Оран – известная ученая, изучающая троллей из племени Сухокожих, и она наверняка сумеет прочесть надписи на табличке."}},
[2783] = {"Незначительные разногласия", nil, {"Поговорите с павшим героем Орды на границе Выжженных земель и болот."}},
[2784] = {"Попавший в немилость", nil, {"Послушайте, как павший герой Орды рассказывает свою историю."}},
[2801] = {"Печальная история", nil, {"Послушайте, как павший герой Орды рассказывает свою историю."}},
[2821] = {"Знак качества", nil, {"Принесите 10 толстых шкур йети Пратту Макгруббену в Крепость Оперенной Луны."}},
[2822] = {"Знак качества", nil, {"Принесите 10 толстых шкур йети Джангору Быстрому Страннику в Лагерь Мохаче."}},
[2841] = {"Техновойны", nil, {"Добудьте в Гномрегане чертежи боевой машины и код от сейфа Термоштепселя в Гномрегане и принесите их Ноггу в Оргриммар."}},
[2842] = {"Главный инженер Скути", nil, {"Поговорите со Скути в Пиратской Бухте."}},
[2843] = {"Поехалиии!", nil, {"Дождитесь, пока Скути настроит гоблинский импульсный повторитель."}},
[2844] = {"Великан-опекун", nil, {"Найдите потерявшуюся ночную эльфийку."}},
[2845] = {"Шая-бродяжница", nil, {"Достаньте из сундучка колокольчик Шаи.\n\nПроводите Шаю Листогон в лагерь Камнедробителя."}},
[2846] = {"Тиара Глубин", nil, {"Принесите Тиару Глубин Табете в Пылевых топях."}},
[2847] = {"Доспехи из дикой кожи", nil, {"Принесите 10 кусков плотной кожи Пратту Макгруббену в Крепость Оперенной Луны."}},
[2848] = {"Наплечники из дикой кожи", nil, {"Принесите 6 накладок из толстой кожи и дикую лозу Пратту Макгруббену в крепость Оперенной Луны."}},
[2849] = {"Жилет из дикой кожи", nil, {"Принесите 2 черепашьих чешуйчатых кирасы, 2 пары черепашьих чешуйчатых перчаток и дикую лозу Пратту Макгруббену в крепость Оперенной Луны."}},
