[7653] = {"Имперский латный пояс", nil, nil},
[7654] = {"Имперские латные сапоги", nil, nil},
[7655] = {"Имперские латные наручи", nil, nil},
[7656] = {"Имперский латный нагрудник", nil, nil},
[7657] = {"Имперский латный шлем", nil, nil},
