[8706] = {"Молот надвигающейся бури", nil, {"Принесите киражскую шипастую рукоять, 2 янтарных идола, 5 скарабеев из слоновой кости и 5 бронзовых скарабеев Надзирателю Харо в Силитус. Кроме того, вас должны превозносить в Круге Кенария."}},
[8707] = {"Клинок Погребенных тайн", nil, {"Принесите киражскую изысканную рукоять, 2 обсидиановых идола, 5 серебряных и 5 костяных скарабеев Надзирателю Харо в Силитус. Кроме того, вас должны превозносить в Круге Кенария."}},
[8708] = {"Палица Бесконечной жизни", nil, {"Принесите киражскую изысканную рукоять, 2 яшмовых идола, 5 хрустальных и 5 каменных скарабеев Надзирателю Харо в Силитус. Кроме того, вас должны превозносить в Круге Кенария."}},
[8709] = {"Кинжал сокрытых теней", nil, {"Принесите киражскую шипастую рукоять, 2 багровых идола, 5 золотых и 5 глиняных скарабеев Надзирателю Харо в Силитус. Кроме того, вас должны превозносить в Круге Кенария."}},
[8710] = {"Крис Неназванных имен", nil, {"Принесите киражскую изысканную рукоять, 2 ониксовых идола, 5 золотых и 5 глиняных скарабеев Надзирателю Харо в Силитус. Кроме того, вас должны превозносить в Круге Кенария."}},
[8711] = {"Клинок Вечной справедливости", nil, {"Принесите киражскую шипастую рукоять, 2 янтарных идола, 5 бронзовых скарабеев и 5 скарабеев из слоновой кости Надзирателю Харо в Силитус. Кроме того, вас должны превозносить в Круге Кенария."}},
