[8778] = {"Взрывчатка для дружины Стальгорна", nil, {"Принесите 6 бутылок воспламеняющей смеси, 5 бочонков гоблинского ракетного топлива и 10 порций концентрированного взрывчатого вещества чародею Форсунчику возле улья Зора в Силитусе. Кроме того, принесите тыловое задание IV."}},
[8779] = {"Материалы для гадания", nil, {"Принесите 1 большой сияющий осколок, 1 большой светозарный осколок и 1 огромный изумруд геологу Ехидной в крепость Кенария в Силитусе. Также принесите тыловое задание V."}},
[8780] = {"Комплекты брони", nil, {"Принесите 8 комплектов грубой брони и 8 накладок из толстой кожи Джанеле Твердомолот на заставу дружины Стальгорна возле улья Зора в Силитусе. Кроме того, сдайте тыловую задачу VII."}},
[8781] = {"Нехватка оружия", nil, {"Принесите 2 палаша из лунной стали Джанеле Твердомолот на заставу дружины Стальгорна возле улья Зора в Силитусе. Кроме того, сдайте тыловую задачу VI."}},
[8782] = {"Поставки формы", nil, {"Принесите 1 кусок луноткани, 2 рулона рунической ткани и 1 железношелковую паутину призывательнице ветров Гордый Рог в крепости Кенария в Силитусе. Кроме того сдайте тыловую задачу VIII."}},
[8783] = {"Необычные материалы", nil, {"Принесите Варгусу из крепости Кенария в Силитусе 2 зачарованных ториевых слитка и 2 куска зачарованной кожи. Кроме того, принесите тыловое задание IX."}},
[8784] = {"Секреты Кираи", nil, {"Принесите древний киражский артефакт драконам, которые укрылись в храме, недалеко от входа."}},
[8785] = {"Амулеты для легиона Оргриммара", nil, {"Принесите 6 мощных амулетов, 6 бутылей большого колдунства и 8 бутылок воспламеняющей смеси жрице Тьмы Шаи к улью Регал в Силитус. Кроме того, принесите тыловое задание IV."}},
[8786] = {"Нехватка оружия", nil, {"Принесите 3 массивных железных топора Мероку Долгому Шагу в лагерь легиона Оргриммара рядом с ульем Регал. Кроме того, сдайте тыловую задачу VI."}},
