[9993] = {"Масло из семян олембы", nil, {"Принесите 30 семян олембы Ракории в форт Камнеломов."}},
[9994] = {"Что это такое?", nil, {"Доставьте ящик с деталями Дженайе Шепот Звезд в бастион Аллерии в лесу Тероккар."}},
[9995] = {"Что это такое?", nil, {"Доставьте ящик с деталями Туки в форт Камнеломов в лесу Тероккар."}},
[9996] = {"Нападение на лагерь Огнекрылых", nil, {"Убейте 10 защитников, 10 кровавых стражей и 10 чернокнижников из армии Огнекрылых, после чего вернитесь к лейтенанту Меридиану на заставу Аллерии в лесу Тероккар."}},
[9997] = {"Нападение на лагерь Огнекрылых", nil, {"Убейте 10 защитников, 10 кровавых стражей и 10 чернокнижников из армии Огнекрылых, после чего поговорите с сержантом Чауни в лагере Камнеломов, что в лесу Тероккар."}},
[9998] = {"Беспокойные соседи", nil, {"Убейте 12 астральных батраков и доложите об этом Телории Плащ Теней на заставу Аллерии."}},
[9999] = {"Выиграть время", nil, {"Убейте 2 стражей Скверны - легионеров, 3 мо'аргов-инжененеров и 8 ган'аргов-ремонтников."}},
[10000] = {"Нежелательное присутствие", nil, {"Убейте 12 астральных батраков и доложите об этом тенелову Кайду."}},
[10001] = {"Главный планировщик", nil, {"Алтруис Страдалец из западного Награнда просит вас раздобыть чертежи главного планировщика."}},
[10002] = {"Посредник Огнекрылых", nil, {"Найдите и убейте Лизель Огнепрядку, после чего вернитесь к Телории Плащ Теней."}},
[10003] = {"Посредник Огнекрылых", nil, {"Найдите и убейте Лизель Огнепрядку, после чего вернитесь к тенелову Кайду."}},
[10004] = {"Терпение и понимание", nil, {"Покажите чертежи главного планировщика Саль'салабиму в Шаттрате и уговорите его помочь вам."}},
[10005] = {"Вести для землепряда Тавгрена", nil, {"Поговорите с заклинателем земли Тавгреном возле перелеска Кенария в лесу Тероккар."}},
