[10106] = {"Штурмовые укрепления", nil, {"Захватите обзорную площадку, ристалище и Изрезанный холм, и возвращайтесь к прапорщику Трейси Праудвелл в Оплот Чести на полуострове Адского Пламени."}},
[10107] = {"Дипломатическая миссия", nil, {"Джорин Мертвый Глаз из Гарадара в Награнде попросил вас поговорить с Лантрезором Клинком в руинах Пылающего Клинка."}},
[10108] = {"Дипломатическая миссия", nil, {"Мо'мор Разрушитель из Телаара в Награнде попросил вас поговорить с Лантрезором Клинком в руинах Пылающего Клинка."}},
[10109] = {"Я их достану!", nil, {"Принесите 3 порции пара элементалей воздуха Вазату в Южный Награнд."}},
[10110] = {"Штурмовые укрепления", nil, {"Захватите Обзорную площадку, Ристалище и Изрезанный холм, и возвращайтесь в Траллмар к Черноглазке Зову Войны."}},
[10111] = {"Принеси мне яйцо!", nil, {"С помощью Прыготрона-4000 запрыгните в гнездо на ближайшем дереве и украдите оттуда таинственное яйцо."}},
[10112] = {"Личное одолжение", nil, {"Найдите 5 украденных товаров Латрая и верните их хозяину. Латрая можно найти возле таверны \"На краю земли\" в Нижнем городе Шаттрата."}},
[10113] = {"Охотничий лагерь Эрнестуэя", nil, {"Поговорите с Хемингом Эрнестуэем в охотничьем лагере Эрнестуэя на северо-восточной границе Награнда."}},
[10114] = {"Охотничий лагерь Эрнестуэя", nil, {"Олорм Странник просит вас поговорить со Странником Шадо \"Фитцем\" в охотничьем лагере Эрнестуэя в Награнде."}},
[10115] = {"Искажение Остротопи", nil, {"Убейте 3 убийц Остротопи и 15 грязекопов Остротопи."}},
[10116] = {"Разыскивается: вождь Муммаки!", nil, {"Принесите тотем вождя Муммаки Икути в прибежище Оребор."}},
[10117] = {"Разыскивается: вождь Муммаки!", nil, {"Принесите тотем вождя Муммаки темному охотнику Денжаю."}},
[10118] = {"Разговор с Остротопью", nil, {"Убейте 3 убийц и 15 грязекопов из племени Остротопи, и вернитесь к темному охотнику Денжаю в Забра'джин."}},
[10119] = {"Через Темный портал", nil, {"Пройдите через Темный портал и найдите командора Дарона. Он ждет вашего прибытия на другой стороне, в Запределье."}},
[10120] = {"Прибытие в Запределье", nil, {"Доставьте донесение Ориона Влагге Легкоперой у Темного портала на полуострове Адского Пламени."}},
[10121] = {"Истребить Пылающий Легион", nil, {"Поговорите с сержантом Лободробом, который находится при караване к востоку от Траллмара."}},
[10122] = {"Вблизи цитадели", nil, {"Найдите следы рядового Имариона у самого северного лагеря орков Скверны, расположенного у Южного бастиона."}},
[10123] = {"Лощина Вспышки Скверны", nil, {"Убейте 4 бесов-поджигателей, 6 инферналов-завоевателей и 1 вестника Ужаса по просьбе сержанта Лободроба, который находится при караване к востоку от Траллмара."}},
[10124] = {"Передовая застава: Останки Сквернобота", nil, {"Доложитесь командиру передовой То'арху по прибытии к останкам Сквернобота."}},
[10125] = {"Задание: разрушить связь", nil, {"Уничтожьте Антенну Легиона: Злоба, Антенну Легиона: Ярость, Антенну Легиона: Ненависть, Антенну Легиона: Страх с помощью взрывчатки То'арха по приказу командира передовой То'арха в Погибели Сквернобота на Полуострове Адского Пламени."}},
[10127] = {"Задание: Ослабить врага", nil, {"Убейте 4 укротителей Скверны, 8 гневостражей и поработительницу Ши'азив по приказу командира передовой То'арха у останков Сквернобота на Полуострове Адского Пламени."}},
[10129] = {"Задание: Врата Муркет и Шаадраз", nil, {"Поговорите с командиром звена Бракком, разбомбите Врата Муркет и Шаадраз, затем возвращайтесь к командиру передовой То'арху к останкам Сквернобота."}},
[10130] = {"Западный фланг", nil, {"Убейте 10 стражников, 10 берсерков и 5 послушников из клана Изувеченной Длани. О выполнении задания доложите лейтенанту Амади, которая будет ждать вас в западной сторожевой башне Оплота Чести."}},
[10132] = {"Колоссальная угроза", nil, {"Убейте 5 яростных колоссов и возвращайтесь к Тола'тиону на Кенарийскую заставу на полуострове Адского Пламени."}},
[10134] = {"Ключ к разгадке алого кристалла", nil, {"Доставьте осколок алого кристалла Тола'тиону на Кенарийскую заставу на полуострове Адского Пламени."}},
[10136] = {"Безжалостные планы", nil, {"Убейте Араззиуса Безжалостного и возвращайтесь к Назгрелу в Траллмар."}},
[10140] = {"Путешествие в Оплот Чести", nil, {"Долетите до Оплота Чести и доставьте донесение Дурона маршалу Исилдору."}},
[10141] = {"Возрожденный Легион", nil, {"Поговорите с сержантом Альтумой, которая находится рядом с восточным караваном, к востоку от Оплота Чести."}},
[10142] = {"Путь Страданий", nil, {"Убейте 4 бесов-поджигателей, 6 инферналов-завоевателей и 1 вестника ужаса по приказу сержанта Альтумы из восточного каравана, который находится на востоке от Оплота Чести на полуострове Адского Пламени."}},
[10143] = {"Лагерь экспедиции", nil, {"Доложитесь командиру передовой Кингстону в лагере экспедиции."}},
[10144] = {"Лишить Легион подкрепления!", nil, {"Уничтожьте врага вокруг порталов Грим и Каалез и соберите камни с рунами демонов.\n\nЗабросьте запалы Кингстона в порталы Грим и Каалез.\n\nПосле выполнения задания возвращайтесь к командиру передовой Кингстону вместе с оставшейся взрывчаткой."}},
[10145] = {"Mission: Sever the Tie UNUSED", nil, {"Уничтожьте 4 укротителя Скверны, 8 гневостражей и Поработителя Ялквиза по приказу командира передовой Кингстона из лагеря экспедиции на Полуострове Адского Пламени."}},
[10146] = {"Задание: Врата Муркет и Шаадраз", nil, {"Поговорите с командиром звена Даби'ри, разбомбите Врата Муркет и Врата Шаадраз, затем возвращайтесь к командиру передовой Кингстону в Лагерь экспедиции."}},
[10150] = {"Вблизи цитадели", nil, {"Постарайтесь найти следы разведчицы Махи в одном из лагерей, расположенных вдоль Северного бастиона."}},
[10152] = {"Западный фланг", nil, {"Убейте 10 стражников, 10 берсерков и 5 послушников из клана Изувеченной Длани. О выполнении задания доложите каменному стражу Амбелану, который находится на заставе Раздробленного Хребта, рядом с Зет'Гором."}},
[10159] = {"Очистить холм Колючего Клыка!", nil, {"Убейте 8 опустошителей Колючего Клыка и 8 ядоплюев Колючего Клыка. После выполнения задания возвращайтесь к Махураму Крепкому Копыту на Кенарийскую заставу на полуострове Адского Пламени."}},
[10160] = {"Врага нужно знать в лицо", nil, {"Поговорите с лейтенантом Амади в старой сторожевой башне на западе Оплота Чести."}},
[10161] = {"В случае чрезвычайной ситуации...", nil, {"Соберите 30 обломков дирижабля и принесите Скриду Многослову по прозвищу \"Крикун\" на место крушения дирижабля."}},
[10162] = {"Задание: Косогор Бездны", nil, {"Уничтожьте 20 ган'аргов-батраков, 5 мо'аргов-надзирателей и 5 пушек Скверны по приказу командира передовой То'арха у останков Сквернобота на полуострове Адского Пламени."}},
[10163] = {"Задание: Косогор Бездны", nil, {"Уничтожьте 20 ган'аргов-батраков, 5 мо'аргов-надзирателей и 5 пушек Скверны по приказу командира звена Грифонгара с Парящей заставы на полуострове Адского Пламени."}},
[10164] = {"Все будет в порядке", nil, {"Проникните в Аукенайские гробницы и уничтожьте экзарха Маладаара по просьбе Великого Отца Алдримуса, который находится снаружи Аукенайских гробниц Аукиндона."}},
[10165] = {"Жестокая вещь – конкуренция", nil, {"Убейте принца нексуса Шаффара по просьбе принца нексуса Харамада, находящегося снаружи Гробниц маны, и принесите ему обмотки Шаффара."}},
[10166] = {"Память о Белостволе", nil, {"Заройте подвеску Старого Белоствола у рунического камня в Выжженной роще по просьбе Ларианны Речной Ветер."}},
[10167] = {"Аукиндон...", nil, {"Отправляйтесь в Аукенайские гробницы в Костяных пустошах леса Тероккар, убейте экзарха Маладаара и освободите дух Д'оре."}},
[10168] = {"Что видит душа", nil, {"Найдите зеркало Души в Аукенайских гробницах и используйте его, чтобы выманить темного духа из древнего орка-предка в Награнде. Уничтожьте 15 темных духов, чтобы орочьи предки могли упокоиться с миром.\n\nКогда исполните поручение, возвращайтесь к Матери Кашур в Земли Предков."}},
[10169] = {"Проиграть с честью", nil, {"Вылейте сонную сыворотку в котел Харонема на террасе Света, после чего вернитесь к магистру Фальрису."}},
[10170] = {"Возвращение к Великой Матери", nil, {"Возвращайтесь в Гарадар и поговорите с великой матерью Гейей."}},
[10171] = {"Безутешный Вождь", nil, {"Поговорите с Гаррошем."}},
[10172] = {"Надежды нет", nil, {"Вернитесь к Великой Матери Гейе и передайте ей слова Гарроша."}},
[10173] = {"Посох верховного мага", nil, {"Отнимите посох верховного мага Варгота у Эккораш Инквизитора и принесите Равандвиру в Зону 52. Чтобы выманить Эккораш из укрытия, бросьте волшебный порошок на жаровню в центре руин."}},
[10174] = {"Проклятие Аметистовой башни", nil, {"Используйте посох верховного мага Варгота, чтобы связаться с верховным магом Варготом.\n\nЕсли вы утратили посох верховного мага Варгота, поговорите с Равандвиром в Зоне 52, чтобы получить замену."}},
[10175] = {"Тралл, сын Дуротана", nil, {"Отправляйтесь в Оргриммар и передайте Траллу сообщение от Гейи."}},
[10176] = {"Страж Ар'келос", nil, {"Войдите в Аметистовую башню, расположенную в центре деревни Кирин'Вар, и победите Ар'келоса, затем поговорите с верховным магом Варготом."}},
[10177] = {"В Аукиндоне неспокойно", nil, {"Доложите о своем прибытии боевому командиру Мафууну, который находится неподалеку от входа в Темный лабиринт в Аукиндоне в Лесу Тероккар."}},
[10178] = {"Найти шпиона То'гуна!", nil, {"Найдите шпиона То'гуна в Темном лабиринте в Аукиндоне."}},
[10179] = {"Смотритель Кирин'Вара", nil, {"Поговорите с блюстителем Ценой Смерти в деревне Кирин'Вар в Пустоверти."}},
[10180] = {"Неудержимый", nil, {"Поговорите с Исфаром, которого можно найти неподалеку от входа в Сетеккские залы в Аукиндоне."}},
[10182] = {"Боевой маг Датрик", nil, {"Найдите апартаменты боевого мага Датрика в переулке Волшебников и верните меч в оружейную стойку. Затем поговорите со смотрителем Дайвортом в деревне Кирин'Вар."}},
[10183] = {"В Зону 52", nil, {"Передайте записки пустолога ведущему ракетостроителю Фьюзеляжу, находящемуся в Зоне 52 в Пустоверти."}},
[10184] = {"Зловещие останки", nil, {"Смотритель Дайворт в деревне Кирин'Вар просит вас победить 20 покалеченных духов на площади Кирин'Вара."}},
[10185] = {"Участь хуже, чем смерть", nil, {"Убейте 10 убийц магов и 10 искателей маны и вернитесь к смотрителю Дайворту в деревню Кирин'Вар."}},
[10186] = {"Тебя наняли!", nil, {"Соберите 10 астраллитиевых матричных кристаллов и принесите их ведущему ракетостроителю Фьюзеляжу в Зону 52 в Пустоверти."}},
[10187] = {"Послание для верховного мага", nil, {"Поговорите с Верховным магом Варготом на вершине Сиреневой башни в Пустоверти."}},
[10188] = {"Печать Краса", nil, {"Заберите Печать Краса у чароплета Марианы, находящейся в Манагорне Коруу, к северо-западу от Аметистовой башни."}},
[10189] = {"Манагорн Б'наар", nil, {"Патрон шпионов Талодиен, находящийся в Зоне 52, хочет, чтобы вы направились в окрестности манагорна Б'наар и отобрали список персонала манагорна Б'наар у капитана Аратина.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10190] = {"Перезарядка батарей", nil, {"Используйте зарядное устройство для батарей применительно к достаточному количеству внепространственных охотников, чтобы довести зарядку батарей до уровня 10. Затем вернитесь к роботехнику Элли в руины Энкаата в Пустоверти."}},
[10191] = {"Робот модели V жив!", nil, {"Сопроводите \"Маккса А. Миллиона V\" через руины Энкаата и обратно, обеспечив ему безопасность. Потом поговорите с роботехником Элли за пределами руин Энкаата в Пустоверти."}},
[10192] = {"Компендиум Краса", nil, {"Заберите компендиум Краса – глава 1, компендиум Краса – глава 2 и компендиум Краса – глава 3 из района Городской площади деревни Кирин'Вар."}},
[10193] = {"Высокие цели", nil, {"Патрон шпионов Талодиен, находящийся в Зоне 52, просит вас отправиться в манагорн Б'наар и убить 2 мастеров пространственных искривлений, 6 инженеров пространственных искривлений и 8 геологов из клана Ярости Солнца.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10194] = {"Незаметный полет", nil, {"Поговорите с Веронией, находящейся за пределами Зоны 52, и летите к манагорну Коруу.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10196] = {"Больше перьев араккоа", nil, {"Принесите 30 перьев араккоа Малуказу в форт Камнеломов."}},
[10197] = {"Убедительная маскировка", nil, {"Добудьте комплект одеяний чародея из армии Ярости Солнца, одну пару перчаток ученого из армии Ярости Солнца и медальон охранника из армии Ярости Солнца и принесите все это Каледису Сияющему Рассвету в манагорн Коруу в Пустоверти.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10198] = {"Сбор информации", nil, {"Используя маскировку клана Ярости Солнца, проникните в Манагорн Коруу и подслушайте разговор между командиром Кузней Солнца и чародеем Ардонисом. По завершении задачи доложите о результатах Каледису Сияющему Рассвету. \n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10199] = {"Дополнительная сила", nil, {"Принесите 5 шипов скатов Пустоты Бутсу, находящемуся в Зоне 52 в Пустоверти."}},
[10200] = {"Возвращение к Талодиену", nil, {"Вернитесь к патрону шпионов Талодиену в Зону 52 в Пустоверти."}},
[10201] = {"Момент истины", nil, {"Найдите Грека и попросите его смазать маслом свое оружие, после чего вернитесь к Ракории."}},
[10202] = {"Дезертир", nil, {"Отправляйтесь в манагорн Ара и поговорите с магистром Теледорном. \n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10203] = {"Захват бесценного оборудования", nil, {"Используйте ультра-разуплотняющий захват, чтобы доставить гиперротационный Рой-О-Мат, сервоприводную пневматическую землечерпалку, радужный световой анализатор грунта и большой фургон с взрывчаткой обратно в Зону 52. Затем сообщите старшему саперу Бджимбуму, находящемуся на восточном конце расселины, идущей через Зону 52 в Пустоверти."}},
[10204] = {"Кристаллы камня Крови", nil, {"Магистр Ларинна просит вас отправиться в манагорн Б'наар и отобрать осколок камня крови у магистра из клана Ярости Солнца. Используйте этот осколок поблизости от более крупного кристалла камня крови и возвращайтесь к Ларинне в Зону 52.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10205] = {"Астральный налетчик Несаад", nil, {"Убейте астрального налетчика Несаада, затем возвращайтесь к ловчему Пустоты Кай'джи, находящемуся в Зоне 52 в Пустоверти."}},
[10206] = {"В погоне за технологиями", nil, {"Соберите 10 астральных технологий и вернитесь с ними к Папаше Колесуну, находящемуся в Зоне 52 в Пустоверти."}},
[10208] = {"Лишить Легион подкрепления!", nil, {"Уничтожьте врагов вокруг портала Зилус и портала Круул, соберите камни с рунами демонов.\n\nИспользуйте запалы То'арха внутри порталов Зилус и Круул.\n\nВозвращайтесь к То'арху в Гибель Сквернобота на полуострове Адского Пламени вместе с оставшимися запалами."}},
[10209] = {"Трофей призывателя Кантина", nil, {"Победите призывателя Кантина, находящегося в Манагорне Даро, на острове к северу от Форта Ярости Солнца, и заберите у него камень Студенора."}},
[10210] = {"А'дал", nil, {"Поговорите с А'далом, предводителем Ша'тар, в центре террасы Света."}},
[10211] = {"Город Света", nil, {"Проследуйте за слугой Кадгара и выслушайте историю, которую он вам расскажет, после чего вернитесь к Кадгару."}},
[10212] = {"Герой маг'харов", nil, {"Вернитесь к великой матери Гейе в Гарадар в Награнде."}},
[10213] = {"Найти место крушения", nil, {"Отыщите уцелевших в катастрофе."}},
[10214] = {"Весь рудник ходуном", nil, {"Убейте 15 ган'аргов-саперов и возвращайтесь к штейгеру Разлкразу."}},
[10216] = {"Прежде всего - безопасность!", nil, {"Убейте 10 эфириалов – осквернителей гробниц, 5 эфириалов-колдунов, 5 ловчих нексуса и 5 эфириалов-чароплетов по просьбе работника Морфалиуса.\n\nПосле этого найдите эфириальский телепорт в зале, предшествующем палате Шаффара, и активируйте его."}},
[10218] = {"Someone Else's Hard Work Pays Off", nil, {"Проводите криоинженера Ша'хина в Гробницы маны, чтобы он мог собрать астральную субстанцию в коллекторах Шаффара.\n\nВ случае успеха явитесь к принцу нексуса Харамаду в лагере Консорциума."}},
[10220] = {"Заставь их слушать", nil, {"Убейте 12 непреклонных пехотинцев, 8 непреклонных рыцарей и 6 непреклонных колдунов в оружейной экспедиции к западу от заставы Хребтолома по просьбе Мордибы."}},
[10221] = {"Доктор Бум!", nil, {"Убейте Доктора Бума и возвращайтесь с хорошими новостями к старшему саперу Бджимбуму в Зону 52 в Пустоверти."}},
[10222] = {"Гарнизон Ярости Солнца", nil, {"Смотритель Дайворт из деревни Кирин'Вар просит вас убить 10 лучников из армии Ярости Солнца."}},
[10223] = {"Покончить с Даэллисом", nil, {"Смотритель Дайворт из деревни Кирин'Вар просит вас убить наставника Даэллиса Стрелка Рассвета."}},
[10224] = {"Эссенция для двигателей", nil, {"Соберите 7 единиц эссенции призрака магии и доставьте главному инженеру Трепу в Зону 52 в Пустоверти."}},
[10225] = {"Рапорт главному инженеру", nil, {"Передайте астраллитиевые матричные кристаллы главному инженеру Трепу, находящемуся в Зоне 52 в Пустоверти."}},
[10226] = {"Извлечение силы стихии", nil, {"Извлеките 5 единиц силы стихии для главного инженера Трепа, находящегося в Зоне 52 в Пустоверти."}},
[10227] = {"Я вижу мертвых дренеев", nil, {"Поговорите с Рамдором Безумным, который стоит на груде костей неподалеку от Аукенайских гробниц."}},
[10228] = {"Иезекииль", nil, {"Найдите Иезекииля из Шаттрата в городе Шаттрат и поговорите с ним."}},
[10229] = {"Секрет фолианта", nil, {"Отнесите таинственный фолиант Альтену Историку на Заставу Хребтолома."}},
[10230] = {"Боевой рог", nil, {"Заберите у лейтенанта-командора Талвоса Боевой рог Непреклонных и принесите его Альтену Историку на Заставу Хребтолома."}},
[10231] = {"Какая книга? Не вижу никакой книги", nil, {"Иезекииль говорит, что книга могла быть украдена. Найдите головорезов и попытайтесь узнать что-нибудь у них."}},
[10232] = {"Легион – на мусор!", nil, {"Убейте 5 мо'аргов-кузнецов рока и 15 ган'аргов-инженеров. Затем возвращайтесь к Папаше Колесуну в Зону 52 в Пустоверти."}},
[10233] = {"Пожар в форте Ярости Солнца", nil, {"Используя горящий факел, уничтожьте 4 баллисты и 4 шатра в форте Ярости Солнца, затем вернитесь к лейтенанту-чародею Моррану в деревню Кирин'Вар."}},
[10234] = {"Что одному демону мусор...", nil, {"Соберите 10 деталей сквернобота и доставьте их Мамаше Колесун, находящейся на заставе в заповеднике \"Срединные земли\" в Пустоверти."}},
[10235] = {"Коготь Рока? Вырвать с мясом!", nil, {"Заберите руку Когтя Рока и доставьте ее Мамаше Колесун, находящейся на заставе в заповеднике \"Срединные земли\" в Пустоверти."}},
[10236] = {"Гадость это ваше Запределье!", nil, {"Найдите 6 частей крошшера и верните их штейгеру Разлкразу на рудник, который находится к северо-западу от Траллмара."}},
[10237] = {"Предупредить Зону 52!", nil, {"Передайте плохие новости ведущему ракетостроителю Фьюзеляжу в Зоне 52 в Пустоверти."}},
[10238] = {"Каково работать на гоблинов", nil, {"Спасите Манни, Моха и Джакка из лагерей орков Скверны и доставьте их штейгеру Разлкразу на рудник, который находится к северо-западу от Траллмара."}},
[10239] = {"Потенциальный источник энергии", nil, {"Лейтенант-чародей Морран из деревни Кирин'Вар просит вас захватить 10 энергетических кубов-изоляторов, используемых эльфами крови в манагорне Коруу."}},
[10240] = {"Постройка периметра", nil, {"Используйте устройство пробуждения рун поблизости от восточной, северо-восточной и западной руны. Руны можно найти по периметру районов переулка Волшебников и главной площади деревни Кирин'Вар. По завершении задачи вернитесь к лейтенанту-чародею Моррану."}},
[10241] = {"Отвлекающий маневр в манагорне Б'наар", nil, {"Убейте 8 магистров и 8 кровостражей клана Ярости Солнца, затем возвращайтесь к экзарху Орелису в Зону 52.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10242] = {"Застава Хребтолома", nil, {"Доставьте образец крови орков из клана Кровавой Глазницы аптекарю Альбреку на заставу Хребтолома по просьбе аптекаря Зеланы из Погибели Сквернобота. Чтобы добраться до заставы на виверне, обратитесь к командиру звена Бракку."}},
[10243] = {"Технология наару", nil, {"Зайдите в манагорн Б'наар и найдите панель управления манагорном.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10244] = {"Учи матчасть!", nil, {"Отнесите инструкцию по созданию сквернобота Мамаше Колесун, находящейся на заставе в заповеднике \"Срединные земли\" в Пустоверти."}},
[10245] = {"Описание панели управления Б'наара", nil, {"Передайте описание панели управления Б'наара анахорету Карже в Зону 52 в Пустоверти.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10246] = {"Нападение на манагорн Коруу", nil, {"Отправляйтесь к манагорну Коруу и убейте 8 чародеев и 5 ученых из армии Ярости Солнца. По завершении задачи возвращайтесь к экзарху Орелису.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10247] = {"Вомиза, доктор псевдотехнических наук", nil, {"Поговорите с доктором псевдотехнических наук Вомизой на Испытательном полигоне в Пустоверти."}},
[10248] = {"Ты, робот", nil, {"Активируйте пульт управления \"Утилизатора Х6000\" и проверьте его возможности. Затем передайте результаты доктору псевдотехнических наук Вомице, находящемуся на испытательном полигоне в Пустоверти. Если возникнут проблемы, разберитесь с ними.."}},
[10249] = {"Обратно к командующему!", nil, {"Немедленно явитесь к ведущему ракетостроителю Фьюзеляжу в Зону 52 в Пустоверти!"}},
[10250] = {"Кровавая месть", nil, {"Подуйте в боевой рог Непреклонных рядом со знаменем Альянса. Убейте Уртрака и возвращайтесь к Альтену Историку на Заставу Хребтолома."}},
[10251] = {"Великий замысел господина?", nil, {"Найдите Нитрина Сведущего в Заброшенной оружейной в Награнде."}},
[10252] = {"Посмотри в глаза мертвым", nil, {"Принесите Нитрину из Заброшенной оружейной Награнда 1 глаз горного гронна, 1 безупречный клюв большого вертуха и 1 сальник старого копытня."}},
[10253] = {"Левиксус Призыватель Душ", nil, {"Убейте Левиксуса и заберите у него Книгу Мертвых.\n\nОтнесите Книгу Рамдору Безумному, который находится неподалеку от входа в Аукенайские гробницы в Аукиндоне.\n\nВы ведь с ним об этом договаривались, помните?"}},
[10254] = {"Командир армии Данат", nil, {"Доставьте по приказу маршала Исилдора донесение командиру армии Данату Троллебою в Оплот Чести."}},
[10255] = {"Проверка противоядия", nil, {"Введите кенарийское противоядие крупному зловепрю и пронаблюдайте за результатом. После проведения эксперимента возвращайтесь к Тие Рыжей Гриве на Кенарийскую заставу на полуострове Адского Пламени."}},
[10256] = {"Найти Хранителя Ключей", nil, {"Выследите Камнегора и заберите у него кристальное средоточие. Вернитесь к Аметистовой башне и используйте кристальное средоточие Камнегора поблизости от шара верховного мага Варгота для того, чтобы обнаружить, у кого хранятся ключи от проклятия башни."}},
[10257] = {"Захват краеугольного камня", nil, {"Проникните в Ботанику в Крепости Бурь и заберите ключ у командира Сараннис. Отнесите ключ верховному магу Варготу в Аметистовую башню."}},
[10258] = {"Честь Павших", nil, {"Явитесь к командиру Хогарду, который находится в оружейной экспедиции."}},
[10259] = {"Битва в Проломе", nil, {"Убейте 6 вторгшихся стражей Скверны и затем вернитесь к посланнику ордена Серебряного Рассвета Праудвелл."}},
[10260] = {"Пустолог Медноклеппер", nil, {"Поговорите с Пустологом Медноклеппером у моста, ведущего от Пустоверти к Острогорью."}},
[10261] = {"Разыскивается: аннигиляторный сервопривод!", nil, {"Заберите аннигиляторный сервопривод и доставьте его Папаше Колесуну в Зону 52 Пустоверти."}},
[10262] = {"Груда духов Астрала", nil, {"Соберите 10 знаков отличия братства Заксис и принесите их ловчему Пустоты Кай'джи, находящемуся в Зоне 52 в Пустоверти."}},
[10263] = {"Содействие Консорциуму", nil, {"Поговорите с ловчим Пустоты Кай'джи в Зоне 52 в Пустоверти."}},
[10264] = {"Содействие Консорциуму", nil, {"Поговорите с ловчим Пустоты Кай'джи в Зоне 52 в Пустоверти."}},
[10265] = {"Добыча артефакта для Консорциума", nil, {"Заполучите кристаллический артефакт Арклона и верните его ловчему Пустоты Кай'джи, находящемуся в Зоне 52 в Пустоверти."}},
[10266] = {"Запрос о содействии", nil, {"Найдите Гаруджа и предложите ему свои услуги. Он находится на заставе внутри заповедника \"Срединные земли\" в Пустоверти."}},
[10267] = {"Изъятие по праву", nil, {"Заберите 10 ящиков с геодезическим снаряжением и верните их Гаруджу, находящемуся на заставе внутри заповедника \"Срединные земли\" в Пустоверти."}},
[10268] = {"Аудиенция у принца", nil, {"Доставьте геодезическое снаряжение к проекции принца нексуса Харамада в Штормовую Вершину в Пустоверти."}},
[10269] = {"Триангуляция: точка первая", nil, {"Используйте триангуляционный прибор, чтобы найти путь к первой точке триангуляции. Как только найдете точку, сообщите ее местонахождение дельцу Хаззину, находящемуся на заставе Протектората на острове манагорна Ультрис в Пустоверти."}},
[10270] = {"Нескромное предложение", nil, {"Найдите аккумулятор для телепортатора и отнесите его к площадке астрального телепорта на Небесной гряде. Активируйте площадку и отдайте аккумулятор проекции торговца ветром Марида, чтобы он мог установить его."}},
[10271] = {"Деловая активность", nil, {"Соберите 8 единиц эссенции дракона Пустоты, затем возвращайтесь в зону площадки астрального телепорта. Эссенцию дракона Пустоты можно получить из старых или молодых драконов Пустоты."}},
[10272] = {"Многообещающее начало", nil, {"Соберите 15 яиц драконидов Пустоты, затем возвращайтесь к Тири и Джораду на Небесную гряду."}},
[10273] = {"Пустые хлопоты", nil, {"Поговорите с торговцем Ветром Маридом и выманите его из торгового поста. Покончите с ним, затем вернитесь к Тири и Джораду на Небесную гряду."}},
[10274] = {"Безопасность Небесной гряды", nil, {"Используйте вызов синих драконов, чтобы вызвать на бой Вераку, живущего на южном склоне Небесной гряды. Победите его и возвращайтесь к Тири и Джораду, ожидающим на Небесной гряде."}},
[10275] = {"Триангуляция: точка вторая", nil, {"Используйте триангуляционный прибор, чтобы найти путь ко второй точке триангуляции. Как только найдете точку, сообщите ее местонахождение торговцу ветром Тулуману, находящемуся в лагере Тулумана, по другую сторону моста, ведущего от острова манагорна Ара в Пустоверти."}},
[10276] = {"Полный треугольник", nil, {"Заберите кристалл Ата'мала и передайте его проекции принца нексуса Харамада, находящейся в Штормовой Вершине в Пустоверти."}},
[10277] = {"Пещеры Времени", nil, {"Андорму попросил вас пройти по пещерам вместе с хранительницей времени."}},
[10278] = {"Астральные провалы", nil, {"Используйте нестабильный генератор астральных провалов на Искривленных полях. Соберите три горсти пыли Пустоты с нестабильных демонов Бездны и возвращайтесь к Огафу Безумному на заставу Хребтолома."}},
[10279] = {"Логово господина", nil, {"Поговорите с Андорму в пещерах Времени."}},
[10280] = {"Специальный груз в город Шаттрат", nil, {"Передайте кристалл Ата'мала А'далу на террасе Света в городе Шаттрат."}},
[10281] = {"Формальное представление", nil, {"Найдите Тири на северной оконечности Небесной гряды и поговорите с ней."}},
[10282] = {"Старый Хилсбрад", nil, {"Отправьтесь по тропе времени в старый Хилсбрад и поговорите с Эрозионом."}},
[10283] = {"План Тареты", nil, {"Проберитесь в крепость Дарнхольд и заложите пять зажигательных бомб возле бочек внутри каждого из бараков на выселках. Используйте упаковку зажигательных бомб, полученных от Эрозиона.\n\nПосле того как подожжете бараки, разыщите Тралла в подземной темнице."}},
[10284] = {"Побег из Дарнхольда", nil, {"Когда вы будете готовы идти, сообщите об этом Траллу. Помогите ему бежать из крепости Дарнхольд, освободить Тарету и исполнить свое предназначение.\n\nПосле этого возвращайтесь к Эрозиону в Старый Хилсбрад."}},
[10285] = {"Возвращение к Андорму", nil, {"Возвращайтесь к маленькому Андорму в Пещеры Времени."}},
[10286] = {"Тайна Арелиона", nil, {"Расспросите магистра Аледиса, которого в последний раз видели отправляющимся из Соколиного дозора в Зангартопь, по требованию магистра Каринды."}},
[10287] = {"Кем оказалась любовница", nil, {"Возвращайтесь к магистру Каринде в Соколиный Дозор, что на Полуострове Адского Пламени."}},
[10288] = {"Прибытие в Запределье", nil, {"Доставьте донесение Дарона Амишу Громовому Молоту по приказу командира Дарона у Темного портала на полуострове Адского Пламени."}},
[10289] = {"Путешествие в Траллмар", nil, {"Долетите на ветрокрыле Влагги Легкоперой в Траллмар. Доставьте донесение Ориона генералу Кракорку."}},
[10290] = {"В поисках фаралита", nil, {"Соберите 4 куска сырого фаралита и вернитесь к Зубену Элгенуби в Штормовую Вершину."}},
[10291] = {"Донесение Назгрелу", nil, {"Поговорите с Назгрелом по приказу генерала Кракорка из Траллмара."}},
[10292] = {"Больше энергии!", nil, {"Зубен Элгенуби хочет, чтобы вы захватили 8 пушек Легиона и принесли их в Штормовую Вершину."}},
[10293] = {"Попасть на главную жилу", nil, {"Зубен Элгенуби, находящийся в Штормовой Вершине, хочет, чтобы вы добыли фаралитовую сердцевину."}},
[10294] = {"Пустынный обрыв", nil, {"Отправляйтесь на Пустынный обрыв и убейте существ, которые там обитают. Соберите 40 осколков душ Пустынного обрыва и возвращайтесь к Огафу Безумному на Заставу Хребтолома."}},
[10295] = {"Сила бездны", nil, {"Убейте Барона Бездны Галаксиса и добудьте осколок его души. Принесите осколок души Огафу Безумному на Заставу Хребтолома."}},
[10296] = {"Черные топи", nil, {"Пройдите сквозь Пещеры времени до Черных топей времен открытия Темного портала и поговорите с Са'атом."}},
[10297] = {"Открытие Темного портала", nil, {"Защищайте Медива, пока он не откроет Темный портал.\n\nКогда выполните задание, возвращайтесь к Са'ату в Черные топи."}},
[10298] = {"Герой драконьего племени", nil, {"Возвращайтесь к маленькому Андорму в пустыню Танарис в Пещерах Времени."}},
[10299] = {"Отключить манагорн Б'наар", nil, {"Вернитесь в манагорн Б'наар и заберите кристалл доступа у надзирателя Тередиса. Используйте его на панели управления манагорна, чтобы выключить его, затем сообщите об этом анахорету Карже. \n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10300] = {"Восстановление посоха", nil, {"Заберите 4 осколка безупречного кристалла у призраков в руинах Энкаата и принесите Равандвиру в Зону 52."}},
[10301] = {"Открыть Компендиум", nil, {"Заберите Халцедоновое око у Рушительницы Чар Маратиль, находящейся в Форте Ярости Солнца."}},
[10302] = {"Неустойчивые мутации", nil, {"Убейте 8 неустойчивых мутаций и вернитесь к ботанику Таэрикс на месте крушения в долине Аммен."}},
[10303] = {"Эльфы крови", nil, {"Убейте 10 эльфов крови – разведчиков и вернитесь к Толаану на хребет Теней в долине Аммен."}},
[10304] = {"Воздаятель Алдар", nil, {"Поговорите с воздаятелем Алдаром на месте крушения в долине Аммен."}},
[10305] = {"Клятвопреступница Белмара", nil, {"Найдите апартаменты клятвопреступницы Белмары в переулке Волшебников и верните книгу на полку. Затем поговорите со смотрителем Дайвортом в деревне Кирин'Вар."}},
[10306] = {"Кудесник Люминрат", nil, {"Найдите апартаменты кудесника Люминрата в переулке Волшебников и верните плащ в его гардероб. Затем поговорите со смотрителем Дайвортом в деревне Кирин'Вар."}},
[10307] = {"Кольен Повелитель Льда", nil, {"Найдите апартаменты Кольена Повелителя Льда в переулке Волшебников и верните шляпу на вешалку. Затем поговорите со смотрителем Дайвортом в деревне Кирин'Вар."}},
[10308] = {"Еще одна группа духов Астрала", nil, nil},
[10309] = {"Это сквернобот, но у него есть сердце!", nil, {"Заберите сердце сквернобота и отнесите Н. Д. Лагерфелту на Груду в Пустоверти."}},
[10310] = {"Саботаж в портале", nil, {"Сопроводите Дризья до точки вторжения: Разрушитель и проследите за безопасностью во время попыток саботажа. Затем поговорите с Гаруджем на заставе в заповеднике \"Срединные земли\" в Пустоверти."}},
[10311] = {"Дризья нужна твоя помощь", nil, {"Поговорите с Дризья, находящимся около Места Вторжения \"Разрушитель\" в Пустоверти."}},
[10312] = {"Анналы Кирин'Вара", nil, {"Заберите анналы Кирин'Вара из ратуши деревни Кирин'Вар и отнесите ее смотрителю Дайворту."}},
[10313] = {"Измерение энергии Искажения", nil, {"Экзарх Орелис, находящийся в Зоне 52, просит вас отнести настроенный на энергию искривления шар к манагорну Б'наар и использовать шар у окончания четырех трубопроводов. \n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10314] = {"Длительные подозрения", nil, {"Соберите 10 омерзительных останков искаженных духов во дворе часовни деревни Кирин'Вар и отнесите их смотрителю Дайворту."}},
[10315] = {"Нейтрализовать чародеев Пустоты", nil, {"Убейте 15 пустомантов клана Ярости Солнца и возвращайтесь к ловчему Пустоты Оазулу в Лагерь Тулумана в Пустоверти."}},
[10316] = {"В поисках доказательств", nil, {"Поищите во дворе часовни в деревне Кирин'Вар свидетельства того, кто именно манипулирует духами убитых."}},
[10317] = {"Покончить со штейгером", nil, {"Покончите со штейгером Закатом Солнца, находящимся в руднике Треллеума под манагорном Ара в Пустоверти."}},
[10318] = {"Покончить с Подчинителем", nil, {"Убейте Подчинителя Гриндгарра. Затем вернитесь к торговцу Ветром Тулуману в Лагерь Тулумана в Пустоверти."}},
[10319] = {"Захватить талисман", nil, {"Заберите филактерию Набериуса из района часовни деревни Кирин'Вар и принесите его смотрителю Дайворту."}},
[10320] = {"Уничтожить Набериуса!", nil, {"Смотритель Дайворт в деревне Кирин'Вар просит вас сразиться с Набериусом, находящимся во дворе часовни, и уничтожить его."}},
[10321] = {"Отключить манагорн Коруу", nil, {"Отправляйтесь в манагорн Коруу к востоку от Зоны 52 и заберите кристалл доступа Коруу у надзирательницы Сейланны. Используйте его на панели управления манагорна Коруу, чтобы отключить манагорн, затем вернитесь к анахорету Карже.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10322] = {"Отключить манагорн Даро", nil, {"Анахорет Каржа просит вас отправиться в манагорн Даро и забрать кристалл доступа у надзирателя Атанеля. Используйте этот кристалл на панели управления манагорном Даро, чтобы отключить его.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10323] = {"Отключить манагорн Ара", nil, {"Отправляйтесь в манагорн Ара и заберите кристалл доступа у надзирателя Азарада. Используйте его на панели доступа манагорна Ара, чтобы отключить его.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10324] = {"Великая охота на лунных оленей", nil, {"Принесите 6 шкур самцов лунного оленя Актеону с Лазурной заставы."}},
[10325] = {"Знаки Кил'джедена", nil, {"Принесите 10 знаков Кил'джедена Адуину Стражу Света в Шаттрат.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10326] = {"Больше знаков Кил'джедена", nil, nil},
[10327] = {"Знак Кил'джедена", nil, nil},
[10328] = {"Инструкции Ярости Солнца", nil, {"Отправляйтесь в манагорн Даро и заберите военные инструкции Ярости Солнца и чародейские инструкции Ярости Солнца. По завершении задачи возвращайтесь к экзарху Орелису.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10329] = {"Отключить манагорн Б'наар", nil, {"Вернитесь в манагорн Б'наар и заберите кристалл доступа у надзирателя Тередиса. Используйте его на панели управления манагорна Б'наар, чтобы выключить его, затем сообщите об этом патрону шпионов Талодиену. \n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10330] = {"Отключить манагорн Коруу", nil, {"Заберите кристалл доступа Коруу у надзирателя Сейланны. Используйте его на панели управления Коруу, чтобы отключить манагорн, затем вернитесь к Каледису Сияющему Рассвету.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10331] = {"Необходимые инструменты", nil, {"Заберите кузнечный молот у кирин'варских учеников и отнесите обратно ученику Андретану в деревню Кирин'Вар."}},
[10332] = {"Мастер-кузнец Ронсус", nil, {"Ученик Андретан из деревни Кирин'Вар просит вас убить призрак Ронсуса, мастера-кузнеца поселения."}},
[10333] = {"Помощь Мамаше Колесун", nil, {"Поговорите с Мамашей Колесун, находящейся на заставе в заповеднике \"Срединные земли\" в Пустоверти."}},
[10334] = {"Колокольчик для коровы", nil, {"Найдите Бесси в полях близ Часовенного холма деревни Кирин'Вар в Пустоверти."}},
[10335] = {"Исследование руин", nil, {"Разместите три геодезических знака, затем возвращайтесь к Зефириону в Штормовую Вершину в Пустоверти."}},
[10336] = {"Приспешники Кулутаса", nil, {"Убейте 10 псов Кулутаса и 5 злобоглазов Кулутаса. Затем вернитесь к ловчему Пустоты Наутису, находящемуся в Штормовой Вершине в Пустоверти."}},
[10337] = {"Когда коровы возвращаются домой", nil, {"Благополучно сопроводите Бесси к ее владельцу, Таделлу."}},
[10338] = {"Отключить манагорн Даро", nil, {"Вернитесь в манагорн Даро и заберите кристалл доступа у надзирателя Атанеля. Используйте его на панели управления манагорна Даро, чтобы отключить его, затем сообщите об этом патрону шпионов Талодиену. \n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10339] = {"Братство Эфириум", nil, {"Командир Амир просит вас уничтожить 5 убийц из Эфириума, 5 штормовиков из Эфириума, 2 ученых Эфириума и капитана Зовакса из Эфириума, затем включить передатчик Эфириума Зета."}},
[10340] = {"Парящая застава", nil, {"По приказу командира передовой Кингстона поговорите с командиром звена Даби'ри, доберитесь на грифоне до Парящей заставы и поговорите с Рунтогом Громовым Молотом."}},
[10341] = {"Лежачего не бьют – лежачего пинают", nil, {"Убейте 8 кудесников, 6 лучников и 4 центурионов клана Ярости Солнца. После завершения задачи вернитесь к магистру Ларинне, находящейся в Зоне 52.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10342] = {"Добыча кожи глиношкурых", nil, {"Соберите 5 кусков кожи глиношкурых и отдайте Бутсу в Зоне 52 в Пустоверти."}},
[10343] = {"Бесконечное вторжение", nil, {"Обыщите кишащий мана-тварями район деревни Кирин'Вар в поисках фрагмента мана-бомбы и принесите его лейтенант-колдуну Моррану."}},
[10344] = {"Командир звена Грифонгар", nil, {"Поговорите с командиром звена Грифонгаром на Парящей заставе."}},
[10345] = {"Усеял мертвыми костями...", nil, {"Примените поджигатель Протектората к 12 высохшим трупам. Когда задача будет завершена, верните поджигатель Протектората посланнику Араксису, находящемуся у входа в шахту Зеон."}},
[10346] = {"Возвращение на Косогор Бездны", nil, {"Уничтожьте 20 ган'аргов-батраков, 5 мо'аргов-надзирателей и 5 пушек Скверны по просьбе грифоньера Ветрорева с Парящей заставы на полуострове Адского Пламени."}},
[10347] = {"Возвращение на Косогор Бездны", nil, {"Уничтожьте 20 ган'аргов-батраков, 5 мо'аргов-надзирателей и 5 пушек Скверны по просьбе командира звена Бракка из Гибели Сквернобота на полуострове Адского Пламени."}},
[10348] = {"Новые возможности", nil, {"Соберите 15 \"кремовых колокольчиков\" и вернитесь к Мердаду на Заставу срединных земель."}},
[10349] = {"Землепряд", nil, {"Отнесите осколок алого хрусталя заклинательнице земли Галандрии Ночной Бриз на Кенарийскую заставу на полуострове Адского Пламени."}},
[10350] = {"Бехомат", nil, {"Поговорите с Бехоматом в Торговом ряду Экзодара."}},
[10351] = {"Целительная сила природы", nil, {"Посадите Семя Возрождения в Круге землепряда, чтобы исцелить землю вокруг кристалла. После этого возвращайтесь к землепряду Галандрии Ночной Бриз на Кенарийскую заставу и расскажите о том, что получилось."}},
[10352] = {"Пожертвование: шерсть", nil, nil},
[10353] = {"Арконус Ненасытный", nil, {"Посланник Иасикс, находящийся у входа в шахту Зеон, просит вас убить Арконуса Ненасытного и вернуться к командиру Амиру на заставу стражи Протектората в Пустоверти."}},
[10354] = {"Пожертвование: шелк", nil, nil},
[10355] = {"Иссохшее тело", nil, {"Соберите 5 образцов опаленной гидры и 5 образцов ткани высохшего болотника и принесите их Руаму в Телредор."}},
[10356] = {"Пожертвование: магическая ткань", nil, nil},
[10357] = {"Пожертвование: руническая ткань", nil, nil},
