[10476] = {"Лютые враги", nil, {"Принесите стражу-смотрителю Мой'прну Джиллу 10 обсидиановых боевых ожерелий, снятых с огров из Награнда."}},
[10477] = {"Боевые ожерелья", nil, nil},
[10478] = {"Боевые ожерелья!", nil, nil},
[10479] = {"Покажи свою силу", nil, {"Принесите 10 обсидиановых боевых ожерелий, снятых с огров, населяющих Награнд, стражу-смотрителю Быккроку в Гарадар."}},
[10480] = {"Разъяренные бесы воды", nil, {"Используйте тотем духов, чтобы поймать 5 бесов воды для целителя земли Торлока у Алтаря Проклятия в Долине Призрачной Луны."}},
[10481] = {"Разъяренные бесы воздуха", nil, {"Поймайте с помощью тотема духов 10 бесов воздуха для целителя земли Торлока из Алтаря Проклятия."}},
[10482] = {"Падальщики орков Скверны", nil, {"Убейте 20 орков из клана Костеглодов и возвращайтесь к Амади в Оплот Чести."}},
[10483] = {"Дурные предзнаменования", nil, {"Принесите 1 проклятый талисман капралу Железному Хребту в лагерь экспедиции по просьбе лейтенанта Амади в Оплоте Чести."}},
[10484] = {"Проклятые талисманы", nil, {"Принесите 12 проклятых талисманов капралу Железному Хребту в Лагерь экспедиции."}},
[10485] = {"Воевода клана Кровавой Глазницы", nil, {"Принесите разбитые доспехи воеводы Морха капралу Железному Хребту в лагерь экспедиции."}},
[10486] = {"Угроза хищников", nil, {"Убейте 12 острокрылых кровопускателей."}},
[10487] = {"Пыльца волшебных драконов", nil, {"Соберите пыльцу с крыльев 4 волшебных драконов."}},
[10488] = {"Защитить свой дом", nil, {"Помажьте мазью Гор'дрека 5 лютых волков клана Громоборцев."}},
[10489] = {"Свалить ходячее древо!", nil, {"Добудьте ствол Лесодрева Глубокорня."}},
[10490] = {"Зов Воды", nil, {"Найдите на острове Лазурной Дымки в Кристальном зале Экзодара предсказателя Нобундо и поговорите с ним."}},
[10491] = {"Зов воздуха", nil, {"Поговорите с предсказателем Нобундо в Кристальном зале Экзодара на острове Лазурной Дымки."}},
[10492] = {"Серьезное предложение", nil, {"Добудьте 15 порций крови животных Зимних Ключей и 20 золотых и отдайте их вместе с наручниками Стихий Делиане в Стальгорне."}},
[10493] = {"Серьезное предложение", nil, {"Добудьте 15 порций яда силитида и 20 золотых и принесите их вместе с наручами из светлостали Моквару в Оргриммар."}},
[10495] = {"Справедливое вознаграждение", nil, {"Принесите Моквару в Оргриммар пояс из светлостали и рукавицы из светлостали."}},
[10497] = {"Прощальные слова Антиона", nil, {"Отдайте Моквару в Оргриммаре сапоги из светлостали, ножные латы из светлостали и наплеч из светлостали."}},
[10499] = {"Сладкое – на закуску", nil, {"Отдайте Моквару шлем из светлостали и кирасу из светлостали."}},
[10500] = {"Орде нужна твоя помощь!", nil, {"Поговорите с вождем Горчуком на аллее Духов Оргриммара."}},
[10501] = {"Альянсу нужна твоя помощь!", nil, {"Поговорите с фельдмаршалом Снежной Лавиной, который находится в Палатах войны Стальгорна."}},
[10502] = {"Клан Кровавого Молота", nil, {"Убейте 30 любых огров Кровавого Молота на заставе Кровавого Молота."}},
[10503] = {"Угроза Камнерогов", nil, {"Убейте 30 любых огров из клана Камнерогов и 10 камнерогих ящеров в Форте Камнерогов и на землях Камнерогов."}},
[10504] = {"Огры из клана Камнерогов", nil, {"Убейте 30 любых огров из клана Камнерогов в Форте Камнерогов и на землях Камнерогов."}},
[10505] = {"Клан Кровавого Молота", nil, {"Убейте 30 любых огров Кровавого Молота на заставе Кровавого Молота."}},
[10506] = {"Тяжелая ситуация", nil, {"Рина Лунный Родник хочет, чтобы вы взяли ее уменьшающий порошок и посыпали им 5 лютых волков клана Кровавого Молота, которых можно найти на заставе и в лощине Кровавого Молота."}},
[10507] = {"Поворотная точка", nil, {"Используйте камень телепортации Сокретара в точке вторжения: Властитель к северу от базы Легиона: Забвение, чтобы перенести свой отряд в лагерь Сокретара. Оказавшись там, используйте \"Присутствие Ворен'таля\", чтобы победить Сокретара. \n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10508] = {"Дар для Ворен'таля", nil, {"Патрон шпионов Талодиен, находящийся в Зоне 52, просит вас забрать первую половину камня Сокретара у начальника кузни Моруга с базы Легиона: Забвение и вторую половину камня Сокретара у Силрота с базы Легиона: Геенна. \n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10509] = {"И все ради славы", nil, {"Отнесите камень телепортации Сокретара Ворен'талю Предсказателю на ярус Провидцев в Шаттрате. \n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10510] = {"В дренетистовые копи", nil, {"Соберите 5 кристаллов дренетиста."}},
[10511] = {"Странное пиво", nil, {"Добудьте 11 бочонков забористого пойла Кровавого Молота."}},
[10512] = {"Пусть Камнероги упьются!", nil, {"Возьмите бочонок забористого пойла Кровавого Молота и попытайтесь напоить допьяна 5 огров Камнерогов."}},
[10513] = {"Оронок Горемычный", nil, {"Найдите Оронока Горемычного на Разрушенной Отмели, на северном берегу водохранилища Змеиных Колец."}},
[10514] = {"Кем только я не был...", nil, {"Принесите 10 клубней долины Призрачной Луны с Разоренных равнин Ороноку Горемычному на его ферму.\n\nЕму также необходимо вернуть его свисток."}},
[10515] = {"Усвоенный урок", nil, {"Уничтожьте 10 яиц прожорливого камнедера на Разоренных равнинах для Оронока Горемычного."}},
[10516] = {"Вещи Поборницы", nil, {"Добудьте клинок и щит воздаятельницы Вуулины."}},
[10517] = {"Горр'Дим, твой час пробил...", nil, {"Убейте Горр'Дима, вождя огров форта Камнерогов."}},
[10518] = {"Водружение знамени", nil, {"Водрузите знамя Камнерогов на башне Северного Молота, чтобы раздразнить Гурна Обжору. Добудьте знамя и шлем Гурна."}},
[10519] = {"Код Проклятия – правда и миф", nil, {"Поговорите с Ороноком Горемычным на его ферме и выслушайте историю, которую он вам поведает."}},
[10520] = {"Помощь верховному друиду Оленьему Шлему", nil, {"Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнасе."}},
[10521] = {"Гром'тор, сын Оронока", nil, {"Найдите Гром'тора сына Оронока в лагере Змеиных Колец в долине Призрачной Луны."}},
[10522] = {"Код Проклятия: Вклад Гром'тора", nil, {"Найдите первый фрагмент Кода Проклятия для Гром'тора, сына Оронока из лагеря Змеиных Колец в долине Призрачной Луны."}},
[10523] = {"Код Проклятия: Первый Фрагмент", nil, {"Принесите Сейф Гром'тора Ороноку Горемычному на ферму Оронока в Долине Призрачной Луны."}},
[10524] = {"Сокровища клана Громоборцев", nil, {"Добудьте барабан, стрелу и табличку клана Громоборцев."}},
[10525] = {"Путеводный огонек", nil, {"При помощи фрагмента пылающей души превратите свой дух в путеводный огонек, который приведет вас туда, где находится последнее сокровище Громоборцев."}},
[10526] = {"Громовая пика", nil, {"Добудьте Громовую пику."}},
[10527] = {"Ар'тор, сын Оронока", nil, {"Найдите Ар'тора, сына Оронока, на аванпосте иллидари в долине Призрачной Луны."}},
[10528] = {"Тюрьма из демонических кристаллов", nil, {"На аванпосте иллидари разыщите и убейте госпожу боли Габриссу и вернитесь к телу Ар'тора, сына Оронока, с хрустальным ключом."}},
[10529] = {"Путь охотника", nil, {"Поговорите с лейтенантом Вестницей Зари в Обители Странников, что в Лесах Вечной Песни."}},
[10530] = {"Путь охотника", nil, {"Поговорите с лейтенантом Вестницей Зари в Обители Странников, что в Лесах Вечной Песни."}},
[10531] = {"Битва за Низину Арати", nil, {"Нападите на рудник, лесопилку, кузницу и ферму и возвращайтесь к фельдмаршалу Освету в Опорный пункт."}},
[10532] = {"Перекрыть линии поставок Аратора", nil, {"Выиграйте битву за низину Арати, добудьте ящик с ресурсами из Арати и принесите его стражу смерти Мортису в Павший Молот."}},
[10534] = {"Возвращение домой", nil, {"Кедмос из Экзодара просит вас прийти поговорить с ним."}},
[10535] = {"Ресурсы Низины Арати", nil, {"Выиграйте битву за низину Арати, добудьте ящик с ресурсами из Арати и принесите его сержанту Маклир в Опорный пункт."}},
[10537] = {"Лон'горон, лук Горемычного", nil, {"Отберите у демонов Лон'горон, лук Горемычного, и верните его духу Ар'тора на аванпосте иллидари."}},
[10538] = {"Кипящая кровь", nil, {"Поместите кровь орков из клана Кровавой Глазницы в проклятый котел, чтобы получить кипящую кровь. Принесите 12 образцов кипящей крови аптекарю Альбреку на заставу Хребтолома."}},
[10539] = {"Возвращение домой", nil, {"Кедмос из Экзодара просит вас прийти и поговорить с ним."}},
[10540] = {"Код Проклятия: Вклад Ар'тора", nil, {"Заберите вторую часть Кода Проклятия у Венератуса Многого для духа Ар'тора с аванпоста иллидари. Существа, убитые или раненые охотником на духов, не приносят вам ни добычи, ни опыта."}},
[10541] = {"Код Проклятия – Второй фрагмент", nil, {"Отнесите сейф Ар'тора Ороноку Горемычному на ферму Оронока в Долине Призрачной Луны.\r\n."}},
[10542] = {"У меня сперли кальян и выпивку!", nil, {"Добудьте кальян Т'чали и 10 кружек забористого пойла Кровавого Молота."}},
[10543] = {"Гримнок и Коргаах, я за вами!", nil, {"Убейте Гримнока Дитя Войны и Коргааха."}},
[10544] = {"Чума на оба ваших клана!", nil, {"При помощи сильного губительного фетиша прокляните 5 домов или башен в крепости Камнерогов и 2 на заставе Кровавого Молота."}},
[10545] = {"Бочонок клана Камнерогов", nil, {"С помощью бочонка забористого пойла Кровавого Молота напоите 5 огров из клана Камнерогов."}},
[10546] = {"Борак, сын Оронока", nil, {"Найдите Борака сына Оронока возле лагеря Затмения в долине Призрачной Луны."}},
[10547] = {"Выгодный обмен", nil, {"Найдите тухлое яйцо араккоа и доставьте его Тобиасу Грязежору в город Шаттрат, расположенный на северо-западе леса Тероккар."}},
[10548] = {"Горькая правда", nil, {"Эраланна из Транквиллиона просит вас найти усохшую голову археолога в одном из сундуков Зеб'Телы или Зеб'Новы."}},
[10550] = {"Пучок кровопийки", nil, {"Отнесите пучок кровопийки Бораку сыну Оронока на мост возле лагеря Затмения, в долине Призрачной Луны. \r\n."}},
[10551] = {"Присяга верности Алдорам", nil, nil},
[10552] = {"Присяга верности Провидцам", nil, nil},
[10553] = {"Ворен'таль Предсказатель", nil, {"Поговорите с Ворен'талем в его библиотеке на ярусе Провидцев в Шаттрате.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10554] = {"Ишана", nil, {"Поговорите с Ишаной в святилище Вечного Света на возвышенности Алдоров в Шаттрате.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10555] = {"Недомогание", nil, {"Соберите 18 выдернутых перьев Лашш."}},
[10556] = {"Рисунки", nil, {"При помощи горсти перьев в круге заклинаний Лашш наложите заклинания."}},
[10557] = {"Испытательный полет: Конденсаторий зефира", nil, {"Поговорите с Ралли Быстроцапом и испытайте Конденсанторий зефира."}},
[10558] = {"Уважительное отношение Оплота Чести", nil, {"Командир армии Данат Троллебой из Оплота Чести на полуострове Адского Пламени желает, чтобы вы заслужили уважительное отношение Оплота Чести, продолжив сражаться с орками Скверны в Разрушенных залах цитадели Адского Пламени."}},
[10559] = {"Уважительное отношение Траллмара", nil, {"Заслужите уважение Траллмара, сражаясь с орками Скверны в Разрушенных залах цитадели Адского Пламени по совету Назгрела из Траллмара на полуострове Адского Пламени."}},
[10560] = {"Почтительное отношение Ша'тар", nil, {"Заслужите почтительное отношение Ша'тар, сразившись с силами Кель'таса в Крепости Бурь, после чего поговорите с А'далом в Шаттрате."}},
[10561] = {"Уважение Хранителей Времени", nil, {"Заслужите уважение Хранителей Времени. Для этого необходимо проследить, чтобы никто не нарушил течение времени и не изменил ход прошлых событий в старом Хилсбраде и в Черных топях в пещерах Времени."}},
[10562] = {"Окружены!", nil, {"Убейте 10 боевых инферналов для командира звена Нуаинна из цитадели Громового Молота."}},
[10563] = {"В форт Легиона", nil, {"Отправляйтесь в форт Легиона, найдите там переговорное устройство и, воспользовавшись маскировкой, выведайте планы Легиона. Вернитесь к командиру звена Нуаинну в цитадель Громового Молота, когда выполните задание."}},
[10564] = {"Перебей инферналов!", nil, {"Используйте бомбу Скверны, находясь в центре зеленого знака в форте Легиона, чтобы уничтожить огненных големов. Доложите о выполнении задания командиру звена Нуаинну из цитадели Громового Молота."}},
[10565] = {"Камни Векх'ниров", nil, {"Добудьте кристалл Векх'ниров и отнесите в круг заклинаний Векх'ниров, чтобы зарядить его."}},
[10566] = {"Пробы и ошибки", nil, {"Испытайте все жезлы Дертрока на огре-рабе араккоа, затем возвращайтесь к Дертроку. Для использования каждого жезла понадобится пылающий кристалл Векх'ниров."}},
[10567] = {"Создание подвески", nil, {"Добудьте 6 когтей Рууан'оков и положите их круг оракула Рууан'ока, чтобы призвать Предвестника Ворона. Убейте его и получите подвеску Предвестника."}},
[10568] = {"Таблички Баа'ри", nil, {"Соберите для анахоретки Сейлы 12 табличек Баа'ри. Таблички могут быть где-то закопаны или находиться у Пеплоустов-рабочих.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10569] = {"Развалины Скет'лона", nil, {"Добудьте у темного конклава араккоа и принесите наезднику на грифоне Киерану первую, вторую и третью страницы дневника командира Скет'лона."}},
[10570] = {"Охота на Посла", nil, {"Заберите письмо Ярости Бури на мосту возле лагеря Затмения для Борака сына Оронока."}},
[10571] = {"Старец Орону", nil, {"Найдите в руинах Баа'ри приказы Акамы, адресованные Орону Старцу, и принесите их анахоретке Сейле к алтарю Ша'тар.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10572] = {"Сборка бомбы", nil, {"Добудьте энергетический сердечник и часть обшивки сквернобота и принесите материалы командиру звена Нуаинну из цитадели Громового Молота."}},
[10573] = {"Кузница Смерти", nil, {"Поговорите с штормером Эваном Дикокрылом в башне Кузницы Смерти."}},
[10574] = {"Осквернители из клана Пеплоустов", nil, {"Добудьте 4 фрагмента медальона у Хаалума, Айкенена, Уйлару, и Лакаана и вернитесь к анахоретке Сейле к алтарю Ша'тар в долине Призрачной Луны.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10575] = {"Клеть Стражницы", nil, {"Отправляйтесь в Клеть Стражницы, к югу от руин Баа'ри и расспросите Санору о местонахождении Акамы для анахоретки Сейлы.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10576] = {"Уловки Призрачной Луны", nil, {"Принесите 6 фрагментов доспехов из лагеря Затмения Бораку, сыну Оронока, на мост возле лагеря Затмения в долине Призрачной Луны."}},
[10577] = {"Чего Иллидан хочет, Иллидан получит...", nil, {"Доставьте послание Иллидана главнокомандующему Рууску в лагерь Затмения."}},
[10578] = {"Код Проклятия – Вклад Борака", nil, {"Отберите для Борака, сына Оронока, третью часть Кода Проклятия у Руула Светокрада.\n\nДля выполнения этого задания вам потребуются помощники."}},
[10579] = {"Код Проклятия – Третий фрагмент", nil, {"Принесите сейф Борака Ороноку Горемычному на ферму Оронока в Долине Призрачной Луны."}},
[10580] = {"Куда подевались эти клятые гномы?", nil, {"Обыщите южные районы Зазубренной гряды Острогорья, чтобы найти, где сейчас Тошли и прочие гномы."}},
[10581] = {"Иди по крошечкам!", nil, {"Пройдите по следу из запчастей вверх по склону на Островерхий гребень. Поговорите с Тошли на станции в Острогорье."}},
[10582] = {"Приспешники Совета Теней", nil, {"Убейте 10 стражей и 5 призывателей из Кузницы Смерти для штормера Эвана Дикокрыла."}},
[10583] = {"Судьба Фланиса", nil, {"Разыщите Фланиса Быстрокрыла в Кузнице Смерти, и принесите его сумку штормеру Эвану Дикокрылу в башню Кузницы Смерти."}},
[10584] = {"Спасти преобразователи энергии!", nil, {"C помощью протовольтаического магнетоколлектора поймайте 5 электроменталей."}},
[10585] = {"Чертог Призыва", nil, {"Раздобудьте стихийный подчинитель у кузнеца или ремонтника из Кузницы Смерти и используйте его, чтобы прервать ритуал в чертоге призыва. Доложите о выполнении задания штормеру Эвану Дикокрылу из башни Кузницы Смерти."}},
[10586] = {"Смерть вестнику войны!", nil, {"Убейте Разууна Завоевателя, заберите его приказы и вернитесь к командиру звена Нуаинну в цитадель Громового Молота."}},
[10587] = {"Тренировочная площадка Карабора", nil, {"Отберите для экзарха Онаалы у соискателей и посвященных охотников на демонов, сосредоточенных в руинах Карабора, 8 глеф армии Ярости Солнца.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10588] = {"Код Проклятия", nil, {"Используйте Код Проклятия у Алтаря Проклятия, чтобы призвать Цируха Повелителя Огня.\n\nУничтожьте его и пообщайтесь с целителем земли Торлоком, которого вы найдете там же."}},
[10589] = {"Получение доступа", nil, {"Принесите пульт от телепорта Легиона командиру звена Нуаинну из цитадели Громового Молота. Пульты можно найти у мо'аргов-оружейников в форте Легиона."}},
[10590] = {"Доказательство ненависти", nil, {"Принесите 20 камней приспешников Плети Мелару Клинку Рассвета из Бастиона."}},
[10592] = {"Мудрость королевы банши", nil, {"Отнесите фрагменты камня Плети Леди Сильване Ветрокрылой в Подгород."}},
[10593] = {"Древнее зло", nil, {"Раскройте тайны храма Атал'Хаккар, освободите Атал'алариона и добудьте гнилую лозу из его плоти. По выполнении задания вернитесь к Мелару в Бастион."}},
[10594] = {"Калибровка резонансной частоты", nil, {"Расположите осцилляционные сканеры таким образом, чтобы стоя среди них вы оказались в точке перекрытия пяти осцилляционных полей."}},
[10595] = {"Окружены!", nil, {"Убейте 10 боевых инферналов для кровавого стража Галмока из деревни Призрачной Луны."}},
[10596] = {"В форт Легиона", nil, {"Отправляйтесь в форт Легиона, найдите переговорное устройство и, замаскировавшись, выведайте планы Легиона. Вернитесь к кровавому стражу Галмоку в деревню Призрачной Луны, когда выполните задание."}},
[10597] = {"Сборка бомбы", nil, {"Добудьте энергетический сердечник и часть обшивки сквернобота для кровавого стража Галмока из деревни Призрачной Луны."}},
[10598] = {"Перебей инферналов!", nil, {"Используйте бомбу Скверны, находясь в центре зеленого знака в форте Легиона, чтобы уничтожить инферналов. Доложите о выполнении кровавому стражу Галмоку из деревни Призрачной Луны."}},
[10599] = {"Кузница Смерти", nil, {"Поговорите с разведчиком Заграном на Вышке на золе."}},
[10600] = {"Приспешники Совета Теней", nil, {"Убейте 10 стражей и 5 призывателей из Кузницы Смерти для разведчика Заграна с Вышки на золе."}},
[10601] = {"Судьба Кагроша", nil, {"Разыщите Кагроша в Кузнице Смерти и принесите его сумку разведчику Заграну на вышку на золе."}},
[10602] = {"Чертог Призыва", nil, {"Раздобудьте стихийный подчинитель у кузнеца или ремонтника из Кузницы Смерти и используйте его, чтобы прервать ритуал в чертоге призыва. Доложите о выполнении задания разведчику Заграну на Вышке на золе."}},
[10603] = {"Смерть вестнику войны!", nil, {"Убейте завоевателя Разууна, добудьте у него приказы Разууна и вернитесь к кровавому стражу Галмоку в деревню Призрачной Луны."}},
[10604] = {"Получение доступа", nil, {"Принесите пульт от телепорта Легиона кровавому стражу Галмоку в деревню Призрачной Луны. Пульты можно найти у мо'аргов-оружейников в форте Легиона."}},
[10605] = {"Призыв Карендина", nil, {"Поговорите с Карендином Халгаром в Подгороде."}},
[10606] = {"Искусство ухода за скверноботом", nil, {"Убейте доктора Потрошилло и заберите у него ключ. Откройте этим ключом сейф доктора и заберите книгу \"Искусство ухода за скверноботом\". Принесите ее Плекси в Точку вторжения: Катаклизм."}},
[10607] = {"Шепот Бога-ворона", nil, {"Убейте араккоа Гришны, чтобы на две минуты обрести Понимание речи Ворона. Находясь под действием этого заклятия, вы можете найти и получить пророчества в четырех святилищах."}},
[10608] = {"Ясно как день!", nil, {"Убейте 10 кристальных камнедеров."}},
[10609] = {"Что было первым, дракон или яйцо?", nil, {"Соберите 3 сущностей прародителя драконов Пустоты, 3 субстанции взрослого дракона и 3 субстанции зрелого дракона."}},
[10611] = {"Искусство ухода за скверноботом", nil, {"Убейте доктора Потрошиллера и заберите у него ключ. Откройте этим ключом сейф доктора и заберите книгу \"Искусство ухода за скверноботом\". Принесите его Наканси в Точку вторжения: Катаклизм."}},
[10612] = {"Машина для убийства", nil, {"Используйте панель управления скверноботом, чтобы захватить контроль над скверноботом-часовым. Уничтожьте 60 инферналов из Кузницы Смерти, прежде чем ваш сквернобот падет. По выполнении вернитесь к Плекси в точку вторжения: Катаклизм."}},
[10613] = {"Машина для убийства", nil, {"Используйте панель управления скверноботом, чтобы захватить контроль над скверноботом-часовым. Уничтожьте 60 инферналов из Кузницы Смерти, прежде чем ваш сквернобот падет. По выполнении вернитесь к Наканси в точку вторжения: Катаклизм."}},
[10614] = {"Шепот ветра", nil, {"Поговорите с Леороксом в деревне Мок'Натал в Острогорье."}},
[10615] = {"Чащоба Рууан", nil, {"Поговорите с Времионом в Вечной роще, что в чащобе Рууан в Острогорье."}},
[10616] = {"<nyi>Breadcrumb", nil, nil},
[10617] = {"Коконы шелкокрыла", nil, {"Добудьте 8 коконов шелкокрыла с личинок шелкокрыла.\n\nБыстро убейте личинок, пока те не начали превращаться в шелкокрылов, прогрызая свои коконы."}},
[10618] = {"Нежнейшие крылья", nil, {"Соберите 12 радужных крыльев."}},
[10619] = {"Клан Пеплоустов", nil, {"Убейте для воздаятеля Алуумена 3 укротителей, 4 воинов и 6 шаманов из племени Пеплоустов.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10620] = {"Угроза горного хребта", nil, {"Убейте 10 пауков высокогорья."}},
[10621] = {"Осколок погибели Иллидари", nil, {"Принесите осколок проклятия иллидари Ординну Громовому Кулаку в цитадель Громового Молота."}},
[10622] = {"Доказательство верности", nil, {"Убейте Зандраса в Клети Стражницы и возвращайтесь к Санору."}},
[10623] = {"Осколок погибели Иллидари", nil, {"Принесите осколок проклятия иллидари Грокому Мертвому Глазу в деревню Призрачной Луны."}},
[10624] = {"История про привидений...", nil, {"Принесите главному аптекарю Хильдегард из деревни Призрачной Луны 15 горстей неостывающего пепла."}},
[10625] = {"Спектроскоп", nil, {"Убейте для главного аптекаря Хильдегард из деревни Призрачной Луны 12 ревнителей из клана Призрачной Луны."}},
[10626] = {"Сдать оружие!", nil, {"Принесите глефу Маказрадона и глефу Моргророна Ординну Громовому Кулаку в цитадель Громового Молота."}},
[10627] = {"Сдать оружие!", nil, {"Принесите глефу Мазакрадона и глефу Моргророна Грокому Мертвому Глазу в деревню Призрачной Луны."}},
[10628] = {"Акама", nil, {"Поговорите с Акамой в тайной комнате Клети Стражницы."}},
[10629] = {"Шизанутая работенка", nil, {"Используйте свисток гончей Скверны, чтобы призвать гончую. Выведите ее на прогулку и убейте парочку безумных зловепрей. Поищите ключи от крошшера в помете гончей Скверны. Верните ключи штейгеру Разлкразу, который находится на руднике к северо-западу от Траллмара."}},
[10630] = {"Рядом с Траллмаром", nil, {"Убейте Урга'зза и возвращайтесь к штейгеру Разлкразу на рудник к северо-западу от Траллмара."}},
[10632] = {"Точи зубы!", nil, {"Соберите 5 острейших зубов Кинжальной Пасти."}},
[10633] = {"Терон Кровожад – Правда и Вымысел", nil, {"Найдите древнего духа долины Призрачной Луны для главного аптекаря Хильдегард из деревни Призрачной Луны в одноименной долине. Если вы потеряете свой спектроскоп, Хильдегард может сделать вам новый."}},
[10634] = {"Прорицание: Броня Кровожада", nil, {"Завладейте броней Кровожада по просьбе древнего духа Долины Призрачной Луны, что обитает у Алтаря Теней."}},
[10635] = {"Прорицание: Плащ Кровожада", nil, {"Завладейте плащом Кровожада по просьбе древнего духа Долины Призрачной Луны, что обитает у Алтаря Теней."}},
[10636] = {"Прорицание: буздыхан Кровожада", nil, {"Завладейте буздыханом Кровожада по просьбе древнего духа Долины Призрачной Луны, что обитает у Алтаря Теней."}},
[10637] = {"Необходимая уловка", nil, {"Вернитесь в Руины Карабора и освободите для экзарха Онаалы Разрушителя Азалота.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10638] = {"NOT A QUEST", nil, {"Seek <NAME> in the <DISTRICT> of Silvermoon City."}},
[10639] = {"Терон Кровожад, то есть я...", nil, {"Древний дух долины Призрачной Луны обвел вас вокруг пальца! Вы сделали все, что он просил, но оказалось, что этот дух – сам Терон Кровожад. Чтобы освободиться от его власти вы должны выполнить его распоряжение и убить Карсиуса Дозорного Древности.\n\nПо выполнении задания вернитесь к главному аптекарю Хильдегард в деревню Призрачной Луны."}},
[10640] = {"Алтруис", nil, {"Отправляйтесь в западный Награнд и найдите Алтруиса Страдальца.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10641] = {"Против Легиона", nil, {"Алтруис Страдалец просит вас добыть свежей крови зловещей жрицы на базе Легиона: Геенна в Пустоверти. Пролейте кровь на землю и убейте аватару Саталь. По выполнении задания вернитесь к Алтруису."}},
[10642] = {"Дух из машины", nil, {"Принесите 15 горстей неостывающего пепла Зорусу Ревнителю в цитадель Громового Молота в долине Призрачной Луны."}},
[10643] = {"Вестники Призрачной Луны", nil, {"Убейте 12 предвестников из клана Призрачной Луны для Зоруса Ревнителя из цитадели Громового Молота."}},
[10644] = {"Терон Кровожад – Правда и Вымысел", nil, {"Найдите древнего духа долины Призрачной Луны для Зоруса Ревнителя в цитадели Громового Молота. Если вы потеряете свой спектроскоп, Зорус может сделать вам новый."}},
[10645] = {"Терон Кровожад, то есть я...", nil, {"Древний дух долины Призрачной Луны обвел вас вокруг пальца! Вы сделали все, что он просил, но оказалось, что этот дух – сам Терон Кровожад. Чтобы освободиться от его власти вы должны выполнить его распоряжение и убить Карсиуса Дозорного Древности.\n\nПо выполнении задания вернитесь к Зорусу Ревнителю в цитадель Громового Молота."}},
[10646] = {"Ученик Иллидана", nil, {"Выслушайте рассказ Алтруиса."}},
[10647] = {"Разыскивается: Увурос, бич долины Призрачной Луны", nil, {"Убейте Увуроса и доставьте его пламенеющую гриву военному глашатаю Сардону Чистому Удару в деревню Призрачной Луны в одноименной долине."}},
[10648] = {"Разыскивается: Увурос, бич долины Призрачной Луны", nil, {"Убейте Увуроса и доставьте его пламенеющую гриву военному глашатаю Пивному Брюху в цитадель Громового Молота в долине Призрачной Луны."}},
[10649] = {"Книга Имен Скверны", nil, {"Проберитесь в Темный лабиринт в Аукиндоне и добудьте у Черносерда Подстрекателя Книгу Имен Скверны. После этого вернитесь в Награнд к Алтруису."}},
[10650] = {"Возвращение к Алдорам", nil, {"Поговорите с экзархом Онаалой у алтаря Ша'тар, в долине Призрачной Луны.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10651] = {"Варедиса нужно остановить!", nil, {"Отправляйтесь в Руины Карабора и убейте Аландиен, Тераса, Нетариеля и самого Варедиса. Используйте Книгу Имен Скверны, когда Варедис будет перевоплощаться, чтобы ослабить его. Вернитесь к экзарху Онаале с книгой Имен Скверны, по выполнении задания.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10652] = {"В тылу врага", nil, {"Поговорите с Веронией, когда будете готовы отбыть в манагорн Коруу. Оказавшись там, поговорите с Каледисом Сияющим Рассветом.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10653] = {"Знаки Саргераса", nil, {"Принесите 10 знаков Саргераса Адуину Стражу Света в Шаттрат.\n\nВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10654] = {"Больше знаков Саргераса", nil, nil},
[10655] = {"Знак Саргераса", nil, nil},
[10656] = {"Перстни Ярости Солнца", nil, {"Принесите магистру Файленн 10 перстней Ярости Солнца.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10657] = {"Оседлать молнию", nil, {"Абсорбируйте 25 ударов молний от чешуекрылых змеев при помощи реполяризованной магнитной сферы. Также добудьте 5 молниевых желез чешуекрылых."}},
[10658] = {"Больше перстней Ярости Солнца", nil, nil},
[10659] = {"Перстень Ярости Солнца", nil, nil},
[10660] = {"Что за странные создания?", nil, {"Добудьте 8 селезенок сквернопламенных деметродонов для ученого Тиоруса в деревне Призрачной Луны в одноименной долине."}},
[10661] = {"Умопотрошительно!", nil, {"Принесите 8 селезенок сквернопламенных деметродонов Гномусу в цитадель Громового Молота."}},
[10662] = {"Кузнец–отшельник", nil, {"Принесите ящик со слитками Дэвиду Уэйну в приют Уэйна, который находится в лесу Тероккар."}},
[10663] = {"Кузнец–отшельник", nil, {"Принесите ящик со слитками Дэвиду Уэйну в приют Уэйна, который находится в лесу Тероккар."}},
[10664] = {"Дополнительные материалы", nil, {"Принесите адамантитовую рамку, 4 куска толстой узловатой кожи и тотем защиты от демонов Дэвиду Уэйну в Приют Уэйна."}},
[10665] = {"Новинка из Механара", nil, {"Принесите избыточно заряженный аккумулятор маны Дэвиду Уэйну в Приют Уэйна."}},
[10666] = {"Лексикон демоника", nil, {"Добудьте книгу \"Лексикон демоника\" у великого мастера Ворпила и принесите его Дэвиду Уэйну в Приют Уэйна."}},
[10667] = {"Суглинок из Нижнего мира", nil, {"Достаньте фиал с суглинком Нижнего мира у гидромантки Теспии и принесите его Дэвиду Уэйну в приют Уэйна."}},
[10668] = {"Против иллидари", nil, {"Алтруис Страдалец просит вас убить Лотроса на аванпосте иллидари в долине Призрачной Луны."}},
[10669] = {"Вопреки всему", nil, {"Алтруис Страдалец хочет, чтобы вы взяли прочное серебряное копье и вонзили его на прогалине с порталом близ озера Болотных Огоньков, чтобы пробудить Кселета. Когда убьете демона, вернитесь к Алтруису."}},
[10670] = {"Слеза Матери-Земли", nil, {"Добудьте слезу Матери-Земли у О'мрогга Завоевателя и принесите ее Дэвиду Уэйну в приют Уэйна."}},
[10671] = {"Не только фунт мяса", nil, {"Добудьте 5 кусков мяса, напитанного энергией Астрала."}},
[10672] = {"Честно говоря, это – бред!", nil, {"Отправляйтесь к потокам лавы возле руин Баа'ри и используйте пульт управления чарами, чтобы контролировать волшебного скорпиона. Переправьтесь на нем через лаву и пометьте 8 больших сквернопламенных деметродонов для ученого Тиоруса."}},
[10673] = {"Хребтоскверн Старший", nil, {"Убейте Хребтоскверна Старшего и принесите его шкуру ученому Тиорусу в деревню Призрачной Луны в одноименной долине."}},
[10674] = {"Ловля фантастического света", nil, {"Поймайте 15 световых сфер братства Разаани при помощи световой ловушки."}},
[10675] = {"Попомните гномскую доброту!", nil, {"Добудьте коллекцию душ."}},
[10676] = {"Проклятие иллидари", nil, {"Подождите, пока Дэвид Уэйн откует для вас клинок."}},
[10677] = {"Второе блюдо...", nil, {"Добудьте для Гномуса из цитадели Громового Молота в долине Призрачной Луны потроха крупного деметродона."}},
[10678] = {"Гвоздь программы", nil, {"Принесите шкуру Хребтоскверна Гномусу в цитадель Громового Молота в долине Призрачной Луны."}},
[10679] = {"Закалка Меча", nil, {"Отправляйтесь к Жгучим прудам возле подножия Руки Гул'дана, и закалите кованый меч погибели иллидари в озерах лавы. После этого возвращайтесь к Дэвиду Уэйну в приют Уэйна."}},
[10680] = {"Рука Гул'дана", nil, {"Поговорите с целителем земли Торлоком у Алтаря Проклятия в Долине Призрачной Луны."}},
[10681] = {"Рука Гул'дана", nil, {"Поговорите с целителем земли Торлоком у Алтаря Проклятия в Долине Призрачной Луны."}},
[10682] = {"Время для переговоров...", nil, {"Переговорите с надзирателем Нуааром."}},
[10683] = {"Таблички Баа'ри", nil, {"Соберите для чародея Тэлиса из святилища Звезд 12 табличек Баа'ри. Таблички могут быть где-то закопаны или находиться у Пеплоустов-рабочих.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10684] = {"Старец Орону", nil, {"Найдите в руинах Баа'ри приказы Акамы, адресованные старцу Орону, и принесите их чародею Телису.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10685] = {"Осквернители из клана Пеплоустов", nil, {"Соберите 4 фрагмента медальона у Хаалума, Айкенена, Уйлару, и Лакаана и вернитесь к чародею Тэлису в святилище Звезд.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10686] = {"Клеть Стражницы", nil, {"Отправляйтесь в Клеть Стражницы, к югу от руин Баа'ри и расспросите Санору о местонахождении Акамы для чародея Телиса.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10687] = {"Тренировочная площадка Карабора", nil, {"Отберите у соискателей и посвященных охотников на демонов, сосредоточенных в руинах Карабора, 8 глеф армии Ярости Солнца. Принесите их Лариссе Солнцесвет в святилище Звезд.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10688] = {"Необходимая уловка", nil, {"Вернитесь в Руины Карабора и освободите Азалота для Лариссы Солнцесвет.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10689] = {"Алтруис", nil, {"Отправляйтесь в западный Награнд и найдите Алтруиса Страдальца.\n\nВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10690] = {"Мать логова", nil, {"Убейте Рему."}},
